INSERT INTO `news` (`id`, `title`, `description`, `content`, `image`, `author`, `created_at`, `category`, `is_featured`, `views`) VALUES
(9, 'Malaysia ủng hộ cách tiếp cận của Việt Nam với Mỹ', 'Thủ tướng Phạm Minh Chính điện đàm với Thủ tướng Anwar Ibrahim, trong đó lãnh đạo Malaysia ủng hộ cách tiếp cận của Việt Nam trong vấn đề với Mỹ.', 'Thủ tướng Anwar Ibrahim chia sẻ rằng Malaysia và nhiều nước trong khu vực hoan nghênh, đánh giá cao Tổng Bí thư Tô Lâm có cuộc điện đàm với Tổng thống Trump, bày tỏ nhất trí và ủng hộ cách tiếp cận của Việt Nam trong vấn đề này.\r\n\r\nÔng Ibrahim cũng thông báo một số biện pháp của Malaysia trên cương vị Chủ tịch ASEAN để giải quyết vấn đề Myanmar, nhấn mạnh chủ trương của Malaysia thúc đẩy đối thoại và đảm bảo đoàn kết, thống nhất trong giải quyết vấn đề Myanmar.\r\n\r\nThủ tướng Phạm Minh Chính hoan nghênh những nỗ lực của Malaysia trên cương vị Chủ tịch ASEAN, đồng thời cho biết về các biện pháp hỗ trợ nhanh chóng của Việt Nam giúp Myanmar ngay sau khi xảy ra động đất, bao gồm cử lực lượng cứu hộ hơn 100 người cùng nhiều trang thiết bị, vật tư y tế, thuốc men đến Myanmar và viện trợ 300.000 USD hỗ trợ nước này.\r\n\r\nThủ tướng khẳng định Việt Nam sẽ tiếp tục tích cực đồng hành với Malaysia và các nước thành viên ASEAN, sẵn sàng làm trung gian, hòa giải trong nỗ lực chung thúc đẩy đối thoại và hòa giải dân tộc tại Myanmar.', '1744216281_h14.webp', 'Hồ Ngọc Vy', '2025-04-06 16:19:13', 'Thế giới', 0, 1),
(20, '\'Khi cuộc đời cho bạn quả quýt\' dẫn đầu đề cử Baeksang', '\"Khi cuộc đời cho bạn quả quýt\" nhận tám đề cử ở lễ trao giải nghệ thuật Baeksang, gồm giải quan trọng - Phim truyền hình hay nhất. ', 'Theo danh sách ban tổ chức công bố hôm 7/4, tác phẩm cạnh tranh các giải gồm: Phim truyền hình xuất sắc, Đạo diễn xuất sắc, Kịch bản xuất sắc, Nam - nữ chính xuất sắc, Nam - nữ phụ xuất sắc và Nữ diễn viên mới xuất sắc.\r\n\r\nPhim gồm 16 tập, lấy bối cảnh ở đảo Jeju, kể về mối tình của Ae Sun (IU đóng) và Gwan Sik (Park Bo Gum). Ae Sun là cô gái thông minh, sinh ra trong gia đình nghèo, có mẹ làm thợ lặn. Cô và chàng trai Gwan Sik vượt nhiều biến cố để xây dựng hạnh phúc gia đình, nuôi nấng con cái.\r\n\r\nÊkíp lồng ghép nhiều chủ đề, từ tình yêu đôi lứa, tình mẫu tử đến xung đột giàu nghèo và ý chí vươn lên của con người. Trên chặng đường trưởng thành, dù kiên trì đến đâu, vẫn có sự cố xảy ra, khiến mục tiêu của họ chệch hướng.\r\n\r\nTừ khi ra mắt hôm 7/3, tác phẩm nhanh chóng vào top 10 phim truyền hình không nói tiếng Anh được xem nhiều nhất trên nền tảng Netflix toàn cầu suốt hai tuần. Theo Nate, phim để lại dấu ấn trong lòng người xem, trong đó có nhiều khán giả gen Z. Diễn xuất của diễn viên chính IU và Park Bo Gum được người hâm mộ thảo luận sôi nổi trên các diễn đàn phim ảnh, mạng xã hội.', '1744216585_h15.jpg', 'Hồ Ngọc Vy', '2025-04-08 15:44:01', 'Giải trí', 0, 2),
(21, 'Đổ xô mua iPhone vì sợ tăng giá do thuế mới', 'Nhiều người đang đến các Apple Store để mua iPhone và sản phẩm khác của Apple vì lo chúng sẽ đắt đỏ hơn.', 'Nhiều người đang đến các Apple Store để mua iPhone và sản phẩm khác của Apple vì lo chúng sẽ đắt đỏ hơn.\r\n\r\n\"Gần như mọi khách hàng đều hỏi tôi rằng, giá iPhone và các sản phẩm AI có sớm tăng hay không\", một nhân viên Apple Store nói với Bloomberg. \"Mọi người có vẻ đều lo lắng và vội vã khi hỏi han sau chính sách thuế mới\".\r\n\r\nTại các cửa hàng Apple, không có cảnh xếp hàng dài nhưng bầu không khí \"tương tự những đợt mở bán sản phẩm\". Một quản lý tiết lộ doanh số thiết bị, chủ yếu là iPhone, đã tăng cao cuối tuần qua, ngay sau khi Tổng thống Mỹ Donald Trump thông báo áp thuế đối ứng với hàng loạt quốc gia.', '1744216628_h16.webp', 'Hồ Ngọc Vy', '2025-04-08 15:45:07', 'Công nghệ', 0, 1),
(22, 'Trần Gia Bảo: \'U17 Việt Nam chẳng còn gì để mất\'', 'Tiền đạo Trần Gia Bảo tin Việt Nam sẽ đánh bại UAE để vào tứ kết U17 châu Á, đồng thời giành vé dự World Cup 2025', 'Sau hai trận, Việt Nam đứng thứ ba với hai điểm, lần lượt kém UAE một điểm và Nhật Bản hai điểm. Vì vậy, chiến thắng trước UAE là điều kiện bắt buộc để Trần Gia Bảo cùng các đồng đội đi tiếp.\r\n\r\n\"Chúng tôi không còn gì để mất và sẽ chơi hết mình để thắng UAE\", Gia Bảo nói ngày 8/4. \"Trước đó, chúng tôi đã đạt kết quả tốt trước Nhật Bản và Australia vốn rất khó chơi\".\r\n\r\nGia Bảo là người hùng của Việt Nam trước Nhật Bản khi kiếm về quả phạt đền ở phút bù thứ tư hiệp hai. Khi thấy hàng thủ đối phương do dự, anh bứt tốc vào vòng cấm kiểm soát bóng rồi bị thủ môn Ibuki Ejike phạm lỗi. Sau đó, chính cầu thủ của HAGL thực hiện thành công với cú đá vào góc thấp bên phải đánh lừa thủ môn Nhật Bản.\r\n\r\n\"Khi đứng trước chấm 11m, tôi tin tưởng bản thân sẽ làm thực hiện tốt nhất\", Gia Bảo cho hay. \"Sau khi bóng vào lưới, suy nghĩ đầu tiên chạy qua đầu tôi là chạy ra ăn mừng cùng đồng đội và các HLV\".\r\n\r\nNhững cầu thủ Việt Nam ít phút trước còn quỳ gối cầu nguyện ở khu kỹ thuật đã chạy một mạch đến cuối sân để ôm nhau ăn mừng. Trong khi đó, tác giả bàn thắng rớm nước mắt. Anh khẳng định bàn thắng là động lực to lớn để thúc đẩy bản thân hoàn thiện hơn nữa trong tương lai.\r\n\r\nBảo là một trong sáu tiền đạo Việt Nam đợt này. Tuy nhiên, anh được xem như quân bài dự bị chiến lược, còn Hoàng Trọng Duy Khang, Nguyễn Văn Bách và Nguyễn Thiên Phú được ưu tiên sử dụng từ đầu. Thể lực của Bảo được đánh giá cần cải thiện thêm. Ở trận ra quân hòa Australia 1-1, anh vào sân từ phút 60, còn trước Nhật Bản là từ phút 66.', '1744216213_h13.webp', 'Hồ Ngọc Vy', '2025-04-08 15:50:06', 'Thể thao', 0, 7),
(24, 'Ông chủ ChatGPT nói gì khi bị chỉ trích vi phạm bản quyền Ghibli?', 'Trước cáo buộc vi phạm bản quyền, CEO OpenAI cho rằng cơn sốt tạo ảnh phong cách Ghibli bằng ChatGPT là \"chiến thắng chung\" cho xã hội', 'Trước cáo buộc vi phạm bản quyền, CEO OpenAI cho rằng cơn sốt tạo ảnh phong cách Ghibli bằng ChatGPT là \"chiến thắng chung\" cho xã hội.\r\n\r\nTrong phỏng vấn với nhà sáng tạo nội dung Varun Mayya ngày 7/4, Sam Altman, CEO OpenAI, được hỏi về làn sóng phản đối sau khi công ty ra tính năng tạo ảnh theo phong cách Studio Ghibli trên ChatGPT.\r\n\r\nĐáp lại, Altman nói \"sự đánh đổi này xứng đáng\" vì AI giúp dễ dàng sáng tạo nghệ thuật hơn so với 30 năm trước. Thời đó, mọi người thường cần đến thiết bị như máy quay phim và băng VHS, chỉnh sửa \"một cách phức tạp\", tìm cách phân phối vì không có Internet hoặc YouTube.\r\n\r\n\"Còn bây giờ, chỉ cần một smartphone và một ý tưởng, bất kỳ ai cũng có thể xuất bản thứ gì đó có ý nghĩa\", CEO OpenAI nói. \"Nếu một người có điều gì thú vị để chia sẻ, họ sẽ đưa nó ra ngoài kia và thế giới sẽ được hưởng lợi từ điều đó\".\r\n\r\nDù thừa nhận AI thay đổi bản chất của nghệ thuật, việc mọi người có thể tiếp cận công cụ sáng tạo dễ dàng là chiến thắng chung cho xã hội, ngay cả khi quá trình chuyển đổi không hoàn hảo. \"Việc cung cấp cho mọi người nhiều công cụ hơn sẽ tạo cho họ cơ hội thực hiện mọi thứ dễ hơn, giảm rào cản và tăng số lượng người có thể đóng góp cho xã hội\", ông nói. \"Nhìn chung, tất cả đều được hưởng lợi từ điều đó\".\r\n\r\nNgày 26/6, OpenAI công bố trình tạo ảnh Images in ChatGPT, sử dụng sức mạnh của mô hình GPT-4o. Bên cạnh việc cho ra ảnh \"như thật\", hỗ trợ chỉnh sửa hay phục hồi ảnh cũ, công cụ còn giúp tạo ảnh hoạt hình theo nhiều phong cách khác nhau như Ghibli, hay tự \"đóng vỉ\" chân dung bản thân.\r\n\r\nTính năng mới nhanh chóng gây sốt, khiến số lượng người dùng chatbot tăng mạnh. Tuy nhiên, công cụ của OpenAI cũng gây lo ngại khi việc tái tạo phong cách tác phẩm có bản quyền trở nên dễ dàng. Evan Brown, luật sư chuyên về sở hữu trí tuệ tại công ty luật Neal & McDevitt, cho rằng sản phẩm như của OpenAI hoạt động trong \"vùng xám pháp lý\", tức không được bảo vệ rõ ràng bởi luật bản quyền.\r\n\r\nTrong buổi nói chuyện với Mayya, Altman dự đoán AI sẽ sớm tự động hóa công việc thiết kế. Tuy nhiên theo ông, công nghệ này cũng mở ra những cơ hội sáng tạo mới, thay vì làm suy giảm công việc liên quan.\r\n\r\n\"Ý tưởng vẫn là yếu tố quan trọng nhất. Chúng ta cần rất nhiều thiết kế đồ họa tốt trên thế giới\", ông nói. \"Nhu cầu về thiết kế sẽ tăng vọt. Có thể ít người làm thiết kế hơn, nhưng họ sẽ kiếm được nhiều tiền hơn\".', '1744214385_h11.webp', 'Hồ Ngọc Vy', '2025-04-09 10:04:10', 'Công nghệ', 1, 40),
(25, 'Nga chỉ trích Mỹ \'coi thường tập quán thương mại\'', 'Bộ Ngoại giao Nga nói đòn thuế mới của ông Trump cho thấy sự coi thường với các tập quán, quy chuẩn quốc tế, lo ngại thương chiến toàn cầu bùng nổ.', 'Bộ Ngoại giao Nga nói đòn thuế mới của ông Trump cho thấy sự coi thường với các tập quán, quy chuẩn quốc tế, lo ngại thương chiến toàn cầu bùng nổ.\r\n\r\n\"Việc áp thuế quan vi phạm quy tắc cơ bản của Tổ chức Thương mại Thế giới (WTO) và chứng minh rằng Mỹ không còn coi mình bị ràng buộc bởi các tập quán của luật thương mại quốc tế\", Maria Zakharova, người phát ngôn Bộ Ngoại giao Nga, nói trong cuộc họp báo ngày 9/4.\r\n\r\nBình luận của Nga được đưa ra cùng ngày mức thuế đối ứng 11-84% của chính quyền Tổng thống Donald Trump bắt đầu có hiệu lực với hàng chục đối tác thương mại lớn của Mỹ. Trước đó, Mỹ áp mức thuế chung 10% với một nửa trong số 180 đối tác thương mại từ ngày 5/4.\r\n\r\n\"Bất kỳ cú sốc nào với nền kinh tế thế giới, đe dọa cản bước tăng trưởng và làm suy giảm mức tiêu thụ đều dẫn tới triển vọng tiêu cực cho nhiều quá trình toàn cầu. Tình hình hiện tại càng dấy lên lo ngại nghiêm trọng hơn khi chúng ta nói về hai nền kinh tế lớn của thế giới\", bà Zakharova nói, dường như đề cập tới cuộc đối đầu thương mại giữa Mỹ và Trung Quốc.\r\n\r\nTrung Quốc từ ngày 9/4 chịu mức thuế đối ứng cao nhất với 84%, nâng tổng mức thuế bổ sung nước này phải chịu trong nhiệm kỳ hai của ông Trump lên 104%. Trung Quốc lập tức tuyên bố sẽ thực hiện các biện pháp \"mạnh mẽ và quyết liệt\" để bảo vệ lợi ích và thông báo áp thuế 84% với hàng hóa nhập khẩu từ Mỹ, có hiệu lực từ ngày 10/4.\r\n\r\nHàng nhập khẩu từ một số đối tác lớn khác của Mỹ như Liên minh châu Âu (EU), Malaysia, Nhật Bản, Hàn Quốc, Ấn Độ cũng chịu thuế 20-26%. Giới chức EU đã chỉ trích động thái của Mỹ là \"giáng đòn vào nền kinh tế thế giới\".\r\n\r\nDù Nga không nằm trong danh sách các nước bị Mỹ áp thuế, Thống đốc Ngân hàng Trung ương Nga Elvira Nabiullina ngày 9/4 mô tả cuộc chiến thương mại đang diễn ra là \"rủi ro đáng kể\", thêm rằng những thay đổi trong thương mại toàn cầu đang xảy ra.\r\n\r\n\"Rất khó để đánh giá xem chúng sẽ đưa nền kinh tế thế giới về đâu và điều đó sẽ ảnh hưởng đến Nga như thế nào. Đây là rủi ro đáng kể mới mà chúng ta phải xem xét\", bà nói trong phiên họp với các nghị sĩ Nga.', '1744208545_h1.webp', 'Hồ Ngọc Vy', '2025-04-09 14:22:25', 'Thế giới', 0, 3),
(26, 'Uzbekistan ký thỏa thuận khoáng sản với Mỹ', 'Uzbekistan ký thỏa thuận cho phép các công ty Mỹ đầu tư vào lĩnh vực khai thác khoáng sản ở nước này để đáp ứng nhu cầu tăng vọt.', 'Uzbekistan ký thỏa thuận cho phép các công ty Mỹ đầu tư vào lĩnh vực khai thác khoáng sản ở nước này để đáp ứng nhu cầu tăng vọt.\r\n\r\nBộ Thương mại Uzbekistan ngày 9/4 cho biết phái đoàn nước này đã ký thỏa thuận khoáng sản quan trọng với các công ty Mỹ tại Washington hôm 7/4.\r\n\r\n\"Thỏa thuận bao gồm các khoản đầu tư vào hoạt động thăm dò và khai thác khoáng sản, gồm cả xây dựng hệ thống máy nghiền và đào tạo chuyên gia Uzbekistan\", Bộ Thương mại tuyên bố.\r\n\r\nTổng thống Shavkat Mirziyoyev đang thúc đẩy tự do hóa nền kinh tế của Uzbekistan sau hơn 20 năm bị cô lập trong thời kỳ của người tiền nhiệm. Ông hồi tháng 3 công bố kế hoạch đầu tư 2,6 tỷ USD cho ngành khoáng sản của Uzbekistan.\r\n\r\nUzbekistan đã thu hút quan tâm từ các nước phương Tây, trong đó có Mỹ, vốn đang tìm cách đa dạng hóa chuỗi cung ứng và nguồn năng lượng, giảm phụ thuộc vào Nga và Trung Quốc.\r\n\r\nQuốc gia Trung Á này có nguồn khoáng sản lớn gồm vàng, đồng, chì, kẽm, uranium, dầu mỏ và khí đốt với trữ lượng lớn. Theo dữ liệu năm 2019, Uzbekistan là quốc gia sản xuất uranium lớn thứ 5 thế giới, sản xuất vàng lớn thứ 12 và sản xuất rhenium lớn nhất thứ 7. Nước này có mỏ vàng lộ thiên lớn nhất thế giới.\r\n\r\n', '1744208694_h2.webp', 'Hồ Ngọc Vy', '2025-04-09 14:24:54', 'Thế giới', 0, 10),
(27, 'Những người Trung Quốc sẵn sàng từ bỏ hàng Mỹ vì thuế quan', 'Người tiêu dùng ở Bắc Kinh đang xem xét lại thói quen mua sắm và cho biết sẵn sàng ngừng mua các sản phẩm từ Mỹ giữa cuộc chiến thuế quan.', 'Người tiêu dùng ở Bắc Kinh đang xem xét lại thói quen mua sắm và cho biết sẵn sàng ngừng mua các sản phẩm từ Mỹ giữa cuộc chiến thuế quan.\r\n\r\nBên ngoài một trung tâm mua sắm ở thủ đô Bắc Kinh, Trung Quốc, chuyên gia massage trị liệu 26 tuổi Gao Xin nghe nhạc trên chiếc iPhone và tự hỏi liệu chiếc điện thoại tiếp theo của anh có phải là một thương hiệu khác hay không.\r\n\r\n\"Tôi trước nay luôn sử dụng các sản phẩm của Mỹ, gồm cả chiếc điện thoại đang dùng. Nhưng nếu thực sự phải đối mặt làn sóng tăng giá lớn, tôi có thể sẽ chọn hàng nội địa\", anh nói.\r\n\r\nNhững người tiêu dùng như Gao lo ngại sản phẩm yêu thích của họ có thể tăng giá sau khi Trung Quốc và Mỹ liên tục áp thuế đáp trả nhau. Trung Quốc từ ngày 9/4 chịu mức thuế đối ứng cao nhất từ Mỹ với 84%, nâng tổng mức thuế bổ sung nước này phải chịu trong nhiệm kỳ hai của Tổng thống Donald Trump lên 104%.\r\n\r\nCùng ngày, Bộ Tài chính Trung Quốc thông báo áp thuế 84% với hàng hóa nhập khẩu từ Mỹ để đáp trả đòn thuế của ông Trump, có hiệu lực từ 10/4.\r\n\r\n\"Tình hình rất đáng lo ngại\", luật sư Yu Yan, 54 tuổi, nói và thêm rằng bà thấy những dấu hiệu suy thoái sau các sự kiện gần đây. \"Nền kinh tế có thể rơi vào suy thoái, đó là điều mà tất cả chúng ta đều không muốn thấy\".\r\n\r\nGao xem mức thuế mà Bắc Kinh và Washington áp với nhau là \"biện pháp răn đe\". \"Tôi nghĩ chắc chắn nó sẽ gây tác động. Nhưng đối với hầu hết những người dân bình thường, đó không phải vấn đề lớn, trừ khi bạn có những hoạt động thương mại nước ngoài\", anh nói.\r\n\r\nSun Fanxi, kỹ sư công nghệ 27 tuổi, cho biết cô thấy lo lắng khi đọc những thông tin về đòn ăn miếng trả miếng thuế quan giữa Mỹ và Trung Quốc.\r\n\r\n\"Tôi thực sự sợ thuế quan sẽ dẫn tới thương chiến khốc liệt. Điều đó không tốt cho tất cả mọi người\", cô nói.\r\n\r\nTuy nhiên, Sun thêm rằng dù bất kỳ chuyện gì xảy ra, cô hoàn toàn ủng hộ các quyết định mà chính phủ Trung Quốc đưa ra. \"Nếu đất nước muốn chúng tôi làm điều gì đó, hãy làm như vậy\", cô nói.\r\n\r\nTrong bài xã luận cuối tuần trước, People\'s Daily, cơ quan ngôn luận của đảng Cộng sản Trung Quốc, mô tả đòn thuế của Mỹ là \"cơ hội chiến lược\" để Trung Quốc coi tiêu dùng là động lực tăng trưởng chính của nền kinh tế.\r\n\r\n\"Chúng ta phải biến áp lực thành động lực\", bài xã luận có đoạn.', '1744208794_h3.webp', 'Hồ Ngọc Vy', '2025-04-09 14:26:34', 'Thế giới', 0, 2),
(28, 'Pháp giữ nhân viên chính phủ Nga tại sân bay', 'Moskva cho biết họ đang chờ \"lời giải thích\" từ Pháp sau khi một nhân viên chính phủ Nga bị giữ suốt nhiều giờ tại sân bay ở Paris.', 'Moskva cho biết họ đang chờ \"lời giải thích\" từ Pháp sau khi một nhân viên chính phủ Nga bị giữ suốt nhiều giờ tại sân bay ở Paris.\r\n\r\nNga hôm nay cho biết một nhân viên làm việc cho Bộ Ngoại giao ngày 6/4 đến Pháp với tư cách thành viên của một phái đoàn chính thức. Tuy nhiên, cảnh sát biên giới Pháp đã tịch thu điện thoại và máy tính của cô sau khi máy bay hạ cánh.\r\n\r\nHọ cũng giữ cô ở khu vực kiểm soát nhập cảnh suốt nhiều giờ, dù cô đã được cấp thị thực Pháp.\r\n\r\n\"Không thể lý giải nổi những gì đã xảy ra tại sân bay Charles de Gaulle. Đại sứ quán Nga đã lập tức cử viên chức lãnh sự đến sân bay. Nhà ngoại giao của chúng tôi phải chờ suốt 7 giờ mới gặp được đồng nghiệp của mình. Chúng tôi không định cứ để chuyện này cứ thế trôi qua\", phát ngôn viên Bộ Ngoại giao Nga Maria Zakharova nói tại cuộc họp báo ở Moskva, gọi sự việc là \"cảnh tượng đáng xấu hổ\".\r\n\r\n\r\nThế giớiThứ tư, 9/4/2025, 19:21 (GMT+7)\r\nPháp giữ nhân viên chính phủ Nga tại sân bay\r\nMoskva cho biết họ đang chờ \"lời giải thích\" từ Pháp sau khi một nhân viên chính phủ Nga bị giữ suốt nhiều giờ tại sân bay ở Paris.\r\n\r\nNga hôm nay cho biết một nhân viên làm việc cho Bộ Ngoại giao ngày 6/4 đến Pháp với tư cách thành viên của một phái đoàn chính thức. Tuy nhiên, cảnh sát biên giới Pháp đã tịch thu điện thoại và máy tính của cô sau khi máy bay hạ cánh.\r\n\r\nHọ cũng giữ cô ở khu vực kiểm soát nhập cảnh suốt nhiều giờ, dù cô đã được cấp thị thực Pháp.\r\n\r\n\"Không thể lý giải nổi những gì đã xảy ra tại sân bay Charles de Gaulle. Đại sứ quán Nga đã lập tức cử viên chức lãnh sự đến sân bay. Nhà ngoại giao của chúng tôi phải chờ suốt 7 giờ mới gặp được đồng nghiệp của mình. Chúng tôi không định cứ để chuyện này cứ thế trôi qua\", phát ngôn viên Bộ Ngoại giao Nga Maria Zakharova nói tại cuộc họp báo ở Moskva, gọi sự việc là \"cảnh tượng đáng xấu hổ\".\r\n\r\nHành khách tại sân bay Charles de Gaulle ở Paris, Pháp ngày 29/3. Ảnh: AFP\r\nHành khách tại sân bay Charles de Gaulle ở Paris, Pháp ngày 29/3. Ảnh: AFP\r\n\r\nĐiện Kremlin mô tả động thái của cảnh sát biên giới Pháp là \"hoàn toàn không thể chấp nhận được\" và sẽ \"làm trầm trọng thêm\" mối quan hệ giữa Moskva và Paris. Nga cũng đã gửi công hàm phản đối tới Bộ Ngoại giao Pháp và triệu đại sứ Pháp tại Moskva.\r\n\r\nPháp hiện chưa lên tiếng về sự việc.\r\n\r\nMối quan hệ giữa Nga và Pháp suy giảm từ khi Moskva mở chiến dịch quân sự ở Ukraine. Quan hệ song phương tiếp tục xấu đi những tuần gần đây khi Tổng thống Pháp Emmanuel Macron liên tục chỉ trích Nga, liên quan đề xuất ngừng bắn ở Ukraine.\r\n\r\nCả Nga và Pháp đều đã trục xuất hàng chục nhà ngoại giao của nhau từ khi chiến sự Ukraine bùng phát. Pháp ủng hộ mạnh mẽ Ukraine và đưa ra ý tưởng triển khai lực lượng gìn giữ hòa bình tại quốc gia này như một phần giải pháp hòa bình trong tương lai, đề xuất bị Nga cho là tương đương với hành động chiến tranh.\r\n\r\nÔng Macron hôm 7/4 cáo buộc Nga đang thực hiện \"chiến thuật trì hoãn\" khi bác bỏ đề xuất ngừng bắn của Mỹ và Ukraine, đồng thời đặt ra các điều kiện cho lệnh ngừng bắn ở Biển Đen. Trước đó một ngày, Tổng thống Pháp kêu gọi \"hành động quyết liệt\" nếu Nga tiếp tục \"từ chối hòa bình\".', '1744208933_h4.webp', 'Hồ Ngọc Vy', '2025-04-09 14:28:53', 'Thế giới', 1, 6),
(29, 'Thế khó của các đối tác khi đàm phán thuế quan với ông Trump', 'Đại diện hàng loạt quốc gia gặp khó khi tìm cách đàm phán với Mỹ, do không biết nên tiếp cận ai, còn ông Trump không nêu rõ mong muốn của mình với đòn thuế.\r\n\r\n', 'Đại diện hàng loạt quốc gia gặp khó khi tìm cách đàm phán với Mỹ, do không biết nên tiếp cận ai, còn ông Trump không nêu rõ mong muốn của mình với đòn thuế.\r\n\r\nTổng thống Mỹ Donald Trump ngày 2/4 thông báo kế hoạch áp thuế 10% với toàn bộ hàng nhập khẩu vào Mỹ từ ngày 5/4. Sau đó, kể từ ngày 9/4, hàng loạt đối tác thương mại của Mỹ chịu mức thuế đối ứng cao hơn, lên tới 50%. Nhiều bên đã nhanh chóng tìm cách tiếp cận chính quyền Trump để thương lượng.\r\n\r\nTrả lời phỏng vấn Fox Business ngày 7/4, Bộ trưởng Tài chính Mỹ Scott Bessent cho biết ông Trump \"giỏi hơn ai hết trong việc giữ cho mình lợi thế tối đa\". Đó là lý do ông chủ Nhà Trắng công bố kế hoạch áp thuế và \"cho đối tác vài ngày để suy ngẫm\".\r\n\r\n\"Như đã khuyên hôm 2/4, tôi đề nghị các bên giữ cái đầu lạnh, không leo thang và mang đến Mỹ đề nghị tốt nhất về cách giảm thuế quan, rào cản phi thuế quan, chấm dứt thao túng tiền tệ, chấm dứt trợ giá. Một lúc nào đó, Tổng thống Trump sẽ thương lượng\", ông Bessent nói.\r\n\r\nTheo ông Bessent, \"50, 60, có thể là gần 70 đối tác\" đã liên hệ với chính quyền Tổng thống Trump để thương lượng.\r\n\r\n\"Tổng thống sẽ trao đổi với bất kỳ bên nào sẵn sàng nhấc điện thoại lên gọi. Tôi có thể nói rằng tiếng chuông điện thoại liên tục vang lên, vì mong muốn thương lượng với chính quyền này\", thư ký báo chí Nhà Trắng Karoline Leavitt phát biểu ngày 8/4. \"Có những lãnh đạo muốn đến Washington ngay đêm nay\".\r\n\r\nNhưng hạn chót 9/4 đã trôi qua mà không có bất cứ thỏa thuận nào được chính quyền Trump công bố. Các đối tác thương mại bị Mỹ áp thuế vẫn chưa thể tìm ra cách giải quyết bài toán thuế đối ứng. Họ không biết cần phải tiếp cận quan chức nào ở Mỹ, hay phải đưa ra những nhượng bộ gì để thuyết phục ông chủ Nhà Trắng.\r\n\r\nGiới quan sát cho rằng bất chấp những lời trấn an, Mỹ còn cách rất xa một thỏa thuận thương mại thực chất với các đối tác lớn. Quá trình thương lượng song phương khó đẩy nhanh và việc quá nhiều bên liên hệ cùng lúc khiến Nhà Trắng không theo kịp, vì chính quyền Trump có ít quan chức thương mại và kinh tế.\r\n\r\n\"Nếu muốn giải quyết tất cả cùng lúc, chắc chắn họ sẽ gặp vấn đề về nguồn lực\", Bill Reinsch, nhà nghiên cứu tại Trung tâm Chiến lược và Nghiên cứu Quốc tế (CSIS), nói với Politico. \"Tôi nghĩ sẽ có sự ưu tiên. Hãy chờ xem ai là người kế tiếp. Nhưng cũng có thể là bất kỳ ai đến trước cũng được\".\r\n\r\nMột số quan chức Nhà Trắng cho hay Mỹ sẽ ưu tiên đàm phán với các đồng minh như Nhật và Hàn Quốc. Tuy nhiên, một đồng minh khác của Mỹ là Philippines lại chưa được ưu tiên như vậy.\r\n\r\n\"Tôi không chắc lắm về mức độ tiếp nhận của Bộ Thương mại Mỹ với đề xuất Bộ trưởng Philippines gặp người đồng cấp Mỹ. Tất cả chúng tôi đều đang chờ câu trả lời\", quan chức Philippines nói, nhắc đến đại diện một số quốc gia Đông Nam Á khác.\r\n\r\nMột quan chức ngoại giao nói nước họ đã tiếp cận trợ lý của ông Trump ở nhiều cấp, nhưng những người này không trả lời hoặc không muốn làm gì khác ngoài lắng nghe.\r\n\r\nKhông có quan chức hàng đầu nào của ông Trump \"được ủy nhiệm để thương lượng\", quan chức ngoại giao thứ ba nói. Những vị trí cấp thấp lại không đủ thẩm quyền hoặc không biết về ý định của Nhà Trắng. \"Ngay cả khi có một cuộc họp thì bạn cũng không nhận được gì ngoài một dòng tweet tốt đẹp\".\r\n\r\n\"Phía Mỹ đang gửi đi những tín hiệu trái chiều. Vài quan chức nói không có chỗ cho đàm phán\", theo một quan chức ngoại giao Đông Nam Á. \"Và rồi tôi lại nghe có bên bảo rằng Israel đang đến, Nhật Bản đang đến. Tôi nghĩ chắc chắn có thể thương lượng\".\r\n\r\nTrong khi đó, giới chức Mỹ chưa nói cụ thể chính quyền Mỹ đang tìm kiếm những nhượng bộ gì để mở đường đàm phán.\r\n\r\nTổng thống Trump hoan nghênh các cuộc trao đổi với lãnh đạo Nhật Bản và Hàn Quốc, gặp trực tiếp Thủ tướng Israel Benjamin Netanyahu ngày 7/4. Nhưng Nhà Trắng không thông báo những sự kiện này có giúp đẩy nhanh đàm phán hay ông Trump có cân nhắc giảm thuế cho đồng minh hay không.\r\n\r\nÔng Trump dường như không lay chuyển, ngay cả khi ông Netanyahu nêu rõ Israel cam kết xóa bỏ thâm hụt thương mại với Mỹ và nới lỏng các rào cản thương mại trong lúc hai lãnh đạo trả lời báo giới ở Phòng Bầu dục.\r\n\r\n\"Điều đó thật tốt\", ông Trump chỉ đáp lại ngắn gọn.\r\n\r\nTổng thống Trump từng nhấn mạnh ông quan ngại nhất về thâm hụt thương mại của Mỹ và muốn khắc phục tình trạng này trước khi cân nhắc dỡ bỏ thuế quan.\r\n\r\nÔng chủ Nhà Trắng và các quan chức thương mại Mỹ đã đánh giá nhượng bộ mà một số bên đưa ra là chưa đủ. Campuchia, đối mặt thuế đối ứng 49%, đã đề xuất miễn thuế với 19 nhóm hàng hóa nhập từ Mỹ. Thái Lan tỏ ý muốn tăng nhập khẩu dầu và khí đốt từ Mỹ để giảm thặng dư.\r\n\r\nMột trở ngại nữa là các bên dù muốn cũng khó thay đổi những rào cản thương mại của họ như thuế hay tiêu chuẩn nội địa. Nhiều bên có những yếu tố không thể kiểm soát trong thương mại với Mỹ, như cấu trúc của nền kinh tế hay sức mua, xu hướng tiêu dùng từ Washington.\r\n\r\n\"Mọi thứ phức tạp hơn những gì ông Trump nghĩ, vì khi xét đến rào cản phi thuế quan, chúng ta sẽ không chỉ nói về những con số, mà còn về cách các bên tổ chức nền kinh tế và xã hội của họ\", theo ông Reinsch.\r\n\r\nTrong thời gian chờ đợi, một số bên đã chọn đáp trả. \"Chúng tôi vẫn cho rằng thương lượng là biện pháp hợp lý hơn, nhưng vấn đề là họ không đàm phán\", quan chức ngoại giao một nước đã tìm cách liên hệ với giới chức Mỹ nói.\r\n\r\nSố khác xây dựng chiến lược dài hạn để giảm thiểu tác động từ thuế quan. Chính phủ Colombia trao đổi với các lãnh đạo khu vực tư nhân về việc đa dạng hóa thị trường xuất khẩu, song song với vạch lộ trình tiếp cận Washington, theo một quan chức Colombia.\r\n\r\n\"Họ vẫn đang cân nhắc xem nên làm gì\", một quan chức Mỹ Latin đã trao đổi với chính quyền Trump nói. \"Họ trì hoãn, rồi lại thông báo, lặp đi lặp lại. Tôi nghĩ họ chỉ đang chờ xem chuyện gì sẽ xảy ra\".', '1744209167_h5.webp', 'Hồ Ngọc Vy', '2025-04-09 14:32:47', 'Thế giới', 0, 2),
(30, 'Thủ tướng Tây Ban Nha: Việt Nam nằm trong nhóm đối tác ưu tiên hàng đầu tại ASEAN', 'Thủ tướng Tây Ban Nha Pedro Sanchez hội đàm với Thủ tướng Phạm Minh Chính, khẳng định Việt Nam là một trong những đối tác ưu tiên hàng đầu tại ASEAN. ', 'Thủ tướng Tây Ban Nha Pedro Sanchez hội đàm với Thủ tướng Phạm Minh Chính, khẳng định Việt Nam là một trong những đối tác ưu tiên hàng đầu tại ASEAN.\r\n\r\nTrong cuộc hội đàm ngày 9/4, Thủ tướng Phạm Minh Chính khẳng định Việt Nam coi trọng và mong muốn đẩy mạnh hợp tác với Tây Ban Nha, Đối tác Chiến lược đầu tiên của Việt Nam trong EU, theo Bộ Ngoại giao.\r\n\r\nÔng Sanchez nhấn mạnh đây là chuyến thăm Việt Nam đầu tiên của một Thủ tướng Tây Ban Nha kể từ khi hai nước thiết lập quan hệ ngoại giao năm 1977. Ông khẳng định hai nước còn nhiều dư địa hợp tác; nhất trí cùng Việt Nam hướng tới thiết lập quan hệ Đối tác Chiến lược Toàn diện trong thời gian tới.\r\n\r\nThủ tướng Tây Ban Nha chúc mừng Việt Nam kỷ niệm 50 năm giải phóng miền Nam, thống nhất đất nước. Ông khẳng định Việt Nam là một trong những đối tác ưu tiên hàng đầu tại ASEAN và Tây Ban Nha mong muốn đẩy mạnh hợp tác toàn diện với Việt Nam, một thành viên tích cực và có trách nhiệm tại khu vực châu Á - Thái Bình Dương.\r\n\r\nÔng cũng cho biết ngày càng nhiều doanh nghiệp, du khách Tây Ban Nha mong muốn đến thăm Việt Nam và tìm hiểu cơ hội đầu tư, kinh doanh, nhất là trong các lĩnh vực về hợp tác đường sắt và đường sắt đô thị, năng lượng, hàng không.\r\n\r\nVề chính trị - ngoại giao, hai bên nhất trí tăng cường trao đổi đoàn, tiếp xúc các cấp và nghiên cứu thiết lập các cơ chế hợp tác mới trong các lĩnh vực chuyên ngành.\r\n\r\nSau khi Thủ tướng Tây Ban Nha trao đổi các thách thức, khó khăn của nền kinh tế thế giới hiện nay cũng như phương thức ứng phó với căng thẳng thương mại, Thủ tướng Phạm Minh Chính nhấn mạnh chính phủ Việt Nam đang phát huy tối đa phương châm \"bình tĩnh, bản lĩnh, linh hoạt và sáng tạo\", \"tránh đối đầu, kiên trì đối thoại\", tái cấu trúc nền kinh tế theo hướng xanh, bền vững để chủ động ứng phó, nâng cao năng lực nội tại và sức chống chịu của nền kinh tế trước biến động bên ngoài.\r\n\r\nThủ tướng khẳng định Việt Nam đã thực hiện những biện pháp để đảm bảo môi trường đầu tư, kinh doanh ổn định, an toàn, minh bạch, tạo điểm tựa để các nhà đầu tư nước ngoài làm ăn lâu dài tại Việt Nam. Việt Nam đang tập trung cao vào đa dạng hóa thị trường, đa dạng hóa các sản phẩm và các chuỗi cung ứng.\r\n\r\nHai lãnh đạo khẳng định lĩnh vực kinh tế, thương mại, đầu tư là một trong những trụ cột quan trọng trong mối quan hệ Đối tác chiến lược Việt Nam - Tây Ban Nha.\r\n\r\nHai lãnh đạo nhất trí cùng triển khai hiệu quả Hiệp định Thương mại tự do Việt Nam - EU (EVFTA) và Nghị định thư về hợp tác tài chính song phương mới ký kết, phấn đấu đưa kim ngạch thương mại song phương vượt mức 8 tỷ USD trong thời gian tới.\r\n\r\nÔng Sanchez đánh giá cao những tiềm năng hợp tác mở ra khi Hiệp định Bảo hộ đầu tư Việt Nam - EU (EVIPA) chính thức có hiệu lực, cam kết hỗ trợ vận động các nước EU còn lại sớm phê chuẩn Hiệp định này; thúc đẩy EC sớm gỡ bỏ thẻ vàng \"IUU\" đối với hải sản Việt Nam.\r\n\r\nThủ tướng Sanchez đề nghị Việt Nam giảm các thủ tục hành chính phi thuế quan, mở cửa thị trường đối với các mặt hàng nông sản, gia cầm của Tây Ban Nha. Ông cảm ơn Việt Nam đã gia hạn việc miễn thị thực cho công dân Tây Ban Nha, góp phần thúc đẩy du lịch, giao lưu nhân dân, văn hóa hai nước. Thủ tướng Phạm Minh Chính đề nghị hai nước nghiên cứu mở đường bay thẳng, để gia tăng số lượng khách du lịch giữa hai nước.\r\n\r\nÔng Sanchez cho biết sẽ tiếp tục quan tâm, tạo điều kiện thuận lợi cho cộng đồng người Việt Nam tại Tây Ban Nha hội nhập thành công ở sở tại.\r\n\r\nHai lãnh đạo nhất trí tiếp tục làm sâu sắc hợp tác trên các lĩnh vực giáo dục, văn hóa, thể thao và du lịch, phát triển nông nghiệp bền vững, hợp tác địa phương và giao lưu nhân dân, đào tạo nguồn nhân lực chất lượng cao, quốc phòng - an ninh, mở rộng hợp tác trên các lĩnh vực tiềm năng khác như khoa học - công nghệ, đổi mới sáng tạo, trí tuệ nhân tạo, bán dẫn và chuyển đổi số.\r\n\r\nSau hội đàm, hai Thủ tướng đã chứng kiến lễ trao các văn kiện hợp tác trong các lĩnh vực ngoại giao, tài chính, văn hóa và thể thao, nông nghiệp và giáo dục.\r\n\r\nViệt Nam là đối tác thương mại lớn nhất của Tây Ban Nha trong khối ASEAN. Kim ngạch thương mại song phương năm 2024 đạt 4,72 tỷ USD, tăng 20% so với năm 2023 và là mức cao nhất từ trước đến nay. Tây Ban Nha hiện có 97 dự án tại Việt Nam với mức vốn gần 144 triệu USD. Việt Nam có ba dự án đầu tư sang Tây Ban Nha với mức vốn hơn 64 triệu USD.', '1744209646_h6.webp', 'Hồ Ngọc Vy', '2025-04-09 14:40:46', 'Thế giới', 0, 2),
(31, 'Bỉ công bố gói viện trợ quân sự hơn một tỷ USD cho Ukraine', 'Thủ tướng Bỉ công bố gói viện trợ quân sự hơn một tỷ USD cho Ukraine và cam kết chuyển thêm tiêm kích F-16. ', 'Thủ tướng Bỉ công bố gói viện trợ quân sự hơn một tỷ USD cho Ukraine và cam kết chuyển thêm tiêm kích F-16.\r\n\r\nTrong cuộc họp báo chung với Tổng thống Ukraine Volodymyr Zelensky tại Kiev hôm 8/4, Thủ tướng Bỉ Bart De Wever công bố gói viện trợ quân sự trị giá một tỷ euro (gần 1,1 tỷ USD) cho Ukraine. Số tiền này sẽ được dùng để mua sắm vũ khí và nâng cao năng lực cho ngành công nghiệp quốc phòng Ukraine, giúp nước này sản xuất các loại khí tài mới.\r\n\r\nThủ tướng Wever thêm rằng Bỉ sẽ cung cấp cho Ukraine ít nhất một tỷ euro mỗi năm cho các mục đích liên quan quốc phòng trong nhiệm kỳ của ông. Lãnh đạo Bỉ cũng khẳng định Brussels sẽ bàn giao cho Kiev toàn bộ số tiêm kích F-16 đã cam kết.\r\n\r\n\"Chúng tôi sẽ chuyển hai chiếc F-16 để rã lấy linh kiện thay thế trong năm nay và hai tiêm kích nữa trong năm sau. Chúng tôi hy vọng có thể làm được nhiều hơn, song điều này còn phụ thuộc vào thời điểm tiếp nhận tiêm kích tàng hình F-35. Bỉ không chậm trễ nhưng việc bàn giao F-35 đang bị trì hoãn. F-16 sẽ rời đi ngay khi F-35 đến, đơn giản vậy thôi\", ông Wever nói.\r\n\r\nTổng thống Zelensky cho biết hai lãnh đạo đã thảo luận về hợp tác chế tạo vũ khí và đầu tư vào hoạt động sản xuất tại Ukraine, điều ông cho là sẽ mang lợi ích cho cả châu Âu. Lãnh đạo Ukraine cũng tuyên bố đã đạt được ba thỏa thuận với các nhà sản xuất của Bỉ.\r\n\r\nThủ tướng Wever, Ngoại trưởng Maxime Prevot và Bộ trưởng Quốc phòng Theo Francken ngày 8/4 tới Kiev trong khuôn khổ chuyến thăm chính thức Ukraine. Đây là lần đầu tiên lãnh đạo Bỉ đến Ukraine kể từ khi chính phủ mới được thành lập hồi tháng 2.\r\n\r\nBỉ là một trong những nước tích cực hỗ trợ Ukraine kể từ khi xung đột bùng phát cuối tháng 2/2022. Brussels đã cung cấp cho Kiev viện trợ tài chính, nhân đạo và quân sự, trong đó có vũ khí, đạn dược, đào tạo binh sĩ.\r\n\r\nBỉ là một trong các quốc gia đầu tiên tham gia liên minh được thành lập vào mùa hè năm 2023 nhằm hỗ trợ Ukraine nâng cao năng lực phòng không. Brussels cũng đã cam kết chuyển giao cho Kiev tổng cộng 30 tiêm kích F-16 trước năm 2029.', '1744210024_h7.webp', 'Hồ Ngọc Vy', '2025-04-09 14:47:04', 'Thế giới', 0, 0),
(32, 'Cố vấn Nhà Trắng nói Musk chỉ là \'người lắp ráp xe\'', 'Cố vấn Thương mại Nhà Trắng Peter Navarro cho rằng tỷ phú Elon Musk chỉ \"lắp ráp xe\", không phải sản xuất xe tại Mỹ và không am hiểu chính sách thuế.', 'Cố vấn Thương mại Nhà Trắng Peter Navarro cho rằng tỷ phú Elon Musk chỉ \"lắp ráp xe\", không phải sản xuất xe tại Mỹ và không am hiểu chính sách thuế.\r\n\r\n\"Về chính sách thuế quan và thương mại, bất kỳ ai ở Nhà Trắng, và cả người dân Mỹ, đều cho rằng Elon Musk là người sản xuất ôtô, nhưng không phải vậy. Cậu ấy là người lắp ráp ôtô\", Cố vấn Thương mại Nhà Trắng Peter Navarro ngày 7/4 cho biết trong cuộc phỏng vấn với CNBC, thêm rằng nhiều linh kiện của Tesla được nhập từ Nhật, Trung Quốc đại lục và đảo Đài Loan.\r\n\r\nTheo Navarro, những người chuyên lắp ráp ôtô như Musk sẽ chỉ muốn được nhập linh kiện giá rẻ từ bên ngoài, trong khi chính quyền Tổng thống Donald Trump muốn tăng cường sản xuất tại Mỹ.\r\n\r\n\"Khác biệt về tư duy giữa chúng tôi và Elon là chúng tôi muốn lốp xe được sản xuất ở Arkon, hộp số được sản xuất ở Indianapolis, động cơ được làm tại Flint và Saginaw. Chúng tôi muốn ôtô được sản xuất ở Mỹ\", ông nói.\r\n\r\nKhi được hỏi về những bình luận gần đây từ tỷ phú công nghệ Elon Musk bày tỏ hoài nghi về năng lực của ông trong chính sách thương mại, Navarro cho hay hai người vẫn chưa nói chuyện trực tiếp, nhưng khẳng định \"giữa tôi và Elon, mọi thứ vẫn ổn\".\r\n\r\nÔng nhấn mạnh những bình luận của lãnh đạo Ban Hiệu suất Chính phủ (DOGE) \"không có gì to tát\" và hai người sớm muộn cũng gặp nhau tại Nhà Trắng trong các cuộc họp cùng Tổng thống Trump.\r\n\r\nNavarro cho rằng Musk và đội ngũ tại DOGE đang đóng góp thiết thực cho đất nước bằng cách cắt giảm \"lãng phí và gian lận\" ngân sách. Tuy nhiên, cố vấn Nhà Trắng lưu ý rằng tỷ phú công nghệ không am hiểu chính sách thuế của chính quyền Trump.', '1744210105_h8.webp', 'Hồ Ngọc Vy', '2025-04-09 14:48:25', 'Thế giới', 0, 4),
(33, 'Ông Tập cam kết Trung Quốc tăng cường quan hệ với láng giềng', 'Chủ tịch Trung Quốc Tập Cận Bình tuyên bố sẽ tăng cường quan hệ chiến lược với các nước láng giềng thông qua quản lý phù hợp những khác biệt và thúc đẩy hợp tác về chuỗi cung ứng.', 'Chủ tịch Trung Quốc Tập Cận Bình tuyên bố sẽ tăng cường quan hệ chiến lược với các nước láng giềng thông qua quản lý phù hợp những khác biệt và thúc đẩy hợp tác về chuỗi cung ứng.\r\n\r\nCam kết được ông Tập đưa ra hôm nay tại hội nghị công tác đối ngoại chuyên đề về các quan hệ với nước láng giềng, diễn ra trong hai ngày 8-9/4. Đây là phát biểu công khai đầu tiên của lãnh đạo Trung Quốc kể từ ngày 2/4, khi căng thẳng thương mại Bắc Kinh - Washington leo thang trở lại do chính sách áp thuế đối ứng từ Tổng thống Mỹ Donald Trump.\r\n\r\nÔng Tập nhận định nước này đang bước vào giai đoạn then chốt, trong đó các yếu tố khu vực và toàn cầu đang đan xen và biến động mạnh mẽ.\r\n\r\nBản tin trên Xinhua mô tả quan hệ giữa Trung Quốc và các nước trong khu vực đang ở \"mức tốt nhất trong lịch sử hiện đại\".\r\n\r\nTrung Quốc cam kết củng cố lòng tin chiến lược, ủng hộ các quốc gia trong khu vực theo đuổi con đường phát triển riêng một cách ổn định, đồng thời giải quyết khác biệt một cách thích hợp. Trung Quốc chủ trương đẩy mạnh hội nhập kinh tế bằng cách tăng cường kết nối và hợp tác chuỗi cung ứng, công nghiệp.\r\n\r\nHội nghị lần này là phiên họp chuyên đề đầu tiên về ngoại giao láng giềng được lãnh đạo cấp cao Trung Quốc tổ chức kể từ năm 2013. Các ủy viên Thường vụ Bộ Chính trị Trung Quốc gồm Lý Cường, Triệu Lạc Tế, Vương Hộ Ninh, Thái Kỳ, Đinh Tiết Tường, Lý Hy và Phó chủ tịch nước Hàn Chính cùng tham dự.\r\n\r\nTheo SCMP, hội nghị là dấu hiệu cho thấy giới lãnh đạo Trung Quốc đang ưu tiên tăng cường quan hệ với các nước châu Á, trong bối cảnh cạnh tranh chiến lược với Mỹ ngày càng gay gắt. Bắc Kinh từng khẳng định trong năm 2023 rằng quan hệ với các nước láng giềng luôn giữ vị trí hàng đầu trong tổng thể chương trình nghị sự ngoại giao.\r\n\r\nHội nghị cũng nêu chủ trương phối hợp bảo đảm ổn định khu vực thông qua hợp tác an ninh và thực thi pháp luật để đối phó nguy cơ và thách thức chung. Lãnh đạo Trung Quốc cam kết tăng cường giao lưu nhân dân giữa các nước. Hội nghị kêu gọi nhìn nhận khu vực láng giềng dưới góc nhìn toàn cầu, tăng cường trách nhiệm trong thúc đẩy đối ngoại với những nước ở gần Trung Quốc.', '1744213775_h9.webp', 'Hồ Ngọc Vy', '2025-04-09 15:49:35', 'Thế giới', 0, 2),
(34, 'Singapore thất vọng với đòn thuế của Mỹ', 'Thủ tướng Lawrence Wong nói Singapore thất vọng với mức thuế của Tổng thống Trump vì đây \"không phải điều bạn bè làm với nhau\".', 'Thủ tướng Lawrence Wong nói Singapore thất vọng với mức thuế của Tổng thống Trump vì đây \"không phải điều bạn bè làm với nhau\".\r\n\r\n\"Chúng tôi rất thất vọng về hành động của Mỹ, nhất là khi xét tới tình hữu nghị sâu sắc và lâu dài giữa hai nước. Đây không phải hành động mà những người bạn có thể làm với nhau\", Thủ tướng Singapore Lawrence Wong phát biểu trước quốc hội ngày 8/4, nói về chính sách áp thuế mới của Tổng thống Mỹ Donald Trump.\r\n\r\nTheo sắc lệnh về thuế đối ứng được Tổng thống Trump công bố hôm 2/4, khoảng một nửa số nền kinh tế thế giới chịu mức thuế chung 10%, trong đó có Singapore, dù nước này có Hiệp định Thương mại Tự do (FTA) với Mỹ.\r\n\r\nMức thuế 10% thấp hơn nhiều so với các quốc gia khác, song Thủ tướng Wong lo ngại Singapore sẽ bị ảnh hưởng nghiêm trọng trước sự suy thoái của nền kinh tế thế giới và sự tan rã của hệ thống thương mại toàn cầu dựa trên thương mại tự do.\r\n\r\nÔng Wong cảnh báo về việc \"gia tăng khả năng xảy ra thương chiến toàn cầu\" và thúc giục Singapore, quốc gia phụ thuộc chủ yếu vào thương mại, chuẩn bị cho thời điểm khó khăn sắp tới.\r\n\r\n\"Singapore có thể rơi vào suy thoái trong năm nay hoặc không, nhưng không còn nghi ngờ gì về việc tăng trưởng của chúng ta sẽ bị ảnh hưởng đáng kể\", Thủ tướng Wong nói, thêm rằng Bộ Thương mại Singapore đang đánh giá lại mức dự báo 1-3% cho tăng trưởng kinh tế năm nay và \"có khả năng sẽ điều chỉnh giảm xuống\".\r\n\r\nThủ tướng Wong cho biết theo FTA giữa Singapore và Mỹ, quốc đảo này áp dụng mức thuế bằng 0 đối với hàng hóa của Mỹ. Singapore cũng đang thâm hụt thương mại với Mỹ.\r\n\r\n\"Nếu mức thuế thực sự đối ứng và chỉ nhằm vào những nước có thặng dư thương mại, thì mức thuế đối với Singapore phải bằng 0\", ông Wong nhấn mạnh.\r\n\r\nSingapore là nơi đặt trụ sở của hàng nghìn công ty Mỹ và nhiều công ty trong số đó đã chọn quốc đảo này làm trụ sở chính ở khu vực châu Á - Thái Bình Dương. Singapore không phải là đồng minh theo hiệp ước quốc phòng của Mỹ, nhưng cũng có mối quan hệ chính trị, quân sự chặt chẽ với nước này.', '1744213870_h10.webp', 'Hồ Ngọc Vy', '2025-04-09 15:51:10', 'Thế giới', 1, 52),
(35, 'Công ty Mỹ ngừng bán laptop vì đòn thuế của ông Trump', 'Razer và Framework, hai hãng máy tính của Mỹ, đã tạm ngừng bán một số dòng máy với lý do thuế quan của Tổng thống Donald Trump.', 'Razer và Framework, hai hãng máy tính của Mỹ, đã tạm ngừng bán một số dòng máy với lý do thuế quan của Tổng thống Donald Trump.\r\n\r\n\"Do mức thuế mới có hiệu lực từ ngày 5/4, chúng tôi tạm thời dừng bán một số mẫu máy tính xách tay dòng Framework 13 chạy chip Ultra 5 125H và Ryzen 5 7640U tại Mỹ\", Framework Computer thông báo trên X ngày 8/4. \"Hiện tại, các mẫu máy này sẽ bị xóa khỏi website cho thị trường Mỹ. Chúng tôi sẽ tiếp tục cung cấp thông tin khi có chuyển biến mới\".\r\n\r\nTrong các bài viết tiếp theo, Framework cho biết hãng sản xuất các dòng máy của mình tại Đài Loan, do đó phải đối mặt với mức thuế nhập khẩu 10% khi vào Mỹ. Khi lệnh thuế mới được áp dụng từ 9/4, hãng sẽ phải chịu mức thuế mới 32%.\r\n\r\nNgoài Framework 13, công ty hiện đặt trụ sở tại San Francisco cũng cân nhắc hoãn bán một số mẫu máy khác tại Mỹ, gồm Framework Laptop 12 mới nhất. Hãng nói thêm rằng sẽ định giá lại sản phẩm \"nếu không có lựa chọn thay thế nào khác\".\r\n\r\nFramework Computer được thành lập tháng 1/2020 bởi Nirav Patel, người từng là trưởng bộ phận phần cứng của Oculus trước khi công ty này về tay Meta. Trong giai đoạn 2021-2024, công ty đã huy động được hàng chục triệu USD mỗi lần từ nhiều quỹ lớn. Bên cạnh PC và laptop, Framework hiện mở rộng kinh doanh sang các thiết bị điện tử tiêu dùng.\r\nMột công ty Mỹ khác là Razer cũng đã hạ một số mẫu laptop chủ lực của mình tại thị trường Mỹ, gồm loạt Razer Blade 14, Razer Blade 16 và Razer Blade 18. Thiết bị chơi game cầm tay Razer Edge cũng không còn trên website bán hàng, thay vào đó chỉ bán vỏ máy và phụ kiện.\r\n\r\nRazer không đưa ra bình luận. Tuy nhiên, theo PCWorld, động thái dừng bán hàng của công ty này có thể là chiến thuật trì hoãn nhằm ứng phó với mức thuế đối ứng mới mà ông Trump vừa ban hành tuần trước.\r\n\r\nRazer được thành lập năm 1998 bởi Min-Liang Tan, và Robert Krakoff, có trụ sở ở Irvine, California. Công ty chuyên kinh doanh các sản phẩm dành cho game thủ, gồm PC, laptop chơi game, máy tính bảng, thiết bị đeo và phụ kiện. Hầu hết sản phẩm của công ty được sản xuất tại Trung Quốc - nơi Mỹ đánh thuế sản phẩm nhập khẩu tới 34% và hiện đã tăng lên 104% sau các đòn trả đũa.\r\n\r\nTrong khi đó, Micron, nhà sản xuất chip nhớ cho các thiết bị điện tử, ngày 8/4 cũng ra thông báo với khách hàng Mỹ rằng sẽ áp dụng phụ phí từ 9/4 do liên quan đến vấn đề thuế quan. Công ty này cũng nhắc đến những bình luận đã công bố ngày 21/3, rằng \"có ý định chuyển chi phí cho khách hàng ở những khu vực chịu tác động của thuế quan\".\r\n\r\nNói với Reuters, CEO của một nhà sản xuất module bộ nhớ NAND đặt trụ sở tại châu Á cũng cho biết đang áp dụng cách tiếp cận tương tự Micron. \"Nếu họ không muốn chịu thuế mới, chúng tôi không thể chuyển hàng. Chúng tôi không thể chịu trách nhiệm về các quyết định mà chính phủ của các bạn đưa ra\", người này nói. \"Với thuế suất mới, sẽ không có công ty nào hào phóng nói rằng: Tôi sẽ chịu gánh nặng này\".\r\n\r\nTrước đó, Apple, công ty hiện sản xuất hầu hết thiết bị tại Trung Quốc và Ấn Độ, cũng tìm cách giảm tác động của thuế mới bằng cách tích trữ sản phẩm để bán tại Mỹ. Times of India ngày 7/4 dẫn nguồn từ quan chức cấp cao của Ấn Độ rằng đã có 5 chuyến bay về Mỹ chở đầy iPhone và các sản phẩm Apple khác từ Ấn Độ và Trung Quốc \"chỉ trong ba ngày cuối tháng 3\".\r\n\r\nGiới chuyên gia dự đoán giá iPhone có thể tăng 30-40% nếu Apple chuyển chi phí thuế mới sang người tiêu dùng. Theo tính toán của nhóm phân tích tại Chứng khoán Rosenblatt Securities, giá của mẫu smartphone rẻ nhất hiện tại là iPhone 16 có thể tăng từ 799 lên 1.142 USD, tương đương 43%. Tương tự, điện thoại đắt nhất iPhone 16 Pro Max 1 TB có thể tăng thêm 700 lên 2.300 USD.', '1744216707_h17.webp', 'Hồ Ngọc Vy', '2025-04-09 16:38:27', 'Công nghệ', 0, 1),
(36, 'Messi lập cú đúp, đưa Inter Miami vào bán kết', 'Lionel Messi lần đầu ghi bàn phạt đền sau một năm, giúp Inter Miami thắng Los Angeles FC 3-2 sau hai lượt tứ kết Concacaf Champions Cup.', 'Lionel Messi lần đầu ghi bàn phạt đền sau một năm, giúp Inter Miami thắng Los Angeles FC 3-2 sau hai lượt tứ kết Concacaf Champions Cup.\r\n\r\nGhi bàn: Messi 35\', phạt đền 84\', Allen 61\' - Long 9\'\r\n\r\nSau khi thua 0-1 ở lượt đi và sớm thủng lưới trong trận lượt về, Inter Miami tưởng như sẽ phải dừng bước tại giải đấu giống như Cup C1 Bắc Trung Mỹ. Tuy nhiên, Messi tỏa sáng với cú đúp, xen giữa là bàn thắng của Noah Allen, giúp Miami vào bán kết với thắng lợi chung cuộc 3-2.\r\n\r\nPhút 31, khi Inter Miami đang bị dẫn hai lượt 2-0, Messi đá phạt nhanh đưa bóng vào lưới, khi thủ môn Hugo Lloris đang bận điều chỉnh hàng rào. Trọng tài Cesar Arturo Ramos ban đầu công nhận bàn thắng, sau đó hủy quyết định này vì trước đó ông chưa cho phép trận đấu tiếp tục.\r\n\r\nNhưng không phải chờ lâu, Messi ghi tuyệt phẩm sau đó chỉ vài phút. Từ đường chuyền của Luis Suarez, siêu sao Argentina nhận bóng ở bên trái cấm địa, rồi sút chéo góc tầm cao từ cự ly khoảng 15 m, hạ gục thủ môn Hugo Lloris. Bàn thắng này mở đầu cho cuộc lội ngược dòng của chủ nhà.\r\n\r\nSau khi Allen gỡ hòa 2-2 ở phút 61, hai đội đều có nhiều cơ hội để ghi bàn quyết định, nhưng Miami tận dụng thời cơ tốt hơn. Phút 82, phát hiện tiền đạo đội khách Marlon Santos cản bóng bằng tay trong cấm địa Los Angeles, VAR gọi trọng tài Ramos xem lại tình huống. Sau đó, trọng tài người Mexico quyết định thổi phạt đền cho Miami.\r\n\r\nKhông như những lần gần đây nhường phạt đền cho đồng đội, Messi đích thân sút vì tình huống này có thể quyết định suất đi tiếp của Miami. Anh chạy đà chậm, nhìn thấy thủ môn không di chuyển, mới sút về góc phải để ấn định thắng lợi 3-1 tại lượt về, chung cuộc 3-2.\r\n\r\nĐây là lần đầu Messi ghi bàn từ phạt đền ở mọi đấu trường, kể từ ngày 20/4/2024, khi anh chọc thủng lưới Nashville. Siêu sao 37 tuổi cũng ghi bàn thắng thứ 857 trong sự nghiệp với 1.092 trận đấu. Quan trọng hơn, Miami lần đầu vào bán kết Cup C1 Bắc Trung Mỹ.\r\n\r\nMiami thắng, nhưng họ vẫn không thể hài lòng với các trọng tài. Phút 67, Messi treo bóng cho Luis Suarez đánh đầu cận thành ghi bàn. Sau khi kẻ vạch, VAR cho rằng Suarez đã việt vị, nên báo trọng tài Ramos hủy bàn thắng của tiền đạo người Uruguay.\r\n\r\nTuy nhiên, dường như VAR đã chọn sai cầu thủ để kẻ vạch việt vị. Hậu vệ số 14 Sergi Palencia dường như đứng thấp nhất ở hàng thủ Los Angeles, nhưng VAR lại chọn trung vệ số 33 Aaron Long để kẻ vạch việt vị màu đỏ. Nếu họ chọn Palencia, nhiều khả năng Suarez sẽ không việt vị.\r\n\r\nĐối thủ của Miami ở bán kết sẽ là đội thắng trong cặp đấu giữa Pumas UNAM và Vancouver.', '1744270328_h18.webp', 'Hồ Ngọc Vy', '2025-04-10 07:32:08', 'Thể thao', 0, 3),
(41, 'PSG đặt một chân vào bán kết Champions League', 'Pháp - PSG thắng Aston Villa 3-1 ở lượt đi tứ kết Champions League hôm 9/4, nhờ ba bàn thắng đẹp từ cánh trái.', 'Dù PSG thủng lưới một bàn, có thể nói họ gần như không cho đối thủ chơi bóng trên đất Pháp. Chủ nhà kiểm soát bóng 75% thời lượng, dứt điểm 29 lần, trong đó có 10 lần trúng hướng cầu môn. Ba bàn của thầy trò Luis Enrique đều đến từ cánh trái, nơi hậu vệ Nuno Mendes và tiền vệ Khvicha Kvaratskhelia liên tục làm khổ hàng thủ Aston Villa.\r\n\r\nBàn ấn định chiến thắng của Mendes ở phút bù thứ hai có ý nghĩa lớn, khi nó mang đến lợi thế hai bàn cho PSG sau lượt đi. Aston Villa từ chỗ còn nguyên hy vọng ở lượt về, giờ đang thấy tấm vé bán kết ở xa tầm tay.\r\nBóng gần như chỉ lăn bên phần sân Aston Villa ở cả hai hiệp đấu. PSG một lần nữa áp đảo một đội bóng Anh trên sân nhà mùa này, nhưng họ lại tiếp tục bị dẫn trước giống như trận gặp Liverpool ở vòng 1/8. Từ pha mất bóng giữa sân, chủ nhà không đủ quân số để ngăn cản pha phản công của Villa. Marcus Rashford chồng biên cho Youri Tielemans căng ngang để Morgan Rogers dứt điểm vào lưới trống ở phút 35.\r\n\r\nĐiểm khác của PSG so với chính họ ở trận gặp Liverpool ở Parc des Princes là khả năng ghi bàn. Hàng thủ số đông của Aston Villa bất lực khi tài năng trẻ Desire Doue đi bóng từ cánh trái vào trung lộ, vẽ nên đường cong đẹp mắt vào góc cao, dù sút bóng trong tư thế mất trụ ở phút 39.\r\n\r\nHLV Unai Emery nhận ra vấn đề ở vị trí hậu vệ phải của Aston Villa, khi Matty Cash không theo nổi các ngôi sao bên phía chủ nhà. Ông thay Axel Disasi vào ngay đầu hiệp hai, nhưng không hiệu quả. Disasi thậm chí chậm hơn Cash, không kèm nổi Kvaratskhelia. Cầu thủ Gruzia vê bóng bằng gầm giày, vượt qua đối thủ rồi dứt điểm vào góc gần, tung nóc lưới Emiliano Martinez ở đầu hiệp hai.\r\nKhông chỉ mạnh trong tấn công, PSG còn gây ấn tượng bởi khả năng thu hồi bóng. Aston Villa rất ít khi cầm được bóng quá vài giây, bởi chủ nhà áp sát và đọc tình huống quá nhanh. Chính bởi khả năng này mà PSG được danh thủ Rio Ferdinand mô tả trên TNT Sports là \"chơi như đội bóng từng nhiều lần vô địch Champions League\". Thực tế, PSG vẫn đang tìm kiếm danh hiệu đầu tiên tại đấu trường danh giá này.\r\n\r\nHọ tiến một bước dài, với bàn thắng thứ ba ở phút bù. Mendes nhận đường chọc khe từ Ousmane Dembele, làm động tác giả, lừa cả hậu vệ và thủ môn đối thủ, rồi dứt điểm vào lưới trống bằng chân không thuận. Trước đó, Achraf Hakimi cũng đưa được bóng vào lưới Martinez, nhưng bị hủy bàn vì lỗi việt vị.\r\n\r\nBàn thua phút cuối tàn nhẫn với Aston Villa - đội lần đầu vào tứ kết từ năm 1983. Nhưng đó cũng là thành quả xứng đáng cho PSG. HLV Enrique biến PSG từ một đội bóng phụ thuộc vào siêu sao trở thành một tập thể gắn kết, nhưng không mất đi sức sáng tạo của mỗi cá nhân - điều làm nên ba bàn thắng trận này. Với lợi thế dẫn 3-1 cùng màn trình diễn áp đảo, PSG xem như đặt một chân vào bán kết Champions League.', '1744549168_h173.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-11 09:28:02', 'Thể thao', 0, 3);
INSERT INTO `news` (`id`, `title`, `description`, `content`, `image`, `author`, `created_at`, `category`, `is_featured`, `views`) VALUES
(42, 'HLV Park Hang-seo làm Phó chủ tịch LĐBĐ Hàn Quốc', 'Hàn Quốc - Cựu HLV tuyển Việt Nam Park Hang-seo và cựu HLV tuyển Indonesia Shin Tae-yong được chọn làm Phó chủ tịch Liên đoàn bóng đá Hàn Quốc (KFA) nhiệm kỳ 2025-2028.', 'Quyết định của KFA được công bố vào sáng nay. Park và Shin nằm trong số 27 người được chọn vào Ủy ban điều hành thứ 55, dưới sự chỉ đạo của Chủ tịch Chung Mong-gyu.\r\n\r\nPhó chủ tịch có 5 thành viên. Ông Park có nhiệm vụ hỗ trợ đội tuyển quốc gia, còn ông Shin phụ trách hợp tác đối ngoại.\r\nĐợt bổ nhiệm này của KFA gồm các nhà lãnh đạo, chuyên gia và HLV trong nhiều lĩnh vực khác nhau, nhằm tạo sự đa dạng về quan điểm khi xây dựng các chính sách. \"Liên đoàn muốn xây dựng không gian quản lý cởi mở, khác với trước đây\", hãng tin Yonhap cho hay. \"Nhân sự Ủy ban điều hành được thành lập sau khi nhận nhiều khuyến nghị từ các tổ chức trong từng lĩnh vực\".\r\n\r\nĐây được xem là động thái thay đổi mạnh mẽ của KFA ở nhiệm kỳ thứ tư của Chủ tịch Chung Mong-gyu. Ở nhiệm kỳ trước, Chung chịu sức ép dữ dội, thậm chí bị đề nghị từ chức sau những cáo buộc quản lý yếu kém dẫn đến sự sa sút của toàn bộ nền bóng đá. Đỉnh điểm là bê bối đánh nhau của hai ngôi sao Lee Kang-in và Son Heung-min trước trận bán kết thua Jordan 0-2 ở Asian Cup 2023. Cuối cùng, ông Chung vẫn tại vị và trúng cử nhiệm kỳ này.\r\n\r\nHLV Park Hang-seo sinh năm 1959, từng làm trợ lý ở đội tuyển Hàn Quốc dưới thời HLV Guus Hiddink giai đoạn 2000-2002, với thành tích đứng thứ 4 World Cup 2002. Sau đó, ông dẫn dắt đội U23 dự ASIAD 2002 cũng trên sân nhà, nhưng bị loại từ bán kết dẫn đến bị sa thải. Park dẫn dắt thêm bốn CLB khác nhau trong nước, từ năm 2005.\r\n\r\nCuối năm 2017 ông bén duyên với bóng đá Việt Nam, và gắn bó đến tháng 1/2023. Ông gặt hái nhiều thành công với tấm HC bạc giải U23 châu Á 2018, vô địch AFF Cup 2018, vào tứ kết Asian Cup 2019, hai lần giành HC vàng SEA Games 2019 và 2021 và đưa đội tuyển lần đầu vào tới vòng loại cuối cùng World Cup 2022 - khu vực châu Á. Dưới thời của ông, Việt Nam luôn duy trì vị thế là quốc gia đứng đầu khu vực, đồng thời là quốc gia Đông Nam Á giữ vị trí lâu nhất trong top 100 của FIFA. Giải đấu cuối cùng của HLV Park là ASEAN Cup 2022, với tấm HC bạc khi thua Thái Lan tại chung kết.\r\n\r\nHiện, ông Park điều hành một Trung tâm bóng đá trẻ ở Việt Nam và làm cố vấn cho đội hạng Nhì Bắc Ninh.\r\nHLV Shin Tae-yong sinh năm 1970, và có danh tiếng tại Hàn Quốc được đánh giá cao hơn Park. Thành tích lớn đầu tiên của ông là giúp Seongnam FC vô địch AFC Champions League 2010, giành FA Cup Hàn Quốc 2011. Sau đó, ông gắn bó với các cấp độ đội tuyển Hàn Quốc, từng vào tứ kết Olympic Rio 2016, vượt qua vòng bảng giải U20 World Cup 2017 và là người \"chữa cháy\" ở World Cup 2018 với trận thắng ấn tượng 2-0 trước Đức - đội khi ấy là đương kim vô địch.\r\n\r\nCuối năm 2019, Shin đến Indonesia. Ông giúp bóng đá xứ vạn đảo hoàn thành các mục tiêu lớn, như vượt qua vòng bảng Asian Cup 2023, vào bán kết U23 châu Á 2024 và vào vòng loại ba World Cup 2026. Đây đều là những thành tích lịch sử của bóng đá quốc gia Đông Nam Á này. Những thành tựu của HLV Shin với bóng đá Indonesia từng được báo chí Hàn Quốc so sánh với Park Hang-seo khi dẫn dắt Việt Nam.\r\n\r\nSau khi cùng đội hình U20 bị loại từ vòng bảng ASEAN Cup 2024, Shin nhận trát sa thải. Nguyên nhân thực sự có lẽ vì ông không được lòng nhiều cầu thủ nhập tịch gốc Hà Lan. Trước khi ra đi, ông giúp Indonesia giành chiến thắng đầu tiên tại vòng loại ba World Cup 2026, khi hạ Arab Saudi 2-0 vào tháng 11/2024.\r\n\r\nTrong một phát biểu trên báo Hàn Quốc vào ngày 7/4, HLV Shin cho biết sẽ nghỉ làm HLV trong một năm, để trau dồi thêm kiến thức về bóng đá trước khi trở lại.', '1744549120_h172.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-11 09:53:08', 'Thể thao', 0, 2),
(43, 'HLV U17 Việt Nam: \'Chúng tôi xứng đáng với kết thúc có hậu\'', 'Arab Saudi - HLV Cristiano Roland tin Việt Nam xứng đáng đi tiếp, nhưng thất bại trong việc giành vé dự World Cup cũng là điều rất quan trọng trong chặng đường phát triển của cầu thủ trẻ.', 'Ở lượt cuối bảng B vòng chung kết U17 châu Á tối 10/4, Việt Nam đã ở rất gần chiến thắng với bàn mở tỷ số phút 23 của Duy Khang. Nhưng thoáng sơ sểnh phút 87 khiến đội bóng của Roland trả giá. Từ quả phạt biên phải, bóng được treo ra cột xa tưởng chừng đi hết biên. Nhưng Jayden Adetiba vẫn kịp đưa bóng ngược vào trong khiến các cầu thủ Việt Nam lúng túng, tạo điều kiện cho Hazaa Faisal ập vào đánh đầu cận thành ghi bàn gỡ hòa 1-1.\r\n\r\nTrận hòa ba liên tiếp, trước đó là với Australia và Nhật Bản, khiến Việt Nam tụt xuống cuối bảng B, kém ba đối thủ chỉ một điểm.\r\n\"Bóng đá luôn có biến số và chúng tôi đã thủng lưới theo cách không ngờ tới ở những phút cuối\", HLV Roland nói sau trận đấu trên sân King Fahd. \"Chúng tôi xứng đáng với kết thúc có hậu, nhưng các cầu thủ trẻ đã có kinh nghiệm quý giá. Thất bại cũng là điều rất quan trọng trên đường phát triển sự nghiệp của họ\".\r\n\r\nÔng cũng cho rằng kết quả vừa qua trước các đối thủ hàng đầu châu lục cho thấy toàn đội rất nỗ lực, nhắc nhở họ còn chặng đường dài phía trước và cần cố gắng tập luyện để hướng tới những điều tốt đẹp hơn.\r\n\r\n\"Tôi muốn họ ngẩng cao đầu lên để hướng tới trở thành những cầu thủ lớn\", HLV Roland cho biết. \"Sau giải này, họ phải khát khao nhiều hơn cho sự nghiệp, vì đây chính là tương lai của bóng đá Việt Nam\".\r\nHLV Roland dẫn dắt đội tuyển từ giải giao hữu U16 Peace Cup vào tháng 8/2024 tại Trung Quốc. Nếu không tính trận thua chủ nhà 0-4 ngày ra quân, Việt Nam sau đó bất bại với bảy thắng, ghi 24 và thủng lưới 5 bàn. Tại vòng chung kết U17 châu Á, đội duy trì kỷ luật và tuân thủ chiến thuật phòng ngự, rồi tận dụng tốt các sai lầm của đối thủ.\r\n\r\nNhà cầm quân sinh năm 1976 đánh giá rằng, các cầu thủ đã phát triển chuyên môn sau mỗi trận. Tính từ 2024, lứa cầu thủ này đã thi đấu 18 trận với 50% chiến thắng, còn lại là 6 hòa và 3 thua.\r\n\r\nTrong giai đoạn 2025-2029, giải U17 châu Á và U17 World Cup sẽ được tổ chức hàng năm, thay vì hai năm một lần như trước. Vì vậy, các cầu thủ trẻ Việt Nam sẽ liên tục có cơ hội cọ xát quốc tế.\r\n\r\n', '1744549035_h171.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-11 09:55:46', 'Thể thao', 0, 0),
(55, 'Thiết bị \'hiểu\' đất bằng AI đoạt giải về giải pháp xanh', 'Bộ thiết bị đo chất lượng đất bằng AI của Enfarm là một trong ba giải pháp đoạt giải Startup bền vững được yêu thích.', 'Bộ thiết bị đo chất lượng đất bằng AI của Enfarm là một trong ba giải pháp đoạt giải Startup bền vững được yêu thích.\r\n\r\nEnfarm nằm trong số ba công ty khởi nghiệp thắng giải Startup bền vững được yêu thích tại Demo Day, trong khuôn khổ sự kiện Towards Zero Waste Accelerator tại TP HCM ngày 4/4. Công ty thu hút sự chú ý với bộ thiết bị enfarmF có thể đo dinh dưỡng đất, gồm bộ phát đáp và hai cảm biến S3, S7. Trong đó, S3 làm nhiệm vụ đo nồng độ N-P-K, tình trạng dinh dưỡng đất, theo dõi sức khỏe cây trồng. Cảm biến S7 cũng dùng đo nồng độ N-P-K, nhưng thêm tính năng đo độ pH và độ ẩm đất, khuyến cáo bón đúng loại phân cho đất.\r\n\r\nCác dữ liệu được thu thập và gửi về ứng dụng trên smartphone, sau đó AI sẽ phân tích và đưa ra thông số chất lượng và dưỡng chất trong đất, cũng như lời khuyên giúp người dùng thêm bớt phân bón, nước và các yếu tố khác. Ứng dụng cũng có chức năng dự báo thời tiết, ước tính giá và lợi nhuận, sổ tay nông nghiệp điện tử, cũng như giải đáp thắc mắc của nông dân bằng AI.\r\n\r\nTheo đại diện của Enfarm, hầu hết việc bón phân và tưới tiêu hiện nay có thể gây tình trạng dư thừa. Khoảng 60% phân bón không được cây trồng hấp thụ, gây lãng phí có thể đến 120 tỷ USD mỗi năm, thoái hóa một phần ba diện tích trồng trọt và tạo ra 5% tổng lượng khí thải gây hiệu ứng nhà kính toàn cầu. EnfarmF ra đời với mục đích \"hiểu đất muốn gì\", giúp tiết kiệm đến 50% chi phí phân bón, tăng 20% năng suất cây trồng tại những nơi bộ thiết bị đang được ứng dụng thực tế.\r\n\r\nBên cạnh Enfarm, hai startup đoạt giải còn lại là Wesolife với giải pháp công nghệ điện hóa để tạo clo từ nước biển, cung cấp giải pháp xử lý và khử trùng nước an toàn, không dùng hóa chất; và Lemit Foods với giải pháp công nghệ thực phẩm, tận dụng mít non để tạo ra các sản phẩm thịt thực vật như pa-tê mít, chả lụa, chả giò.\r\n\r\nTowards Zero Waste Accelerator là chương trình giúp mở rộng cơ hội cho các giải pháp đổi mới sáng tạo đóng góp vào nền kinh tế tuần hoàn tại Việt Nam. Dự án do Bộ Hợp tác Kinh tế và Phát triển Liên bang Đức (BMZ) hỗ trợ, với hai đơn vị triển khai tại Việt Nam là BambuUP và Schoolab Asia, cùng Trung tâm Khởi nghiệp sáng tạo Saigon Innovation Hub thuộc Sở Khoa học và Công nghệ TP HCM để mở rộng tầm ảnh hưởng.\r\n\r\nDiễn ra trong 7 tháng, Towards Zero Waste Accelerator nhận hơn 120 đơn đăng ký từ các dự án kinh tế tuần hoàn giảm rác thải đang hoạt động tại Việt Nam. Trong số này có 10 giải pháp được lựa chọn. Trước đó, chương trình cung cấp hỗ trợ chuyên sâu 1-1 giữa startup và chuyên gia, giúp hoàn thiện giải pháp và tìm kiếm nhu cầu của thị trường.\r\n\r\n\"Các nhà sáng lập và giải pháp trong sự kiện đã thể hiện sự sáng tạo, bền bỉ nhằm tái định nghĩa cách mọi người sản xuất và tiêu dùng vì một tương lai bền vững cho các thế hệ sau\", bà Mira Nagy, quản lý chương trình Go Circular tại Việt Nam và đại diện GIZ, cho biết. \"Họ đại diện cho các mô hình kinh doanh kinh tế tuần hoàn đa dạng, từ việc giảm chất thải ô nhiễm cho đến tạo ra chuỗi cung ứng bền vững và các mô hình kinh tế mới. Họ cũng giải quyết nhiều loại vật liệu và lĩnh vực khác nhau, khai thác sự tương hỗ trong các ngành nhựa, dệt may, hữu cơ, nông nghiệp và điện tử\".', '1744285567_h37.webp', 'Hồ Ngọc Vy', '2025-04-10 04:46:07', 'Công nghệ', 0, 1),
(56, 'Trương Trí Lâm, Lữ Tụng Hiền tung chưởng ở phim võ hiệp', 'Tài tử Hong Kong Trương Trí Lâm, Lữ Tụng Hiền đóng cao thủ ở phim \"Phó sơn hải\" cùng sao trẻ Thành Nghị, Cổ Lực Na Trát.', 'Tài tử Hong Kong Trương Trí Lâm, Lữ Tụng Hiền đóng cao thủ ở phim \"Phó sơn hải\" cùng sao trẻ Thành Nghị, Cổ Lực Na Trát.\r\n\r\nTối 9/4, đoàn phim tung video giới thiệu dàn diễn viên, sự xuất hiện của những tên tuổi gắn liền phim võ hiệp TVB gây chú ý với nhiều khán giả. Trương Trí Lâm và Lữ Tụng Hiền có các cảnh tỉ thí với nam chính Tiêu Minh Minh (Thành Nghị đóng). Các tài tử đu dây cáp, tung chiêu thức trên không, đấu tay đôi hoặc dùng kiếm.\r\n\r\nPhim cải biên tiểu thuyết Thần châu kỳ hiệp của Ôn Thụy An, xoay quanh cuộc sống của Tiêu Minh Minh - chàng trai mê tiểu thuyết võ hiệp, đặt bản thân trong thế giới của hiệp khách giang hồ Tiêu Thu Thủy. Vốn non trẻ, võ công thấp kém, Tiêu Thu Thủy được các cao thủ chỉ giáo, trở thành đại hiệp.\r\n\r\nTác phẩm ghi hình năm 2024 tại phim trường Hoành Điếm, Trung Quốc, đánh dấu tái xuất của Trương Trí Lâm ở mảng phim võ hiệp, sau hơn 10 năm chỉ đóng phim bối cảnh hiện đại. Trương Trí Lâm nổi tiếng với vai chính Quách Tĩnh ở Anh hùng xạ điêu của đài TVB năm 1994.\r\n\r\nCòn Lữ Tụng Hiền lưu dấu ấn với vai kiếm khách Lệnh Hồ Xung trong Tiếu ngạo giang hồ TVB năm 1996. Những năm gần đây anh đóng vai phụ ở một số phim truyền hình, dự sự kiện, kinh doanh nhà hàng chay.\r\n\r\nPhó sơn hải do Nhậm Hải Đào và Lâm Phong đạo diễn, còn có sự tham gia của những gương mặt trẻ nổi bật làng phim Trung Quốc như Cổ Lực Na Trát, Đồng Mộng Thực, Trần Ngọc Kỳ.\r\n\r\nTừ khi bấm máy, phim thu hút chú ý của hàng triệu fan do quy tụ dàn sao nổi tiếng các thế hệ. Những video hậu trường đấu võ nhận hàng trăm triệu lượt xem. Theo thống kê của nền tảng Tencent Video, gần ba triệu tài khoản chờ xem tác phẩm.', '1744285854_h38.webp', 'Tài tử Hong Kong Trương Trí Lâm, Lữ Tụng Hiền đóng cao thủ ở phim \"Phó sơn hải\" cùng sao trẻ Thành N', '2025-04-10 04:50:54', 'Giải trí', 0, 0),
(57, 'Ed Sheeran nói về tình bạn 13 năm với Taylor Swift', 'Ca sĩ Ed Sheeran và Taylor Swift thường tâm sự hơn sáu tiếng đồng hồ mỗi khi gặp mặt, động viên nhau lúc khó khăn.', 'Ca sĩ Ed Sheeran và Taylor Swift thường tâm sự hơn sáu tiếng đồng hồ mỗi khi gặp mặt, động viên nhau lúc khó khăn.\r\n\r\nTrên podcast Call Her Daddy ngày 9/4, Ed Sheeran cho biết kỷ niệm yêu thích với Taylor Swift là khi anh hát mở màn cho chuyến lưu diễn Red Tour của cô năm 2013. Đây là chuỗi concert quảng bá album phòng thu thứ tư của Swift - Red (2012).\r\n\r\nEd Sheeran nói: \"Tôi từng sống ở Nashville, cô ấy cũng thế và chúng tôi từng bay đi bay về từ các buổi diễn cùng nhau, làm đủ chuyện linh tinh mà tôi không nhớ rõ. Tôi gần như bên cạnh cô ấy hàng ngày suốt sáu tháng, nên lúc đó đáng nhớ. Bây giờ chúng tôi chỉ gặp nhau mỗi khi có dịp\".\r\n\r\nDù hiện tại đôi bạn ít dành thời gian cho nhau, họ vẫn tranh thủ trò chuyện sâu nếu có cơ hội. Ed Sheeran cho biết chỉ gặp Taylor Swift khoảng bốn lần trong năm, nhưng lần nào cũng tâm sự khoảng sáu tiếng. \"Tôi nghĩ cách giữ mối quan hệ này khá hay nhưng tôi vẫn nhớ hồi năm 2013\", ca sĩ nói thêm.\r\n\r\nTình bạn của hai nghệ sĩ bắt đầu từ năm 2012 khi hợp tác trong ca khúc Everything Has Changed. Một năm sau, hai người vướng nghi vấn hẹn hò nhưng Ed Sheeran bác bỏ tin đồn trên một chương trình ở New Zealand. Đến nay, đôi bạn sáng tác thêm nhiều bài hát như End Game (2017), Run (2021), The Joker and the Queen (2021). Tháng 8/2024, Ed Sheeran khiến người hâm mộ thích thú khi làm khách mời concert The Eras Tour của bạn thân tại London.\r\n\r\nHơn 10 năm làm bạn, cả hai nhiều lần nói về nhau trong các cuộc phỏng vấn. Dịp Ed Sheeran lọt vào Top 100 người ảnh hưởng nhất thế giới do Time bình chọn năm 2017, Taylor Swift viết bài chúc mừng anh trên tạp chí. Trong đó, cô so sánh lòng nhiệt huyết của Sheeran như \"tấm khiên bất khả xâm phạm\" giúp anh chống chọi mọi vấp ngã, thất vọng hay những lúc bị xem thường. Năm 2019, Swift vướng mâu thuẫn bản quyền với nhà sản xuất Scooter Braun, Ed Sheeran bị fan chỉ trích giữ im lặng khi bạn thân gặp khó khăn. Trên trang cá nhân, anh phủ nhận việc bỏ rơi cô, cho biết đã nói chuyện trực tiếp với Swift như cách anh thường làm trước đây.\r\n\r\nTrong cuộc phỏng vấn với Zane Lowe\'s Apple Music 1 tháng 5/2023, Ed Sheeran khen Taylor Swift là \"một trong những người duy nhất hiểu anh\" nên hai người có thể tâm sự rất lâu. Anh nói: \"Theo tôi, những cuộc hội thoại đó như một phương pháp trị liệu tinh thần bởi bạn được trò chuyện với người thực sự hiểu mình nói gì. Tất cả xoay quanh cảm xúc cá nhân, sự bất an và cách mọi người, gia đình và bạn bè đối xử với bạn. Về cơ bản, cô ấy sống cùng thế giới với tôi\".\r\n\r\nEd Sheeran, 34 tuổi, là ca sĩ Anh, gia nhập làng nhạc từ năm 2004 và gây chú ý với khả năng sáng tác. Năm 2011, anh phát hành đĩa đơn đầu tay The A Team, nổi tiếng toàn cầu. Năm 2019, anh nhận danh hiệu Nghệ sĩ của thập niên nhờ thành công về mặt thương mại trong làng nhạc Anh, do Official Charts Company - tổ chức điều hành các bảng xếp hạng âm nhạc uy tín nhất nước Anh - công bố. Theo Daily Mail, Sheeran bí mật làm đám cưới với Cherry Seaborn năm 2019, hiện có hai con gái.\r\n\r\nTaylor Swift, 36 tuổi, được mệnh danh \"công chúa nhạc đồng quê\" của Mỹ sau khi ra mắt năm 16 tuổi. Năm 2014, cô chuyển sang thể loại pop, ghi dấu ấn với album 1989. Trong gần 20 năm làm nghề, cô nhận 14 tượng vàng Grammy, 30 cúp VMAs, tổ chức nhiều chuyến lưu diễn lớn như The Eras Tour. Doanh thu của tour diễn giúp cô bước vào hàng ngũ tỷ phú với khối tài sản 1,6 tỷ USD, theo Forbes.', '1744285981_h39.webp', 'Hồ Ngọc Vy', '2025-04-10 04:53:01', 'Giải trí', 1, 0),
(58, 'Nguyên Phó thủ tướng Trương Hòa Bình bị cách tất cả chức vụ trong Đảng', 'Ban Chấp hành Trung ương Đảng quyết định cách tất cả các chức vụ trong Đảng với ông Trương Hòa Bình, nguyên Ủy viên Bộ Chính trị, nguyên Phó thủ tướng Thường trực.\r\n\r\n', 'Ban Chấp hành Trung ương Đảng quyết định cách tất cả các chức vụ trong Đảng với ông Trương Hòa Bình, nguyên Ủy viên Bộ Chính trị, nguyên Phó thủ tướng Thường trực.\r\n\r\nThông tin trên được nêu trong thông cáo của Văn phòng Trung ương Đảng chiều 10/4 - sau ngày đầu tiên Hội nghị Trung ương 11. Đây là hình thức kỷ luật nặng thứ hai trong 4 hình thức kỷ luật đảng viên gồm khiển trách, cảnh cáo, cách chức, khai trừ.\r\n\r\nTheo kết luận của Trung ương, ông Trương Hòa Bình trong thời gian giữ chức vụ Phó bí thư Ban cán sự đảng, Phó thủ tướng Thường trực Chính phủ đã vi phạm quy định của Đảng, pháp luật của Nhà nước trong thực hiện chức trách, nhiệm vụ được giao, trong phòng, chống tham nhũng, tiêu cực. Ông cũng vi phạm quy định những điều đảng viên không được làm và trách nhiệm nêu gương. Những vi phạm này \"gây hậu quả rất nghiêm trọng, dư luận xấu, ảnh hưởng đến uy tín của Đảng và Nhà nước\".\r\n\r\nTrước đó ngày 4/4, Ủy ban Kiểm tra Trung ương cũng đánh giá vi phạm của ông Trương Hòa Bình rất nghiêm trọng nên đề nghị cấp có thẩm quyền xem xét, thi hành kỷ luật. Sau đó Bộ Chính trị đề nghị Ban Chấp hành Trung ương Đảng xem xét kỷ luật ông Bình.\r\n\r\nÔng Trương Hòa Bình 70 tuổi, là Phó thủ tướng Thường trực nhiệm kỳ 2016-2021. Ông nghỉ hưu tháng 7/2021. Hồi tháng 12/2024, ông bị Bộ Chính trị cảnh cáo do một số vi phạm khi đương chức.\r\n\r\nTrong ngày làm việc hôm nay, Trung ương đồng ý cho ông Nguyễn Văn Hiếu, Phó trưởng ban Tuyên giáo và Dân vận Trung ương thôi chức Ủy viên dự khuyết Trung ương Đảng khóa 13.\r\n\r\nTrung ương kết luận ông Nguyễn Văn Hiếu trong thời gian giữ cương vị Bí thư Thành ủy Cần Thơ đã vi phạm quy định của Đảng, pháp luật của Nhà nước trong thực hiện chức trách, nhiệm vụ được giao, trong phòng, chống tham nhũng, lãng phí, tiêu cực; vi phạm quy định những điều đảng viên không được làm và trách nhiệm nêu gương, \"gây hậu quả nghiêm trọng\", dư luận xấu, làm giảm uy tín của tổ chức đảng và chính quyền địa phương.\r\n\r\nTuần trước, ông Hiếu bị Bộ Chính trị cảnh cáo và cho thôi giữ chức Phó trưởng ban Tuyên giáo và Dân vận Trung ương.\r\n\r\n', '1744286099_h40.webp', 'Hồ Ngọc Vy', '2025-04-10 04:54:59', 'Thời sự', 0, 2),
(59, 'Nữ kỹ sư Việt đầu tiên trở thành chuyên gia GDE của Google', 'Nguyễn Khánh Linh, sinh năm 1991, được lựa chọn tham gia chương trình mạng lưới chuyên gia Google Developer Expert (GDE).', 'Nguyễn Khánh Linh, sinh năm 1991, được lựa chọn tham gia chương trình mạng lưới chuyên gia Google Developer Expert (GDE).\r\n\r\nSáng 9/4, Google cho biết Khánh Linh không chỉ là người phụ nữ Việt Nam đầu tiên trở thành Google Developer Expert, mà còn là chuyên gia GDE về máy học đầu tiên thuộc lĩnh vực AI tạo sinh trong năm 2025 của hãng.\r\n\r\nGoogle Developer Expert (GDE) là mạng lưới chuyên gia công nghệ toàn cầu do Google lựa chọn, có kiến thức chuyên sâu và kinh nghiệm thực tế về công nghệ của hãng, chứng minh kiến thức vững chắc, đóng góp tích cực cho cộng đồng. Trước Khánh Linh, Việt Nam có 9 nam chuyên gia GDE hoạt động trong nhiều mảng như AI/ Machine Learning, Android, Cloud, WorkSpace và Angular.\r\n\r\n\"Đây là sự ghi nhận cho chuyên môn sâu rộng về nghiên cứu cốt lõi và ứng dụng thực tiễn, đặc biệt trong lĩnh vực như mô hình ngôn ngữ lớn (LLM), MLOps và hệ thống AI có khả năng mở rộng, đồng thời ghi nhận đóng góp của cô trong việc giải quyết các vấn đề xã hội thông qua công nghệ\", Google cho hay.\r\n\r\nNguyễn Khánh Linh là cựu sinh viên chuyên ngành kỹ thuật phầm mềm tại Trường Đại học FPT, tốt nghiệp thạc sĩ kỹ thuật về AI tại Đại học Quốc gia Singapore và có nhiều đóng góp cho lĩnh vực công nghệ về mặt học thuật, với hai công bố khoa học gồm Phương pháp đánh giá hội thoại mới trong hệ thống dựa trên tri thức, và khảo sát được trích dẫn rộng rãi về Hệ thống hội thoại đồng cảm trong lĩnh vực thông tin.\r\n\r\nTrong chia sẻ với cộng đồng nhà phát triển năm 2024, Linh cho biết cô đam mê máy tính từ nhỏ, học lập trình từ cuối cấp hai và theo đuổi ngành kỹ thuật phần mềm tại đại học.\r\n\r\n\"Tôi được truyền cảm hứng bởi những thành tựu như IBM Watson và DeepBlue, những hệ thống AI đã chứng minh khả năng hiểu và tương tác với con người một cách đáng kinh ngạc\", Linh nói, cho biết điều này thôi thúc cô tìm hiểu sâu hơn về cách máy móc có thể hiểu ngôn ngữ con người.\r\n\r\nNăm 2011, Linh xây dựng chatbot đầu tiên. Tự nhận dự án còn đơn giản, nhưng nó giúp cô nhận ra tiềm năng của AI trong việc giải quyết các vấn đề phức tạp và cải thiện cuộc sống con người. Linh bắt đầu tìm hiểu sâu mảng xử lý ngôn ngữ tự nhiên (NLP) và trí tuệ nhân tạo. \"Tôi tin AI có thể tạo ra những tác động tích cực đáng kể\", cô nói.\r\n\r\nVới 14 năm kinh nghiệm trong lĩnh vực NLP, máy học và khoa học dữ liệu, Linh hiện là Trưởng bộ phận AI tại công ty Obello - nền tảng thiết kế đồ họa có trụ sở tại Mỹ, chuyên phát triển giải pháp AI, máy học để tự động hóa thiết kế. Năm 2024, cô cũng được công nhận là \"Top Voice\" - những người có các bài viết chất lượng, được đánh giá cao trên mạng xã hội nghề nghiệp LinkedIn.\r\n\r\n\"Tôi có niềm đam mê sâu sắc với công nghệ, đặc biệt là AI, và tôi tin có thể đóng góp tích cực cho xã hội thông qua những tiến bộ công nghệ một cách có trách nhiệm\", Linh nói. \"Mục tiêu của tôi là giúp phát triển và triển khai hệ thống AI không chỉ mang tính đổi mới mà còn có đạo đức, an toàn và hữu ích cho mọi người\".', '1744286185_h41.webp', 'Hồ Ngọc Vy', '2025-04-10 04:56:25', 'Công nghệ', 0, 2),
(60, 'Lyon - Man Utd: \'Quỷ Đỏ\' dễ đi, khó về ở Europa League', 'Man Utd được dự đoán gặp nhiều khó khăn khi gặp chủ nhà Lyon có phong độ cao, ở lượt đi tứ kết Europa League.', 'Man Utd được dự đoán gặp nhiều khó khăn khi gặp chủ nhà Lyon có phong độ cao, ở lượt đi tứ kết Europa League.\r\n\r\nLyon đã thắng 8 trong 10 trận gần nhất trên mọi đấu trường, trong đó có những bại tướng hạng khá ở Ligue 1 như Nice, Lille hay Brest. Ở vòng 1/8 Europa League, đoàn quân dưới trướng HLV Paulo Fonseca cũng thắng FCSB 7-1 sau hai lượt. \"Lyon nhanh, khỏe và mạnh hơn so với Real Sociedad\", HLV Ruben Amorim nhận xét về đối thủ tại họp báo trước trận.\r\n\r\nMan Utd cũng thắng đậm Sociedad 5-2 chung cuộc ở vòng 1/8, nhưng họ dự kiến sẽ khó tạo ra tỷ số tương tự với Lyon. Chủ nhà từng trải qua chuỗi 6 trận không thắng trong tháng 1 và đầu tháng 2/2025. Nhưng kể từ khi HLV Fonseca lên nắm quyền, họ thể hiện bộ mặt khác, chỉ thua PSG và Strasbourg trong 10 trận.\r\n\r\nLyon đã ghi bàn trong 13 trận liên tiếp, và hàng công của họ cũng được đánh giá cao nhờ tài năng 21 tuổi Rayan Cherki, Thiago Almada hay tiền đạo người Gruzia, Georges Mikautadze. Ba cầu thủ này đều giàu kỹ thuật và có khả năng ghi bàn từ trong lẫn ngoài cấm địa.\r\n\r\nĐây cũng là cơ hội tốt để Lyon thắng Man Utd lần đầu trong lịch sử. Hai đội đã gặp nhau 4 lần, khi đại diện Anh thắng hai, hòa hai trận. Lần gần nhất hai đội so tài diễn ra ở lượt về vòng 1/8 Champions League năm 2008, khi Cristiano Ronaldo ghi bàn duy nhất đưa Man Utd vào tứ kết, sau đó vô địch.\r\n\r\nMan Utd đang cần thành tích tương tự ở Europa League, để giành suất trở lại Champions League. Thầy trò Amorim không còn cơ hội vào Top 5 Ngoại hạng Anh, nên họ sẽ phải dốc hết sức trong hai trận gặp Lyon.\r\n\r\n\"Quỷ Đỏ\" sẽ tiếp tục vắng tiền vệ Amad Diallo và trung vệ Lisandro Martinez vì chấn thương dài hạn, còn Matthijs de Ligt nghỉ thi đấu ngắn hạn. Đổi lại, Amorim có thể dùng Mason Mount, Kobbie Mainoo hay thậm chí Luke Shaw đá chính, sau thời gian dài dưỡng thương.\r\n\r\nMan Utd đang đứng thứ 26 thế giới, hơn Lyon 9 bậc, theo chỉ số sức mạnh Opta. Tuy nhiên, nhiều chuyên trang thể thao lại nhận định tỷ số nghiêng về chủ nhà. Trang Goal hay 90min cùng dự đoán Lyon sẽ thắng 2-1, còn Sportsmole nghiêng về tỷ số 1-1.\r\n\r\nCặp đấu này còn hấp dẫn ở cuộc khẩu chiến giữa tiền vệ chủ nhà Nemanja Matic, và thủ môn đội khách Andre Onana. Trước trận, Onana nói rằng Lyon rất mạnh, nhưng không thể được coi là đội bóng lớn như Man Utd.\r\n\r\nMatic, cũng là cựu cầu thủ Man Utd, phản hồi bằng cách ví Onana như thủ thành tệ bậc nhất lịch sử \"Quỷ Đỏ\". Thủ môn người Cameroon ngay lập tức viết lên mạng xã hội đáp trả rằng anh đã đoạt Cup FA cùng Man Utd, còn Matic trắng tay trong năm mùa giải chơi cho Quỷ Đỏ thời 2017-2022.\r\n\r\nMatic và Onana dự kiến sẽ cùng đá chính trong trận đấu sáng sớm mai 11/4, giờ Hà Nội. Chưa rõ cầu thủ nào sẽ được phép nở nụ cười chiến thắng.\r\n\r\nĐội hình dự kiến\r\n\r\nLyon: Perri; Maitland-Niles, Mata, Niakhate, Tagliafico; Tessmann, Matic; Cherki, Tolisso, Almada; Mikautadze.\r\n\r\nMan Utd: Onana; Mazraoui, Maguire, Yoro; Dalot, Casemiro, Ugarte, Dorgu; Fernandes, Garnahco; Zirkzee.', '1744286306_h42.webp', 'Hồ Ngọc Vy', '2025-04-10 04:58:26', 'Thể thao', 0, 1),
(61, 'Báo Nhật Bản: \'Thêm một lần muối mặt trước bóng đá Việt Nam\'', 'Truyền thông Nhật Bản thất vọng khi lần thứ hai không thắng Việt Nam trong hai năm, đồng thời lỡ cơ hội sớm giành vé dự FIFA U17 World Cup.', 'Truyền thông Nhật Bản thất vọng khi lần thứ hai không thắng Việt Nam trong hai năm, đồng thời lỡ cơ hội sớm giành vé dự FIFA U17 World Cup.\r\n\r\nTháng 8/2024, Nhật Bản thua Việt Nam 0-1 ở giải giao hữu U16 Peace Cup tại Trung Quốc. Tám tháng sau, hầu hết cầu thủ tham dự trận này tái ngộ ở vòng bảng U17 châu Á 2025 đang diễn ra tại Arab Saudi. Nhật Bản chiếm thế trận và sớm dẫn bàn ở phút 13, nhưng sai lầm cuối trận khiến họ chịu phạt đền và bị cầm hòa 1-1.\r\n\r\n\"Một trận hòa đáng thất vọng vì để lỡ cơ hội sớm giành vé dự U17 World Cup\", bài viết trên Soccer Digest có đoạn. \"Thêm một lần nữa bóng đá Nhật Bản muối mặt trước Việt Nam\".\r\n\r\nViệt Nam hiếm khi hòa hoặc thắng Nhật Bản. Nếu tính riêng giải U17, hôm qua mới là trận hòa đầu tiên của đội tuyển trước nền bóng đá số một châu Á. Trước đó, Việt Nam toàn thua năm trận, lần lượt là 0-2 (vòng bảng 2000), 2-4 (tranh HC đồng 2000), 0-6 (vòng bảng 2010), 0-7 (vòng bảng 2016) và 0-4 (vòng bảng 2023).\r\n\r\nDù vậy, những năm gần đây, Việt Nam cũng vài lần gây sốc cho Nhật Bản. Đáng kể là trận hòa 1-1 ở lượt cuối vòng loại ba World Cup 2022 trên sân Saitama. Còn ở ASIAD 2018, Việt Nam từng thắng 1-0 ở vòng bảng. Khi ấy, đội tuyển sở hữu thế hệ vàng U23 như Quang Hải, Công Phượng cùng ba cầu thủ quá tuổi hàng đầu là Nguyễn Văn Quyết, Nguyễn Anh Đức và Đỗ Hùng Dũng, trong khi Nhật Bản cử đội U20. Cả hai trận đều dưới thời HLV Park Hang-seo.\r\n\r\nBàn thua của U17 Nhật Bản trên sân Okadh tối qua đến sau đường chuyền dài từ sân nhà của thủ môn Hoa Xuân Tín. Bóng nảy ra sau hàng hậu vệ, còn thủ môn Ibuki Ejike do dự trong băng ra, đến khi thực hiện thì đã chậm hơn Trần Gia Bảo một nhịp dẫn đến phạm lỗi và chịu phạt đền. Chính Gia Bảo sau đó thực hiện thành công, giúp Việt Nam giật lại một điểm.\r\n\r\n\"Cầu thủ Nhật Bản lứa này vật lộn với việc xử lý các pha bóng đơn giản kể từ khi thành lập đội\", tờ Gekisaka nhận xét. \"Một kết thúc gây sốc khiến một số cầu thủ đã đổ gục trên sân\".\r\n\r\nSai lầm này tương tự bàn thua trong trận thắng UAE 4-1 ngày trận ra quân. Gekisaka cũng đánh giá rất khó phá vỡ hàng thủ Việt Nam kiên cường có lúc chơi với sáu hậu vệ. Nhưng Nhật Bản cũng thiếu mạo hiểm và không tự tin dù có bàn dẫn trước sớm từ tình huống cố định.\r\n\r\n\"Vấn đề không phải Nhật Bản tệ, mà là Việt Nam chơi tốt. Cầu thủ có kỹ năng tốt, còn tập thể được tổ chức tốt\", bài viết trên Soccer Digest có đoạn. \"Nếu thế hệ này phát triển tốt, họ có thể trở thành đối thủ đáng gờm với Nhật Bản trong tương lai\".\r\n\r\nTrong khi đó, Báo Hàn Quốc Xports News châm ngòi cho cuộc khẩu chiến giữa hai nền bóng đá kỳ phùng địch thủ. Tờ này dẫn thông tin trong cuộc họp Ủy ban kỹ thuật LĐBĐ Nhật Bản, có người đã chế giễu trận Hàn Quốc thua Indonesia 0-1 tại lượt ra quân bảng C U17 châu Á 2025.\r\n\r\n\"Trận hòa Việt Nam cho thấy Nhật Bản cũng không khác biệt nhiều\", Xports News bình luận. \"Sau khi chế giễu Hàn Quốc, họ phải chịu sự sỉ nhục lớn\".\r\n\r\nNhật Bản vẫn đang dẫn đầu bảng B với bốn điểm, hơn UAE một điểm, Việt Nam hai điểm và Australia ba điểm. Ở lượt cuối vào ngày 10/4, họ chỉ cần hòa Australia là chắc suất đi tiếp.\r\n\r\nTrang chủ LĐBĐ châu Á (AFC) nhận định: \"Sự bế tắc của Nhật Bản đã giúp lượt trận cuối trở nên thú vị hơn\". Còn Soccer Digest thì hy vọng \"việc mất hai điểm vừa xong không ảnh hưởng đến thành tích tương lai tại giải\".', '1744286486_h43.webp', 'Hồ Ngọc Vy', '2025-04-10 05:01:26', 'Thể thao', 0, 8),
(62, 'Hậu trường hóa trang thời chiến của phim \'Địa đạo\'', 'Đoàn phim \"Địa đạo\" thuê chuyên gia người Mỹ tái hiện chân dung lính du kích trong cuộc chiến ở Củ Chi năm 1967.', 'Đoàn phim \"Địa đạo\" thuê chuyên gia người Mỹ tái hiện chân dung lính du kích trong cuộc chiến ở Củ Chi năm 1967.\r\n\r\nĐạo diễn Bùi Thạc Chuyên cho biết một trong những khâu êkíp dành nhiều tâm sức nhất là hóa trang. Theo anh, nhóm chiến sĩ trong phim dành phần lớn thời gian sống dưới địa đạo, mặt mũi luôn lấm lem, da đen sạm vì ám khói. Anh đặt tiêu chí các vệt than, bùn trên người các diễn viên cần được xử lý tự nhiên. Ở một số cảnh người lính bị trọng thương do bom đạn, khâu tạo hình phức tạp hơn, Jefferson Cabral - chuyên viên hóa trang người Mỹ - phải mất nhiều giờ để thực hiện.\r\n\r\nĐoàn phim cũng đầu tư cho phần trang phục - yếu tố quan trọng góp phần phác họa giai đoạn cuối thập niên 1960. Đạo diễn cùng tổ thiết kế tìm hiểu kỹ về lịch sử để tránh các trường hợp gây tranh cãi. Theo bà Mai Lâm - phụ trách phục trang, từng bộ quần áo, phụ kiện phải làm tự nhiên, thô sơ nhưng không được qua loa. Mỗi bộ đồ còn phản ánh tính cách từng nhân vật, như Út Khờ (Diễm Hằng Lamoon) mặc trẻ trung, giàu màu sắc, còn Ba Hương (Hồ Thu Anh) giản dị hơn với đồ tự thêu.\r\n\r\nBùi Thạc Chuyên cho biết tôn trọng tính chân thực của phim vì muốn cùng khán giả suy ngẫm, học những bài học lịch sử về chiến tranh qua tác phẩm. Nhiều phân cảnh được anh lấy ý tưởng từ nguyên mẫu có thật. Chẳng hạn, ở đoạn nhóm du kích đón chú Sáu đến thăm địa đạo, họ nói: \"Chú phải về đây ở thật lâu thì mới biết\", để miêu tả cuộc sống trong lòng đất. Lời thoại được đạo diễn lấy cảm hứng từ câu nói của anh hùng lực lượng vũ trang Tô Văn Đực - người góp phần chế tạo mìn gạt trong cuộc chiến ở Củ Chi.\r\n\r\nCâu chuyện xoay quanh nhóm du kích 21 người bám trụ ở Củ Chi, sau trận càn Cedar Falls (năm 1967) của Mỹ. Đội trưởng Bảy Theo (diễn viên Thái Hòa đóng) và các đồng đội được giao nhiệm vụ bảo vệ địa bàn cho nhóm thông tin tình báo chiến lược. Tuy nhiên, các cuộc liên lạc của họ bị quân địch phát hiện. Những chiến sĩ du kích rơi vào tình thế nguy hiểm, đối mặt nhiều trận càn quét. Dự án kỷ niệm 50 năm thống nhất đất nước, là phim chiến tranh được làm từ ngân sách xã hội hóa.\r\n\r\nĐạo diễn - biên kịch Bùi Thạc Chuyên, 57 tuổi, sinh tại Hà Nội. Năm 1995, anh học khoa Diễn viên, Đại học Sân khấu Điện ảnh Hà Nội, trở thành nghệ sĩ của Nhà hát kịch Việt Nam. Năm 1997, anh theo học đạo diễn, gây chú ý với Cuốc xe đêm - tác phẩm Việt đầu tiên đoạt giải ở hạng mục Phim ngắn Cinefondation tại Liên hoan phim quốc tế Cannes. Năm 2005, phim Sống trong sợ hãi của anh giành nhiều giải trong nước và quốc tế. Năm 2022, Tro tàn rực rỡ - phim chuyển thể từ truyện Nguyễn Ngọc Tư - cũng thắng lớn, trong đó có Bông Sen Vàng tại Liên hoan phim Việt Nam 2023.\r\n\r\n', '1744286612_h44.webp', 'Hồ Ngọc Vy', '2025-04-10 05:03:32', 'Giải trí', 0, 1),
(63, 'Dung mạo mỹ nhân đóng nữ vương \'Tây du ký\' gây chú ý', 'Nghệ sĩ Chu Lâm, đóng quốc vương Nữ Nhi quốc phim \"Tây du ký\", khiến fan bất ngờ với sắc vóc tuổi 73.', 'Nghệ sĩ Chu Lâm, đóng quốc vương Nữ Nhi quốc phim \"Tây du ký\", khiến fan bất ngờ với sắc vóc tuổi 73.\r\n\r\nNgày 5/4, video về hoạt động của diễn viên tại thành phố cổ ở tỉnh Hà Nam, Trung Quốc, được hàng chục nghìn khán giả chia sẻ trên mạng xã hội. Theo Sohu, bà dự sự kiện về quảng bá du lịch, văn hóa đồng thời tham gia chương trình đọc thơ.\r\n\r\nKhi Chu Lâm tham quan phố cổ, một diễn viên hóa trang Đường Tăng đến làm lễ, tặng nghệ sĩ hoa mẫu đơn. Nhiều khán giả vây quanh Chu Lâm chụp hình.\r\n\r\nTrên Douyin, khán giả cho biết ngỡ ngàng trước phong thái, dung mạo của Chu Lâm tuổi thất tuần. Các fan viết: \"Bà ấy như mới ngoài 50 tuổi\", \"Làm sao bà ấy có thể trẻ lâu như vậy, trông gương mặt không hề có dấu hiệu phẫu thuật thẩm mỹ\", \"Tôi vẫn rung động vì đôi mắt của nữ vương\", \"Vai diễn của bà trong Tây du ký không ai vượt qua được\".\r\n\r\nLần hiếm hoi Chu Lâm dự sự kiện, nhiều năm qua, mỗi năm diễn viên chỉ ghi hình một hoặc hai chương trình văn hóa dịp Tết âm lịch hoặc dịp Ngày sách thế giới, vì bà có đam mê dành cho sách.\r\n\r\nTheo Beijing News, nghệ sĩ còn thích các môn thể thao như bóng rổ, bơi. Bà tập gym hàng ngày, nhờ vậy sức khỏe tốt. Chu Lâm du lịch nhiều, ghi lại cảnh vật, con người làm bà cảm động. Nghệ sĩ nói luôn tò mò, háo hức trước khi đến những vùng đất mới.\r\n\r\nDiễn viên kín tiếng đời tư, bà kết hôn từ trước khi vào đoàn phim Tây du ký của đạo diễn Dương Khiết. Vì chuyện tình trong phim với Đường Tăng (Từ Thiếu Hoa đóng), vợ chồng Chu Lâm vướng nhiều tin đồn ly hôn, khiến họ dở khóc dở cười. Diễn viên từng cho biết bà và chồng tin tưởng nhau, tình cảm chưa hề bị ảnh hưởng bởi lời đồn đoán. Vợ chồng Chu Lâm không có con.\r\n\r\nNghệ sĩ sinh năm 1952, tốt nghiệp Học viện Y khoa Trung Quốc năm 1978. Năm 1981, bà thi vào Học viện Điện ảnh Bắc Kinh, ngành Biểu diễn, từ đó theo con đường nghệ thuật. Năm 30 tuổi, Chu Lâm vào đoàn phim Tây du ký của đạo diễn Dương Khiết, đóng quốc vương Nữ Nhi Quốc - người si mê Đường Tăng. Tờ The Paper viết diễn xuất và ngoại hình của Chu Lâm mềm mại như nước, trở thành hình tượng kinh điển trong lòng khán giả.\r\n\r\nTây du ký bấm máy tháng 7/1982, sau đó được phát thử tập Trừ yêu ở nước Ô Kê vào tháng 10 cùng năm. Từ năm 1986, phim được phát trọn vẹn trên truyền hình, gây tiếng vang tại nhiều quốc gia châu Á. Theo Xinhua, tác phẩm có sức sống lâu bền, luôn được phát lại mỗi dịp hè hàng chục năm qua. Dù kỹ xảo thô sơ, tác phẩm đặc sắc nhờ nội dung, diễn xuất của dàn nghệ sĩ.', '1744286905_h45.webp', 'Hồ Ngọc Vy', '2025-04-10 05:08:25', 'Giải trí', 0, 1),
(64, 'Hơn một thập niên bên nhau của Tuấn Hưng, Hương Baby', 'Ca sĩ Tuấn Hưng bớt nóng tính, giảm nhậu nhẹt, dành sự ưu tiên hàng đầu cho gia đình, sau 11 năm kết hôn Hương Baby.', 'Ca sĩ Tuấn Hưng bớt nóng tính, giảm nhậu nhẹt, dành sự ưu tiên hàng đầu cho gia đình, sau 11 năm kết hôn Hương Baby.\r\n\r\nKỷ niệm ngày cưới hôm 5/4, ca sĩ dành tặng vợ những câu trong bài Chỉ có thể là yêu do anh sáng tác: \'\'Mình cùng hứa yêu nhau là suốt đời. Mình cùng hứa bên nhau trọn kiếp người. Đường đời bao trái ngang, lòng người ai đoán được. Dù nhiều lúc ta hay hờn dỗi. Là vì quá yêu nhau mà thôi. Đắng cay nào nước mắt nào cũng chỉ là yêu\'\'.\r\n\r\nBà xã Tuấn Hưng cho biết ngày này mọi năm đều tổ chức lớn, mời nhiều bạn bè dự tiệc song năm nay hai người tận hưởng niềm vui theo cách riêng tư. \'\'Đôi khi có những điều chỉ hai ta biết và hiểu là đủ\'\', cô viết.\r\n\r\n11 năm chung sống, Tuấn Hưng và Hương Baby đều thay đổi để dung hòa. Trước khi về chung một nhà năm 2014, Tuấn Hưng được biết đến là sao nam đào hoa còn Hương Baby quen thuộc với danh xưng \'\'hot girl Hà thành\'\', cùng thời Huyền Baby, Hạnh Sino.\r\n\r\nKhi công khai mối quan hệ với giọng ca Tìm lại bầu trời, Hương Baby gặp nhiều áp lực. Mọi nhất cử nhất động của cô đều bị công chúng chú ý. Người đẹp nhớ mãi ngày mà tên, hình ảnh của mình tràn lan các mặt báo với khoảnh khắc đi chơi cùng Tuấn Hưng hồi đầu năm 2012. Quá trình yêu, hai người tan, hợp nhiều lần song vẫn gắn bó vì chữ duyên.\r\n\r\nSau đám cưới vài tháng, Tuấn Hưng ra mắt ca khúc Nắm lấy tay anh (Tú Dưa) như món quà dành cho bà xã kém 12 tuổi, gửi gắm mong muốn của anh về hôn nhân bền vững, hạnh phúc. Cùng năm, ca sĩ và vợ đón con trai đầu lòng - bé Su Hào. Những năm đầu hôn nhân, anh mải mê đi hát, không có thời gian chăm sóc vợ con. Thời điểm ấy, ca sĩ nóng nảy, chưa biết kiểm soát mình trong khi Hương Baby còn trẻ, cái tôi lớn. Những khác biệt trong quan điểm sống, cách chăm sóc con, thói quen sinh hoạt khiến hai người thường xuyên căng thẳng.\r\n\r\nGiai đoạn đầu, Hương Baby từng muốn khép lại hôn nhân. Có lúc bực bội, giận chồng, cô không muốn để tâm chuyện nhà cửa, con cái. Người đẹp từng thừa nhận những bài báo đặt nghi vấn hai người ly hôn khi ấy không phải không có cơ sở. Sau này ngẫm lại, cô thấy bản thân có phần trẻ con, ích kỷ, cho rằng vợ chồng đôi lúc \'\'xô bát xô đũa\'\' là chuyện bình thường.\r\n\r\nKhoảng bảy năm trước, Tuấn Hưng quyết định lùi lại, hỗ trợ công việc kinh doanh của vợ để có nhiều thời gian cho gia đình. Ban đầu, đây là lựa chọn khó khăn với ca sĩ song hiện tại cả hai đều thấy đúng. Anh chọn lọc show, không đi hát xa. Nếu đi lưu diễn nước ngoài, ca sĩ đưa vợ con theo, kết hợp du lịch. Được bao nhiêu cát-xê, Tuấn Hưng đưa hết cho bà xã. Anh thừa nhận Hương Baby kiếm tiền giỏi hơn nhưng khẳng định mình vẫn là trụ cột trong nhà.\r\n\r\nTổ ấm của Tuấn Hưng, Hương Baby thêm tiếng cười khi hai con tiếp theo - bé gái Son, 8 tuổi, và bé trai Sâm, 6 tuổi - lần lượt ra đời. Mỗi lần chứng kiến Hương Baby sinh nở, ca sĩ càng thấm những đánh đổi của bà xã, từ đó thương cô nhiều hơn. Anh tự nhắc bản thân không được khiến vợ buồn, phải là tấm gương tốt cho các con. \'\'Hiện tôi chưa quá hoàn hảo nhưng biết nỗ lực để gia đình trở nên hạnh phúc\'\', ca sĩ nói.\r\n\r\nTheo Hương Baby hiện chồng bớt nóng tính, nghĩ cho vợ con nhiều hơn. Chẳng hạn khi cãi nhau, anh sẽ để cô nói hết, hôm sau mới tìm cách trao đổi. Trước đây, ca sĩ nhậu nhẹt nhiều vì có những mối quan hệ không thể từ chối, giờ đã giảm bớt. Về phía mình, Hương Baby biết nhẫn nại, quán xuyến gia đình chu đáo.\r\n\r\nTuấn Hưng nói tiền không thể mua được những giá trị tình cảm nên anh luôn cố gắng ở bên gia đình mọi lúc. Thời điểm con còn nhỏ, ca sĩ không ngại việc thay quần áo, tắm giặt cho hai bé trai. Anh cũng biết nấu những món cơ bản trong bữa ăn gia đình. Đưa các con đi học xong, ca sĩ mới dành thời gian cho bản thân.\r\n\r\nKhi ba bé bắt đầu lớn và biết tự lập, Tuấn Hưng trở lại hoạt động nghệ thuật sôi nổi. Ca sĩ ra mắt loạt MV Gấp đôi yêu thương, Quả táo vàng, tham gia chương trình Anh trai vượt ngàn chông gai, làm chuỗi concert Góc ban công. Đầu năm ngoái, anh đưa bố mẹ, vợ con vào TP HCM sống để thuận tiện phát triển sự nghiệp.\r\n\r\nBên cạnh công việc kinh doanh nhà hàng chay, Hương Baby đồng hành mọi hoạt động của chồng. Hồi tháng 6/2024, cô đưa ba con đến ủng hộ Tuấn Hưng tại show Anh trai vượt ngàn chông gai. Tháng 11 năm ngoái, hai người tham gia trình diễn cho bộ sưu tập Ký ức Hà Nội của nhà thiết kế Anh Thư, được nhiều người cổ vũ khi nắm tay sải bước trên sàn catwalk.\r\n\r\nTrải qua nhiều thăng trầm, Hương Baby khẳng định cưới Tuấn Hưng là điều đúng đắn. Cô không nhận bản thân có thể chế ngự \'\'con ngựa bất kham\'\' Tuấn Hưng mà cho rằng đến một thời điểm, ai cũng phải thay đổi để thích nghi. Hai người chung mục tiêu cố gắng mang đến cuộc sống hạnh phúc cho ba con, chúc nhau đủ bao dung, mạnh mẽ để vượt qua mọi thử thách.', '1744287053_h46.webp', 'Hồ Ngọc Vy', '2025-04-10 05:10:53', 'Giải trí', 0, 2),
(65, 'Alcaraz vào chung kết Monte Carlo Masters', 'Monaco - Hạt giống số hai Carlos Alcaraz đánh bại đàn anh đồng hương Alejandro Davidovich Fokina 7-6(2), 6-4 để lần đầu có cơ hội giành Cup Masters 1000 tại Monaco.', 'Alcaraz chơi tốt hơn nhiều so với trận tứ kết thắng Arthur Fils, dù Fokina là tay vợt hàng đầu ở mặt sân đất nện vài mùa gần đây. Trước đối thủ khó chơi và đã quá hiểu nhau, Alcaraz tránh mắc những sai lầm không đáng có, cải thiện cú giao bóng để không rơi vào thế phải cứu quá nhiều break-point.\r\n\r\nDù vậy, thói quen khởi đầu chậm khiến Alcaraz ba lần có nguy cơ thua game giao đầu tiên. Nhưng tay vợt 21 tuổi cứu cả ba điểm nhờ tìm được nhịp giao đúng lúc và dứt điểm quyết đoán. Tới game kế sau, đến lượt Davidovich Fokina giao không tốt và Alcaraz tận dụng thành công break-point thứ hai để vượt lên dẫn 3-0, rồi 5-2. Ở game 8, Alcaraz đứng trước hai set-point nhưng đều bỏ lỡ vì những lần dứt điểm thuận tay nóng vội. Điều này khiến anh mất nhịp ở game giao sau đó, để Fokina gỡ hoà và kéo set đấu vào tie-break.\r\nLoạt đấu cân não chứng kiến những pha đôi công hấp dẫn, nhưng Alcaraz trội hơn ở kỹ năng phòng thủ. Sự chắc chắn sau baseline và phản đòn hiệu quả giúp tay vợt sở hữu 17 danh hiệu ATP Tour sớm dẫn 4-0. Dù mất một mini-break, Alcaraz vẫn bảo toàn lợi thế để thắng đậm 7-2.\r\n\r\nSet hai tiếp tục chứng kiến thế trận khó lường mà Fokina tạo ra trước đối thủ được đánh giá cao hơn. Á quân năm 2022 phòng ngự bền bỉ nhờ bộ chân nhanh và sức rướn tốt. Sự kiên cường được Fokina thể hiện ở game chín, khi anh cứu 4 match-point trong 13 phút căng thẳng. Dù vậy, sai lầm thua ngay game giao đầu set của tay vợt 25 tuổi không được bù đắp, khi Alcaraz giữ chắc các game giao nhờ tỷ lệ ăn điểm khi giao bóng trong sân duy trì hơn 70% cả bóng một và bóng hai.\r\n\r\nDù phải cần tới match-point thứ 6 để kết thúc trận đấu, Alcaraz gây ấn tượng khi không cho Fokina có cơ hội thắng game đỡ bóng ở set hai. Anh sẽ vào chung kết Masters 1000 lần đầu tiên sau 13 tháng kể từ BNP Paribas Open tháng 3/2024.\r\n\r\n\"Tôi đã kiên nhẫn một thời gian dài mới có thêm một trận chung kết nữa ở cấp độ này\", Alcaraz nói sau trận. \"Đôi khi mọi người kỳ vọng tôi vào chung kết mọi giải nhưng điều đó là không thể. Tôi rất vui khi có thêm cơ hội cống hiến một trận chung kết nữa tới các CĐV\".\r\n\r\nAlcaraz sẽ gặp người thắng trong cặp bán kết còn lại giữa Lorenzo Musetti và Alex de Minaur ở trận tranh vô địch ngày mai 13/4.\r\n\r\n', '1744472516_h54.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 15:41:56', 'Thể thao', 0, 0),
(66, 'Djokovic bị loại sớm ở Monte Carlo Masters', 'Monaco - Novak Djokovic thua Alejandro Tabilo 3-6, 4-6 ở trận ra quân tại giải Masters 1000 đất nện ở Monte Carlo, hôm 9/4.', 'Tham vọng vô địch mỗi giải Masters 1000 từ ba lần trở lên của Djokovic bất thành, khi anh lần thứ hai liên tiếp thua Tabilo trong chưa đầy một năm. Tabilo, người từng hạ Djokovic ở Rome Masters năm ngoái, trở thành tay vợt thứ chín trong lịch sử đánh bại Djokovic trong cả hai lần gặp đầu tiên, bên cạnh những tên tuổi như Rafael Nadal hay Roger Federer.\r\n\r\nĐang sa sút phong độ, Tabilo vẫn cho thấy anh là đối thủ kỵ giơ với Nole - người thắng liền 10 trận đất nện trước khi gặp đối thủ Chile. Djokovic khởi đầu tốt khi sớm tận dụng break-point trong game đầu, nhưng lép vế trong phần còn lại trận đấu trước tay vợt thuận tay trái. Nole mắc tới 29 lỗi tự đánh hỏng, gần gấp đôi Tabilo, trong khi chỉ có số winner tương đương đàn em.\r\nLần thứ ba trong bốn giải gần nhất Djokovic bị loại ngay trận đầu, sau các trận thua Matteo Berrettini ở Doha và Botic Van De Zandschulp tại Indian Wells. \"Tôi biết đối thủ khó chơi và bản thân có thể sẽ đánh không tốt. Nhưng tệ đến mức này thì tôi không ngờ tới\", Djokovic nói sau trận. \"Cảm giác này thực sự rất tồi tệ, và tôi xin lỗi tất cả những người đã phải chứng kiến màn trình diễn của tôi\".\r\n\r\nDjokovic tiếp tục lỡ hẹn với danh hiệu thứ 100. Anh sẽ tiếp tục hành trình mùa sân đất nện tại Madrid Masters - nơi Nole dự kiến sẽ tái hợp với HLV Andy Murray. Ở Monte Carlo, người đồng hành với tay vợt Serbia là em trai Marko Djokovic.\r\n\r\nCarlos Alcaraz, người thua Nole 4-6 ở set đấu tập trước giải, giành quyền vào vòng ba Monte Carlo Masters, khi thắng ngược 3-6, 6-0, 6-1 trước Francisco Cerundolo.\r\n\r\nTrận thắng thứ 68 giúp Alcaraz vượt qua cựu ngôi sao Andy Roddick để lấy vị trí thứ ba trong danh sách những tay vợt nam thắng nhiều nhất ở hệ thống Masters 1000 trước khi sang tuổi 22. Hai người đứng trên Alcaraz trong danh sách là Rafael Nadal (110 trận) và Novak Djokovic (75).\r\nDiễn biến trận đấu cho thấy Alcaraz đã thể hiện xuất sắc khả năng thay đổi chiến thuật. Sau khi thua set đầu vì để Cerundolo làm chủ cuộc chơi bằng những game giao hiệu quả và tiến vào trong sân dứt điểm, Alcaraz đã đổi cách trả bóng trong set hai. Những cú trả có điểm rơi khó, với tốc độ nhanh hơn giúp Alcaraz đạt tỷ lệ 44% trả bóng một thành công, làm điểm tựa cho việc thắng 12 trong 13 game cuối, trong đó có bảy game thắng liên tiếp từ đầu set hai.\r\n\r\nTay vợt số 22 thế giới Cerundolo không phải đối thủ dễ chịu. Trước trận này, anh hoà 14-14 khi đối đầu top 10 và chỉ thua 2-3 khi gặp top 3. Thành tích thắng 17 trận từ đầu năm của Cerundolo là tốt nhất ATP Tour, ngang với Alex de Minaur. Nhưng Cerundolo thua Alcaraz cả hai lần gặp, mới nhất là tứ kết BNP Paribas Open.\r\n\r\nGiao bóng bất ổn vẫn là điểm yếu của Alcaraz khi anh đối mặt bốn break-point ở set hai, bao gồm hai điểm trong game giao đầu tiên. Nhưng những pha dứt điểm quyết đoán từ cú thuận tay uy lực và sự biến hoá từ cuối sân đã giúp tay vợt 21 tuổi cứu toàn bộ bốn điểm này. Ở set ba, Alcaraz không cho Cerundolo cơ hội bẻ game, vượt lên dẫn 5-1 nhờ tận dụng hai break-point liên tiếp. Dù đối thủ Argentina nỗ lực cứu hai match-point ở game cuối, Alcaraz vẫn dứt điểm thành công bằng một cú bỏ nhỏ để thắng chung cuộc.\r\n\r\nDự Monte Carlo Masters từ năm 2022 nhưng đây mới là lần đầu Alcaraz thắng trận tại giải đấu này. Tay vợt sở hữu 4 Grand Slam không dự giải hai năm gần nhất do chấn thương. Anh đã chơi 100 trận trên mặt sân bụi đỏ và thắng 82 trong số đó. Tỷ lệ thắng 82% của anh chỉ kém Nadal (91%) và Bjorn Borg (86%), đồng thời nhỉnh hơn Djokovic (81%).\r\n\r\nĐối thủ ở vòng ba của Alcaraz là tay vợt chơi từ vòng loại Daniel Altmaier. Tay vợt Đức đang trở thành ngựa ô của giải khi lần lượt hạ Felix Auger-Aliassime và Richard Gasquet ở hai vòng đầu.', '1744472710_h55.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 15:45:10', 'Thể thao', 0, 0),
(67, 'Djokovic thay đổi thế nào dưới thời HLV Murray?', 'Cú giao bóng của Novak Djokovic cải thiện rõ rệt, nhưng trả bóng lại có hiệu suất thấp hơn kể từ khi anh hợp tác với người bạn Andy Murray.', 'Sau ba tháng hợp tác, Novak Djokovic và Andy Murray gặt hái một số kết quả tốt, như việc vào bán kết Australia Mở rộng và chung kết Miami Mở rộng. Dù vậy, tay vợt Serbia chưa thể chấm dứt cơn khát danh hiệu ATP Tour và những thất bại cho thấy thay đổi trong lối chơi chưa phát huy hiệu quả tốt nhất cho Djokovic khi anh sắp sang tuổi 38.\r\n\r\nCú giao bóng đột phá\r\n\r\nĐiểm nổi bật của Djokovic năm 2025 là những chỉ số giao bóng ấn tượng nhất sự nghiệp. Tỷ lệ cú ace hiện tại của anh là 16,2% – cao nhất kể từ khi TennisViz bắt đầu thống kê số liệu này năm 2011. Đây là bước tiến đáng kinh ngạc so với 10 năm gần đây, khi con số này của anh thường chỉ từ 11-13%.\r\n\r\nTỷ lệ giao bóng một thành công cũng đạt 73,9%, đứng đầu ATP Tour và vượt trội hơn cả những mùa giải anh giành 3/4 Grand Slam (2011, 2015, 2021, 2023). Đặc biệt, tại Miami, Djokovic giao bóng một thành công 83% trước Sebastian Korda (tứ kết) và 87% trước Grigor Dimitrov (bán kết). Trong suốt giai đoạn từ 2016-2024, tỷ lệ bóng một trong sân của Djokovic chỉ quanh mốc 65%.\r\n\"Năm nay tôi giao bóng khá tốt,\" Djokovic phát biểu sau chiến thắng trước Dimitrov. \"Có thể một số yếu tố khác chưa ổn, nhưng giao bóng luôn vững vàng. Tôi không ngờ mình có thể vượt cả màn trình diễn trước đó (83%). Chỉ sai sót 5-6 quả giao bóng cả trận. Điều đó thật tuyệt vời\".\r\n\r\nAnh còn đùa rằng: \"Giờ tôi hiểu cảm giác của Isner hay Opelka rồi – giao bóng một toàn trúng vị trí hiểm hóc. Tôi không nhiều ace bằng họ, nhưng điều này giúp giảm áp lực lên những cú đánh khác và gây sức ép cho đối thủ.\"\r\n\r\nYếu tố tâm lý\r\n\r\nDù không trực tiếp liên quan tới huấn luyện kỹ thuật, sự hiện diện của Murray đã mang lại giá trị tinh thần lớn cho Djokovic. Trong môn thể thao được cho là \"90% thành công phụ thuộc vào tâm lý\", việc có một huyền thoại từng trải ở bên là lợi thế không nhỏ. Điều này đã được chứng minh ở các trận đấu khó như với Carlos Alcaraz tại Australia Mở rộng, hay chuỗi trận thắng ở Miami Mở rộng.\r\nSau chiến thắng trước Lorenzo Musetti ở vòng 1/8 Miami Mở rộng, Djokovic chia sẻ: \"Trong lúc trì hoãn vì mưa, tôi và Andy đã có cuộc trò chuyện sâu sắc. Tôi luôn kính trọng anh ấy với tư cách đối thủ, giờ còn hơn thế. Thật khó tin khi đối thủ cũ của tôi giờ là HLV, nhiệt tình cổ vũ từ khán đài. Đôi lúc tôi tự hỏi: \'Đây là mơ hay thực?\".\r\n\r\nHai người cũng tận hưởng thời gian bên nhau, khi cùng chơi golf và ăn tối trong những ngày thi đấu ở Indian Wells và Miami.\r\n\r\nDjokovic chưa hoàn thiện dưới thời Murray\r\n\r\nDù giao bóng xuất sắc, Djokovic lại có tỷ lệ điểm trả giao bóng thắng và game trả giao bóng thắng thấp nhất 10 năm qua (theo TennisViz). Điều này có thể liên quan tới việc anh muốn tấn công nhiều hơn, nhằm cắt ngắn các pha bóng để tiết kiệm sức. Nhưng nó vẫn cần kiểm chứng thêm ở các giải đấu tới.\r\n\r\nTỷ lệ ghi điểm từ trả bóng của Djokovic mùa này đang là 36,5% và tỷ lệ thắng game đỡ bóng là 24,2%. Hai chỉ số này đều thấp hơn các mùa trước. Thông thường Djokovic đạt tỷ lệ ghi điểm trả bóng quanh mốc 40% và tỷ lệ thắng game đỡ bóng khoảng 30%.\r\n\r\nĐây sẽ là bài toán cho bộ đôi Djokovic-Murray trước mùa đất nện, khởi tranh từ Monte-Carlo Masters vào 6/4. Dù sống ở Monaco, Djokovic mới chỉ hai lần vô địch giải này. Anh chắc chắn muốn có khởi đầu thuận lợi để hướng tới Roland Garros – chặng cuối trong hành trình chinh phục Grand Slam thứ 25.\r\nNếu đoạt Monte Carlo Masters, Djokovic cũng sẽ trở thành người duy nhất thắng cả 9 Masters 1000 từ ba lần trở lên. Anh cũng đang hướng đến chức vô địch ATP Tour thứ 100.', '1744472790_h56.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 15:46:30', 'Thể thao', 0, 0);
INSERT INTO `news` (`id`, `title`, `description`, `content`, `image`, `author`, `created_at`, `category`, `is_featured`, `views`) VALUES
(68, 'Djokovic thua chung kết Miami Mở rộng 2025', 'Mỹ - Novak Djokovic chưa thể chạm vào danh hiệu ATP Tour thứ 100 trong sự nghiệp, sau khi thua tay vợt tuổi teen Jakub Mensik 6-7(4), 6-7(4) hôm 30/3.', 'Trận chung kết Miami Mở rộng bị hoãn 5 tiếng, do một cơn mưa lớn ở sân Hard Rock. Hai tay vợt chỉ bắt đầu trận đấu khi đồng hồ điểm 20 giờ. Trời mát hơn, nhưng Djokovic vẫn bắt nhịp chậm. Ở game giao đầu tiên, bóng một của Nole không đạt vận tốc bóng một như mong muốn, tạo điều kiện để Mensik trả bóng sâu và khó. Tay vợt Serbia bị dẫn 15-40 rồi thua game vì lỗi tự đánh hỏng.\r\nThua trước một game giao là thói quen của Djokovic ở Miami Mở rộng năm nay. Do đó, ngay cả khi bị dẫn 0-3, kỷ lục gia Masters 1000 vẫn chơi tỉnh táo. Anh tìm lại nhịp giao, dứt điểm quyết đoán để giữ được hai game giao kế tiếp. Bên kia lưới, khả năng giao bóng khủng khiếp của Mensik vẫn được duy trì, với tỷ lệ bóng một trong sân 66% và những cú ace liên tiếp.\r\n\r\nTuy nhiên, giữ được nhịp giao đều đặn trong cả set là điều không đơn giản cho các tay vợt mỗi khi đối mặt chuyên gia trả bóng như Djokovic. Tới game bảy, Mensik liên tục lỗi bóng một, và không đạt độ khó khi giao bóng hai. Điều này khiến tay vợt tuổi teen đối mặt những cú trả sâu và hiểm của Djokovic. Nhận ra sự bối rối của đàn em, Djokovic bền bỉ bám điểm và tới được break-point. Ở điểm số này, Mensik lại giao hỏng bóng một, rồi tự đánh ra ngoài ở loạt đôi công sau đó.\r\n\r\nLấy lại break-point giúp Djokovic chơi tự tin ở cuối set. Nhưng điều đó cũng không giúp anh có thêm game đỡ bóng thành công, khi Mensik lập tức tìm lại kỹ năng giao bóng để duy trì thế cân bằng. Sau khi Nole gỡ hòa 6-6 bằng một game trắng, hai tay vợt bước vào tie-break – loạt đánh mà Mensik từng hạ đàn anh 7-4 ở tứ kết Thượng Hải Masters mùa trước. Lần này, tay vợt 19 tuổi tái hiện đúng tỷ số đó, khi tận dụng những lỗi đánh hỏng của Nole để có hai mini-break và dẫn sâu 5-0.\r\nỞ điểm cuối loạt tie-break, Djokovic bị trượt chân, dẫn tới trả bóng lỏng và để Mensik ghi winner. Tay vợt Serbia tiến ra khu vực ghế nghỉ và phàn nàn với trọng tài về mặt sân – nơi đã chịu trận mưa trước đó. Trong lúc các nhân viên lau lại mặt sân, Djokovic cẩn thận thay giày để tránh những cú trượt chân như set đầu. Nhưng điều này cũng không giúp thế trận của anh dễ thở hơn.\r\n\r\nMensik duy trì tự tin cao độ trong trận chung kết ATP Tour thứ hai từ đầu sự nghiệp. Tay vợt CH Czech có break-point trước ở game 5 set hai, khi tận dụng được những tình huống lỏng tay của Nole. Tuy nhiên, mỗi khi rơi vào thế khó Djokovic lại tìm được cú giao bóng một để ép lại đàn em và ghi điểm. Mensik lỡ cả hai break-point ở game này, nhưng cũng không cho Djokovic break-point nào trong hai game giao kế sau.\r\n\r\nSet đấu tiếp tục kéo dài, trong những tiếng hô \"Nole\" từ các CĐV để ủng hộ chủ nhân 24 Grand Slam gỡ hòa. Sau khi bảo toàn các game đấu một cách chật vật, Djokovic đưa set hai vào tie-break và ở đây, anh tiếp tục thua điểm giao đầu tiên vì một cú đánh rúc lưới. Trong cú giao kế sau của Mensik, Djokovic trả bóng tốt nhưng không còn đủ sức rướn cho những cú cứu bóng sau đó, dẫn tới lỗi rúc lưới thứ hai liên tiếp.\r\n\r\nDù thể hiện bộ mặt mệt mỏi, Djokovic vẫn nỗ lực gỡ hòa 2-2, bằng hai cú ra vợt hết tay, một khiến Mensik đỡ hỏng và một cú ace. Tuy nhiên, mỗi điểm số với Djokovic như một sự tra tấn, khi Mensik lỳ lợm từ những cú đánh cuối sân. Tay vợt tuổi teen tạo ra loạt đôi công giằng co, điều bóng liên tục ra hai góc và khiến Djokovic tự đánh rúc lưới. Sau khi nhìn bóng lỗi, Djokovic buông vợt nằm sân và thở dốc. Anh gần như kiệt sức vì những điểm số căng thẳng liên tiếp.\r\nThể lực suy giảm khiến Djokovic không còn duy trì được sức mạnh của bóng một và chỉ giao bóng hai ở mức an toàn. Điều này khiến anh thua thêm hai mini-break và bị dẫn 3-6. Dù gỡ được một điểm nhưng Nole không thể cản cú giao bóng cuối mạnh về góc chữ A của Mensik. Tay vợt kỳ cựu tiếp tục thua 4-7 ở loạt đánh này và nhận thất bại chung cuộc sau hai set.\r\n\r\nMensik thắng bảy loạt tie-break ở Miami Mở rộng năm nay, bao gồm bốn loạt liên tiếp tại bán kết và chung kết. Tay vợt CH Czech hạ \"ông vua tie-break\" Djokovic bằng 14 cú ace cả trận, để có danh hiệu ATP Tour đầu tiên trong sự nghiệp ở tuổi 19. Anh lễ phép chắp tay cúi chào thần tượng khi tiến lên lưới mừng chiến thắng.\r\n\r\nDjokovic lỡ thêm một cơ hội có danh hiệu thứ 100 trong sự nghiệp. Đây là trận chung kết Masters 1000 thứ hai liên tiếp anh thất bại, sau Thượng Hải Masters mùa trước. Cơn hạn danh hiệu vẫn kéo dài với chủ nhân 24 Grand Slam. Ngoại trừ HC vàng Olympic Paris hè năm ngoái, anh chưa có cup ATP nào kể từ tháng 11/2023.', '1744472873_h57.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 15:47:53', 'Thể thao', 0, 0),
(69, 'Thần đồng Philippines lỡ chung kết Miami Mở rộng', 'Mỹ - Tài năng 19 tuổi Alexandra Eala thua sít sao 6-7(3), 7-5, 3-6 trước Jessica Pegula trong trận bán kết Miami Mở rộng hôm 28/3.', 'Mỹ - Tài năng 19 tuổi Alexandra Eala thua sít sao 6-7(3), 7-5, 3-6 trước Jessica Pegula trong trận bán kết Miami Mở rộng hôm 28/3.\r\nBước vào trận bán kết WTA 1000 đầu tiên trong sự nghiệp, Eala vẫn duy trì phong độ cao từ đầu giải. Cô tạo hàng loạt pha đôi công dai dẳng và hấp dẫn với đàn chị hơn 12 tuổi. Sau khi giành được hai game đỡ bóng, Eala tới được game giao quyết định set một ở tỷ số dẫn 5-4. Ở game đấu này, tay vợt Philippines có set-point khi dẫn 40-30.\r\n\r\nTuy nhiên, một tình huống nôn nóng đánh lỗi, cùng cú giao bóng bất ổn đã khiến Eala thua liền hai điểm và bị gỡ 5-5. Trong loạt tie-break sau đó, tài năng của học viện Rafael Nadal cũng mắc sai sót ở cuối loạt đánh khi thua liên tiếp hai điểm và thất bại 3-7.\r\n\r\nLối đánh bền bỉ của cả hai tay vợt đưa người xem tới hàng loạt pha đấu dai dẳng trận này. Việc giao bóng không tốt cũng khiến họ đối mặt bảy break-point trong set hai và chỉ cứu được hai lần. Eala là người chơi tốt hơn khi có ba lần tận dụng thành công cơ hội thắng game đỡ bóng, trong đó có game 11 quan trọng giúp cô vượt lên dẫn 6-5. Ở game kế sau, Eala không phạm sai lầm như set đầu, khi duy trì thế dẫn điểm để tận dụng thành công set-point thứ hai.\r\n\r\nGiống Eala, Pegula chơi bền bỉ, linh hoạt và thích ngắt nhịp bằng những pha bóng ngắn. Nhưng ở trận này, tay vợt Mỹ thường xuyên đánh hỏng khi tiến vào trong sân dứt điểm. Bên kia lưới, Eala cũng đánh hỏng nhiều hơn thường lệ, trong đó có nhiều pha dứt điểm mà đàn chị đã chôn chân đứng nhìn. Ở game tám, cú dứt điểm thuận tay chệch dây khiến Eala trả giá bằng break-point và bị dẫn 3-5. Tay vợt Philippines cố trở lại ở game đỡ bóng sau đó, nhưng chỉ cứu được một match-point, trước khi thua 3-6.\r\n\r\nThất bại đáng tiếc khiến Eala lỡ cơ hội trở thành tay vợt đầu tiên nhận vé đặc cách dự Miami Mở rộng vào tới chung kết giải này. Dù vậy, nhờ đi đến bán kết, cô chắc chắn sẽ vào top 100 thế giới khi bảng điểm cập nhật tuần tới.\r\n\r\nPegula – hạt giống số bốn – thì sẽ đấu số một thế giới Aryna Sabalenka ở chung kết. Trong trận bán kết đầu tiên, Sabalenka tiếp tục thể hiện sức mạnh vượt trội khi thắng nhanh Jasmine Paolini 6-2, 6-2. Cũng như BNP Paribas Open tuần trước, Sabalenka vào chung kết Miami Mở rộng mà không thua set nào.', '1744472985_h58.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 15:49:45', 'Thể thao', 0, 0),
(70, 'Đô vật Belarus: \'Đừng nghĩ Hà Văn Hiếu già mà lơ là\'', 'Bắc Ninh - Zakhar Dzmitrychenka tôn trọng và dè chừng Hà Văn Hiếu dù được đánh giá cao hơn ở nhiều mặt trước cuộc đấu vật vào ngày mai 11/4.', 'Trận đấu giữa Zakhar và Hà Văn Hiếu diễn ra tại một hội vật làng ở Bắc Ninh. Trước đó, cả hai từng chốt kèo đấu vào ngày 10/3, rồi dự kiến đấu vào ngày 31/3, nhưng lần lượt đổi lịch.\r\n\r\nTrận đấu ngày mai sẽ diễn ra theo luật đấu vật truyền thống của Việt Nam. Người chiến thắng là khi nâng được đối thủ lên khỏi mặt thảm đấu 30 cm, hoặc đến xương bả vai.\r\nZakhar cho biết mới bay ra Hà Nội chiều nay 10/4 để chuẩn bị lên sới ngày mai. \"Tôi duy trì tập luyện hàng ngày tại võ đường ở Nha Trang một tháng qua\", đô vật Belarus nói với VnExpress. \"Các bài tập chủ yếu ở phòng gym liên quan đến võ tổng hợp MMA và Brazil Jiu-jitsu (BJJ)\".\r\n\r\nZakhar cao 1,81 m và đang nặng 94 kg. Anh nhẹ hơn Hà Văn Hiếu khoảng 30 kg và thấp hơn 10 cm. \"Tôi rất hiếm khi gặp những đối thủ to lớn như vậy, nhưng vẫn kỳ vọng sẽ có trận đấu đẹp và trải nghiệm hay ngày mai\", anh cho hay.\r\n\r\nDù thể trạng nhỏ hơn Hà Văn Hiếu, Zakhar sinh năm 1996 lại trẻ hơn 11 tuổi. Anh đang vào độ sung sức nhất, còn Hà Văn Hiếu mới tập lại thể lực sau khi nghỉ thi đấu gần một năm. Trên các diễn đàn, nhiều người tin Zakhar có cửa thắng cao hơn. Tuy nhiên, đô vật Belarus vẫn dè dặt khi dự đoán về trận đấu.\r\n\r\n\"Đừng nghĩ Hà Văn Hiếu già mà lơ là. Tôi cũng không nghĩ anh ấy già. Tôi chỉ thấy đối thủ của mình là VĐV vật giàu kinh nghiệm số một Việt Nam, có kỹ thuật và sức mạnh tốt\", Zakhar cho biết. \"Có người nói rằng tôi sẽ thắng nhanh, nhưng đã là cuộc chiến thì chuyện gì cũng có thể xảy ra\".\r\nTrong một video do VĐV BJJ Đào Hồng Sơn đăng tải, Hà Văn Hiếu vẫn vác Zakhar lên vai rồi leo 20 bậc cầu thang khá dễ dàng. Trong khi đó, Zakhar cũng có thể dùng tay siết eo nhấc bổng đàn anh. Tuy nhiên, thể lực vẫn là điểm yếu hơn của đô vật Việt Nam, vì vậy, nếu trận đấu diễn ra lâu, Văn Hiếu sẽ chịu bất lợi.\r\n\r\nGiải thưởng cho trận đấu giữa Zakhar và Hà Văn Hiếu hiện đang ở mức khoảng 250 triệu đồng.\r\n\r\nZakhar từng đạt danh hiệu Master of Sports môn vật Belarus, giành HC đồng vật cổ điển thế giới 2018 do Liên đoàn Vật Quốc tế tổ chức, đồng thời vào top 10 giải U23 Thế giới nội dung vật cổ điển. Đô vật này sang Việt Nam năm 2024 và làm việc tại một võ đường ở Nha Trang. Đến tháng 11/2024, anh tham dự sự kiện võ thuật tổng hợp MMA LION Championship 19 ở Phú Quốc. Tại đây, Zakhar thắng áp đảo võ sĩ Việt Nam Trần Quốc Toản ở hạng 84 kg.\r\n\r\nTrong khi đó, Hà Văn Hiếu sinh năm 1985, được xem là tượng đài của làng vật Việt Nam hạng trên 100kg, ở cả nội dung tự do và cổ điển, trước khi giải nghệ năm 2022. Văn Hiếu hiện giữ kỷ lục 15 lần vô địch Quốc gia từ năm 2005, cùng bốn HC vàng SEA Games (2009, 2011, 2019, 2022). Đô vật quê Lạng Sơn cũng từng tham gia đóng phim \"Cha cõng con\" năm 2017. Sau khi giải nghệ, anh vẫn tham gia công việc trong giới võ thuật, trong đó có làm trọng tài tại Lion Championship, hay đi dự các sới vật làng.\r\n\r\nKèo vật giữa Zakhar và Hà Văn Hiếu được mong đợi, khi VĐV người Belarus nổi lên qua các trận đấu tại hội vật làng ở miền Bắc sau Tết Nguyên Đán 2025. Nhiều người hâm mộ ví Zakhar là \"Thor\" - vị thần sấm trong thần thoại Bắc Âu, nhờ vóc dáng cao to, ngoại hình đẹp và mái tóc dài màu vàng. Zakhar cũng gửi lời thách đấu tới cựu đô vật hạng siêu nặng Hà Văn Hiếu, sau khi thắng ở sới vật làng Khu Ba (Thạch Thất, Hà Nội) vào giữa tháng 2.', '1744473063_h59.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 15:51:03', 'Thể thao', 0, 0),
(71, 'VĐV Belarus và Hiếu \'khổng lồ\' bất phân thắng bại sau 2 giờ đấu vật', 'Bắc Ninh - Zakhar Dzmitrychenka và Hà Văn Hiếu hòa sau 12 hiệp quần thảo ở trận đấu vật tại hội làng Mẫn Xá, Bắc Ninh chiều 11/4.', 'Hà Văn Hiếu sinh năm 1985, cao 1,92 m và nặng 130 kg. Đối thủ Zakhar sinh năm 1996, cao 1,81 m và nặng 94 kg. Văn Hiếu đã nghỉ thi đấu chuyên nghiệp một năm và mới bắt đầu tập thể lực trở lại khoảng hai tuần. Trong khi đó, Zakhar đang sung sức, duy trì tập luyện tại võ đường ở Nha Trang để chuẩn bị cho các sự kiện võ tổng hợp MMA sau này.\r\n\r\nTrận đấu được chốt kèo từ hôm 10/3, diễn ra theo luật đấu vật dân tộc Việt Nam không giới hạn thời gian và không chấm điểm. Người chiến thắng là khi nâng được đối thủ lên khỏi mặt thảm đấu 30 cm, hoặc đến xương bả vai hoặc ngửa bụng (lấm lưng trắng bụng). Khi khống chế tay, hai đô vật không được đan ngón tay vào nhau để tránh gây chấn thương.\r\nKhán giả địa phương xếp thành vòng tròn khoảng 1.000 người tại sân đình Đền Tướng Quốc Linh và Chùa Thánh Quang. Nhiều người leo lên đền, chùa cao xung quanh để theo dõi. Những người đến muộn chọn leo lên các cột, cây cao để có thể nhìn thấy sới vật.\r\n\r\nZakhar và Hà Văn Hiếu bắt đầu đấu từ 16h20 đến 18h30, với gần một tiếng đấu sức trên sới vật. Ban tổ chức chủ động kết thúc trận đấu sau 12 hiệp và chia đôi giải thưởng 10.000 USD, tương đương 260 triệu đồng.\r\n\r\nSau màn se đài, Văn Hiếu và Zakhar trao nhau nụ cười tươi, rồi lao vào đối đầu, tóm gáy và đấu tay. Diễn biến này kéo dài suốt 12 hiệp. Hai đô vật chủ yếu dùng sức, gần như không lộ sơ hở để áp dụng kỹ thuật, hoặc túm được khố, eo hay chân đối phương.\r\n\r\nZakhar nhanh nhẹn hơn, chủ động tấn công nhưng với kinh nghiệm dày dạn, Hà Văn Hiếu bình tĩnh hóa giải. Đô vật Việt Nam thường chủ động lùi về sát vạch sân để giữ miếng thủ.\r\nKhi hai đô vật nghỉ giữa hiệp, các trận đấu khác được diễn ra. Với tính chất hội làng, trận cũng nhiều lần tạm dừng để các mạnh thường quân thưởng nóng. Theo đó, Văn Hiếu và Zakhar mỗi người nhận thêm 2.500 USD tiền mặt.\r\n\r\nỞ hiệp 5, Hiếu có lần hiếm hoi túm được khố đối thủ, nhưng Zakhar rất nhanh hạ thấp trọng tâm rồi lùi ra ngoài vạch để giữ thế. Khi được trọng tài đề xuất kết thúc hiệp, hai đô vật vẫn muốn tiếp tục. Tuy nhiên, chỉ nửa phút sau, Hiếu bị chảy máu ở gần mắt phải, nên phải tạm dừng để chăm sóc y tế. Chấn thương này cũng khiến trận đấu nhiều lần bị gián đoạn về sau.\r\n\r\nĐô vật 40 tuổi trở lại với đầu quấn băng. Lúc này, ban tổ chức phải bật thêm đèn khi trời chuyển tối. Sau vài phút giằng co, Hà Văn Hiếu bất ngờ tấn công dồn dập nhưng Zakhar vẫn thoát kịp. Khi hai đô vật bắt đầu hăng máu, trận đấu lại phải tạm dừng để Hiếu tiếp tục được chăm sóc vết thương rỉ máu.\r\n\r\nTừ hiệp 10, cuộc chiến thể lực sẽ quyết định phần lớn vào thắng thua và Văn Hiếu là phía bất lợi. Tuy nhiên, Zakhar không tạo ra được khác biệt. Trái lại, ở hiệp 11, Hà Văn Hiếu túm tay trái vào khố đối thủ, nhưng Zakhar lùi nhanh ra ngoài vòng để thoát hiểm. Hai đô vật siêu nặng ngã vào một số khán giả lớn tuổi ngồi gần sới, khiến ban tổ chức có một phen lo lắng.\r\n\r\nSau 12 hiệp không phân thắng bại, ban tổ chức quyết định hòa và chia đôi giải thưởng. Zakhar và Văn Hiếu chấp nhận quyết định. Đô vật Belarus nhấc bổng đàn anh thể hiện tinh thần thượng võ. Văn Hiếu cũng thở dốc sau thời gian thi đấu dài.\r\nVăn Hiếu được xem là tượng đài của làng vật Việt Nam hạng trên 100kg, ở cả nội dung tự do và cổ điển, trước khi giải nghệ năm 2022. Văn Hiếu hiện giữ kỷ lục 15 lần vô địch Quốc gia từ năm 2005, cùng bốn HC vàng SEA Games (2009, 2011, 2019, 2022). Đô vật quê Lạng Sơn cũng từng tham gia đóng phim \"Cha cõng con\" năm 2017. Sau khi giải nghệ, anh vẫn tham gia công việc trong giới võ thuật, trong đó có làm trọng tài tại Lion Championship, hay đi dự các sới vật làng.\r\n\r\nZakhar từng đạt danh hiệu Master of Sports môn vật Belarus, giành HC đồng vật cổ điển thế giới 2018 do Liên đoàn Vật Quốc tế tổ chức, đồng thời vào top 10 giải U23 Thế giới nội dung vật cổ điển. Đô vật này sang Việt Nam năm 2024 và làm việc tại một võ đường ở Nha Trang. Đến tháng 11/2024, anh tham dự sự kiện võ thuật tổng hợp MMA LION Championship 19 ở Phú Quốc. Tại đây, Zakhar thắng áp đảo võ sĩ Việt Nam Trần Quốc Toản ở hạng 84 kg.\r\n\r\nVĐV người Belarus nổi lên qua các trận đấu tại hội vật làng ở miền Bắc sau Tết Nguyên Đán 2025, khi theo chân VĐV Brazil jiu-jitsu (BJJ) Đào Hồng Sơn. Nhiều người hâm mộ ví Zakhar là \"Thor\" - vị thần sấm trong thần thoại Bắc Âu, nhờ vóc dáng cao to, ngoại hình đẹp và mái tóc dài màu vàng.\r\n\r\nKhoảng 550 lễ hội diễn ra hàng năm trên địa bàn tỉnh Bắc Ninh. Hội làng Mẫn Xá ở huyện Yên Phong thường diễn ra vào ngày giữa tháng 3 âm lịch hàng năm. Ngoài Zakhar, hội làng năm nay còn có một đô vật nước ngoài là Rafael Raimond đến từ Cộng hòa Dagestan, cùng nhiều VĐV vật hay BJJ chuyên nghiệp của Việt Nam.', '1744473150_h60.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 15:52:30', 'Thể thao', 0, 0),
(72, 'Raphinha suýt tái hiện tình huống Ronaldo bị trộm bàn thắng', 'Tây Ban Nha - Tiền đạo Raphinha xin lỗi trung vệ Pau Cubarsi, vì đá nối trên vạch cầu môn khi bóng chuẩn bị lăn vào lưới, trong trận Barca thắng Dortmund ở Champions League.', 'Phút 25, tiền vệ Fermin Lopez treo bóng vào cấm địa cho trung vệ Inigo Martinez đánh đầu vào trước cầu môn. Trung vệ 18 tuổi Pau Cubarsi rướn người đá về góc xa, đưa bóng từ từ lăn vào lưới. Nhưng khi bóng lăn đến vạch cầu môn, Raphinha ập vào đá tung lưới đội khách. Cubarsi chạy mừng, nhưng bàn thắng được tính cho đồng đội ngôi sao Brazil.\r\nTheo Guardian, Raphinha không có lý gì phải làm thế. Bởi khi Cubarsi chạm bóng, Raphinha dường như ở vị trí việt vị. Các trọng tài VAR mất vài phút để xác định tình huống. Cuối cùng, công nghệ bắt việt vị bán tự động cho rằng Raphinha vẫn đứng trên một cầu thủ Dortmund. Vì thế, Barca được công nhận bàn mở tỷ số.\r\n\r\nSau trận Raphinha tiết lộ anh đã trải qua những phút âu lo, khi chờ VAR vào cuộc. \"Tôi đã nói với Cubarsi rằng đã chạm bóng trước khi qua vạch, nên bàn thắng được tính cho tôi\", tiền đạo 28 tuổi nói. \"Tôi đã xin lỗi và cậu ấy nói không có vấn đề gì\".\r\nRaphinha cho biết có hai lý do khiến anh làm vậy. Đầu tiên là anh không chắc chắn bóng có lăn vào lưới hay không. Thứ hai là anh cũng khao khát ghi bàn. Cựu tiền đạo Leeds đang dẫn đầu cuộc đua Vua phá lưới Champions League mùa này, với 12 bàn thắng, nhiều hơn đồng đội Robert Lewandowski một bàn.\r\n\r\nBáo Marca cho rằng Raphinha đã \"trộm\" bàn thắng của Cubarsi. \"Anh ấy đáng lẽ không nên làm như vậy, đặc biệt là với cầu thủ mới 18 tuổi có cơ hội ghi bàn đầu tay ở Champions League\", bài báo viết. \"Khi VAR kiểm tra bàn thắng, gương mặt âu lo của Raphinha cho thấy điều đó. Nhưng dễ hiểu vì anh đang đua Vua phá lưới\".\r\n\r\nTình huống của Raphinha gợi nhớ lần Luis Nani \"trộm\" bàn thắng của Cristiano Ronaldo trong trận giao hữu giữa Bồ Đào Nha và Tây Ban Nha ở Lisbon ngày 17/11/2010.\r\nKhi đó, thủ quân Bồ Đào Nha có bóng phản công bên trái, cách cầu môn khoảng 40 m. Anh rê vào cấm địa, giả sút chân trái để ngoặt bóng sang chân phải, khiến trung vệ Gerard Pique trượt dài trên cỏ. Sau đó, Ronaldo bấm bóng qua đầu thủ môn Iker Casillas, đưa bóng hướng vào cầu môn. Khi bóng bay đến vạch cầu môn, Nani ập vào đánh đầu nối vào lưới. Trọng tài biên ngay lập tức căng cờ báo việt vị Nani, không công nhận bàn thắng cho Ronaldo.\r\n\r\nSiêu sao Bồ Đào Nha tức giận, liên tục chửi mắng đàn em vì bị tước bàn thắng đẹp. Dù vậy, chủ nhà vẫn thắng Tây Ban Nha 4-0 trong trận này, đúng với tỷ số giữa Barca và Dortmund tối 9/4.', '1744473249_h61.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 15:54:09', 'Thể thao', 0, 0),
(73, 'Barca đại thắng ở tứ kết Champions League', 'Tây Ban Nha - Tam tấu Lewandowski, Raphinha và Lamine Yamal cùng ghi bàn, giúp Barca thắng đội khách Dortmund 4-0 ở lượt đi tứ kết Champions League.', 'Sau hiệu còi mãn cuộc, Yamal giơ hai ngón tay hướng về khán đài, được máy quay hướng đến một hồi lâu. Dù vậy, tiền đạo Robert Lewandowski mới được UEFA chọn là cầu thủ hay nhất trận, với cú đúp bàn thắng. Còn cầu thủ điểm cao nhất trận theo thống kê Fotmob là Raphinha với một bàn, hai đường kiến tạo và 9,2 điểm.\r\n\r\nHàng nghìn khán giả đội khách thì chưa buồn ra về sau khi hết trận, mà đứng lặng đi. Họ hiểu rằng cơ hội đi tiếp ở cặp đấu này gần như không còn, khi Dortmund sẽ phải thắng cách biệt bốn bàn trở lên ở lượt về. Tình thế đã có thể không tệ đến vậy, nếu đoàn quân Niko Kovac không phung phí cơ hội trong trận tối 9/4.\r\nBarca mở tỷ số ở phút 25, từ một tình huống cố định. Tiền vệ Fermin Lopez treo bóng vào cấm địa cho trung vệ Inigo Martínez đánh đầu cắt ngang cầu môn. Trung vệ 18 tuổi Pau Cubarsi nhanh chân đá về góc xa, đưa bóng từ từ lăn vào cầu môn. Nhưng ngay khi bóng chuẩn bị lăn hết vạch, Raphinha ập vào đá tung lưới đội khách.\r\n\r\nBàn thắng được tính cho Raphinha, nhưng ngôi sao Brazil suýt ở vị trí việt vị, khiến Barca có thể bị từ chối bàn. Nếu điều đó xảy ra, Raphinha sẽ tái hiện tình huống Luis Nani làm hỏng bàn thắng của siêu sao Cristiano Ronaldo trong một trận giao hữu của Bồ Đào Nha năm 2010.\r\n\r\nBarca nhân đôi cách biệt đầu hiệp hai, trong tình huống có dấu giày của tam tấu hàng công. Yamal treo bóng từ bên phải về cột hai, cho Raphinha đánh đầu chuyền ngang về góc xa. Bóng bay như đặt vào đầu Lewandowski, để tiền đạo Ba Lan dễ dàng dứt điểm tung nóc lưới ở cự ly chưa đến 1 m.\r\nLewandowski vẫn thường xuyên gieo sầu cho Dortmund, kể từ khi rời CLB này miễn phí để gia nhập Bayern Munich hè 2014. Phút 66, anh sút một chạm chìm về góc gần để lập cú đúp, từ đường căng ngang của Lopez. Đây là bàn thắng thứ 29 của tiền đạo 36 tuổi, trong 28 trận gặp Dortmund. Dù vậy, công đầu thuộc về Yamal với đường chọc khe dọc biên phải để Lopez thoát xuống thoáng.\r\n\r\nLewandowski đã ghi 11 bàn ở Champions League mùa này, còn Raphinha dẫn đầu cuộc đua Vua phá lưới với 12 bàn. Hai cầu thủ này lần lượt cướp bóng và chuyền ngang trong pha phản công phút 77, để dọn cỗ cho đàn em Yamal. Với cú thúc mũi giày chìm về góc xa, khi thủ môn Gregor Kobel đứng chôn chân, ngôi sao 17 tuổi ấn định thắng lợi cho chủ nhà.\r\n\r\nTheo phóng viên Sid Lowe từ Guardian, Yamal mới là cầu thủ quan trọng nhất ở Barca hiện tại, dù anh mới có bốn bàn và ba đường kiến tạo ở Champions League mùa này. Tam tấu của Barca đã góp công lớn giúp họ ghi 144 bàn trên mọi đấu trường. Và thầy trò Hansi Flick vẫn chưa dừng lại ở đó.', '1744473326_h62.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 15:55:26', 'Thể thao', 0, 0),
(74, 'Yamal: \'Tôi thăng tiến nhanh như trò chơi điện tử\'', 'Tây Ban Nha - Trước lượt đi tứ kết Champions League gặp Dortmund, Lamine Yamal cảm thấy như đang trong trò chơi điện tử PES khi thăng tiến mạnh mẽ ở cả cấp CLB lẫn đội tuyển hai năm qua.', 'Yamal sinh ngày 13/7/2007, trưởng thành từ học viện La Masia, và ra mắt La Liga tháng 4/2023. Anh là cầu thủ trẻ nhất chơi cho Barca, khi 15 tuổi 9 tháng và 16 ngày. Yamal còn lập hai kỷ lục cầu thủ đá chính trẻ nhất cho Barca (16 tuổi 38 ngày) và người trẻ nhất kiến tạo tại La Liga (16 tuổi 45 ngày).\r\n\r\nKể từ đó, Yamal thăng tiến mạnh mẽ, trở thành cầu thủ trẻ nhất ghi bàn tại La Liga và vòng loại trực tiếp Champions League, cầu thủ ra mắt và ghi bàn trẻ nhất của tuyển Tây Ban Nha. Ở tuổi 16, anh đã là nhân tố chủ chốt giúp Tây Ban Nha vô địch Euro 2024.\r\nYamal ví sự thăng tiến nhanh chóng này như trong Pro Evolution Soccer (PES) và khẳng định không lo lắng khi chơi các trận cầu lớn.\r\n\r\n\"Cứ như tôi đang chơi PES vậy. Thật không thể tin được\", Yamal nói với ESPN ngày 7/4. \"Tôi không thấy áp lực, mà chỉ cố gắng tận hưởng bản thân, không nghĩ quá nhiều về những thứ khác. Tôi cũng không nghĩ quá nhiều về những gì đang làm, những gì sẽ làm vào ngày mai, hay trận nào quan trọng hơn. Tôi biết tầm quan trọng của việc đá cho Barca, nhưng không ngán áp lực\".\r\n\r\nMùa này, Yamal đá chính 21 trận, thi đấu tổng cộng 3.448 phút cho Barca trên mọi đấu trường, chỉ kém Jules Kounde, Pedri và Raphinha. Tiền đạo 17 tuổi cho biết không bị quá tải như truyền thông lo lắng và tự tin sẽ tiếp tục đạt thể trạng tốt nhất cho hai tháng cuối mùa.\r\n\r\n\"Tôi cảm thấy rất ổn\", anh bày tỏ. \"Một điều tôi học được trong năm nay là cách làm chủ bản thân. Có người hỏi tại sao tôi không di chuyển trong 10 phút? Nhưng cuối cùng, có những khoảnh khắc bạn không phải chạy. Bạn phải biết cách quản lý bản thân trong trận đấu, trong suốt mùa giải. Đó là điều tôi học được và đang làm tốt\".\r\nYamal góp 13 bàn và 20 kiến tạo qua 42 trận trên mọi đấu trường mùa này. Anh cùng Barca vô địch Siêu Cup Tây Ban Nha, và đang hướng tới ba danh hiệu còn lại. CLB xứ Catalonia dẫn đầu La Liga, hơn Real ba điểm, vào chung kết Cup Nhà Vua và gặp Dortmund ở tứ kết Champions League.\r\n\r\nYamal tự tin sẽ cùng Barca hoàn thành mục tiêu này, qua đó tái hiện thành tích mùa 2008-2009 và 2014-2015. \"Ngay từ đầu mùa, tôi đã đặt mục tiêu cao nhất\", anh nhấn mạnh. \"Mục tiêu là giành cú ăn ba và bây giờ cũng không khác gì. Bạn phải tin vào chính mình\".', '1744473390_h63.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 15:56:30', 'Thể thao', 0, 0),
(75, 'Raphinha nổi nóng sau trận hòa của Barca', 'Tây Ban Nha - Tiền đạo Raphinha phản ứng trọng tài, rồi xô xát với đội trưởng Ter Stegen sau trận Barca hoà Betis 1-1 ở La Liga.', 'Sau khi trọng tài Gil Manzano thổi còi kết thúc trận đấu trên sân Montjuic tối 5/4, các cầu thủ chủ nhà phản đối về thời gian bù giờ quá ngắn (bốn phút). Họ cũng chất vấn về việc không được thực hiện nốt quả phạt góc.\r\n\r\nTrong số này, Raphinha tức giận ra mặt, khiến một số cầu thủ Barca phải kéo ra, yêu cầu đi về phòng thay đồ. Robert Lewandowski thậm chí phải lớn tiếng quát tháo, trong khi Raphinha quay lại nói thêm điều gì đó.\r\nKhi tiền đạo người Brazil chuẩn bị đi vào đường hầm với vẻ hằm hằm, đội trưởng chính thức của Barca, thủ môn Marc Andre Ter Stegen tới trấn an. Nhưng sau đó, tiền đạo người Brazil hất tay và đẩy đội trưởng.\r\n\r\nSau trận, trọng tài không đưa Raphinha vào báo cáo. Do đó, Liên đoàn bóng đá Tây Ban Nha nhiều khả năng bỏ qua vụ việc.\r\n\r\nTrận này, Barca mở tỷ số nhờ công Gavi phút thứ 7, nhưng không giữ được lợi thế. Betis gỡ hòa sau đó chừng 10 phút. Kết quả này khiến Barca phải dừng mạch 9 trận thắng ở La Liga. Nó cũng khiến đội dẫn đầu lỡ cơ hội tận dụng tối đa lợi thế trong lúc Real thua Valencia 1-2. Barca hiện có 67 điểm sau 30 vòng, hơn Real bốn điểm.\r\n\r\nRaphinha vào sân trong hiệp hai. Tiền đạo người Brazil chỉ để lại ấn tượng duy nhất là pha cứa lòng chệch cột.\r\nTrước trận Argentina - Brazil hôm 25/3, Raphinha từng tuyên bố hạ nhục nhà đương kim vô địch World Cup. Nhưng sau đó, tiền đạo 28 tuổi chẳng những không làm được, mà còn tịt ngòi và thua thảm 1-4.\r\n\r\nSau khi trở lại Barca, ngôi sao ghi 27 bàn và kiến tạo 20 lần trong 42 trận đầu mùa vẫn chưa lấy lại phong độ. Hai trận gần nhất, anh đều vào sân từ ghế dự bị, chơi thiếu đột biến và bỏ lỡ nhiều cơ hội.', '1744473451_h64.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 15:57:31', 'Thể thao', 0, 28),
(76, 'Flick: \'Barca được phép mơ về cú ăn ba\'', 'Tây Ban Nha - HLV Hansi Flick không loại trừ khả năng Barca đoạt Champions League, La Liga và Cup Nhà vua mùa này.', '\"Chúng tôi được phép mơ ước, nhưng phải tập trung vào những gì muốn đạt được\", HLV Flick trả lời về khả năng giành cú ăn ba sau trận bán kết lượt về Cup Nhà vua tối 2/4. \"Từ Madrid, chúng tôi sẽ về đến Barcelona tầm 3-4h sáng hôm sau, sau đó phải ngủ, quay lại tập và hồi phục. Chúng tôi sẽ phải đấu Betis cuối tuần này. Vài tuần tới sẽ không dễ dàng, nhưng Barca đang có một đội bóng tuyệt vời. CLB và CĐV có thể tự hào về đội bóng\".\r\n\r\nVới chiến thắng 1-0 trước Atletico hôm qua, Barca vượt qua vòng bán kết lượt với tổng tỷ số 5-4, sẽ gặp Real ở chung kết Cup Nhà vua ngày 26/4. Họ cũng đang dẫn đầu La Liga với 66 điểm qua 29 vòng - hơn Real ba điểm, và chuẩn bị gặp Dortmund ở tứ kết Champions League.\r\nTrong lịch sử, Barca từng giành cú ăn ba mùa 2008-2009 và 2014-2015, bằng thành tích với Bayern. Các đội khác từng giành một cú ăn ba gồm Celtic, Ajax, PSV, Man Utd, Inter và Man City.\r\n\r\nTrong trận đấu tối 2/4, Atletico không còn ghi bàn vào lưới Barca như ba trận đầu mùa (thắng 2-1, hòa 4-4 và thua 2-4). Barca không chỉ tấn công tốt, mà còn buộc đối thủ rời sân với không lần nào sút trúng đích. HLV Flick ca ngợi hàng thủ, sự dũng cảm và tiến bộ so với đầu mùa.\r\n\r\nBàn duy nhất trận đấu được ghi do công Ferran Torres phút 27. Khi được hỏi về việc chọn Torres đá chính thay Robert Lewandowski, Flick trả lời: \"Ferran đá chính vì đã chơi các trận đấu Cup khác và đạt phong độ tốt. Vào thứ Bảy tuần tới, chúng ta sẽ thấy một số 9 khác. Các cầu thủ đều đang đạt phong độ tốt và giúp chúng tôi rất nhiều\".\r\n\r\nChung kết Cup Nhà vua ngày 26/4 sẽ là trận El Clasico thứ ba mùa này. Hai trận trước, Barca toàn thắng Real, với các kết quả 4-0 ở lượt đi La Liga và 5-2 ở chung kết Siêu Cup Tây Ban Nha.\r\n\r\nKhi được hỏi về trận đại chiến sắp tới, Flick từ chối. \"Tôi không muốn nói về trận chung kết. Bây giờ tôi phải tập trung vào trận gặp Betis ở La Liga. Thật tuyệt khi tham dự El Clasico, nhưng chúng tôi còn nhiều trận khác\", ông nói.\r\n\r\nLần gần nhất El Clasico diễn ra ở chung kết Cup Nhà vua là mùa 2013-2014, với chiến thắng 2-1 nghiêng về Real. Năm 2011, hai đội cũng gặp nhau ở chung kết Cup Nhà vua và Real thắng 1-0.', '1744473512_h65.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 15:58:32', 'Thể thao', 0, 40),
(77, 'Rooney từng suýt bỏ Man Utd để gia nhập Barca', 'Anh - Cựu đội trưởng Man Utd từng cân nhắc chuyển sang Barca để được chơi cùng thế hệ vàng Lionel Messi, Andres Iniesta, Xavi.', 'Năm 2010, Rooney từng gây ồn ào khi không muốn gia hạn hợp đồng, đồng thời yêu cầu Man Utd cho ra đi. Sau đó, ba CLB gồm Chelsea, Real và Barca cùng tiếp cận anh.\r\n\r\n\"Khi đó, tôi đã sẵn sàng tới Tây Ban Nha\", Rooney nói với BBC hôm 31/3. \"Một số cuộc đàm phán đã diễn ra. Một thỏa thuận với Real có vẻ khả thi, nhưng Barca mới là đội bóng mà tôi nghĩ đến nhiều nhất. Tôi đã tính đến việc làm thế nào để hòa nhập với Lionel Messi, Andres Iniesta, Xavi và Sergio Busquets. Sau cùng, tôi quyết định ở lại, nhưng Barca lúc đó là một đội bóng đáng kinh ngạc. Bất kỳ cầu thủ nào cũng muốn đến chơi cho họ\".\r\nGiai đoạn 2008-2012, Barca là CLB mạnh nhất thế giới. Chỉ trong bốn năm, thế hệ của Messi, Iniesta, Xavi cùng nhau giành hai Champions League và ba La Liga liên tiếp. Năm 2010, sau khi Rooney quyết định ở lại Man Utd, Barca ký hợp đồng với David Villa. Trong trận chung kết Champions League 2010-2011 mà Barca thắng Man Utd 3-1, hai cầu thủ này cùng ghi bàn.\r\n\r\nRooney ở lại Man Utd đến 2017, giành tổng cộng năm Ngoại hạng Anh, một Champions League và một Europa League. Cựu đội trưởng người Anh cũng lập kỷ lục ghi 253 bàn cho \"Quỷ Đỏ\".\r\n\r\nHè tới, Trent Alexander-Arnold chuẩn bị rời Liverpool để gia nhập Real. Do hợp đồng với Liverpool sắp hết hạn, hậu vệ người Anh sẽ ra đi theo dạng chuyển nhượng tự do.\r\n\r\nRooney hiểu tại sao Alexander-Arnold muốn tới Real - đội giành hai Champions League và hai La Liga trong ba mùa gần nhất. Theo cựu đội trưởng Man Utd, đội chủ sân Bernabeu không khác nhiều so với Barca 15 năm trước, nên dễ dàng thu hút mọi cầu thủ.\r\n\r\n\"Real hiện sở hữu những siêu sao như Kylian Mbappe, Jude Bellingham hay Vinicius, nên tất nhiên Alexander-Arnold muốn chơi cho họ. Bất kỳ cầu thủ nào cũng sẽ nghĩ như vậy\", Rooney nói. \"Cậu ấy đã ở Liverpool gần 20 năm - từ khi 6 tuổi - nhưng thực tế là hầu hết các cầu thủ đều chuyển đi vào một thời điểm nào đó. Đó là lý do rất ít cầu thủ gắn bó lâu dài với một CLB như Gary Neville hay Jamie Carragher\".', '1744473582_h66.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 15:59:42', 'Thể thao', 0, 8),
(78, 'HAGL thua ngược Công an Hà Nội', 'Hà Nội - Sớm dẫn bàn, nhưng HAGL vẫn thua Công an Hà Nội 1-3 ở vòng 18 V-League 2024-2025, tối 12/4.', 'Làm khách tại sân Hàng Đẫy, HAGL không có trung vệ tuyển thủ quốc gia Phạm Lý Đức vì chấn thương, phải thay bằng trung vệ 18 tuổi Đinh Quang Kiệt. Tiền vệ Marciel cũng phải ngồi ngoài vì thẻ phạt, Lê Hữu Phước đá thay. Trong khi đó, chủ nhà vắng mũi nhọn Leo Artur - tiền đạo đã ghi 8 bàn từ đầu mùa.\r\nDù sứt mẻ lực lượng và đá sân khách, HAGL bất ngờ tấn công phủ đầu ngay khi nhập cuộc. Phút thứ 4, hậu vệ Phan Du Học thoát xuống từ cánh trái, dẫn bóng xâm nhập vòng cấm rồi căng vào trong để Washington Brandao đánh đầu vào góc gần mở tỷ số. Thủ môn Nguyễn Filip đứng chôn chân vì không kịp khép góc.\r\n\r\nCơn mưa nặng hạt trên sân Hàng Đẫy khiến lối chơi hai đội bị ảnh hưởng. Trong đó, đội chủ nhà, vốn đá nhỏ, kỹ thuật, không thể luân chuyển nhuần nhuyễn để tạo cơ hội nguy hiểm. Đến phút 23, từ quả phạt ngoài vòng cấm, họ mới có thể ghi bàn. Từ khoảng 25 mét, khi các cầu thủ đều nghĩ Quảng Hải sẽ đá phạt trong ngày sinh nhật, thì tiền vệ Lê Văn Đô bất ngờ ra chân, sút qua hàng rào HAGL, ghim bóng vào góc xa gỡ hòa 1-1.\r\n\r\nCó bàn gỡ, đội chủ nhà chơi tốt hơn, bắt đầu có một số cơ hội cho Alan, Quang Hải dứt điểm, nhưng không thể thắng được thủ môn 22 tuổi Trần Trung Kiên. HAGL cũng tiếc nuối khi tiền vệ Lê Hữu Phước xâm nhập vòng cấm rồi kết thúc căng ở góc gần, đưa bóng trúng người thủ môn Nguyễn Filip.\r\n\r\nSau hiệp đầu cân bằng, Công an Hà Nội chơi bùng nổ hơn ở hiệp hai. Ngay những giây đầu tiên sau khi giao bóng, đội chủ nhà tổ chức tấn công rồi Alan vẩy bóng để Phan Văn Đức sút căng hạ gục Trung Kiên nâng tỷ số 2-1.\r\nVới lợi thế dẫn bàn, các học trò của HLV Mano Polking thoải mái tấn công dồn dập. Nhưng lần lượt Quang Hải, Văn Đô rồi Alan đều bỏ lỡ. Ở phút 69, Vital đã đánh bại thủ môn Trung Kiên, nhưng bị Lê Văn Sơn phá bóng trên vạch vôi.\r\n\r\nỞ vòng trước, HAGL thắng tưng bừng Bình Dương 4-0. Nhưng hôm nay, thiếu các mũi nhọn như Marciel, Bảo Toàn, họ không thể gây được sức ép sau khi bị dẫn ngược. Cơ hội tốt nhất ở hiệp hai là pha sút xa đi chệch cột của Minh Vương.\r\n\r\nTrong khi đó, Công an Hà Nội tạo nhiều cơ hội, rồi cụ thể hóa ưu thế ở phút 90+3. Từ pha phản công hiệu quả, tiền đạo Nguyễn Văn Vinh thoải mái dứt điểm tung lưới đội khách ấn định tỷ số 3-1. Thắng lợi này giúp đội bóng thủ đô vươn lên thứ tư với 28 điểm. Còn HAGL vẫn đứng thứ tám, với 21 điểm.\r\n\r\n\"Tôi rất vui vì đội đã đạt được mục tiêu đề ra\", tiền vệ Quảng Hải nói sau trận. \"Dù bị dẫn bàn trước, chúng tôi luôn có niềm tin sẽ chơi tốt hơn. Đội cũng tận dụng tốt các cơ hội để kết liễu đối thủ\".', '1744473655_h67.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:00:55', 'Thể thao', 0, 1),
(79, 'Runner nổi tiếng Australia bị bắt vì ma túy', 'Nhật Bản - Matthew Fox, người sáng lập Sweat Elite và là YouTuber nổi tiếng trong giới chạy bộ, bị bắt tại Osaka ngày 9/4 vì vi phạm Luật kiểm soát ma túy.', 'Fox bị cáo buộc đã cố gắng buôn lậu thuốc chứa THC (thành phần hoạt chất trong cần sa) vào Nhật Bản từ Mỹ thông qua dịch vụ chuyển phát nhanh quốc tế hồi tháng 2.\r\n\r\nChân chạy 38 tuổi phủ nhận các cáo buộc. Nhưng nếu bị buộc tội và kết án về tội nhập lậu ma túy, Fox có thể phải ngồi tù nhiều năm và, hoặc bị trục xuất. Trong trường hợp đó, anh có khả năng bị cấm vĩnh viễn nhập cảnh vào Nhật Bản.\r\nFox, cũng là người dẫn podcast và là nhà sáng lập công ty truyền thông chạy bộ Sweat Elite, tới Nhật Bản hồi tháng 2 để chạy Osaka Marathon 2025. Cuộc đua này là một phần trong thử thách hoàn thành mỗi tháng một giải marathon với thành tích sub2:30 (dưới 2 giờ 30 phút). Tuy nhiên, runner Australia đã không hoàn thành cuộc đua khi DNF sau 22 km.\r\n\r\nSau khi bỏ cuộc, Fox thông báo trên mạng xã hội rằng anh \"không thể kiểm soát những căng thẳng trong cuộc sống do chính những quyết định của mình gây ra\". Anh còn nói thêm: \"Một chút xui xẻo, nhưng có rất nhiều điều tôi có thể cải thiện trong việc đưa ra quyết định liên quan đến cuộc sống và du lịch\".\r\n\r\nTrong một tập podcast gần đây, Fox giải thích rằng anh thường mang theo kẹo dẻo THC để kiểm soát chứng lo âu và giấc ngủ, thừa nhận rằng nhiều quốc gia khác không cấm sử dụng các sản phẩm THC dùng cho cá nhân.\r\n\r\n\"Tôi luôn ra nước ngoài thi đấu với kẹo dẻo THC. Hầu hết các quốc gia không quan tâm, nhưng Nhật Bản thì khác - họ có xu hướng kiểm tra bạn mọi lúc\", anh nói.\r\n\r\nFox biết rõ luật ma túy nghiêm ngặt của Nhật Bản nên đã tránh mang kẹo dẻo vào nước này. Sau Osaka Marathon 2025, anh dành vài ngày trượt tuyết trên núi, rồi ngừng hoạt động trên các tài khoản mạng xã hội.\r\nĐây không phải lần đầu tiên Fox gặp rắc rối với pháp luật tại Nhật Bản. Năm 2023, anh bị cấm tham gia Ageo City Half Marathon suốt đời sau chạy bằng bib của người khác - hành vi bị xem là gian lận.\r\n\r\nFox có thành tích cá nhân tốt nhất (PB) là 2 giờ 18 phút 41 giây, đạt được tại Indianapolis Marathon 2024 năm ngoái.', '1744473722_h68.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:02:02', 'Thể thao', 0, 6),
(80, 'Ông Đoàn Ngọc Hải: \'Huế đủ sức tổ chức giải marathon 30.000 người\'', 'Ông Đoàn Ngọc Hải cho rằng thành phố Huế đủ điều kiện hạ tầng, dịch vụ để tổ chức giải chạy quy mô lớn tương tự Bangkok Marathon.', 'Nguyên Phó chủ tịch UBND Quận 1 Đoàn Ngọc Hải tham gia VnExpress Marathon Huế hôm 6/4, chạy 42km trong 4 tiếng 29 phút. Ông cho biết, đây là lần thứ 113 hoàn thành cự ly full marathon trong vòng 5 năm qua, trải dài ở nhiều quốc gia. Từng trải nghiệm nhiều sự kiện thể thao đường phố trong và ngoài nước, ông Hải đánh giá Huế là một trong những thành phố ở Việt Nam có tiềm năng tổ chức giải chạy quy mô lớn.\r\n\"Thái Lan làm được thì Việt Nam cũng làm được. Huế đủ sức đáp ứng công suất phòng, dịch vụ, lại có sự ủng hộ của chính quyền địa phương nên hoàn toàn có thể đáp ứng nhu cầu của 30.000 VĐV\", ông Hải nói.\r\n\r\nVnExpress Marathon Huế, giải chạy lớn nhất và lâu đời nhất cố đô, mỗi năm thu hút trung bình 10.000 đến 12.000 vận động viên. Với bốn cự ly 5km, 10km, 21km và 42km, giải tổ chức ở trung tâm thành phố, nơi có hệ thống hạ tầng du lịch, giao thông và không gian phù hợp cho các sự kiện quy mô vừa và lớn. Thống kê từ ban tổ chức cho thấy hơn 70% vận động viên đến từ các tỉnh thành khác, tạo ra tác động rõ rệt đến ngành du lịch địa phương.\r\n\r\nTrong buổi họp báo trước VnExpress Marathon Huế 2025, Phó chủ tịch UBND TP Huế - ông Nguyễn Thanh Bình - khẳng định tổ chức sự kiện thể thao nói chung và chạy bộ nói riêng là định hướng của thành phố. Những năm qua, Huế đã sửa sang các tuyến đường trung tâm, mở tuyến đường đi bộ dọc hai bờ sông Hương để khuyến khích người dân tập luyện. Theo ông Bình, Huế đủ khả năng tổ chức giải chạy 30.000 VĐV như Bangkok Marathon 2024.\r\n\"Tuy nhiên, tổ chức giải quy mô 30.000 người sẽ là thách thức không nhỏ với ban tổ chức. Tôi từng tham gia những giải ở Thụy Điển, quy mô trên 30.000 nhưng trong đó, số người chạy 42km phải tới 20.000. Tôi nghĩ giải chạy không chỉ cần gia tăng số lượng mà phải cả chất lượng. Ban tổ chức cần có biện pháp thúc đẩy VĐV, ngay cả cự ly 5 hay 10km, thi đấu nghiêm túc hơn\", ông Hải nói.\r\n\r\nCung đường Huế độc đáo vì chạy xuyên qua kinh thành cổ - địa điểm được UNESCO công nhận là Di sản Văn hóa Thế giới. VnExpress Marathon Huế thường diễn ra vào tháng 4, thời điểm Huế có thời tiết mát mẻ không kém gì mùa xuân ở miền Bắc. Ngoài ra, đường chạy bằng phẳng, ít dốc khiến Huế hội tụ những điều kiện để tổ chức marathon.\r\nTheo Sở Du lịch Huế, vừa qua, trong tuần diễn ra VnExpress Marathon và kỳ nghỉ lễ Giỗ Tổ Hùng Vương, 109.000 lượt du khách và runner đến mang về cho thành phố 221 tỷ đồng doanh thu, tăng 149,5 tỷ đồng - gấp 3 so với năm ngoái (71,5 tỷ đồng). Khách lưu trú đạt hơn 41.600 lượt, công suất buồng phòng trung bình 90%.\r\n\r\nTừ đầu năm đến nay, Huế liên tục tổ chức các hoạt động văn hóa, thể thao lớn thuộc khuôn khổ Năm Du lịch quốc gia - Huế 2025 và Festival Huế. Trong quý I, thành phố đón khoảng 1,5 triệu lượt khách, tăng 62% so với cùng kỳ 2024; trong đó, khách quốc tế đạt 666.000 lượt. Doanh thu du lịch ba tháng đầu năm khoảng 2.600 tỷ đồng, tăng hơn 50%.', '1744473803_h69.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:03:23', 'Thể thao', 0, 3),
(81, 'Hoãn giải robot chạy half marathon ở Trung Quốc vì gió', 'Trung Quốc - Gió mạnh có thể gây nguy cơ lật đổ robot hình người, vì vậy giải half marathon đặc biệt tại quận Đại Hưng, Bắc Kinh sẽ lùi lịch một tuần.', 'Do ảnh hưởng của không khí lạnh, Bắc Kinh sẽ có gió mạnh và nhiệt độ xuống thấp từ chiều 11/4 đến 13/4 - thời điểm diễn ra giải. Đây là lần đầu tiên trong gần 10 năm, Bắc Kinh phát cảnh báo cam về gió mạnh trên toàn thành phố.\r\nTheo các chuyên gia, robot hình người có nguy cơ bị lật đổ trong thời tiết như vậy do thiếu dữ liệu đào tạo cho tình huống bất thường này. Vì thế, ban tổ chức thông báo dời lịch giải half marathon tại quận Đại Hưng sang 19/4.\r\nTheo kế hoạch, robot hình người và con người lần đầu tiên trong lịch sử sẽ cùng tham gia chạy cự ly 21km, trên cung đường thuộc quận Đại Hưng. Giải thu hút hơn 30.000 người và 20 công ty robot, viện nghiên cứu, CLB robot, trường đại học đăng ký tham gia.\r\n\r\nĐiểm xuất phát nằm ở quảng trường phía Nam của công viên Nam Hải Tử, và đích tại Công viên Đổi mới Thông tin Quốc gia Hồ Thông minh. Giải áp dụng nguyên tắc \"đăng ký đồng thời, cùng đường chạy, xuất phát đồng thời\" cho cả robot và người chạy.\r\n\r\nKhác biệt duy nhất là robot hình người sẽ có đường chạy riêng, được phân cách bằng hàng rào sắt hoặc dải phân cách xanh để đảm bảo an toàn.\r\nBan tổ chức giải quy định giới hạn thời gian hoàn thành (COT) cho robot là 3 giờ 30 phút. Các đội được phép thay pin hoặc chuyển robot theo hình thức tiếp sức trong suốt chặng đua. Kết quả đánh giá dựa trên thời gian hoàn thành và số lần thay robot, với mỗi lần thay bị phạt cộng thêm 10 phút vào thành tích tổng.\r\n\r\nRobot dự giải này phải có thiết kế hình người, di chuyển bằng hai chân, không dùng bánh xe. Phương pháp điều khiển có thể bằng tay, bán tự động hoặc tự động hoàn toàn. Các đội phải đảm bảo robot không gây hư hại đường đua, robot khác hoặc người xung quanh, đồng thời tuân thủ đường chạy và quy tắc kỹ thuật.\r\n\r\nGiải thưởng cho ba vị trí dẫn đầu là 5.000, 4.000 và 3.00 nhân dân tệ. Ngoài ra, sẽ có các giải Sức bền tốt nhất, Tốc độ nhanh nhất và được Bình chọn cao nhất.', '1744473872_h70.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:04:32', 'Thể thao', 0, 2),
(82, '\'Chạy bộ là trò chơi của tâm trí\'', 'TP Huế - Theo độc giả Bình Minh, việc vào vạch xuất phát với suy nghĩ cuộc đua này chỉ là một bài longrun đã giúp cô hoàn thành cự ly 21km vượt xa mong đợi ở VnExpress Marathon Huế.', 'Khi lên đường vào Huế, tôi chỉ đặt mục tiêu chạy vui, tận hưởng bầu không khí phấn khích của một cuộc đua, và mang huy chương về tặng con trai.\r\n\r\nTôi không quá căng thẳng về thành tích, vì mấy tháng vừa rồi tôi tập luyện có phần thiếu bài bản. Buổi chạy dài nhất của tôi trong thời gian này là 17 km, mileage tuần cao nhất chỉ 25 km, và hoàn toàn không có bài tập tốc độ nào. Trong các buổi chạy cuối tuần của tôi cùng CLB Chay365 ở hồ Gươm, Hà Nội, rất lâu rồi tôi cũng không chạy được pace trung bình dưới 6. Thậm chí, tôi còn không theo được hết bài long run theo \"tàu\" 6:30 của CLB. Vì thế, tôi cũng xác định sub2 cho cự ly 21 ở Huế là bất khả thi.\r\n\r\nVào đến Huế, tối muộn thứ Bảy, trước khi đi ngủ, tôi mới tìm xem danh sách pacer, với ý định sẽ bám theo nhóm dẫn tốc sub2:15. Nhưng chỉ có hai nhóm sub2 và sub2:30. Nên tôi tặc lưỡi với suy nghĩ \"Mình sẽ theo tàu sub2, rồi được đến đâu thì đến\".\r\n\r\nSáng Chủ nhật, tôi dậy từ 2h sáng, sớm hai tiếng so với giờ xuất phát cự ly 21. Nếu là trước đây, tôi sẽ stress vì ngủ ít, nhưng lúc này, tôi thấy ngủ thêm một tiếng đồng hồ nữa chắc thành tích cũng không cải thiện được là bao, bởi giấc ngủ lý tưởng trước cuộc đua phải tầm 4 tiếng.\r\n\r\nTôi có mặt sớm khoảng 15 phút, và mất thêm khoảng 10 phút để luồn lách lên phía trên tới gần vạch xuất phát hơn. Tôi không có mong muốn lên ảnh hay chạy nhóm đầu, mà chỉ muốn lên đó để bám theo các pacer thường tập trung hết phía trên.\r\n\r\nSau khi xuất phát, tôi phải chạy gần 5 phút mới đuổi kịp nhóm pacer sub2, đồng nghĩa với việc chạy nhanh hơn pace mục tiêu khoảng 20 giây mỗi km trong một đến hai km đầu tiên. Do đường chạy rất đông runner trong thời gian này, tôi có phần căng thẳng vì vừa phải chạy tốc độ cao hơn vừa phải để ý đề phòng va chạm với những người xung quanh, chân rào và ổ gà chẳng may có trên đường.\r\n\r\nTôi đọc đâu đó rằng não bộ con người không phân biệt được rõ ràng giữa điều đang thực sự xảy ra và điều ta tưởng tượng, nên những suy nghĩ lặp đi lặp lại có thể trở thành sự thật với tâm trí. Vì vậy, trên suốt quãng đường đeo bám pacer, tôi luôn tự nhẩm trong đầu câu nói: \"Đây chỉ là một buổi long run quanh hồ thôi, pace này nhẹ nhàng, không có gì khó cả\". Mỗi khi nghĩ như vậy, tôi lại thấy đỡ mệt hẳn.\r\n\r\nVà khi bám trụ được tới km thứ 14, tôi chuyển sang \"niệm chú\" như sau \"Sức mình hiện tại chắc chắn sẽ đạt sub2. Phải bám chặt các anh pacer thôi, không được tách đoàn\".\r\n\r\nKhoảng 3 km trước khi về đích, tôi tự thấy vẫn còn sức nên quyết định bứt lên rồi all-out để về đích. Nhưng tôi vẫn cẩn thận hỏi các anh pacer liệu còn đoạn dốc nào nữa không để phân phối sức. Và khi được thông tin rằng chỉ còn một dốc nhỏ lên cầu nữa thôi, tôi tự tin bứt lên. Lúc đó, tôi còn nghe một anh pacer nhắc to các runner khác đang theo tàu \"Mọi người cẩn thận, không nên chạy theo bạn này nhé, vì bạn ấy đang chạy pace cao hơn pace mục tiêu đấy\".\r\n\r\nKết quả là tôi về đích với thời gian 1 giờ 57 phút, rút ngắn hai phút so với thành tích tốt nhất (PR) trước đó lập cách đây 5 năm.\r\nTừng chạy bộ 10 năm, tham gia một số giải trong và ngoài nước, tôi không cổ súy cho việc lười tập mà vẫn vào race với mục tiêu cao. Chạy bộ vẫn là hoạt động thể thao đòi hỏi sự kỷ luật, kiên trì và tích lũy sức bền qua năm tháng. Nhưng với trải nghiệm vừa qua ở Huế, tôi tự thấy sức mạnh tâm trí đôi khi có thể giúp làm nên những điều mà ta từng cho là không thể.', '1744473941_h71.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:05:41', 'Thể thao', 0, 0),
(83, 'Guardiola lo mất vé dự Champions League vì thiếu cầu thủ', 'Anh - Với nguồn lực hiện có, HLV Pep Guardiola không chắc Man City có thể về đích trong top 5 Ngoại hạng Anh mùa này.', 'Do đã chắc một trong hai vị trí dẫn đầu bảng hệ số mùa giải UEFA, Ngoại hạng Anh được cử 5 đội dự Champions League mùa sau. Ba vé đầu nhiều khả năng thuộc về Liverpool, Arsenal và Nottingham Forest - đội tạo cách biệt lớn sau 31 vòng. Hai vé còn lại là mục tiêu cạnh tranh của Chelsea, Newcastle, Man City, Aston Villa, Fulham và Brighton.\r\n\r\nMan City hiện xếp thứ sáu với 52 điểm sau 31 trận, kém Chelsea và Newcastle một điểm. Trong đó, Newcastle đang nắm lợi thế khi chơi ít hơn một trận.\r\n\r\n\"Tình hình hiện tại tốt hơn, khi có 5 suất dự Champions League mùa sau thay vì 4. Nhưng với việc có 7 hay 8 ứng cử viên tham gia cuộc đua, chúng tôi có lẽ sẽ phải chờ đến phút cuối\", Guardiola nói trước trận tiếp Crystal Palace ở vòng 32 Ngoại hạng Anh.\r\nGuardiola cho biết vấn đề đáng lo nhất của ông là tình hình nhân sự. Manuel Akanji chỉ có thể trở lại từ cuối tháng 4, Erling Haaland và John Stones là đầu tháng 5, trong khi Nathan Ake và Rodri phải nghỉ hết mùa.\r\n\r\n\"Chúng tôi không có đội hình đủ tốt cho phần còn lại của mùa giải\", Guardiola nói thêm. \"Ba trung vệ vắng mặt, còn tiền đạo giỏi nhất sẽ không trở lại cho đến một hoặc hai trận cuối\".\r\n\r\nHLV của Man City từ chối bình luận về khả năng tăng cường lực lượng hè này, nhưng chắc chắn rằng đội bóng sẽ không mua cầu thủ ồ ạt như Chelsea. \"Tất nhiên là CLB đang xử lý, nhưng chúng tôi sẽ không có đến 45 cầu thủ trong đội hình. Điều đó gây bất ổn về mặt cảm xúc\", Guardiola nói.\r\n\r\nTừ khi dẫn dắt Man City mùa 2016-2017, Guardiola luôn kết thúc trong top 3 Ngoại hạng Anh, trong đó có 6 mùa vô địch. Trước đó, khi dẫn dắt Barca và Bayern, ông thậm chí luôn kết thúc trong top 2 giải quốc nội. Nếu phải nhận kết quả xấu mùa này, Guardiola sẽ không chỉ đứt mạch về đích trong top 3 các giải VĐQG, mà còn mất vé dự Champions League lần đầu tiên.', '1744474019_h72.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:06:59', 'Thể thao', 0, 32),
(84, 'Guardiola muốn Man City dựng tượng De Bruyne', 'Anh - HLV Pep Guardiola ca ngợi Kevin de Bruyne là tiền vệ vĩ đại bậc nhất lịch sử và muốn Man City dựng tượng sau khi anh rời CLB vào cuối mùa.', '\"Tôi không biết, nhưng sẽ cược rất nhiều tiền rằng Man City sẽ dựng tượng De Bruyne\", Guardiola nói hôm 4/4 trong buổi họp báo trước trận gặp Man Utd ở vòng 31 Ngoại hạng Anh. \"De Bruyne xứng đáng với điều đó\".\r\n\r\nTrước cuộc họp báo này ít giờ, De Brunye xác nhận không gia hạn và sẽ rời đi vào cuối mùa. Tiền vệ người Bỉ đến Man City với giá 83 triệu USD từ Wolfsburg hè 2015. Sau 10 mùa, anh giành 16 danh hiệu, trong đó có sáu Ngoại hạng Anh và một Champions League. Về mặt cá nhân, anh còn 5 lần vào đội hình tiêu biểu Ngoại hạng Anh, hai lần là cầu thủ hay nhất mùa và đoạt Quả Bóng Đồng năm 2022.\r\nTheo Guardiola, tầm ảnh hưởng của De Bruyne sánh ngang với các cựu công thần của Man City như Vincent Kompany, Sergio Aguero hay David Silva, và muốn cùng người hâm mộ tận hưởng những màn trình diễn của tiền vệ người Bỉ trong phần còn lại của mùa này. \"Chúng tôi còn 10 trận, và hy vọng là 11, trong đó có sáu trận sân nhà, để tận hưởng. Tôi chắc chắn De Bruyne sẽ nhận được tình yêu và sự công nhận mà cậu ấy xứng đáng có\", HLV người Tây Ban Nha nói, bày tỏ quyết tâm vào chung kết Cup FA - đấu trường duy nhất còn khả năng cạnh tranh danh hiệu.\r\n\r\nGuardiola tiết lộ vài ngày trước đã nói chuyện với De Bruyne về quyết định lần này. \"Chúng tôi có mối quan hệ đặc biệt\", ông kể. \"Chúng tôi đã cùng nhau trải qua bao nhiêu trận đấu, cuộc họp, buổi tập, bữa ăn và những khoảnh khắc tồi tệ, những cái ôm và tiếng hét? De Bruyne gắn bó với CLB trong một thập kỷ và ngoại trừ những ngày lễ, chúng tôi luôn ở bên nhau. Giờ thì mọi chuyện sẽ kết thúc. Đó sẽ là khoảnh khắc đầy cảm xúc\".\r\n\r\nGuardiola sau đó ca ngợi De Bruyne là tiền vệ vĩ đại bậc nhất lịch sử Ngoại hạng Anh cũng như thế giới. Theo ông, học trò luôn duy trì phong độ đỉnh cao trong màu áo Man City, trừ mùa trước nghỉ dài hạn vì chấn thương. \"Quả là một ngày buồn\", ông chia sẻ thêm. \"Tôi không cần phải nói về tầm ảnh hưởng của De Bruyne với thành công của Man City trong thập kỷ qua. Sẽ không thể tưởng tượng được nếu không có cậu ấy\".\r\n\r\nGuardiola cũng không biết De Bruyne có cùng Man City dự FIFA Club World Cup phiên bản mới vào mùa hè không. Club World Cup 2025 diễn ra từ 15/6 đến 13/7 tại 11 thành phố của Mỹ. Ở giải này, Man City cùng bảng với Juventus (Italy), Wydad FC (Morocco), Al Ain (UAE) và có thể đoạt 125 triệu USD nếu vô địch.', '1744474096_h73.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:08:16', 'Thể thao', 0, 3);
INSERT INTO `news` (`id`, `title`, `description`, `content`, `image`, `author`, `created_at`, `category`, `is_featured`, `views`) VALUES
(85, 'De Bruyne chia tay Man City', 'Anh - Tiền vệ 33 tuổi, Kevin de Bruyne thông báo sẽ rời Man City cuối mùa này, kết thúc 10 năm gắn bó với đội bóng.', '\"Gửi Manchester\", De Bruyne viết trên các tài khoản của anh trên mạng xã hội chiều 4/4. \"Đến đây, các bạn có lẽ đã nhận ra bức thư này nói về điều gì. Nên tôi sẽ đi thẳng vào vấn đề. Thời gian tới sẽ là những tháng cuối cùng tôi gắn bó với Man City\".\r\n\r\nTiền vệ Bỉ quyết định không gia hạn hợp đồng với Man City, khi giao kèo hiện tại giữa hai bên sẽ hết hạn ngày 30/6/2025. \"Mọi câu chuyện đều có hồi kết, nhưng thời gian gắn bó với Man City tuyệt vời nhất với tôi\", anh viết thêm. \"Với các cầu thủ, chúng tôi đều biết ngày này rồi cũng phải đến. Và hôm nay, các bạn xứng đáng được nghe tin này đầu tiên từ tôi\".\r\nDe Bruyne cho biết người Manchester đã cho anh mọi thứ, và anh cũng luôn cố gắng giúp đội bóng bằng mọi cách. \"Và đoán xem, chúng tôi đã đoạt mọi danh hiệu\", anh viết.\r\n\r\nDe Bruyne 33 tuổi, cập bến Man City từ Wolfsburg hè 2015 với giá kỷ lục đội bóng thành Manchester khi đó, 83 triệu USD. Sau 10 mùa giải, anh đã giành 16 danh hiệu với đội, trong đó có sáu Ngoại hạng Anh và một Champions League. Tiền vệ Bỉ năm lần vào đội hình tiêu biểu Ngoại hạng Anh, hai lần là cầu thủ hay nhất mùa giải. De Bruyne còn đoạt Quả Bóng Đồng năm 2022.\r\n\r\nDe Bruyne cưới vợ Michele Lacroix năm 2017, và có ba con, gồm bé trai Mason 8 tuổi, Rome 5 tuổi và bé gái Suri 3 tuổi. Trong thư chia tay Man City, anh tiết lộ vợ và các con đều biết ơn những gì Manchester đã làm cho gia đình. \"Manchester sẽ luôn có trong hộ chiếu của các con, và quan trọng hơn là ở trong tim của những đứa nhỏ\", anh bày tỏ.\r\n\r\nGiới chuyên môn coi De Bruyne là huyền thoại Ngoại hạng Anh. Anh cùng Thierry Henry giữ kỷ lục kiến tạo 20 bàn thắng trong một mùa giải. Trong 280 trận đã chơi ở Ngoại hạng, tiền vệ 33 tuổi ghi 70 bàn và kiến tạo 118 bàn khác. Cựu tiền đạo người Anh Chris Sutton coi De Bruyne là một trong ba tiền vệ giỏi nhất Ngoại hạng, kể năm 1992. Chuyên trang GiveMeSport xếp De Bruyne thứ năm trong danh sách tiền vệ vĩ đại tại giải, sau Paul Scholes, Steven Gerrard, Frank Lampard và Ryan Giggs.', '1744474154_h74.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:09:14', 'Thể thao', 0, 32),
(86, 'Nhà sáng lập 9x tham vọng chinh phục thử thách robot hình người', 'Peng Zhihui, sinh năm 1993, nhà sáng lập công ty robot hình người Agibot, là một trong những nhân tài công nghệ trẻ thu hút sự chú ý của Trung Quốc.', 'Chiều 9/4 tại Bắc Kinh, Thủ tướng Trung Quốc Lý Cường chủ trì hội nghị chuyên đề với các chuyên gia kinh tế và doanh nhân. Theo SMM, họ đều là lãnh đạo công ty hàng đầu Trung Quốc về kinh tế, khoa học, vận chuyển, máy móc và hậu cần xuyên biên giới. Trẻ nhất là Peng Zhihui, 32 tuổi, đồng sáng lập và giám đốc công nghệ của Zhiyuan Robotics, tên quốc tế là Agibot, chuyên sản xuất robot, trong đó có robot hình người. Agibot và Unitree Robotics là hai công ty nổi tiếng nhất của Trung Quốc trong lĩnh vực này.\r\nTheo Tân Hoa Xã, Zhihui đại diện cho thế hệ mới gồm những người sáng lập công ty khởi nghiệp công nghệ nổi bật, được giới lãnh đạo Trung Quốc công nhận. Các sự kiện như vậy thường là sự chứng thực từ cấp cao nhất của chính phủ, mở đường cho việc tiếp cận nhiều nguồn lực hơn trong tương lai.\r\n\r\nTrước đó, Liang Wenfeng của DeepSeek và Wang Xingxing của Unitree Robotics cũng đã có cuộc gặp quan trọng với Chủ tịch Tập Cận Bình.\r\n\r\nNhân tài robot trẻ tuổi\r\n\r\nZhihui sinh tại Cát An, tỉnh Giang Tây và học kỹ thuật y sinh tại Đại học Khoa học và Công nghệ điện tử Trung Quốc. Năm 2013, khi đang học năm thứ hai, anh bắt đầu tìm hiểu về khoa học máy tính và đăng ký tham gia cuộc thi thiết kế điện tử dành cho sinh viên toàn quốc. Anh đoạt giải nhất với hệ thống kiểm soát nhiệt độ thông minh tự chế.\r\n\r\nTừ đây, Zhihui nhận ra khả năng của mình và theo đuổi đam mê, tham gia nhiều cuộc thi tương tự trong và ngoài nước, giành hơn 40 giải thưởng. Đây là cách anh tích lũy kinh nghiệm và tiền bạc cho dự án khởi nghiệp sau này.\r\n\r\nNăm 2015, Zhihui và bạn cùng lớp thành lập công ty nhỏ về in 3D và nhanh chóng nhận khoản đầu tư 5 triệu nhân dân tệ (683.573 USD). Anh rời công ty sau khi tốt nghiệp đại học.\r\n\r\nTrong thời gian học thạc sĩ, Zhihui bắt đầu nghiên cứu robot hai chân có thể tránh chướng ngại vật và di chuyển về phía mục tiêu. Sản phẩm được giới chuyên môn đánh giá cao, đoạt giải sáng tạo trong cuộc thi robot quốc tế WRO 2018.\r\n\r\nCùng năm, anh nộp đơn vào hãng điện thoại Oppo với vai trò kỹ sư thuật toán. Hai năm sau, anh nghỉ việc để tham gia dự án tuyển chọn nhân tài trẻ Huawei Genius Youth. Chương trình là sáng kiến của nhà sáng lập Huawei Nhậm Chính Phi năm 2019, sau khi công ty bị Mỹ liệt vào danh sách đen thương mại.\r\n\r\nGenius Youth ưu tiên ứng viên có kết quả nghiên cứu thực tế và có tác động, từng chiến thắng trong các cuộc thi quốc tế hàng đầu về khoa học, công nghệ, kỹ thuật và toán học. Chiến dịch tuyển dụng được hàng loạt tài năng trẻ, nổi bật là Zhihui. Anh sau đó tham gia nghiên cứu hệ thống xử lý trung tâm dữ liệu Shengteng, chuyên về trí tuệ nhân tạo. Theo SCMP, mức lương anh nhận được hàng năm là 2 triệu nhân dân tệ (287.000 USD).\r\n\r\nNăm 2021, Zhihui gây sốt trên mạng xã hội Bilibili Trung Quốc khi đăng video dài 16 phút về cánh tay robot có tên Dummy lấy cảm hứng từ Iron Man. Video nhận 2,5 triệu lượt xem và 10.000 bình luận sau hơn một ngày. Dummy sử dụng bộ xử lý OpenHarmony và Shengteng Atlas AI của Huawei, có thể điều khiển từ xa qua hệ thống 5G của công ty. Quan trọng hơn, chi phí tạo ra cánh tay chỉ 10.000 nhân dân tệ (1.552 USD).\r\n\r\nMột năm sau, Zhihui thông báo nghỉ việc để \"bắt đầu mục tiêu mới và làm điều gì đó thử thách hơn\". \"Trong chiến tranh cần có chiến thuật, nhưng lòng dũng cảm là điều cơ bản nhất\", anh viết trên mạng xã hội, dẫn câu nói của triết gia Trung Quốc cổ đại Mặc Tử để nói về việc chinh phục thử thách mới.\r\n\r\nStartup robot hình người tỷ USD\r\n\r\nZhiyuan Robotics, tức Agibot, ra đời tháng 2/2023. Theo SMM, Zhihui thành lập công ty cùng một số người khác, nhưng có nguồn tin nói anh là nhà sáng lập duy nhất. Anh giữ vai trò giám đốc công nghệ, không phải CEO hay chủ tịch, nhưng lại đại diện cho Agibot trong các sự kiện lớn.\r\n\r\nAgibot nhanh chóng gây chú ý khi được hàng loạt quỹ đầu tư như HongShan, Hillhouse Investment và BYD đổ tiền. Tháng 8/2023, công ty giới thiệu mẫu robot đầu tiên Raise A1 với chiều cao 1,75 mét, nặng 53 kg, có thể chạy, nhảy và đi bộ với tốc độ 7 km/h, có tính ứng dụng cao từ lắp ráp ôtô trong nhà máy cho đến dọn phòng hay làm bạn với con người.\r\n\r\nChỉ sau ba tháng, Agibot trở thành ngôi sao lớn về robot ở Trung Quốc. Công ty được định giá 10 tỷ nhân dân tệ (1,36 tỷ USD).\r\n\r\nNăm 2024, Agibot tung ra hàng loạt sản phẩm mới với ba dòng đại diện cho toàn bộ tham vọng của công ty: Yuanzheng - robot hình người đi bằng hai chân cho các công việc nặng; Lingxi - phiên bản nhỏ gọn hướng tới người dùng cuối; và Genie - robot có bánh xe, hai cánh tay cho môi trường công nghiệp hoặc phòng trưng bày.\r\n\r\nMẫu robot hình người đầu bảng của công ty là Yuanzheng A2, cao 175 cm, nặng 55 kg, trang bị cảm biến AI. A2 có thể xử lý văn bản, audio, dữ liệu hình ảnh và thực hiện công việc như xâu kim. Đầu năm nay, công ty giới thiệu Lingxi X2, robot hình người đa dụng và linh hoạt kết hợp AI cao cấp.\r\n\r\nTheo Rivista, tính đến 15/12/2024, Agibot sản xuất 962 robot, con số được đánh giá ấn tượng. Còn theo Interesting Engineering, công ty của Zhihui dự kiến sản xuất từ 3.000 đến 5.000 robot năm nay, mục tiêu sản lượng ngang robot Optimus của tỷ phú Elon Musk.\r\n\r\nNgoài phần cứng, Agibot cũng chú trọng phần mềm. Hồi tháng 3, công ty ra mắt Genie Operator-1 (GO-1), mô hình AI xử lý hình ảnh và dữ liệu video tăng cường khả năng của robot trong việc diễn giải hành động của con người và thực hiện công việc. Trong các thử nghiệm thực tế, GO-1 nâng tốc độ hoàn thành nhiệm vụ từ 46% lên 75%, chứng minh hiệu quả trong hoạt động như rót và phục vụ nước.\r\nAgibot cũng xây dựng Trung tâm Thu thập dữ liệu robot Zhiyuan rộng 2.000 mét vuông tại Thung lũng robot Zhangjiang ở Thượng Hải, bước đầu đào tạo hơn 100 robot với mục tiêu cho ra đời các cỗ máy hoạt động như con người trong thế giới thực.\r\n\r\n\"Sự xúc tiến về công nghệ và việc gặp gỡ các gương mặt công nghệ trẻ đang cho thấy Trung Quốc hướng đến mục tiêu tự chủ với các yếu tố: trẻ, có tầm nhìn, kỷ luật, đoàn kết\", trang Rivista bình luận. \"Không chỉ là doanh nhân, những người như Zhihui còn là biểu tượng để khẳng định rằng trí tuệ nhân tạo không phải là đặc quyền hay độc quyền của Thung lũng Silicon\".', '1744474287_h75.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:11:27', 'Công nghệ', 0, 0),
(87, 'Chi phí đánh giá các mô hình AI lý luận ngày càng tăng cao', 'Các chuyên gia cho biết mô hình AI lý luận liên tục ra mắt, nhưng việc đánh giá lại tốn kém hơn, khiến hoạt động kiểm chứng độc lập trở nên khó khăn.', 'Hàng loạt công ty AI tung ra các mô hình AI với năng suy nghĩ qua các bước như OpenAI, DeepSeek, hiệu quả hơn so với mô hình không lý luận trước đây.\r\n\r\nTuy nhiên, theo dữ liệu từ Artificial Analysis, tổ chức kiểm tra và đánh giá AI độc lập, chi phí \"chấm điểm\" mô hình lý luận OpenAI o1 theo 7 tiêu chuẩn phổ biến là MMLU-Pro, GPQA Diamond, Humanity’s Last Exam, LiveCodeBench, SciCode, AIME 2024 và MATH-500 có giá 2.767 USD. Tương tự, Claude 3.7 Sonnet, mô hình lai của Anthropic, cần 1.485 USD.\r\nArtificial Analysis cho biết đã chi tổng cộng 5.200 USD để đánh giá chưa đến 10 mô hình lý luận, gấp đôi số tiền 2.400 USD công ty dùng để đánh giá hơn 80 sản phẩm không lý luận. Ví dụ, mô hình không lý luận GPT-4o, ra tháng 5/2024, chỉ đòi hỏi 108,85 USD, o3-mini cần 344 USD, còn Claude 3.6 Sonnet bản không lý luận là 81,41 USD.\r\n\r\nArtificial Analysis không phải là bên duy nhất đối mặt với sự gia tăng chi phí đánh giá AI. Ross Taylor, CEO AI General Reasoning, nói đã chi 580 USD để chấm điểm Claude 3.7 Sonnet với 3.700 câu gợi ý. Ông ước tính một lần sử dụng MMLU Pro - bộ câu hỏi được thiết kế để đánh giá kỹ năng hiểu ngôn ngữ của AI - tốn hơn 1.800 USD.\r\n\r\nViệc thử nghiệm đắt đỏ do mô hình tạo ra nhiều token. Token là phần văn bản thô, ví dụ từ \"fantastic\" chia thành \"fan\", \"tas\", và \"tic\". Artificial Analysis cho biết o1 của OpenAI tạo hơn 44 triệu token trong quá trình thử nghiệm của công ty, gấp 8 lần số lượng GPT-4o tạo ra.\r\n\r\nTheo Jean-Stanislas Denain, nhà nghiên cứu cao cấp tại Epoch AI, các tiêu chuẩn hiện đại cũng kéo theo việc tạo ra nhiều token vì liên quan đến nhiệm vụ phức tạp nhiều bước.\r\n\r\nMột số công ty AI, trong đó có OpenAI, cung cấp quyền truy cập miễn phí hoặc hỗ trợ giảm giá cho một số tổ chức chấm điểm benchmark, nhưng điều này được cho là có thể ảnh hưởng đến kết quả, tác động tới đến tính toàn vẹn của điểm số.', '1744474609_h76.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:16:49', 'Công nghệ', 0, 0),
(88, 'iPhone 17 Pro có thể hỗ trợ ghi hình kép', 'iPhone 17 Pro và 17 Pro Max được cho là sẽ hỗ trợ người dùng quay video bằng camera trước và sau cùng lúc.', 'Jon Prosser, chủ kênh Front Page Tech từng chia sẻ nhiều thông tin chính xác về sản phẩm Apple, cho biết một tính năng camera mới có thể xuất hiện trên iPhone 17 Pro là ghi hình kép, cho phép người dùng kích hoạt cả camera trước và sau.\r\n\r\nHiện nhiều điện thoại Android của Oppo, Samsung, Xiaomi... đã có tính năng này, Một số ứng dụng như Snapchat cũng cung cấp công cụ tương tự, được đánh giá hữu ích cho người sáng tạo nội dung trên nền tảng video như YouTube và TikTok. Họ dễ dàng lồng ghép khuôn mặt mình khi quay cảnh bên ngoài, giúp người xem thấy rõ biểu cảm và phản ứng của họ theo thời gian thực.\r\nJon Prosser cũng chia sẻ một bản dựng về iOS 19 dựa trên thông tin rò rỉ nội bộ. Trong đó, biểu tượng ứng dụng bo tròn hơn nhưng không tròn hẳn như visionOS hay watchOS. Một số biểu tượng có hiệu ứng lấp lánh khi người dùng di chuyển thiết bị.\r\n\r\nTrong khi đó, một \"chuyên gia tin đồn\" khác là Sonny Dickson cũng đăng ảnh hai ốp lưng trong suốt, được cho là của Phone 17 Pro và 17 Pro Max, thể hiện kích cỡ của cụm camera mới. Cụm này khá lớn, có thể do Apple cần thêm không gian cho ống kính tele với cảm biến 48 megapixel.\r\nThế hệ iPhone 17 Pro cũng được cho là sẽ trở lại sử dụng vỏ nhôm thay vì titan như trên 16 Pro, chip xử lý A19 Pro và RAM tăng lên 12 GB.\r\n\r\niPhone 2025 dự kiến ra mắt vào tháng 9 với 4 phiên bản iPhone 17, 17 Air, 17 Pro và 17 Pro Max. Trong đó, iPhone 17 Air là dòng hoàn toàn mới với thiết kế siêu mỏng, thay thế mẫu Plus hiện nay.', '1744474680_h77.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:18:00', 'Công nghệ', 0, 0),
(89, 'SpaceX nhận quyết định thí điểm Internet vệ tinh từ Việt Nam', 'Phó thủ tướng Hồ Đức Phớc trao quyết định thí điểm cung cấp dịch vụ Internet vệ tinh cho SpaceX trong chuyến thăm và làm việc tại Mỹ.\r\n', 'Theo TTXVN, ngày 10/4, tại Washington D.C, Phó thủ tướng Hồ Đức Phớc tiếp ông Tim Hughes, Phó chủ tịch cấp cao của SpaceX, và trao quyết định thí điểm cung cấp dịch vụ Internet vệ tinh cho tập đoàn.\r\n\r\nSpaceX, do Elon Musk sáng lập, phát triển hệ thống Starlink với tham vọng phủ sóng Internet toàn cầu, hiện có mặt ở hơn 125 quốc gia và vùng lãnh thổ, với 5 triệu người sử dụng. Công ty cho biết hiện có 6.750 vệ tinh, hoạt động ở quỹ đạo thấp, cách mặt đất 550 km. Trong khi đó, dịch vụ Internet vệ tinh trước đây sử dụng vệ tinh địa tĩnh, quay quanh Trái Đất ở độ cao hơn 35.000 km.\r\n\r\nKhoảng cách ngắn giúp giảm thời gian truyền dữ liệu. Nhờ đó, Starlink có độ trễ thấp hơn so với dịch vụ Internet vệ tinh truyền thống.\r\nTrước đó, ngày 23/3, Phó Thủ tướng Nguyễn Chí Dũng ký quyết định cho phép thí điểm có kiểm soát dịch vụ viễn thông sử dụng công nghệ quỹ đạo thấp. Quyết định này không giới hạn tỷ lệ sở hữu cổ phần, phần vốn góp hoặc tỷ lệ đóng góp của nhà đầu tư nước ngoài.\r\n\r\nTheo đó, SpaceX được thí điểm kinh doanh Internet vệ tinh tại Việt Nam trong thời hạn 5 năm, kể từ ngày cấp giấy phép. Phạm vi triển khai trên toàn quốc, với số lượng tối đa 600.000 thuê bao. Tuy nhiên, công ty phải tuân thủ các quy định của pháp luật Việt Nam, bảo đảm quốc phòng, an ninh, phải đặt trạm cổng mặt đất (trạm Gateway) trên lãnh thổ Việt Nam, lưu giữ thông tin thuê bao trong suốt thời gian sử dụng dịch vụ.\r\n\r\nViệc thí điểm được vận dụng dựa trên Nghị quyết 193 của Quốc hội, ban hành tháng 2, về thí điểm một số cơ chế, chính sách đặc biệt tạo đột phá phát triển khoa học, công nghệ, đổi mới sáng tạo và chuyển đổi số quốc gia.', '1744474744_h78.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:19:04', 'Công nghệ', 0, 0),
(90, 'Musk chia sẻ bài viết về Starlink tại Việt Nam', 'Elon Musk chia sẻ lại một bài viết trên mạng xã hội X về kế hoạch triển khai Internet vệ tinh của Starlink tại Việt Nam, nhưng không đưa ra bình luận.', 'Bài viết do doanh nhân Mario Nawfal đăng trên X ngày 3/4, trong đó nói trung tâm Starlink sắp được lập ở Việt Nam. Người này dẫn thông tin từ Reuters rằng SpaceX, công ty phát triển Starlink do Musk sáng lập, dự kiến triển khai 10-15 trạm mặt đất, trạm đầu tiên đặt tại Đà Nẵng và hoạt động vào tháng 5 hoặc tháng 6. \"SpaceX đang đưa Việt Nam lên bản đồ vệ tinh của hãng\", người này viết. Sau đó, Elon Musk sử dụng tính năng \"đăng lại\" thông tin trên sáng 4/4.\r\nĐộng thái trên diễn ra sau khi Tổng thống Mỹ Donald Trump công bố mức thuế nhập khẩu đối ứng áp dụng với hơn 180 đối tác thương mại, trong đó Việt Nam chịu mức 46%, nằm trong nhóm nước bị áp thuế cao nhất cùng với Trung Quốc, Campuchia, Indonesia, Myanmar.\r\n\r\nTrong bài viết ngày 1/4, Reuters dẫn nguồn tin cho biết việc triển khai Internet vệ tinh Starlink đang diễn ra sau khi công ty được cấp phép thử nghiệm. SpaceX được cho là đang xây trạm gateway theo yêu cầu của Việt Nam, trạm đầu tiên giá trị khoảng 3 triệu USD. Nếu kế hoạch 10-15 trạm hoàn thành, Việt Nam có thể trở thành một trong những thị trường có nhiều trạm và mạng lưới Starlink lớn nhất.\r\n\r\nTrang này đánh giá Việt Nam có giá cước Internet rẻ hơn nhiều so với Starlink và chỉ một số ít nơi cần Internet tốc độ cao, vì vậy công ty của Mỹ mong muốn cung cấp thêm các dịch vụ khác.\r\nTrong cuộc họp ngày 1/4, Cục Viễn thông - Bộ Khoa học và Công nghệ cho biết đang làm việc với SpaceX để hỗ trợ doanh nghiệp hoàn thiện các khâu tiếp theo trước khi có thể hoạt động ở Việt Nam. Trong đó có việc SpaceX sẽ sớm thành lập doanh nghiệp và lắp đặt Trạm cổng mặt đất (Trạm Gateway) trên lãnh thổ Việt Nam. \"Họ đang chuẩn bị hồ sơ, thiết bị để đưa sang Việt Nam\", đại diện Cục cho biết.\r\n\r\nNgày 23/3, Chính phủ ra quyết định cho phép dịch vụ Internet vệ tinh của Elon Musk được \'thí điểm có kiểm soát\' với thời hạn 5 năm, kết thúc trước 1/1/2031, với số lượng thuê bao tối đa 600.000. Nhà cung cấp phải bảo đảm lưu lượng do thuê bao vệ tinh tạo ra trên lãnh thổ Việt Nam đều đi qua Trạm Gateway và kết nối vào mạng viễn thông công cộng trong nước. Dịch vụ cũng phải bảo đảm thông tin, dữ liệu của người sử dụng dịch vụ tại Việt Nam được lưu trữ tại Việt Nam; thực hiện yêu cầu về ngăn chặn nguồn phát tán mã độc, tấn công mạng, thông tin xấu độc.', '1744474799_h79.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:19:59', 'Công nghệ', 0, 0),
(91, '\'Starlink đang làm thủ tục để vào Việt Nam\'', 'Cục Viễn thông cho biết đang phối hợp với SpaceX để hoàn thiện thủ tục, cơ sở hạ tầng, trước khi mạng lưới Internet vệ tinh Starlink có thể hoạt động trong nước.', 'Trong buổi làm việc với Phó thủ tướng Nguyễn Chí Dũng ngày 1/4, Bộ Khoa học và Công nghệ cho biết đang tiến hành nhiều biện pháp để thúc đẩy hạ tầng số, trong đó đặt ra hai nhiệm vụ chính năm nay là tăng tốc độ Internet di động bằng việc phủ sóng 5G và thí điểm dịch vụ viễn thông sử dụng vệ tinh tầm thấp.\r\n\r\nVới nhiệm vụ thứ hai, ngày 23/3, Chính phủ đã ban hành quyết định cho SpaceX, công ty do Elon Musk sáng lập, thí điểm có kiểm soát trong việc triển khai dịch vụ viễn thông sử dụng công nghệ vệ tinh quỹ đạo tầm thấp Starlink.\r\nÔng Nguyễn Thành Chung, Cục trưởng Viễn thông, cho biết đang làm việc với SpaceX để hỗ trợ doanh nghiệp này hoàn thiện các khâu tiếp theo trước khi có thể hoạt động ở Việt Nam. Trong đó, hai việc cần sớm được hoàn thành là SpaceX thành lập doanh nghiệp và lắp đặt Trạm cổng mặt đất trên lãnh thổ Việt Nam.\r\n\r\n\"Họ đang chuẩn bị hồ sơ, thiết bị để đưa sang Việt Nam\", ông Chung nói, thêm rằng việc này sẽ hoàn thành trong năm nay.\r\n\r\nTrước đó, quyết định của Chính phủ cũng đưa ra yêu cầu và điều kiện thí điểm với SpaceX, như thời hạn 5 năm và phải kết thúc trước 1/1/2031, với số lượng thuê bao tối đa 600.000, đồng thời đảm bảo yêu cầu về quốc phòng, an ninh. Nhà cung cấp phải đặt Trạm cổng mặt đất (Trạm Gateway) trên lãnh thổ Việt Nam, bảo đảm tất cả lưu lượng do thuê bao vệ tinh tạo ra trên lãnh thổ Việt Nam đều đi qua Trạm Gateway và kết nối vào mạng viễn thông công cộng trong nước. Dịch vụ cũng phải bảo đảm thông tin, dữ liệu của người sử dụng dịch vụ tại Việt Nam được lưu trữ tại Việt Nam; thực hiện các yêu cầu về ngăn chặn nguồn phát tán mã độc, tấn công mạng, thông tin xấu độc.\r\n\r\nSpaceX chưa đưa ra thông tin nào về việc này. Trước đó, công ty nhiều lần thể hiện mong muốn phủ sóng Internet vệ tinh tại Việt Nam.\r\n\r\nTheo Bộ trưởng Khoa học và Công nghệ Nguyễn Mạnh Hùng, Internet vệ tinh hoạt động sẽ giúp phủ sóng Internet tới các vùng sâu vùng xa. Bên cạnh đó, ông cũng yêu cầu cần đẩy mạnh tốc độ mạng di động và giao cho Cục Viễn thông \"tăng gấp đôi tốc độ truy nhập Internet di động trong năm nay\".\r\n\r\nTheo đại diện Cục, đơn vị này sẽ tiến hành giải pháp tổng thể để thúc đẩy 5G trong nước nói riêng, Internet di động nói chung, bao gồm mở rộng phạm vi phủ sóng bằng cách tăng tốc độ triển khai trạm; bổ sung dung lượng cáp quang biển; đẩy nhanh việc mở các trung tâm dữ liệu; thúc đẩy việc tiếp cận thiết bị 5G cho người dùng cũng như các gói cước hợp lý.\r\n\r\nBộ trưởng Nguyễn Mạnh Hùng nhiều lần khẳng định tầm quan trọng của hạ tầng số. Hạ tầng số được tạo thành từ các mảnh ghép hạ tầng viễn thông băng rộng, hạ tầng điện toán đám mây với hạ tầng công nghệ số và các nền tảng số.\r\n\r\nTheo Bộ trưởng, cần coi hạ tầng số quan trọng như hạ tầng giao thông, trong khi việc thiết lập nhanh và rẻ hơn. \"Hạ tầng giao thông là dòng chảy vật chất. Hạ tầng số là dòng chảy dữ liệu. Hai dòng chảy này phải luôn tương xứng với nhau. Nhưng hạ tầng số rẻ hơn rất nhiều so với hạ tầng giao thông, có thể làm nhanh hơn, có thể hiện đại ngay\", ông nói tại hội nghị triển khai Nghị quyết 26 của Bộ Chính trị hồi tháng 11/2022.\r\nBộ Khoa học và Công nghệ thời gian qua đã thực hiện nhiều biện pháp thúc đẩy hạ tầng số phát triển, trong đó đóng góp xây dựng Nghị quyết 193 của Quốc hội ban hành vào tháng 2, đồng thời soạn thảo Nghị định quy định chi tiết và hướng dẫn thi hành.\r\n\r\nĐể phát triển hạ tầng viễn thông, Nhà nước sẽ tham gia đầu tư hạ tầng số, đặc biệt về 5G, cáp quang biển. \"Bình thường mỗi nhà mạng đầu tư một năm chỉ xung quanh 5.000 trạm 5G. Nếu muốn họ đầu tư một năm 20.000 trạm để phủ sóng nhanh và trước thì Nhà nước phải hỗ trợ\", ông Nguyễn Mạnh Hùng nói tại hội nghị đầu tháng 3. Mức hỗ trợ của Nghị quyết 193 cho phép là 15% tổng giá trị đầu tư, với điều kiện nhà mạng triển khai mới 20.000 trạm tính đến cuối 2025. Thực tế sau khi Nghị quyết ra đời, Bộ cho biết đã có hai doanh nghiệp là Viettel, VNPT dự kiến triển khai số trạm BTS 5G đạt mức này.\r\n\r\nInternet di động thời gian qua cũng có nhiều chuyển biến khi sau hơn 5 tháng mạng 5G được triển khai, với tốc độ tính đến tháng 2 đạt 144,5 Mbps, xếp thứ 19 thế giới trên bảng xếp hạng của Ookla.\r\n\r\n', '1744474859_h80.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:20:59', 'Công nghệ', 0, 0),
(92, 'Huawei có thể đã bán được 400.000 smartphone gập ba', 'Điện thoại gập ba Huawei Mate XT được cho là đạt doanh số cao dù có giá 2.800 USD.', 'Khi Mate XT ra mắt vào tháng 9/2024, nhiều chuyên gia nhận định mẫu điện thoại gập ba đầu tiên trên thế giới này chỉ để quảng bá thương hiệu cho Huawei. Tuy nhiên, một số nguồn tin từ Trung Quốc cho biết hãng đã bán được 400.000 chiếc Mate XT, chủ yếu tại thị trường nội địa.\r\n\r\nHuawei không đưa ra bình luận.\r\nDoanh số 400.000 được đánh giá cao vì máy có giá khởi điểm 2.800 USD. Để so sánh, Samsung bán được hai triệu điện thoại gập đôi Galaxy Z Fold6 với giá bằng một nửa, kể từ khi ra mắt tháng 7/2024.\r\n\r\nĐiểm đặc biệt của Mate XT là thiết kế gập ba hình chữ Z, cho phép người dùng sử dụng theo nhiều kích thước và hình dạng khác nhau, như 6,4 inch nếu gập lại, 7,9 inch nếu mở một bản lề và lớn nhất 10,2 inch khi mở ra hoàn toàn. Huawei không đề cập vi xử lý trong lễ ra mắt, nhưng sản phẩm bán ra dùng Kirin 9010 sản xuất trên tiến trình 7 nm, bộ nhớ RAM 16 GB và ba tùy chọn bộ nhớ từ 256 GB đến 1 TB.\r\n\r\nMáy có ba camera sau với cảm biến chính 50 megapixel khẩu độ thay đổi f/1.4-4.0, ống tiềm vọng 12 megapixel zoom quang 5.5x và siêu rộng 12 megapixel. Camera trước 8 megapixel. Sản phẩm có trọng lượng 298 gram, nặng hơn so với mức 227 gram của iPhone 16 Pro Max, có một cổng USB-C cho phép sạc nhanh tối đa 66 W. Pin dung lượng 5.600 mAh.\r\n\r\nTheo khảo sát của PhoneArena, 45% số người được hỏi cho biết sẽ mua điện thoại gập ba nói chung trong tương lai, 35% mua khi sản phẩm có giá tốt, còn lại không mua vì máy có kích thước lớn và nặng.', '1744474921_h81.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:22:01', 'Công nghệ', 0, 0),
(93, 'Smartphone gập ba Huawei giá gần 100 triệu đồng ngoài Trung Quốc', 'Huawei Mate XT, smartphone gập ba đầu tiên thế giới, được bán bên ngoài thị trường Trung Quốc với giá 3.499 euro (93 triệu đồng), nhưng chưa lên kệ tại Việt Nam.', 'Sản phẩm xuất hiện tại sự kiện của hãng tại Malaysia ngày 18/2, đánh dấu chiếc điện thoại gập ba của Huawei tiến ra thị trường quốc tế, kể từ khi ra mắt vào tháng 9 năm ngoái.\r\n\r\nSản phẩm có một lựa chọn cấu hình duy nhất với RAM 16 GB và bộ nhớ 1 TB, giá 3.499 euro. Trong khi đó tại Trung Quốc, người dùng có lựa chọn bộ nhớ từ 256 GB, giá 20.000-24.000 CNY (70-84 triệu đồng).\r\n\r\nTại sự kiện, lãnh đạo nhóm sản phẩm Huawei Andreea Zimmer nhiều lần nhấn mạnh đây là \"smartphone gập ba đầu tiên trên thế giới\", đạt nhiều kỷ lục như điện thoại gập mỏng nhất, màn hình lớn nhất khi mở với độ dày 3,6 mm và rộng 10,2 inch, cùng hệ thống camera hỗ trợ cả chụp tele 5,5x, chụp cận cảnh.\r\nĐể đạt được các đặc tính trên, Huawei cho biết điểm độc đáo của sản phẩm đến từ hệ thống bản lề đặc biệt hỗ trợ gập mở cả vào trong và ra ngoài, cùng mặt kính, pin được làm \"siêu mỏng\".\r\n\r\nThiết kế dạng này giúp người dùng có thể thiết lập máy với nhiều cách sử dụng khác nhau như gập hoàn toàn, mở hai hoặc ba màn hình, hoặc thậm chí biến một phần thành chân đỡ cho máy. Ngoài ra, hệ điều hành cũng được tối ưu để phát huy lợi thế của màn hình lớn, giảm tình trạng màn hình đen khi mở rộng, vốn xuất hiện trên một số mẫu smartphone gập hiện nay.\r\n\r\nHuawei chưa thông báo Mate XT sẽ được bán tại những thị trường nào. Tuy nhiên tại một số thị trường hỗ trợ, người dùng có thể đặt hàng trước trên website của hãng và nhận máy vào giữa tháng 3.\r\n\r\nMate XT có ba camera sau với cảm biến chính 50 megapixel khẩu độ thay đổi f/1.4-4.0, ống tiềm vọng 12 megapixel zoom quang 5.5x và siêu rộng 12 megapixel. Camera trước 8 megapixel. Sản phẩm có trọng lượng 298 gram, nặng hơn so với mức 227 gram của iPhone 16 Pro Max, pin dung lượng 5.600 mAh.\r\n\r\nNgoài Huawei, Samsung cũng sẽ tham gia vào cuộc đua này với một mẫu smarpthone gập ba, được cho là mang tên Galaxy G Fold, ra mắt trong năm nay.\r\nTại sự kiện, Huawei cũng giới thiệu một số sản phẩm mới như máy tính bảng, tai nghe, vòng tay thông minh mới. Các sản phẩm này nhiều khả năng sẽ được bán tại Việt Nam.\r\n\r\nMẫu máy tính bảng MatePad Pro 13.2 trang bị màn hình Flexible OLED PaperMatte, độ sáng 1.000 nit và độ phân giải 2.8K, cài sẵn nhiều ứng dụng văn phòng, được kỳ vọng có thể thay thế laptop trong một số tác vụ. Tai nghe FreeArc là mẫu không dây thiết kế mở, với móc tai đầu tiên của hãng, trong khi Band 10 là vòng tay thông minh có tính năng theo dõi những thay đổi cảm xúc của người dùng thông qua dữ liệu sức khỏe theo thời gian thực.', '1744474980_h82.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:23:00', 'Công nghệ', 0, 0),
(94, 'Mở hộp Mac Studio 2025 - \'siêu xe\' trên đường đua AI', 'Mac Studio mới có tùy chọn cấu hình mạnh nhất với máy tính cá nhân như RAM 512 GB, chip M4 Max hoặc M3 Ultra cho phép chỉnh sửa đồ họa, video chuyên nghiệp, chạy mô hình AI.', 'Mac Studio 2025 ra mắt đầu tháng 3, muộn hơn loạt thiết bị Mac đầu tiên dùng chip M4 vào tháng 10 năm ngoái như iMac, Mac Mini, MacBook Pro. Lý do là bởi ngoài tùy chọn chip M4 Max mạnh nhất của dòng M4, model này còn có phiên bản dùng chip M3 Ultra, \"quái vật\" vi xử lý tốt nhất của Apple hiện tại. Đây cũng là máy tính Mac mạnh nhất người dùng có thể sở hữu, trước khi Mac Pro có thể được nâng cấp lên M3 Ultra thời gian tới.\r\n\r\nMac Studio có hai tùy chọn về vi xử lý là M4 Max và M3 Ultra, hai chip đều mới ra mắt gần đây. Trong đó, M3 Ultra thuộc dòng M3 nhưng thực tế mạnh hơn M4 Max do cấu trúc được ghép từ hai chip M3 Max trên một thể thống nhất. Dòng chip Ultra cũng chỉ xuất hiện trên Mac Studio và Mac Pro.\r\nMac Studio là thiết bị dành cho người dùng chuyên nghiệp nhưng được Apple cố gắng thiết kế trong một thân hình nhỏ gọn. Phiên bản 2025 không có khác biệt về thiết kế so với model 2022 và 2023 trước đó. Vẫn là khung nhôm vuông với chiều dài mỗi cạnh 19,7 cm, cao 9,5 cm và nặng tới 3,64 kg với tùy chọn chip M3 Ultra và 2,74 kg với chip M4 Max. Do trọng lượng lớn, sản phẩm được đặt trong hộp với kiểu mở tách bên và cơ cấu chống sốc bên trong giống các thiết bị như iMac, màn hình Studio Display, XDR Display.\r\nPhiên bản thử nghiệm sử dụng chip M4 Max với 16 lõi CPU, 40 lõi GPU, RAM 128 GB, bộ nhớ trong 1 TB với mức giá khoảng 100,5 triệu đồng. Model này cũng sẽ nặng hơn bản cấu hình khởi điểm gần 1 kg. Máy có băng thông bộ nhớ 512 GB/giây, tích hợp Neural Engine nhanh hơn ba lần so với M1 Max.\r\n\r\nMac Studio là cỗ máy siêu mạnh cho AI với kích thước nhỏ gọn nhất hiện nay. Sản phẩm có khả năng chạy các mô hình ngôn ngữ lớn (LLM) với hơn 600 tỷ tham số được lưu trữ hoàn toàn trong bộ nhớ nhờ GPU tiên tiến và bộ nhớ RAM tối đa 512 GB, tương đương bộ nhớ trong của nhiều máy tính phổ thông.\r\nMáy tính Mac mới phiên bản dành cho thị trường Việt Nam với đầy đủ tem nhãn và sách hướng dẫn bằng tiếng Việt. Hộp đựng dùng seal giấy và phụ kiện đi kèm chỉ có cáp nguồn cỡ lớn được bọc dù. Máy đã bắt đầu cho đặt hàng trên trang web của Apple với thời gian giao từ 2-3 ngày với các cấu hình cơ bản hoặc 2-3 tuần với các cấu hình cao, tùy biến theo nhu cầu người dùng.\r\nMac Studio đặt cạnh Mac mini với kích thước vượt trội nhưng vẫn cùng kiểu thiết kế khối nhôm tương tự. Phiên bản cao cấp nhất của Mac mini cũng có thể hỗ trợ nhiều công việc chuyên nghiệp hiện nay trong khi với Mac Studio, cấu hình của model này đều dành cho người dùng chuyên nghiệp cũng như có hệ thống tản nhiệt, số cổng kết nối tối ưu hơn.\r\nVới những mẫu máy tính cá nhân như Mac Studio, việc kết nối trở nên đơn giản hơn rất nhiều khi chỉ cần cáp nguồn và cáp Thunderbolt kết nối tới màn hình. Bên trong tích hợp sẵn tất cả kết nối không dây bao gồm Wi-Fi 6E, Bluetooth 5.3 cũng như kết nối mạng có dây Ethernet 10 Gigabit.\r\n\r\nTuy nhiên, cấu hình của Mac Studio cũng ảnh hưởng nhiều tới trang bị cổng kết nối đi kèm máy nên người dùng cần kiểm tra rõ trước khi mua. Ví dụ, phiên bản dùng chip M4 Max cấu hình khởi điểm hai cổng trước là USB-C đơn thuần trong khi bản M3 Ultra thì hai cổng trước mới là chuẩn Thunderbolt 5. Các cổng phía sau cùng là Thunderbolt 5.\r\nCổng kết nối phía sau nhiều hơn đáng kể so với Mac Mini. Các bản cấu hình cao sẽ có cả 4 cổng đều là Thunderbolt 5, LAN, hai cổng USB, HDMI và tai nghe. Model này vẫn giữ nút nguồn nhỏ ở phía sau thay vì đưa xuống mặt dưới như Mac mini.\r\n\r\nCũng là Thunderbolt 5 nhưng trên Mac Studio có mỗi cổng được hỗ trợ bởi bộ điều khiển thiết kế tùy chỉnh riêng trực tiếp trên chip. Công nghệ này giúp cung cấp băng thông chuyên dụng cho mỗi cổng trên Mac Studio. Thunderbolt 5 cho tốc độ truyền tải dữ liệu tối đa 120 Gb/giây, cao gấp đôi so với Thunderbolt 4.\r\nPhiên bản Mac Studio dùng chip M4 Max (trong hình) là lựa chọn hướng tới người dùng chuyên nghiệp như chỉnh sửa video, chuyên gia hiệu chỉnh màu (colorist), nhà phát triển, kỹ sư, nhiếp ảnh gia, chuyên gia sáng tạo.\r\n\r\nTrong khi đó, lựa chọn M3 Ultra bao gồm CPU 32 lõi với 24 lõi hiệu năng và 8 lõi tiết kiệm điện, cho hiệu năng nhanh hơn 1,5 lần so với M2 Ultra và 1,8 lần so với M1 Ultra. M3 Ultra cũng sở hữu GPU lớn nhất trong mọi chip của Apple là 80 lõi đồ họa, cho hiệu năng nhanh gấp đôi M2 Ultra và nhanh hơn 2,6 lần so với M1 Ultra.\r\n\r\nChip M3 Ultra được chế tạo bằng cách sử dụng cấu trúc đóng gói UltraFusion, liên kết hai khối M3 Max thông qua 10.000 kết nối tốc độ cao cho độ trễ thấp và băng thông lớn. Điều này cho phép hệ thống coi các khối kết hợp như một chip duy nhất, mang đến hiệu năng mạnh trong khi vẫn giữ khả năng tiết kiệm điện. UltraFusion giúp tập hợp tổng cộng 184 tỷ bóng bán dẫn trên một con chip.\r\nThiết kế mặt trước cũng với hai cổng giao tiếp USB-C, đèn báo trạng thái nhưng Mac Studio có thêm khe cắm thẻ SDXC tốc độ cao, phù hợp với người dùng chuyên nghiệp. Khác biệt về khe cắm thẻ cũng tương tự MacBook Pro và MacBook Air.\r\n\r\nMac Studio có giá khởi điểm 57,9 triệu đồng và 52,9 triệu đồng cho người làm trong ngành giáo dục. Phiên bản sử dụng chip M3 Ultra có giá từ 116 triệu đồng, cao nhất là 368,5 triệu đồng với tùy chọn RAM 512 GB, bộ nhớ SSD 16 TB. Máy đã bắt đầu cho đặt hàng trên trang Apple Store Online cho thị trường Việt Nam.', '1744475105_h83.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:25:05', 'Công nghệ', 0, 0),
(95, 'Misa mua chip AI mạnh nhất của Nvidia', 'Misa cho biết đã làm thủ tục mua chip Nvidia, dự kiến về trong năm nay, thực hiện nhiệm vụ xây dựng mô hình ngôn ngữ lớn \"Make in Viet Nam\".', 'Thông tin trên được ông Nguyễn Xuân Hoàng, Phó chủ tịch Misa, chia sẻ với Bộ Khoa học và Công nghệ, chiều 10/4. Đây là buổi làm việc tiếp theo của Bộ với các doanh nghiệp nhận nhiệm vụ làm chủ công nghệ chiến lược, nhằm cập nhật tiến độ cũng như hỗ trợ tháo gỡ vướng mắc.\r\n\r\nTrong 7 doanh nghiệp nhận nhiệm vụ, Misa cam kết sẽ chi 2.500 tỷ đồng trong 5 năm để xây dựng mô hình ngôn ngữ lớn (LLM) cho AI \"Make in Viet Nam\" với tối thiểu 100 tỷ tham số, chuyên sâu cho xử lý văn bản pháp luật nhà nước, kế toán, thuế và quản trị doanh nghiệp, chuyên sâu nông nghiệp hỗ trợ người dân phát triển chăn nuôi và trồng trọt.\r\nTheo ông Hoàng, công ty đang đạt được một số dấu mốc quan trọng. Tuy nhiên, việc xây dựng mô hình AI trên quy mô lớn khác rất nhiều so với những mô hình nhỏ mà đơn vị triển khai trước đây, kéo theo nhiều thách thức. Một trong số đó là cải thiện năng lực tính toán.\r\n\r\nÔng cho biết công ty đang thực hiện song song hai giải pháp là thuê hạ tầng sử dụng và liên hệ Nvidia đặt mua chip, trong đó có mẫu mạnh nhất H200. \"Chúng tôi đã đặt hàng thành công và dự kiến 6 tháng nữa chip sẽ về\", ông Hoàng nói.\r\n\r\nTheo Phó chủ tịch Misa, việc đầu tư nghiên cứu vào AI có thể giúp tạo ra những \"DeepSeek\" của Việt Nam, đồng thời mang đến nhiều lợi ích cho cá nhân và tổ chức trong nước khi có thể tiếp cận và sử dụng AI tiên tiến với giao diện tiếng Việt, đồng thời ứng dụng AI giúp gia tăng năng suất và hiệu quả công việc, tiết kiệm thời gian và nguồn lực.\r\n\r\nNgoài mô hình ngôn ngữ lớn, công ty dự định tạo nền tảng AI Agent Platform cung cấp sẵn các \"nhân sự AI\" với kiến thức chuyên sâu về văn bản pháp luật nhà nước, kế toán, thuế và quản trị doanh nghiệp.\r\n\r\nBộ Khoa học và Công nghệ đồng hành cùng doanh nghiệp\r\n\r\nTại buổi gặp, đại diện Misa đề xuất với Bộ Khoa học và Công nghệ một số chính sách thúc đẩy nghiên cứu, ứng dụng khoa học công nghệ, như ưu đãi thuế cho cá nhân làm trong doanh nghiệp phần mềm Việt Nam, chính sách thuế cho chuyên gia nước ngoài được thuê về Việt Nam cho hoạt động khoa học công nghệ và đổi mới sáng tạo.\r\n\r\nNgoài ra, đơn vị cũng đề nghị được giải đáp chính sách \"người làm nghiên cứu được hưởng tối thiểu 30% kết quả thương mại hóa\", trong trường hợp người làm nghiên cứu đó là nhân viên của công ty, vốn đã được trả lương.\r\n\r\nÔng Đào Ngọc Chiến, Giám đốc Văn phòng các Chương trình trọng điểm cấp nhà nước, nhắc lại thông điệp từ Bộ trưởng Khoa học và Công nghệ Nguyễn Mạnh Hùng, xác định doanh nghiệp tư nhân thời gian tới sẽ là trụ cột trong phát triển khoa học công nghệ, đổi mới sáng tạo và chuyển đổi số. Nhà nước sẽ đóng vai trò đầu tư, đồng hành và kích thích doanh nghiệp phát triển.\r\n\r\nĐại diện Bộ cho biết đang xây dựng chương trình phát triển Công nghệ chiến lược và Công nghiệp chiến lược, dự kiến hoàn thành vào tháng 5. Khi đó, sẽ có nguồn vốn lớn dùng để đồng hành cùng doanh nghiệp thực hiện các chương trình này.\r\nVề việc doanh nghiệp còn nghi ngại khi sử dụng nguồn vốn hỗ trợ từ nhà nước, ông Chiến cho biết Nghị quyết 57 của Bộ Chính trị, Nghị quyết 193 của Quốc hội về cơ bản đã gỡ các điểm nghẽn. Một số nội dung trong đó là người nghiên cứu có thể dùng kết quả của mình để lập doanh nghiệp hoặc góp vốn vào doanh nghiệp; nhiệm vụ khoa học công nghệ được thực hiện theo phương thức khoán chi đến sản phẩm cuối cùng; kết quả nghiên cứu thuộc về người thực hiện.\r\n\r\nVới đề xuất về thuế, ông Chiến cho biết Nghị quyết 193 cũng nêu rõ các khoản chi dành cho nghiên cứu khoa học, phát triển công nghệ và đổi mới sáng tạo trong doanh nghiệp \"được tính vào khoản chi được trừ khi xác định thu nhập chịu thuế thu nhập doanh nghiệp\". Ngoài ra, tiền lương, tiền công từ thực hiện nhiệm vụ khoa học và công nghệ có sử dụng ngân sách nhà nước là khoản không chịu thuế thu nhập cá nhân.\r\n\r\nVới thắc mắc về chính sách 30% kết quả thương mại hóa cho người nghiên cứu, ông Chiến khuyến nghị khi thực hiện dự án nghiên cứu, doanh nghiệp cần xác định rõ thành phần tham gia để có chính sách phù hợp. Kết quả thương mại hóa nghiên cứu cũng cần được bóc tách riêng thay vì gộp chung vào doanh thu của công ty để tránh các rắc rối phát sinh khi thực hiện chính sách.', '1744475165_h84.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:26:05', 'Công nghệ', 0, 0),
(96, 'Samsung tích hợp AI cho loạt thiết bị gia dụng Bespoke', 'Các thiết bị Bespoke AI mới trang bị một số tính năng trí tuệ nhân tạo, tích hợp trợ lý ảo Bixby và điều khiển thông qua màn hình AI Home.', 'Tại sự kiện diễn ra tại Hàn Quốc cuối tháng 3, ông Jeong Seung Moon, Phó chủ tịch Samsung, cho biết AI Home là trọng tâm phát triển của loạt thiết bị gia dụng Bespoke AI năm 2025 của công ty. Giải pháp tận dụng trí tuệ nhân tạo sử dụng trên màn hình thông minh, kết hợp trợ lý ảo Bixby và công nghệ bảo mật Knox. Theo ông, ngoài sự tiện lợi, các thiết bị AI thể hiện ưu thế lớn về khả năng tiết kiệm năng lượng.\r\nMàn hình AI Home trên thiết bị Bespoke sử dụng tấm nền LCD 7 hoặc 9 inch, hỗ trợ cảm ứng. Ngoài thể hiện trạng thái, điều khiển như thông thường, màn hình còn hỗ trợ giao tiếp với trợ lý ảo thông qua giọng nói cùng một số tính năng AI khác. Samsung cho biết một số chức năng sẽ sử dụng \"thuật toán dựa trên AI\" và cần cập nhật định kỳ để cải thiện độ chính xác.\r\n\r\nThay vì màn hình cỡ lớn kiểu tủ lạnh AI Family Hub, các model tủ lạnh thuộc dòng Bespoke có màn hình nhỏ 9 inch. Trong đó, công nghệ AI Vision Inside có thể tự động nhận diện 37 loại thực phẩm như trái cây, rau củ thông qua hình ảnh cung cấp bằng camera bên trong. Tuy nhiên, Samsung cho biết tính năng này không hỗ trợ thực phẩm đặt trên ngăn cửa của ngăn mát và tủ đông.\r\n\r\nVới máy giặt, tính năng AI cho phép nhận biết nhiều loại vải trong một lần giặt thay vì chỉ một như trước, giúp máy đưa ra chu trình giặt, sấy tối ưu tương ứng thông qua AI OptiWash và AI OptiDry. Theo Samsung, AI giúp máy giặt tiết kiệm năng lượng hơn so với tiêu chuẩn. Ví dụ, mức tiêu thụ của model 9 kg là EEI 21.8 theo thử nghiệm nội bộ, tiết kiệm năng lượng 55% so với ngưỡng tối thiểu của chuẩn hiệu quả năng lượng A (EEI 52 cho mẫu 9 kg). Các chỉ số năng lượng được thử nghiệm với chương trình Eco 40-60.\r\nTrong loạt sản phẩm AI mới còn có máy hút bụi cầm tay không dây Bespoke AI Jet Ultra với công suất hút mạnh nhất hiện nay là 400 AW, thời lượng 100 phút sau mỗi lần sạc. Sản phẩm dùng AI nhận diện các bề mặt mà máy cần hút cũng như phân tích bụi, mảng rác để đưa ra lực hút phù hợp.\r\n\r\nSau năm đầu tiên ra mắt robot hút bụi tại thị trường Việt Nam, Samsung giới thiệu phiên bản nâng cấp Bespoke AI Jet Bot Steam Ultra có hệ thống bánh xe leo được bậc cửa, nhận diện môi trường phức tạp AI Object Recognition, tránh chướng ngại vật kích thước nhỏ tới một cm, chất lỏng trong suốt.\r\n\r\nSamsung dự kiến đưa loạt sản phẩm Bespoke AI về Việt Nam trong tháng 4. Các model đầu tiên lên kệ là tủ lạnh và máy giặt.', '1744475314_h85.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:28:34', 'Công nghệ', 0, 0),
(97, '\'Apple chở 600 tấn iPhone từ Ấn Độ về Mỹ\'', 'Apple được cho là đã vận chuyển 600 tấn iPhone bằng đường hàng không về Mỹ để tránh thuế quan mới của Tổng thống Donald Trump.', 'Dẫn các nguồn tin từ chuỗi cung ứng, Reuters cho biết động thái của Apple là \"muốn tránh thuế quan\". Chính phủ Mỹ áp thuế đối ứng 26% với hàng nhập khẩu từ Ấn Độ nhưng hiện hoãn 90 ngày cùng với hầu hết quốc gia khác trong danh sách.\r\nCũng theo nguồn tin này, một quan chức chính phủ Ấn Độ tiết lộ Apple thậm chí vận động Bộ hàng không Ấn Độ cắt giảm thời gian làm thủ tục sân bay từ 30 xuống còn 6 tiếng. Công ty tập kết iPhone chủ yếu tại sân bay Chennai, nằm ở bang Tamil Nadu. Một \"hành lang xanh\" được lập để vận chuyển iPhone. Kể từ tháng 3, đã có 6 chuyến bay chở hàng của Apple với sức chứa hơn 100 tấn mỗi chuyến tới Mỹ, với chuyến gần nhất cất cánh ngay trước 9/4 - ngày mức thuế mới có hiệu lực.\r\n\r\nƯớc tính mỗi chiếc iPhone nặng khoảng 350 gram, tức 600 tấn tương đương 1,5 triệu iPhone.\r\n\r\nApple và Bộ hàng không Ấn Độ không đưa ra bình luận.\r\n\r\nTheo hãng nghiên cứu Counterpoint Research, trung bình Apple bán hơn 220 triệu iPhone mỗi năm trên toàn thế giới. Khoảng 1/5 tổng lượng iPhone nhập khẩu vào Mỹ đến từ Ấn Độ, phần còn lại đến từ Trung Quốc.\r\n\r\nDữ liệu Hải quan Mỹ do Reuters thu thập cho thấy giá trị các lô hàng Apple do đối tác Foxconn trực tiếp thực hiện từ Ấn Độ sang Mỹ đã tăng lên 770 triệu USD vào tháng 1 và 643 triệu USD vào tháng 2. Bốn tháng trước đó, giá trị thường ở mức từ 110-331 triệu USD, dù là mùa mua sắm cuối năm tại Mỹ.\r\n\r\nCũng theo Reuters, tại Ấn Độ, Apple yêu cầu đối tác Foxconn tăng cường sản lượng iPhone. Hai nguồn tin khác từ nhà máy Foxconn ở Chennai cho biết gần đây đã được cấp trên yêu cầu làm thêm vào Chủ nhật. Đây là nhà máy sản xuất iPhone lớn nhất tại Ấn Độ, đảm nhiệm 20 triệu chiếc iPhone vào năm ngoái, gồm cả các mẫu iPhone 15 và 16 mới nhất.\r\n\r\nTrước đó, có những dấu hiệu cho thấy Apple đang tích trữ thiết bị về Mỹ trước thời gian áp lệnh thuế mới. Times of India ngày 7/4 dẫn nguồn tin từ quan chức cấp cao của Ấn Độ rằng đã có 5 chuyến bay chở đầy iPhone và các sản phẩm Apple khác từ Ấn Độ và Trung Quốc \"chỉ trong ba ngày cuối tháng 3\".\r\n\r\nTrước chính sách thuế đối ứng của ông Trump, giá iPhone tại Mỹ được dự đoán tăng mạnh. Dan Ives, Giám đốc nghiên cứu công nghệ tại công ty dịch vụ tài chính Wedbush Securities, nói với CNN rằng giá iPhone có thể tăng từ 1.000 USD hiện tại lên mức 3.500 USD. Reuters Rosenblatt Securities, ngân hàng đầu tư có trụ sở tại New York, nhận định iPhone có thể đắt hơn 43% nếu Apple \"chuyển toàn bộ chi phí thuế mới cho người tiêu dùng\". Trước những dự đoán này, người Mỹ được cho là đã đổ xô mua iPhone vì sợ giá tăng.\r\n\r\n', '1744475429_h86.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:30:29', 'Công nghệ', 0, 0),
(98, 'Loạt smartphone bán trong tháng 4', 'Oppo Find N5 là smartphone cao cấp nhất bán trong tháng, còn các mẫu từ Poco, Honor, Samsung, Xiaomi đều thuộc phân khúc tầm trung và giá rẻ.', 'Oppo Find N5 (45 triệu đồng)\r\n\r\nSmartphone gập mỏng nhất thế giới của Oppo có số đo 4,21 mm khi mở và 8,93 mm khi gập, nhưng đạt dung lượng pin lớn nhất trong số các điện thoại gập là 5.600 mAh. Máy dùng khung nhôm hợp kim 7-series và hợp kim titan in 3D để đảm bảo chịu lực tốt. Sản phẩm có khả năng kháng nước theo tiêu chuẩn IPX6, IPX8 và IPX9.\r\n\r\nMàn hình ngoài 6,62 inch và màn hình chính 8,12 inch sử dụng tấm nền LTPO tần số quét thích ứng 1-120 Hz, hỗ trợ HDR10+. Find N5 có cấu hình với chip mạnh nhất hiện nay là Snapdragon 8 Elite và RAM 16 GB, bộ nhớ trong 512 GB. Oppo không nhắc nhiều đến hệ thống camera do có ít nâng cấp so với bản N3. Camera vẫn mang thương hiệu Hasselblad, ống kính chính và tele cùng độ phân giải 50 megapixel, góc siêu rộng 12 megapixel.\r\nHonor X8c (từ 7 triệu đồng)\r\n\r\nX8c có thiết kế không đổi so với thế hệ trước, như khung sườn kim loại phẳng và cụm camera vuông, nhưng nâng cấp khả năng kháng nước IP64M và chứng nhận 5 sao của SGS, chịu được việc thả rơi từ độ cao 1,8 m. Sản phẩm dùng màn hình AMOLED 6,7 inch, độ phân giải Full HD, tần số quét 120 Hz.\r\n\r\nPhần khuyết màn hình dạng viên thuốc Magic Capsule hiển thị thông báo tương tự Dynamic Island của Apple. Sản phẩm lần đầu tích hợp AI tạo sinh với tính năng Magic Portal giúp tự nhận diện nội dung tin nhắn, điều hướng nhanh đến ứng dụng phù hợp. Máy có ba camera sau với camera chính 108 megapixel chống rung quang học, góc siêu rộng và đo độ sâu trường ảnh 5 megapixel. Camera trước 50 megapixel kèm đèn flash giúp quay và chụp thiếu sáng tốt hơn.\r\n\r\nHonor trang bị cho X8b chip Snapdragon 685, RAM 8 GB cùng hai phiên bản bộ nhớ trong 256 và 512 GB. Máy dùng pin 5.000 mAh, sạc nhanh 35 W.\r\nPoco M7 Pro (6 triệu đồng)\r\n\r\nPoco M7 Pro có thiết kế giống dòng Redmi Note của Xiaomi nhưng được phối màu nổi bật hơn, hỗ trợ chống nước, bụi IP53. Sản phẩm sử dụng màn hình AMOLED 6,67 inch độ phân giải Full HD+, tần số quét 120 Hz, độ sáng 2.100 nit cao nhất trên dòng Poco M.\r\n\r\nMáy có cấu hình tốt nhất trong tầm giá khi dùng chip Dimensity 7025 Ultra, RAM 12 GB, bộ nhớ 256 GB. Camera chính 50 megapixel sử dụng cảm biến IMX882 chống rung quang học OIS. Máy tích hợp Poco Imaging Engine dùng AI để tối ưu hóa khả năng xử lý và chỉnh sửa ảnh. Sản phẩm trang bị pin 5.110 mAh cùng sạc nhanh 45 W.\r\nXiaomi Redmi A5 (từ 2,5 triệu đồng)\r\n\r\nBản nâng cấp của Redmi A3, smartphone rẻ nhất của Xiaomi, có cụm ống kính dạng viên thuốc nhỏ gọn thay vì cụm tròn như mẫu Ultra năm ngoái. Màn hình giọt nước tăng kích thước từ 6,71 lên 6,88 inch nhưng vẫn giữ độ phân giải HD+, tần số quét 120 Hz.\r\n\r\nCamera được nâng cấp mạnh với máy ảnh phía trước 8 megapixel, còn phía sau 32 megapixel, trong khi Redmi A3 lần lượt là 5 và 8 megapixel. Mẫu A5 trang bị chip Unisoc T7250 cùng hai phiên bản RAM 3 GB, ROM 64 GB và RAM 4 GB, ROM 128 GB. Máy có pin 5.200 cùng sạc 15 W.\r\nSamsung Galaxy A06 5G (từ 4 triệu đồng)\r\n\r\nSamsung ra mắt phiên bản 5G cho mẫu smartphone rẻ nhất Galaxy A06. Sản phẩm vẫn dùng màn hình Infinity U giọt nước 6,7 inch độ phân giải HD+, khả năng bảo mật Knox Vault - giải pháp bảo vệ dữ liệu bằng phần cứng của Samsung.\r\n\r\nHai camera sau có độ phân giải 50 và 2 megapixel. Máy trang bị chip Dimensity 6300, RAM 4 hoặc 6 GB, bộ nhớ trong 128 GB, pin 5.000 mAh với sạc nhanh 25 W. Máy không hỗ trợ AI tạo sinh nhưng được nâng cấp hệ điều hành và bảo mật trong 4 năm.', '1744475511_h87.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:31:51', 'Công nghệ', 0, 0),
(99, 'OpenAI kiện ngược, tố Elon Musk \'giả tạo\'', 'OpenAI nộp đơn kiện Elon Musk, yêu cầu tỷ phú Mỹ dừng các hành vi gây rối công ty và \"bất hợp pháp\".', '\"Thông qua các cuộc tấn công trên báo chí, các nội dung độc hại tiếp cận tới hơn 200 triệu người theo dõi qua nền tảng X, Musk đã thực hiện các khiếu nại pháp lý mang tính quấy rối, cùng nỗ lực giả tạo nhằm chiếm đoạt OpenAI. Musk đã thử mọi công cụ có sẵn để gây hại cho OpenAI\", CNN dẫn nội dung đơn kiện được OpenAI gửi lên Tòa án quận khu vực Bắc California cuối ngày 9/4.\r\n\r\nTrong đơn, OpenAI yêu cầu thẩm phán ngăn Musk \"thực hiện bất kỳ cuộc tấn công nào\" nhằm vào công ty trong tương lai. Họ cũng nhấn mạnh tỷ phú Mỹ \"phải chịu trách nhiệm về những thiệt hại đã gây ra.\r\n\r\nPhiên tòa xét xử sẽ được thực hiện vào mùa xuân năm sau, chưa xác định ngày cụ thể.\r\nMarc Toberoff, luật sư của Musk, không bình luận trực tiếp về đơn kiện. Tuy nhiên, ông nhắc lại lời đề nghị mua OpenAI đầu năm nay với giá 97,4 tỷ USD. \"Nếu hội đồng quản trị của OpenAI thực sự xem xét lời chào mua và nghĩa vụ phải làm, họ sẽ thấy nó nghiêm túc thế nào\", Toberoff nói với Reuters. \"Các tài sản của OpenAI được trả giá hợp lý theo thị trường, nhưng bị cáo buộc là \'can thiệp\' vào kế hoạch kinh doanh của họ\".\r\n\r\nTrong bài đăng trên X ngày 10/4, OpenAI cho biết: \"Những hành động không ngừng nghỉ của Elon chống lại chúng tôi chỉ là chiến thuật không thiện chí nhằm làm chậm OpenAI và giành quyền kiểm soát các sáng kiến về AI hàng đầu để phục vụ lợi ích cá nhân của ông ta\".\r\n\r\nMusk không đưa ra bình luận.\r\n\r\nElon Musk đồng sáng lập OpenAI cùng Sam Altman và một số tên tuổi khác vào năm 2015 trước khi rời đi năm 2019. Thời gian sau đó, tỷ phú Mỹ nhiều lần chỉ trích công ty chạy theo lợi nhuận, đi ngược mục tiêu phi lợi nhuận ban đầu. Năm ngoái, ông kiện OpenAI và Altman với cáo buộc lừa dối ông.\r\n\r\nĐầu tháng này, Musk yêu cầu tòa án đưa ra lệnh cấm tạm thời, ngăn OpenAI chuyển sang mô hình vì lợi nhuận. Thẩm phán Yvonne Gonzalez Rogers của tòa án quận Oakland, California, sau đó bác yêu cầu của tỷ phú Mỹ với lý do không cung cấp đủ bằng chứng cần thiết.\r\n\r\nTuy nhiên, bà Rogers cho biết sẵn sàng tổ chức sớm một phiên tòa vào mùa thu để xem xét cáo buộc của Musk, rằng liệu kế hoạch chuyển đổi của OpenAI có bất hợp pháp. Thẩm phán nói bà muốn giải quyết vụ kiện nhanh chóng vì \"lợi ích công cộng đang bị đe dọa và nguy cơ thiệt hại nếu xảy ra chuyển đổi trái pháp luật\".', '1744475576_h88.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:32:56', 'Công nghệ', 0, 0);
INSERT INTO `news` (`id`, `title`, `description`, `content`, `image`, `author`, `created_at`, `category`, `is_featured`, `views`) VALUES
(100, 'Ăn xin online trên TikTok', 'Để nhận quà ảo trên TikTok Live, nhiều người, trong đó có trẻ em, bất chấp mạo hiểm để thực hiện các hành động lôi kéo.', '\"Xin hãy ủng hộ. Chúng cháu rất nghèo\", ba đứa trẻ, được cho là đang ở trong một túp lều ở Afghanistan, đứng trước máy quay và chắp tay. Các buổi phát trực tiếp (livestream) của chúng tiếp cận người xem ở khắp thế giới qua TikTok Live. Trong nhiều giờ, cả ba xin những \"món quà\" ảo có thể đổi thành tiền.\r\n\r\nỞ một video khác, một cô gái nhảy múa, thỉnh thoảng hét lên nếu có ai đó gửi quà. Món quà thường là bông hồng ảo giá 1 penny (0,01 USD), nhưng người được tặng chỉ nhận khoảng nửa giá trị.\r\n\r\nNgoài việc cầu xin, nhiều buổi livestream còn xuất hiện những hành động \"hạ thấp phẩm giá\" hoặc nguy hiểm, chẳng hạn tự đánh mình, thực hiện động tác giật gân, không ngủ vài ngày, phủ bùn lên người...\r\nTikTok cho biết họ cấm trẻ ăn xin và những hình thức tương tự trên nền tảng, coi đó là một hình thức bóc lột. Mạng video của ByteDance cũng có chính sách nghiêm ngặt đối với người dùng phát trực tiếp.\r\n\r\nTuy nhiên, theo Observer, hình thức ăn xin trực tuyến vẫn rất phổ biến trên nền tảng, thậm chí được thuật toán tích cực quảng bá. Người livestream sau khi nhận quà không sở hữu toàn bộ, mà phải \"chia\" cho TikTok, thường chiếm một nửa giá trị quà tặng ban đầu, đôi khi đến 70%.\r\n\r\nĐại diện TikTok không phủ nhận con số trên, nhưng cho biết 30% doanh thu từ quà tặng được chuyển vào \"phí cửa hàng ứng dụng và chi phí nhà cung cấp dịch vụ thanh toán\". Nền tảng hiện bán khoảng 100 quà tặng ảo, từ bông hồng 1 penny đến những món đồ 44.999 penny (578 USD).\r\n\r\nOlivier de Schutter, báo cáo viên đặc biệt của Liên Hợp Quốc, gọi xu hướng này là \"diễn biến gây sốc\". Ông cáo buộc TikTok \"kiếm lợi từ nỗi thống khổ của mọi người\".\r\n\r\n\"Việc cắt một phần tiền từ nỗi thống khổ của người khác cũng không khác gì hành vi \'săn mồi\' trên môi trường kỹ thuật số\", Schutter nói với Guardian. \"Tôi kêu gọi TikTok thực thi các chính sách của mình về nạn ăn xin. Công ty cũng nghiêm túc đặt câu hỏi về \'hoa hồng\' đang lấy từ những người dễ bị tổn thương nhất thế giới\".\r\n\r\nJeffrey DeMarco, chuyên gia về tác hại kỹ thuật số của tổ chức Save the Children, cũng cho rằng TikTok đang \"lạm dụng\" và kêu gọi các cơ quan quản lý cần \"hành động lập tức để đảm bảo các nền tảng không còn cho phép hay hưởng lợi trực tiếp hoặc gián tiếp\" qua các hình thức tương tự.\r\n\r\nTheo phân tích của Observer từ tháng 1 đến tháng 4, việc ăn xin trực tuyến và các hành vi tương tự xuất hiện nhiều nhất tại Indonesia, Pakistan, Afghanistan, Syria, Ai Cập và Kenya. Nhiều luồng livestream cho thấy bối cảnh thực hiện tại nhà, nhưng một số dường như có dấu hiệu của việc ăn xin có tổ chức.\r\n\r\nĐược giới thiệu vào tháng 8/2020, TikTok Live là tính năng cho phép người sáng tạo phát nội dung trực tiếp. Theo nền tảng, 100 triệu người đã livestream năm 2024, tiếp cận \"hàng tỷ người dùng\" theo thời gian thực.\r\n\r\nPhát ngôn viên của TikTok cho biết nền tảng đã dừng hơn 4 triệu buổi phát trực tiếp mỗi tháng \"để giữ nền tảng an toàn\", buộc tài khoản được livestream cần đủ 18 tuổi và có từ 1.000 người theo dõi trở lên. Riêng trẻ em có thể xuất hiện cùng người lớn.\r\n\r\nThực tế, việc \"ăn xin online\" trên TikTok cũng có một số mặt tích cực. Ví dụ, một gia đình ở Philippines huy động thành công tiền từ cộng đồng để phẫu thuật tách cặp song sinh dính liền. Các tổ chức từ thiện cũng có thêm kênh tiềm năng để tiếp cận những người cần giúp đỡ. Tuy nhiên, Catherine Turner, chuyên gia thuộc tổ chức Anti-Slavery International, cho rằng dù trẻ em hoặc người nghèo xuất hiện trên video để cầu xin giúp đỡ, số tiền có thể về đổ túi người \"chăn dắt\" đứng sau.', '1744475643_h89.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:34:03', 'Công nghệ', 0, 0),
(101, 'Microsoft sắp dừng hỗ trợ 240 triệu người dùng Windows 10', 'Hãng phần mềm Mỹ ra thông báo sẽ chính thức dừng hỗ trợ hệ điều hành Windows 10 hiện có 240 triệu người dùng vào 14/10.', 'Hãng phần mềm Mỹ ra thông báo sẽ chính thức dừng hỗ trợ hệ điều hành Windows 10 hiện có 240 triệu người dùng vào 14/10.\r\n\r\nMicrosoft cho biết các máy chạy Windows 10 sẽ không được hỗ trợ dưới bất kỳ hình thức nào sau ngày này, kể cả các bản vá bảo mật với người dùng phổ thông.\r\n\r\n\"Đây là quyết định không thể đảo ngược\", công ty thông báo.\r\n\r\nNgoại lệ chỉ dành cho khách hàng doanh nghiệp, tổ chức nhưng với điều kiện phải trả phí, tùy vào số lượng máy trong hệ thống cũng như các công việc cần xử lý. Microsoft sẽ hỗ trợ theo từng giai đoạn với trường hợp này thay vì liên tục như trước. Tuy nhiên, công ty khuyến khích \"nâng cấp lên Windows 11 là giải pháp tối ưu\".\r\n\r\nThống kê của Statcounter phản ánh nỗi lo của Microsoft khi nhiều người dùng vẫn chưa nâng cấp lên hệ điều hành mới. Vào tháng 12/2024, báo cáo cho thấy thị phần Windows 10 thậm chí không giảm mà còn tăng lên 62,7% so với tháng trước đó, trong khi Windows 11 giảm còn 34,12%. Số người dùng 240 triệu cũng không thay đổi đáng kể so với hai năm trước đó.\r\nViệc dừng cập nhật phần mềm, bản vá bảo mật sẽ gây ra nhiều rủi ro về an toàn thông tin. Công ty Mỹ ví điều này giống như việc để cửa trước của căn nhà hé mở. Hệ thống không có bản cập nhật dễ bị phần mềm gián điệp hoặc độc hại tấn công hơn, đặc biệt với các virus nhằm vào lỗ hổng mới sau khi nhà sản xuất dừng nâng cấp.\r\n\r\nMicrosoft tuyên bố Windows 11 được tạo ra để cải thiện các giao thức bảo mật, hiệu suất, tốc độ và hiện đại hóa với AI tích hợp. Đây được coi là phiên bản thay thế xứng đáng cho Windows 10, hệ điều hành đã hơn 10 năm tuổi.\r\n\r\nĐể biết máy tính mới có hỗ trợ Windows 11 hay không, người dùng có thể tải phần mềm PC Health Check do chính Microsoft phát hành. Tại đây, ứng dụng sẽ thông báo chi tiết các điều kiện và phần cứng đang có liệu có thể thích hợp cho việc nâng cấp hệ điều hành mới hay không.\r\n\r\nWindows 11 yêu cầu thiết bị thế hệ mới như tích hợp chip TPM 2.0, bộ xử lý được hỗ trợ. Đa số máy tính cũ không tương thích vì hai lý do kể trên. Tuy có một số mẹo để bỏ qua yêu cầu về TPM 2.0, nhiều chuyên gia đưa ra lời khuyên người dùng nên nâng cấp phần cứng để chạy hệ điều hành mới, tránh các rắc rối về sau liên quan đến bảo mật.', '1744475707_h90.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:35:07', 'Công nghệ', 0, 1),
(102, 'Google bị cáo buộc ngăn nhân tài AI nhảy việc', 'Nando de Freitas, cựu nhân viên Google, tố cáo công ty cố tình dùng các hợp đồng ràng buộc để tránh các nhân tài AI nhảy việc.', 'Giữ chân các nhân tài AI hàng đầu là điều khó khăn trong bối cảnh cạnh tranh khốc liệt giữa các công ty công nghệ lớn nhất hiện nay. DeepMind, bộ phận AI của Google, được cho là đã phải sử dụng các thỏa thuận không cạnh tranh đối với một số nhân viên AI tại Anh.\r\n\r\nTheo cựu nhân viên Google DeepMind, Nando de Freitas, các hợp đồng làm việc của công ty có thể khiến một nhà phát AI không làm gì trong suốt một năm. Điều này nhằm ngăn nhân tài về trí tuệ nhân tạo rời bỏ công ty. Tuy nhiên, cách làm này có thể khiến các nhà nghiên cứu cảm thấy bị tụt hậu khỏi tốc độ tiến triển nhanh chóng của công nghệ mới.\r\n\r\nDe Freitas mô tả những hợp đồng như vậy là \"lạm dụng quyền lực\".\r\n\r\nCựu nhân viên Google cũng đưa ra lời khuyên cách tốt nhất để tránh các ràng buộc là suy nghĩ thật kỹ trước khi đặt bút ký. Tuy nhiên, với những người cần nguồn thu nhập ổn định, thậm chí bị hấp dẫn bởi mức lương cao, họ có thể chấp nhận các điều khoản bất lợi về thời gian cam kết ở lại công ty. Ủy ban Thương mại Liên bang (FTC) của Mỹ đã cấm các điều khoản không cạnh tranh ở những bang như California từ năm ngoái nhưng do Google DeepMind đặt trụ sở tại Anh, công ty vẫn có thể \"lách\" luật này.\r\n\r\nDe Freitas đã chuyển sang Microsoft và đảm nhận vị trí Phó Chủ tịch AI. Ông cho biết một số nhân viên hiện tại của DeepMind vẫn liên hệ để tìm cách thoát khỏi các hợp đồng \"bẫy\". Tuy nhiên, De Freitas khuyên họ nên tìm kiếm sự giúp đỡ từ chính những người lãnh đạo khác tại Google.\r\n\r\nPhản hồi về sự việc, Google cho biết \"các hợp đồng lao động của chúng tôi phù hợp với tiêu chuẩn thị trường. Do tính nhạy cảm của công việc, chúng tôi sử dụng các điều khoản một cách chọn lọc để bảo vệ lợi ích hợp pháp của mình\".', '1744475766_h91.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:36:06', 'Công nghệ', 0, 0),
(103, 'Giá iPhone có thể tăng gấp ba nếu sản xuất tại Mỹ', 'Giới chuyên gia ước tính giá iPhone có thể tăng lên 3.500 USD nếu được sản xuất tại Mỹ thay vì 1.000 USD như hiện tại.\r\n\r\n', '\"Nếu xây dựng chuỗi cung ứng tại Mỹ, chẳng hạn nhà máy ở Tây Virginia và New Jersey, sẽ có những chiếc iPhone 3.500 USD\", Dan Ives, Giám đốc nghiên cứu công nghệ tại công ty dịch vụ tài chính Wedbush Securities, nói với CNN.\r\nNgày 2/4, Tổng thống Mỹ Donald Trump nói nhờ có thuế quan, nhiều công việc liên quan đến sản xuất cuối cùng sẽ được \"chuyển về Mỹ\", tạo việc làm cho hàng triệu người dân nước này.\r\n\r\nTuy nhiên, Ives đánh giá đây là \"câu chuyện hư cấu\". Để sản xuất iPhone tại Mỹ, điều bắt buộc là \"phải sao chép hệ sinh thái sản xuất cực kỳ phức tạp đang tồn tại ở châu Á\". Ông ước tính Apple cần chi ít nhất 30 tỷ USD trong ba năm đầu để chuyển 10% chuỗi cung ứng của họ sang Mỹ.\r\n\r\nThực tế, dù là công ty Mỹ, toàn bộ quá trình sản xuất của Apple phụ thuộc vào châu Á - nơi xây dựng một hệ sinh thái cung ứng khép kín giúp giá thành ngày càng giảm. Chip cung cấp sức mạnh cho iPhone được tạo ra ở đảo Đài Loan, tấm nền màn hình do Hàn Quốc cung cấp, nhiều thành phần khác sản xuất ở Trung Quốc. Trung Quốc và Ấn Độ cũng đảm nhiệm khâu lắp ráp iPhone.\r\n\r\nKể từ khi ông Trump nhậm chức cuối tháng 1, cổ phiếu Apple mất khoảng 25% giá trị do nhà đầu tư lo ngại về vấn đề thuế tác động đến chuỗi cung ứng, đặc biệt ở Trung Quốc, nơi đảm nhận 90% sản lượng iPhone. Ngày 9/4, ông Trump thông báo giảm thuế đối ứng còn 10%, hoãn thi hành 90 ngày với một số nước, trong khi Trung Quốc tăng lên 125%.\r\n\r\n\"Đó là lý do để giải thích cho giá cổ phiếu ngày càng giảm, vì không có công ty nào bị cuốn sâu vào mặt trận thuế quan này hơn Apple\", Ives nói.\r\n\r\nApple chưa đưa ra bình luận.\r\n\r\nTrước đó, giới chuyên gia dự đoán giá iPhone có khả năng tăng, ngay cả khi chuỗi cung ứng giữ nguyên. Reuters trích dẫn báo cáo từ Rosenblatt Securities, ngân hàng đầu tư có trụ sở tại New York, rằng iPhone có thể đắt hơn 43% nếu Apple \"chuyển toàn bộ chi phí thuế mới cho người tiêu dùng\".\r\n\r\nNeil Shah, Phó chủ tịch nghiên cứu tại Counterpoint Research, dự đoán giá iPhone có thể cao hơn khoảng 30%, nhưng còn tùy thuộc nơi sản xuất. Trước những dự đoán này, người Mỹ được cho là đã đổ xô mua iPhone vì sợ giá tăng, theo Bloomberg.\r\n\r\nCó những dấu hiệu cho thấy Apple đang tích trữ thiết bị về Mỹ trước thời gian áp lệnh thuế mới. Times of India ngày 7/4 dẫn nguồn tin từ quan chức cấp cao của Ấn Độ rằng đã có 5 chuyến bay chở đầy iPhone và các sản phẩm Apple khác từ Ấn Độ và Trung Quốc \"chỉ trong ba ngày cuối tháng 3\".\r\n\r\nTheo giới quan sát, việc tích trữ nhiều hàng có thể giúp Apple trì hoãn tác động tại Mỹ - thị trường lớn nhất của hãng. Dù vậy trong tương lai, họ có thể phải thay đổi chính sách nếu hàng rào thuế giữ nguyên hoặc tiếp tục tăng.\r\n\r\nTrong khi đó, một số công ty công nghệ cũng đối mặt các khoản thuế nếu không đưa sản xuất về Mỹ. Hôm 8/4, ông Trump cho biết sẽ áp \"mức thuế lớn\" lên tới 100% nếu hãng gia công chip TSMC không xây dựng nhà máy tại Mỹ.\r\n\r\n\"Những công ty sản xuất chip rất giàu có\", ông Trump nói, đề cập Đạo luật CHIPS và Khoa học được cựu tổng thống Joe Biden ký năm 2022, trong đó tài trợ hàng tỷ USD cho các công ty để khuyến khích xây nhà máy ở Mỹ. \"Họ được cấp hàng tỷ USD để xây một nhà máy tại Mỹ\".\r\n\r\n\"Tất cả những gì tôi làm là nói rằng: Nếu không xây dựng nhà máy ở đây, các ông sẽ phải trả một khoản thuế lớn. Là 25%, có thể 50%, 75% và cũng có thể 100%\'\", ông nói thêm.\r\n\r\nKhi Business Insider liên hệ, TSMC từ chối bình luận. Tuy nhiên, tháng trước, công ty cho biết sẽ đầu tư 100 tỷ USD để xây 5 nhà máy sản xuất chip mới tại Mỹ. Khoản này nằm ngoài gói đầu tư 65 tỷ USD đã công bố hồi tháng 4/2024.', '1744475841_h92.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:37:21', 'Công nghệ', 0, 0),
(104, 'Rối trí, mất tiền vì AI giả giọng người thân', 'Mỹ - Linda Roan, 62 tuổi, bất ngờ nhận cuộc gọi từ số lạ, bên kia là tiếng nức nở của con gái út, nói bà cần chuyển tiền gấp.', '\"Mẹ ơi con vẫn ổn, nhưng con cần mẹ giúp\", cô bé khóc trong cuộc gọi hồi tháng 2. Roan lập tức nhận ra giọng con gái 26 tuổi. Một người đàn ông lên tiếng, nói cô gái vô tình chứng kiến và làm hỏng vụ giao dịch ma túy của hắn, nên bà Roan cần đền bù, nếu không sẽ không thả con gái bà đi.\r\n\r\nRoan ban đầu nghĩ đây là trò lừa, nhưng rõ ràng đã nghe thấy tiếng con gái mình và kiểu khóc đặc trưng của cô.\r\n\r\nNgười đàn ông hướng dẫn bà đến một siêu thị ở ngoại ô thành phố Denver và gửi tiền đến Mexico thông qua Western Union. Hắn chuẩn bị sẵn cho bà câu chuyện phòng khi nhân viên nghi ngờ, rằng người thân sống ở Mexico đang ốm nặng và cần tiền ngay lập tức.\r\nNgười này liên tục giám sát, yêu cầu bật loa ngoài và giấu điện thoại. Tuy nhiên, Roan không thể chuyển tiền bằng thẻ tín dụng nên phải quay về gửi qua cổng trực tuyến của Western Union 1.000 USD sang Mexico.\r\n\r\nNgười đàn ông nói sẽ thả con gái Roan, nhưng sau đó lại bảo sếp hắn tức giận vì chậm trễ và đòi thêm, thậm chí dọa bán con gái bà với giá 30.000 USD, trong khi Roan nghe thấy tiếng la hét của con mình. Bà van xin được nói chuyện với con nhưng bị từ chối.\r\n\r\nDù vẫn nghi ngờ, bà không dám mạo hiểm. \"Tiếng nói chính xác là của con gái tôi\", bà kể lại.\r\n\r\nRoan chuyển thêm 1.000 USD và kẻ lừa đảo nói vừa thả con gái bà. Roan lập tức gọi cho con, mới biết cô vẫn an toàn trong căn hộ riêng, hoàn toàn không biết gì về sự việc vừa diễn ra.\r\n\r\nSau khi bình tĩnh, bà báo cảnh sát. Một sĩ quan kiểm tra các giao dịch và tra cứu số điện thoại trong cơ sở dữ liệu nhưng không có kết quả, cũng không tìm được thông tin của người nhận tiền. Công ty thẻ tín dụng cho biết Roan không thể lấy lại được tiền vì bà tự nguyện thực hiện giao dịch đó.\r\n\r\nBà Roan chỉ là một trong số rất nhiều nạn nhân của AI giả giọng nói của người thân. Ủy ban Thương mại Liên bang Mỹ (FTC) tháng trước cho biết các vụ lừa đảo mạo danh người thân, đồng nghiệp, bạn bè hoặc quan chức chính phủ là hình thức được ghi nhận nhiều nhất năm 2024, gây thiệt hại gần 3 tỷ USD.\r\n\r\nCục Điều tra Liên bang Mỹ FBI cảnh báo tội phạm đang sử dụng AI ngày càng nhiều để bắt chước giọng nói, khiến các trò lừa trở nên đáng tin hơn.\r\n\r\nTheo hãng bảo mật McAfee, chỉ cần 3 giây ghi âm đã có thể sao chép giọng nói với độ chính xác 85%. Một khảo sát của công ty cho thấy hơn nửa trong số 7.000 người tham gia thường xuyên chia sẻ nội dung có chứa giọng nói lên mạng. Trong khi đó, trung bình cứ 10 người được hỏi có một người nhận được các cuộc gọi tương tự và phần lớn đều mất tiền.\r\n\r\n\"Nỗi sợ hãi có thể làm tê liệt khả năng phán đoán trong tình huống như vậy, khiến chúng ta tin những gì mình nghe là thật\", Mary Poffenroth, giảng viên tại Đại học bang San Jose, cảnh báo.\r\n\r\nBà Roan cảm thấy rất xấu hổ vì bị lừa, nhưng bạn bè đều cảm thông khi biết tin. \"Mọi bà mẹ đều chia sẻ rằng họ sẽ làm điều tương tự ở hoàn cảnh đó\", bà nói.', '1744475898_h93.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:38:18', 'Công nghệ', 0, 1),
(105, 'Bill Gates kể về đoạn mã \'ngầu nhất\' trong sự nghiệp', 'Nhà đồng sáng lập Microsoft Bill Gates nhớ về đoạn mã máy tính mang tính bước ngoặt cách đây 50 năm, mở ra kỷ nguyên mới trong lĩnh vực công nghệ.', 'Đoạn mã được Bill Gates soạn bằng máy đánh chữ teletype thô sơ, nhưng đóng vai trò quan trọng trong quá trình tạo nên Microsoft ngày 4/4/1975.\r\n\r\nTrong dịp kỷ niệm 50 năm thành lập công ty, Gates kể lại khoảng thời gian cùng Paul Allen, bạn thân từ thời trung học, tạo ra \"nhà máy phần mềm\" đầu tiên trên thế giới sau khi đọc được bài báo hồi tháng 1/1975 về Altair 8800, máy tính vận hành bằng chip do Intel, công ty công nghệ ít tên tuổi thời đó, sản xuất.\r\n\r\nBài báo truyền cảm hứng cho Gates, sinh viên năm nhất Đại học Harvard. Ông và Allen tin rằng giúp chip bán dẫn Intel chạy được một phiên bản ngôn ngữ lập trình Basic sẽ tạo nên cuộc cách mạng trong ngành.\r\nTrên blog cá nhân, Bill Gates gọi đó là \"đoạn mã ngầu nhất\" ông từng viết trong suốt 50 năm qua. Đoạn mã về sau trở thành nền tảng giúp Microsoft đưa máy tính cá nhân thành vật dụng quen thuộc trong các gia đình, với bộ phần mềm cơ bản gồm Word, Excel và PowerPoint, cùng hệ điều hành Windows đang vận hành phần lớn máy tính cá nhân hiện nay.\r\n\r\n\"Đó là cuộc cách mạng mở đường cho máy tính cá nhân\", Gates nói và cho biết hành trình 50 năm là chặng đường dài và \"thật điên rồ khi giấc mơ đã thành hiện thực\".\r\n\r\nBill Gates cũng chia sẻ mã nguồn đặt nền móng cho công ty, nằm trong tệp PDF dài 157 trang.', '1744475965_h94.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:39:25', 'Công nghệ', 0, 1),
(106, 'Trung Quốc ra chuẩn GPMI thay thế HDMI', 'GPMI có thể truyền băng thông tối đa 192 Gb/giây và cung cấp điện năng tối đa 480 W cho thiết bị kết nối thay vì chỉ dữ liệu video như HDMI.', 'Liên minh công nghiệp video 8K UHD Thâm Quyến, một nhóm gồm hơn 50 công ty Trung Quốc vừa ra mắt chuẩn kết nối có dây mới là General Purpose Media Interface (GPMI) nhằm thay thế cho hai kết nối phổ biến hiện tại là HDMI và DisplayPort.\r\nTiêu chuẩn được phát triển để hỗ trợ hình ảnh độ phân giải 8K và giảm số lượng cáp cần thiết để truyền dữ liệu, điện năng giữa các thiết bị. Theo HKEPC, cáp GPMI sẽ có hai loại gồm: Type-B với kiểu kết nối độc quyền và Type-C sử dụng thiết kế tương thích với chuẩn USB-C hiện tại.\r\n\r\nDo chuẩn 8K có số lượng điểm ảnh gấp bốn lần 4K và 16 lần so với Full HD, GPMI được xây dựng để mang nhiều dữ liệu hơn vượt trội so với chuẩn chung hiện tại. Tiêu chuẩn này cũng được thiết kế để phù hợp với các biến số khác tác động đến băng thông cần thiết như độ sâu màu, tốc độ làm tươi.\r\nĐầu nối GPMI Type-C có băng thông tối đa 96 Gb/giây và cung cấp công suất 240 W. Con số này cao hơn gấp đôi giới hạn dữ liệu 40 Gb/giây của USB4 và Thunderbolt 4. Tuy nhiên, nó có cùng giới hạn công suất với chuẩn USB Type-C mới nhất sử dụng tiêu chuẩn Dải công suất mở rộng (EPR).\r\n\r\nTrong khi đó, GPMI Type-B có các thông số vượt trội bao gồm băng thông tối đa 192 Gb/giây và cung cấp điện năng tối đa 480 W. Tuy con số này chưa đạt đến mức có thể cấp nguồn cho PC chơi game sử dụng card đồ họa RTX 5090 qua màn hình 8K nhưng vẫn là thừa đủ cho nhiều laptop chơi game cao cấp hiện tại. Điều này giúp đơn giản hóa thiết lập bàn làm việc khi chỉ cần một sợi cáp để cung cấp cả nguồn điện và dữ liệu cho laptop.\r\n\r\nGPMI vẫn hỗ trợ tiêu chuẩn điều khiển chung như HDMI-CEC, có nghĩa người dùng có thể sử dụng một điều khiển từ xa cho tất cả các thiết bị kết nối qua GPMI.\r\n\r\nTiêu chuẩn truyền video được sử dụng rộng rãi hiện nay có cả cung cấp điện năng là USB Type-C (Alt DP/Alt HDMI) và Thunderbolt. Tuy nhiên, điều này chủ yếu giới hạn ở màn hình, với nhiều TV vẫn sử dụng HDMI. Nếu GPMI trở nên phổ biến, thị trường sẽ sớm có thể sử dụng chỉ một loại cáp để thiết lập TV.', '1744476049_h95.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:40:49', 'Công nghệ', 0, 1),
(107, '11 nhân viên đầu tiên của Microsoft', 'Bức ảnh nhóm nhân viên đầu tiên của Microsoft trở thành biểu tượng trong ngày công ty kỷ niệm 50 năm thành lập.', 'Microsoft thành lập ngày 4/4/1975. Trong khi đó, ảnh 11 nhân viên đầu tiên được chụp năm 1978 tại bang New Mexico, trước khi công ty chuyển đến Washington cuối năm đó.\r\n\r\nBob Greenberg, một trong những lập trình viên đầu tiên của Microsoft, trúng thưởng suất chụp ảnh chân dung miễn phí với nhiếp ảnh gia địa phương trong một chương trình radio. \"Nó mang tính biểu tượng và có tầm ảnh hưởng vượt xa tất cả chúng tôi\", Greenberg nói.\r\n\"Bức ảnh ghi lại trọn vẹn bầu không khí và tinh thần chúng tôi có vào những ngày đầu tại Microsoft. Ai cũng đang cười. Ai cũng thật sự rất vui vẻ. Rất khó để người ngoài có thể hiểu được trạng thái của chúng tôi lúc ấy. Một số người, nhất là Bill Gates, từng ngủ lại văn phòng. Có những lúc bạn đến văn phòng và thấy chân cậu ấy thò ra ngoài cửa\", nhà sáng lập Paul Allen chia sẻ.\r\n\r\nBill Gates\r\n\r\nNgười đồng sáng lập Microsoft vị trí ở góc dưới bên trái ảnh, giữ chức CEO đến năm 2000, tiếp tục phục vụ trong hội đồng quản trị đến 2020. Sau khi rời đi, ông tập trung vào công việc từ thiện cùng Gates Foundation.\r\n\r\nTrong nhiều năm, Gates cho đi phần lớn tài sản của mình thông qua sáng kiến Giving Pledge do ông tạo ra cùng vợ cũ Melinda French Gates và nhà đầu tư Warren Buffett.\r\n\r\nBill Gates hiện giàu thứ 6 thế giới, với khối tài sản ròng trị giá 162 tỷ USD. Gần đây, ông cho biết sẽ chỉ để lại cho các con 1% tài sản, tức là khoảng 1,62 tỷ USD. \"Tôi cho rằng để lại nhiều hơn thế sẽ không có lợi cho chúng\", ông nói.\r\n\r\nPaul Allen\r\n\r\nPaul Allen, người cùng Bill Gates tạo nên Microsoft, xuất hiện ở góc dưới bên phải bức ảnh. Là một lập trình viên tài năng, ông có vai trò quan trọng trong phát triển hệ điều hành MS-DOS cho máy tính cá nhân đầu tiên của IBM.\r\n\r\nAllen phụ trách bộ phận nghiên cứu và phát triển sản phẩm mới của Microsoft đến năm 1983, khi ông rời công ty để chữa trị ung thư hạch bạch huyết. Ông có mặt trong hội đồng quản trị Microsoft trong giai đoạn 1981-1985 và 1990-2000. Sau đó, ông vẫn đảm nhiệm vai trò \"cố vấn chiến lược cấp cao\" cho ban lãnh đạo và giữ quan hệ bạn bè với Gates đến khi qua đời năm 2018 do biến chứng liên quan đến ung thư. Khi qua đời, Allen có khối tài sản trị giá 20 tỷ USD.\r\n\r\nBob Greenberg\r\n\r\nGreenberg rời Microsoft năm 1981, sau đó giúp công ty gia đình Coleco phát triển dòng búp bê Cabbage Patch Kids thống trị các cửa hàng vào những năm 1990.\r\n\r\nTính đến năm 2000, giá trị tài sản ròng của ông là 20 triệu USD. Ông hiện là một nhà đầu tư khởi nghiệp.\r\n\r\nGordon Letwin\r\n\r\nGordon Letwin đóng vai trò then chốt trong phát triển hệ điều hành OS/2, trong đó tích hợp giao diện đồ họa người dùng, giúp nó giống các hệ điều hành hiện đại hơn so với hệ thống tiền nhiệm. Ông rời Microsoft năm 1993.\r\n\r\nCùng với vợ lúc bấy giờ là Rose Letwin, Gordon sáng lập Wilburforce Foundation, tổ chức tài trợ chủ yếu cho các dự án về môi trường. Tính đến năm 2000, giá trị tài sản ròng của Letwin là 20 triệu USD.\r\n\r\nAndrea Lewis\r\n\r\nLà một trong hai người phụ nữ xuất hiện trong bức ảnh năm 1978, Andrea Lewis là chuyên viên viết tài liệu kỹ thuật cho Microsoft và rời công ty năm 1983. Sau đó, bà trở thành nhà báo tự do và là tác giả cuốn sách What My Last Man Did xuất bản năm 2017.\r\n\r\nBà cũng giúp xây dựng Hugo House, trung tâm văn học nghệ thuật phi lợi nhuận tại thành phố Seattle. Vào năm 2000, giá trị tài sản ròng của Lewis là 2 triệu USD.\r\n\r\nMaria Wood\r\n\r\nMaria Wood là nhân viên kế toán của Microsoft và từng kiện công ty vì phân biệt giới tính năm 1980. Microsoft đã dàn xếp vụ kiện, Wood sau đó tập trung nuôi dạy hai con và tham gia các hoạt động tình nguyện.\r\n\r\nBà kết hôn với một nhân viên đời đầu khác là Steve Wood. Tổng giá trị tài sản ròng của họ kết hợp lại được ước tính là 15 triệu USD tính đến năm 2000.\r\n\r\nBob O\'Rear\r\n\r\nLà một trong những nhân viên lớn tuổi nhất tại Microsoft khi bức ảnh được chụp, Bob O\'Rear là trưởng nhóm toán học và là một trong những lập trình viên chủ chốt trong phát triển hệ điều hành MS-DOS. Sau đó, ông đảm nhiệm vai trò giám đốc toàn cầu về bán hàng và quảng cáo tại công ty.\r\n\r\nO\'Rear rời công ty năm 1993 và được cho là sở hữu một trang trại nuôi gia súc tại quê nhà Texas. Giá trị tài sản ròng của ông được ước tính ở mức 100 triệu USD vào năm 2000.\r\n\r\nMarc McDonald\r\n\r\nMarc McDonald là lập trình viên và được xem là nhân viên hưởng lương đầu tiên của Microsoft. McDonald rời Microsoft năm 1984 khi nhận thấy ông không còn phù hợp với con đường phát triển của công ty. Sau đó, ông gia nhập công ty phần mềm Asymetrix do Paul Allen thành lập, chuyên về thuyết trình trực tuyến và thị trường e-learning.\r\n\r\nVề sau, ông gia nhập công ty phần mềm Design Intelligence ở Seattle, được Microsoft mua lại năm 2000, McDonald quay lại Microsoft. Ông hoàn toàn rời khỏi công ty năm 2011. Tính đến năm 2000, giá trị tài sản ròng của ông được ước tính là 1 triệu USD.\r\n\r\nSteve Wood\r\n\r\nSteve Wood, chồng của Maria Wood, là tổng quản lý đầu tiên của công ty. Sau này, ông giúp sáng lập nhiều công ty khác, bao gồm cả Asymetrix của Paul Allen, nơi ông giữ vị trí điều hành và xây dựng phần lớn đội ngũ.\r\n\r\nWood cũng sáng lập Wireless Services, nay là SinglePoint, tập trung vào năng lượng tái tạo để \"thúc đẩy tính bền vững và sức khỏe cộng đồng\".\r\n\r\nHiện Wood làm lính cứu hỏa tình nguyện ở bang Washington, đồng thời là chủ sở hữu của AirNote.\r\n\r\nBob Wallace\r\n\r\nBob Wallace gia nhập Microsoft sau khi thấy Bill Gates dán tờ thông báo tuyển lập trình viên trong cửa hàng bán lẻ máy tính tại Seattle, nơi ông đang làm việc. Vào cuối những năm 1970, Wallace và Gates được cho là đã đột nhập vào một công trình xây dựng và lái xe ủi, suýt đè lên chiếc Porsche của Gates.\r\n\r\nMột trong những thành tựu kỹ thuật của ông là giúp phát triển phiên bản ban đầu của ngôn ngữ lập trình Pascal trong thời gian ở Microsoft. Ông rời công ty năm 1983 để thành lập Quicksoft, doanh nghiệp được xem là một trong những ngọn cờ tiên phong trong khái niệm \"phần mềm chia sẻ\", tức phần mềm được cung cấp miễn phí giữa người dùng.\r\n\r\nCổ phần của ông từ thời làm việc ở Microsoft từng có trị giá 15 triệu USD. Wallace qua đời năm 2002 ở tuổi 53.\r\n\r\nJim Lane\r\n\r\nJim Lane là quản lý dự án tại Microsoft và đóng vai trò quan trọng trong thiết lập liên minh \"Wintel\" với Intel, giúp cả hai công ty thống trị thị trường máy tính cá nhân trong nhiều thập kỷ.\r\n\r\nLane cũng tham gia phát triển trình biên dịch Pascal và FORTRAN của Microsoft trước khi rời công ty năm 1985. Sau đó, ông thành lập công ty phần mềm riêng. Tính đến năm 2000, tài sản ròng của ông có giá trị khoảng 20 triệu USD.', '1744476117_h96.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:41:57', 'Công nghệ', 0, 0),
(108, 'Đầu tư tiền triệu chạy Node dù giá Pi giảm mạnh', 'Một số người chơi Pi Network chuyển sang chạy Node với hy vọng kiếm nhiều Pi hơn, dù giá tiền ảo này giảm 6 lần so với đỉnh.', '\"Việc \'kích tia sét\' không còn nhận được nhiều Pi nữa, tôi hiện chuyển sang chạy Pi Node\", Thế Trung (Dak Lak) chia sẻ. \"Với hai Node đang có, mỗi tháng tôi thu về gần 300 Pi\".\r\n\r\nAnh Trung cho biết đã chi gần 30 triệu đồng mua hai máy tính phục vụ chạy Node. Anh cũng lắp một đường mạng mới và nâng cấp mạng hiện có lên tốc độ cao hơn để việc \"khai thác\" nhanh hơn, đồng thời chi bốn triệu đồng mua UPS đề phòng mất điện.\r\n\r\n\"Mỗi tháng, chi phí trung bình cho mỗi Node khoảng 700.000 đồng, chủ yếu tiền mạng và tiền điện\", anh nói. \"Tôi dự tính khoảng 8 tháng sẽ thu hồi vốn\".\r\nAnh Mai Hiển (Bình Phước) cũng bắt đầu chạy Node tại gia được hơn một tháng, tận dụng ba máy tính có sẵn. Tháng đầu tiên, hệ thống mang về 400 Pi, tức khoảng 200 USD nếu tính theo giá Pi hiện tại là 0,5 USD. Trong khi đó, chi phí tiền mạng và tiền điện khoảng 1,3 triệu đồng.\r\n\r\nPi Node, hay nút mạng Pi, là phần mềm trên máy tính với vai trò tạo và lưu trữ Pi. Không như ứng dụng smartphone đóng vai trò đăng ký, đăng nhập, phục hồi tài khoản và \"điểm danh\" nhận Pi, Pi Node có nhiệm vụ xác nhận giao dịch trong hệ thống của Pi Network.\r\n\r\nTrong khi khai thác Bitcoin hay Ethereum dựa trên thuật toán bằng chứng công việc (Proof of Work), tức tính toán quá trình làm việc để nhận phần thưởng, blockchain của Pi Node lại dùng thuật toán giao thức đồng thuận Stellar (SCP). Thông qua vòng tròn bảo mật, các Node xác định ai có hoặc không thể xác thực giao dịch trên sổ cái blockchain của Pi Network.\r\n\r\nTrên một số hội nhóm Facebook về Pi Network, các cuộc thảo luận về chạy Node cũng tăng cao. Văn Duy, quản trị viên hội Pi Node có hơn 40.000 thành viên, cho biết luồng trao đổi về Node rất sôi động, nhất là sau khi dự án \"mở mạng\" ngày 20/2. Đa số bài đăng hỏi về cấu hình tham khảo, cách vận hành, cách nhận phần thưởng và không bị \"ăn trứng\" - thuật ngữ chỉ việc hệ thống bị lỗi khiến không nhận được Pi.\r\n\r\nCác dịch vụ mua bán, thuê, tư vấn lắp đặt cũng nở rộ. Theo Nguyễn Hoàng, người chuyên cung cấp dịch vụ tại Quảng Ngãi và vận hành \"trang trại\" Pi Node hơn 100 máy, mỗi ngày anh vẫn nhận vài đơn hàng, nhưng từ chối đề cập chi tiết.\r\n\r\n\"Nếu khách hàng tự vận hành Pi Node, chúng tôi lắp đặt miễn phí nhưng cài phần mềm tính phí. Còn với việc thuê hoặc đặt Node tại \'trang trại\', mức phí tùy theo tỷ lệ thỏa thuận\", anh Hoàng nói.\r\nTuy nhiên, một số người tham gia dự án Pi Network lâu năm cảnh báo người dùng không nên chạy theo Pi Node, vì lợi nhuận hiện có thể không như kỳ vọng. \"Với giá Pi xuống dưới 0,5 USD và chưa có dấu hiệu tăng trở lại do nguồn cung đổ ra ngày càng nhiều, thời gian thu hồi số tiền đầu tư có thể lâu hơn\", Hoàng Anh, quản trị viên hội nhóm Facebook về Pi Network, khuyến cáo. \"Đó là chưa kể số Pi sau khi \'khai thác\' thường không về ví để có thể bán ngay mà phải đợi thêm. Người chơi sẽ không biết được trước ngày trả token do quyền thuộc về Pi Core Team\".\r\n\r\nThực tế, giá Pi đang giảm mạnh sau khoảng 1,5 tháng công bố Open Network. Từ mức đỉnh 3 USD cuối tháng 2, tiền ảo này \"lao dốc không phanh\" còn 0,48 USD. Theo thống kê từ CoinGecko, tiền ảo này đã giảm giá 52,4% trong hai tuần qua và 75,5% trong 30 ngày qua.\r\n\r\nPi Core Team, đội ngũ đứng sau Pi Network, trước đó thông báo tổng cung tối đa 100 tỷ Pi, nguồn cung ban đầu 6 tỷ Pi được đưa ra thị trường sau khi mở mạng. Tuy nhiên theo CoinGecko, hiện nguồn cung Pi đã vượt 10 tỷ. Dữ liệu từ PiScan cũng cho thấy khoảng 126,6 triệu Pi tiếp tục được mở khóa vào tháng 4. Đến nay, mạng lưới Pi Network đã mở khóa 4,9 tỷ Pi và 1,54 tỷ Pi dự kiến được giải phóng trong 12 tháng tới. Do đó, giá Pi được đánh giá sẽ tiếp tục giảm.\r\n\r\n\"Số lượng mở khóa hàng tháng vượt quá nhu cầu, ảnh hưởng lớn đến giá Pi\", Alex Obchakevich, nhà sáng lập Obchakevich Research, nói với CoinTelegraph.\r\n\r\nTuy nhiên, Obchakevich cũng đánh giá Pi Network \"có thể cạnh tranh với 10 dự án hàng đầu trong tương lai\". Ông nhấn mạnh đội ngũ hiện nên tập trung vào phát triển mạng lưới và công nghệ đứng sau nhằm tăng mức ứng dụng và sự quan tâm, đồng thời đưa ra chiến lược rõ ràng thay vì thiếu minh bạch như hiện tại.\r\n\r\nPi Network ra đời năm 2019, được quảng cáo giúp người tham gia sở hữu tiền ảo miễn phí bằng cách vào ứng dụng trên điện thoại \"bấm tia sét\" để điểm danh mỗi ngày. Việt Nam là một trong những thị trường có nhiều người tham gia \"đào Pi\". Dự án gây tranh cãi khi gần 6 năm mới mở mạng, tức cho phép người dùng di chuyển Pi sang các nền tảng các để trao đổi, giao dịch.\r\n\r\nDự án bị một số nhân vật trong giới tiền số \"cảnh báo đỏ\". CEO sàn Bybit Ben Zhou đánh giá Pi Network nguy hiểm hơn meme coin và cho biết không niêm yết tiền ảo này trên sàn của ông. Justin Bons, nhà sáng lập của Cyber Capital và là nhà phân tích tiền số nổi tiếng, cũng công khai trên X rằng Pi Network là \"trò lừa đảo\" và khuyên mọi người tránh xa.', '1744476225_h97.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:43:45', 'Công nghệ', 0, 0),
(109, 'Đà Nẵng thiếu 33.000 lập trình viên', 'PGS. TS Nguyễn Gia Như của Đại học Duy Tân cho biết việc tuyển dụng lập trình viên tại miền Trung, nhất là Đà Nẵng, gia tăng do hiện chỉ đáp ứng 57% nhu cầu thực tế.', '\"Hiện Đà Nẵng cần 77.000 nhân sự công nghệ thông tin, nhưng mới có 44.000 người làm trong ngành, đồng nghĩa thiếu hụt 33.000 lập trình viên, nhất là vị trí đòi hỏi kinh nghiệm và chuyên môn cao\", PGS. TS Nguyễn Gia Như, Hiệu trưởng Trường Công nghệ Kỹ thuật - Đại học Duy Tân, nói.\r\n\r\nTình trạng thiếu hụt không chỉ xảy ra ở Đà Nẵng mà ở cả khu vực miền Trung, từ Quảng Bình đến Bình Định và Tây Nguyên. Tổng số lập trình viên tại đây khoảng 55.000-60.000 người, trong đó Đà Nẵng chiếm phần lớn.\r\n\r\nĐà Nẵng hiện là trung tâm công nghệ thông tin lớn nhất miền Trung. Hoạt động công nghệ thông tin tại các tỉnh, thành phố khác như Huế, Quảng Nam, Quy Nhơn (Bình Định) chủ yếu gia công phần mềm và các dự án quy mô nhỏ.\r\nTrong bối cảnh Đà Nẵng và các tỉnh miền Trung \"khát\" lập trình viên, PGS. TS Như cho biết các sự kiện như DevDay Đà Nẵng được kỳ vọng sẽ tạo ra những tác động tích cực. DevDay 2025, diễn ra ngày 12/4 tại Đại học Duy Tân, sẽ có hơn 30 phiên hội thảo chuyên sâu, với 40 chuyên gia trong và ngoài nước chia sẻ các xu hướng công nghệ mới với chủ đề AI, blockchain, an ninh mạng, điện toán đám mây, công nghệ tài chính và giáo dục thông minh.\r\n\r\nNgày hội việc làm trong sự kiện cũng tạo cơ hội kết nối giữa doanh nghiệp CNTT và nhân sự tiềm năng. PGS. TS Nguyễn Gia Như cho biết, so với Hà Nội và TP HCM, mức lương lập trình viên tại Đà Nẵng thấp hơn 20-30%. Tuy nhiên, một lợi thế tại miền Trung là chi phí sinh hoạt thấp hơn, giúp thu nhập thực tế không quá chênh lệch.\r\n\r\nTheo Hiệu trưởng Trường Công nghệ Kỹ thuật, các trường đại học trong khu vực như Đại học Bách khoa Đà Nẵng, Đại học Huế, Đại học Duy Tân và Đại học Quy Nhơn hiện cung cấp nguồn nhân lực công nghệ thông tin trẻ, có nền tảng kiến thức tốt.\r\n\r\n\"Lập trình viên Đà Nẵng nói riêng và miền Trung nói chung chăm chỉ và có khả năng tiếp thu công nghệ mới nhanh chóng. Điều này giúp họ cạnh tranh tốt trong các dự án quốc tế, đặc biệt với các thị trường như Nhật Bản, Hàn Quốc, Mỹ và châu Âu\", ông Như đánh giá.\r\n\r\nNhững yếu tố trên đang biến Đà Nẵng thành trung tâm phát triển phần mềm, thu hút nhiều công ty công nghệ lớn như FPT Software, Axon Active, CMC Global, Enclave, Bravo, Softech Đà Nẵng, AsiaSoft, KMS, Game Gear và Rikkeisoft.', '1744476281_h98.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:44:41', 'Công nghệ', 0, 0),
(110, 'Công nghệ trên dòng áo điều hòa Kenko', 'Áo điều hòa Kenko tích hợp động cơ quạt không chổi than, vải ứng dụng công nghệ Anti-UV, chống nước, pin dung lượng cao đạt tiêu chuẩn châu Âu, Mỹ, Nhật Bản.', 'Các dòng sản phẩm áo điều hòa được Kenko thiết kế hướng đến sự thoải mái cho người mặc, tích hợp công nghệ để có thể sử dụng lâu dài trong đa dạng điều kiện thời tiết. Các mẫu áo điều hòa trang bị động cơ quạt 5 cánh không chổi than, tích hợp vòng bi đôi cùng bộ pin dung lượng cao. Chất liệu vải có công nghệ Anti-UV, khả năng chống nước, chịu thời tiết khắc nghiệt.\r\nÔng Trịnh Xuân Chung, đại diện Kenko cho biết, việc nghiên cứu và phát triển sản phẩm được dựa trên ba yếu tố, gồm: quạt hiện đại, pin dung lượng cao và chất liệu vải thông minh. \"Các yếu tố này giúp người mặc luôn thoải mái, mát mẻ và đảm bảo độ bền bỉ trong quá trình sử dụng\", đại diện Kenko Việt Nam nói.\r\n\r\nTheo đó, động cơ quạt không chổi than được Kenko tích hợp hai vòng bi cho mỗi quạt, lượng gió mạnh hơn 30% so với trước đây, trong khi ít tiêu hao điện năng. Doanh nghiệp cũng phát triển công nghệ chip xử lý điện áp thông minh kèm bảng mạch bảo vệ trên pin áo điều hòa. \"Với dòng pin thế hệ mới 22V tích hợp bảng mạch kép an toàn, chip Alpha X5 có thể hoạt động liên tục từ 9,5 đến 15h ở công suất mạnh nhất với một lần sạc đầy\", đại diện doanh nghiệp nói, thêm rằng công nghệ này giúp tối ưu hiệu suất sử dụng, kéo dài tuổi thọ pin, bảo vệ thiết bị khỏi quá tải, quá nhiệt và chập cháy.\r\nPin được sử dụng trên áo điều hòa Kenko đạt chứng nhận từ các tổ chức quốc tế như CE (châu Âu), PSE (Nhật Bản) và UL (Mỹ). Ngoài ra, các dòng áo điều hòa của hãng này sử dụng chất liệu vải Taslan, vải gió siêu nhẹ hoặc vải bò chống cháy nhẹ, thoáng khí để tăng hiệu quả làm mát. Theo hãng, vải có thể cản đến 98% tia UV, bảo vệ da khỏi tác động của ánh nắng mặt trời.\r\nNhãn hiệu Kenko đã được doanh nghiệp đăng ký tại Cục Sở hữu Trí tuệ, Bộ Khoa học và Công nghệ. \"Kenko không ngừng đổi mới, nâng cao chất lượng sản phẩm nhằm mang đến trải nghiệm tốt nhất, chiếm được lòng tin yêu của người dùng\", ông Trịnh Xuân Chung, đại diện Kenko cho biết. \"Trong năm nay, Kenko sẽ cho ra mắt sản phẩm đa dạng hơn, hướng đến cả thị trường trong nước và Đài Loan, Nhật Bản, Hàn Quốc\".', '1744476364_h99.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:46:04', 'Công nghệ', 0, 1),
(111, 'Ca khúc \'Viết tiếp câu chuyện hòa bình\' gây xúc động', 'Nhạc sĩ Nguyễn Văn Chung gửi thông điệp về lòng biết ơn của thế hệ trẻ với những người đã hy sinh vì độc lập dân tộc trong \"Viết tiếp câu chuyện hòa bình\".', 'Trong tháng kỷ niệm 50 năm thống nhất đất nước (30/4/1975-30/4/2025), tác phẩm được nhiều người hưởng ứng. Trên TikTok, bản remix của ca khúc do Đức Tư thực hiện nhận gần 8.000 lượt sử dụng cho các video ghi lại không khí chiến trường xưa hay khung cảnh thời bình. Bài hát cũng xuất hiện trong những chương trình nghệ thuật kỷ niệm dịp lễ lớn của đất nước. Tại nhiều trường học, học sinh các cấp, sinh viên biểu diễn văn nghệ trên nền nhạc tác phẩm.\r\n\r\nBài hát có phần lời giản dị, giai điệu hào hùng, thể hiện lòng biết ơn của người trẻ với thế hệ dựng nước, giữ nước: \"Cha ông ta ngày xưa ngã xuống để cho đời ta ngày sau đổi lấy hòa bình. Giữa khói binh ai cũng nguyện lòng hy sinh. Xin tri ân những người chiến sĩ quên đi niềm riêng quên đi cả bản thân mình. Cuộn chảy trong lòng một dòng máu nóng dòng máu Lạc Hồng\".\r\nĐiệp khúc không chỉ nhắc lại công lao của cha ông mà còn truyền tải thông điệp về sự tiếp nối của thế hệ trẻ. Những câu hát: \'\'Cùng tôi viết tiếp câu chuyện hòa bình. Nhìn quê hương sáng tươi trong bình minh. Nhìn ánh nắng chiếu rực rỡ quốc kỳ tung bay phấp phới\'\' mở ra khung cảnh tươi đẹp của thời bình, nhắc nhở mỗi người cần nỗ lực học tập, lao động, đưa đất nước ngày một phát triển.\r\n\r\nTrên các trang mạng xã hội, khán giả nói xúc động trước ca từ ý nghĩa, giàu cảm xúc, ấn tượng giọng hát nội lực của Duyên Quỳnh. Tài khoản Lê Hùng bình luận: \'\'Nghe mà sục sôi tinh thần yêu nước\'\'. Khán giả Xuân Nam nhận định ca khúc truyền cảm hứng mạnh mẽ tới người trẻ, mang lại niềm tự hào tương tự bài hát Một vòng Việt Nam (Đông Thiên Đức).\r\nNhạc sĩ Nguyễn Văn Chung cho biết vui, vinh dự khi ca khúc được yêu thích. Đây là bài hát chủ đề của album cùng tên do anh phối hợp ca sĩ Duyên Quỳnh thực hiện cuối năm 2023, dịp mừng 79 năm Ngày thành lập Quân đội Nhân dân Việt Nam (22/12/1944-22/12/2023).\r\n\r\nBan đầu, Nguyễn Văn Chung dự định viết một tác phẩm đơn giản để các hội nhóm cùng hát. Sau khi nghe lại, anh và Duyên Quỳnh đều thấy chưa đạt chất lượng. Nhạc sĩ làm lại ca khúc với giai điệu hào hùng, nhiệt huyết hơn. Album sau đó được giới chuyên môn đánh giá cao, song bài hát chưa phổ biến với công chúng. Khi Đức Tư remix một đoạn, ghép vào video ghi lại bàn thắng của đội tuyển bóng đá Việt Nam và đăng tải trên TikTok, ca khúc mới được chú ý. \'\'Tôi và Duyên Quỳnh lập tức liên hệ em Đức Tư để thực hiện bản remix hoàn chỉnh. Đúng như dự đoán, tác phẩm được các bạn trẻ yêu thích và lan tỏa rất nhanh\'\', nhạc sĩ cho hay.\r\nNhạc sĩ Nguyễn Văn Chung, 42 tuổi, có loạt sáng tác về tình yêu, gia đình, được khán giả yêu thích. Nhiều ca khúc anh viết trở thành hit của ca sĩ thể hiện, như Vầng trăng khóc (Nhật Tinh Anh - Khánh Ngọc), Con đường mưa (Cao Thái Sơn), Chiếc khăn gió ấm (Khánh Phương), Mùa đông không lạnh (Akira Phan), Nhật ký của mẹ (Hiền Thục). Sau một thời gian chuyên sáng tác nhạc trẻ, nhạc sĩ viết cho thiếu nhi khi kết hôn, làm bố. Năm 2020, Tổ chức kỷ lục Việt Nam xác nhận anh là Nhạc sĩ trẻ có nhiều ca khúc thiếu nhi nhất.\r\n\r\nNguyễn Duyên Quỳnh, 35 tuổi, sinh tại Buôn Mê Thuột, tốt nghiệp Nhạc viện TP HCM. Trước khi ra mắt với vai trò ca sĩ, cô từng có thời gian dài hoạt động trong nhóm hát bè. Năm 2019, cô được khán giả chú ý khi giành quán quân Người kể chuyện tình.', '1744476620_h100.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:50:20', 'Giải trí', 0, 0),
(112, 'Nguyễn Văn Chung dạy nhạc miễn phí cho trẻ', 'Nguyễn Văn Chung cho biết bỏ tiền túi 1,5 tỷ đồng để mở lớp \"Cùng con tập hát\" - dạy nhạc online miễn phí cho trẻ.', 'Ra mắt lớp dạy âm nhạc cho trẻ hôm 29/5, nhạc sĩ nói ấp ủ ý định giúp các bé tự học nhạc lý, kiến thức thanh nhạc khi chưa có điều kiện đến các trung tâm năng khiếu. Anh cùng êkíp quay video hướng dẫn hát 300 bài thiếu nhi do anh sáng tác, mỗi tập là một ca khúc. Trong mỗi video, ca sĩ Duyên Quỳnh - đồng nghiệp thân thiết của Nguyễn Văn Chung - hỗ trợ anh \"vỡ bài\" cho khán giả nhí.\r\n\r\n\"Tôi cũng mong các video là nguồn tư liệu cho các thầy cô dạy nhạc tại các trường mầm non, tiểu học tham khảo, hoặc làm giáo án phù hợp cho từng đối tượng học sinh\", anh nói.\r\nNguyễn Văn Chung cho biết kinh phí cho dự án chủ yếu đến từ tiền tác quyền anh nhận được từ Trung tâm Bảo vệ quyền tác giả âm nhạc Việt Nam (VCPMC) những năm qua. \"Tôi hiện là một trong số ít tên tuổi sống thoải mái được từ tác quyền ca khúc ở Việt Nam. Tùy theo quý, tôi thường nằm trong top ba hoặc top 5 nhạc sĩ có tiền tác quyền cao nhất, mỗi năm dao động từ 1,2 đến 1,5 tỷ đồng\", nhạc sĩ nói.\r\n\r\nNgoài ra, nhạc sĩ kiếm thêm thu nhập từ việc viết beat, làm giám đốc âm nhạc cho các game show. Nhiều bản hit cũ đến nay vẫn mang lại thù lao lớn cho anh. Chẳng hạn, một trung tâm khiêu vũ ở Đức từng trả cho anh 5.000 USD (khoảng 118 triệu đồng) để sử dụng ca khúc Nhật ký của mẹ.\r\nNhững năm gần đây, nhạc sĩ ngưng mảng đề tài tình yêu đôi lứa, chuyển qua viết nhạc thiếu nhi. Suốt tám năm, 300 ca khúc dành cho trẻ em ra đời, trong đó nhiều bài anh lấy cảm hứng từ hai con của anh, như Gia đình nhỏ hạnh phúc to, Mẹ ơi có biết.\r\n\r\nThời gian đầu viết nhạc cho trẻ em, nhiều lần, nhạc sĩ hụt hẫng vì thu nhập không bằng việc sáng tác cho ca sĩ trẻ. Các con tiếp thêm cho anh niềm tin theo đuổi con đường đã chọn. Những bài hát thiếu nhi đầu tiên anh viết, hai bé là người đầu tiên hát thử ở trường và được thầy cô khen ngợi. \"Khi viết nhạc cho trẻ em, tôi chưa nghĩ đến thu nhập, mà chủ yếu từ niềm vui của các bé. Tôi mong sau này lớn lên, tự hào vì có cha là một nhạc sĩ với những ca khúc thiếu nhi nổi tiếng, như tác giả Bé bé bằng bông hay Cả nhà thương nhau đã làm được\", Nguyễn Văn Chung nói.\r\n\r\nNguyễn Văn Chung, 41 tuổi, sáng tác đầu thập niên 2000. Khi đó, anh còn là sinh viên ngành du lịch - khách sạn, tập viết nhạc vì thất tình. Viết được khoảng 10 bài hát, bạn bè trong lớp khen giai điệu hay, gieo vần tốt, anh tìm cách liên lạc loạt ca sĩ để gửi bài, nhờ thu âm, nhưng chỉ có Nguyên Vũ liên hệ anh. Ca sĩ chọn Người thầy năm xưa để hát trong chương trình nhân ngày 20/11 trên sân khấu Lan Anh. Nhạc phẩm được yêu thích, đưa tên tuổi anh đến gần công chúng.\r\n\r\nSau đó, Nguyễn Văn Chung liên tiếp tạo các hit. Năm 2005, anh viết Vầng trăng khóc, giúp đôi song ca Nhật Tinh Anh - Khánh Ngọc trở thành hiện tượng. Nhiều bài hát của anh là nhạc phẩm đóng đinh trong sự nghiệp ca sĩ, như Cao Thái Sơn (Con đường mưa), Khánh Phương (Chiếc khăn gió ấm), Akira Phan (Mùa đông không lạnh), Nam Cường (Bay giữa Ngân Hà), Bảo Thy - Quang Vinh (Ngôi nhà hoa hồng), Hiền Thục (Nhật ký của mẹ).\r\n', '1744476676_h101.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:51:16', 'Giải trí', 0, 0),
(113, 'Nguyễn Văn Chung viết ca khúc \'Dắt mẹ đi khắp thế gian\'', 'Nhạc sĩ Nguyễn Văn Chung sáng tác \"Dắt mẹ đi khắp thế gian\" làm quà tặng mẹ nhân mùa Vu lan.', 'Nhạc phẩm thuộc thể loại ballad, do ca sĩ Như Thùy thể hiện. Trên nền hòa âm với tiếng piano, sáo chủ đạo, nhạc sĩ viết về nỗi sợ người con khi thấy mẹ ngày càng già yếu. Anh mong có đủ thời gian được đưa mẹ đi khắp nơi, ngắm \"biển xanh nắng tươi\", nếm \"nhiều món ngon trên đời\".\r\n\r\nỞ điệp khúc, tác giả viết về những điều đơn giản anh muốn mãi được làm cho mẹ:\r\n\r\n\"...Để con được bóp chân cho mẹ khi mỗi tối\r\nĐể con được đấm lưng cho mẹ khi nhức mỏi\r\nLắng nghe những tiếng càu nhàu\r\nVuốt ve mái tóc bạc màu vì năm tháng...\".\r\n\r\nNguyễn Văn Chung ấp ủ ca khúc suốt hơn một năm. Anh bắt đầu viết bài hát từ tháng 3/2020, lúc mẹ anh nằm viện. Nhạc sĩ nói: \"Khi đó, tôi sợ thời gian không còn nhiều. Ngồi bên giường bệnh, tôi hối hận vì từng làm bà buồn, hối tiếc vì còn nhiều điều muốn bù đắp cho mẹ. Tôi chọn cách kể với mẹ những điều chưa từng nói. May mắn, sau đó mẹ hồi phục dần và khỏe mạnh đến nay\". Ngoài bản thu của Như Thùy, ca sĩ Chi Dân cũng trình bày nhạc phẩm với phiên bản MV hoạt hình - ra mắt hôm 12/8.\r\nNguyễn Văn Chung sinh năm 1983, khởi nghiệp sáng tác khi còn là sinh viên ngành Du lịch Khách sạn. Năm 2005, anh viết Vầng trăng khóc, giúp đôi song ca Nhật Tinh Anh - Khánh Ngọc trở thành hiện tượng. Nhiều bài hát của anh là nhạc phẩm đóng đinh trong sự nghiệp ca sĩ, như Cao Thái Sơn (Con đường mưa), Khánh Phương (Chiếc khăn gió ấm), Akira Phan (Mùa đông không lạnh), Nam Cường (Bay giữa Ngân Hà), Bảo Thy - Quang Vinh (Ngôi nhà hoa hồng), Hiền Thục (Nhật ký của mẹ)... Sau thời gian chuyên sáng tác nhạc trẻ, những năm gần đây nhạc sĩ chuyển sang viết cho thiếu nhi.', '1744476726_h102.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:52:06', 'Giải trí', 0, 0),
(114, 'Lisa khuấy động Coachella', 'Lisa lần đầu tham gia Coachella - lễ hội âm nhạc lớn nhất Mỹ - với tư cách nghệ sĩ solo, thể hiện nhiều bản hit và vũ đạo nóng bỏng.', 'Tối 11/4 (giờ California), ca sĩ Lisa trình diễn 13 ca khúc tại sự kiện. Hầu hết là các bài hát thuộc album mới nhất - Alter Ego, phát hành hồi tháng 2. Ca sĩ được khen có sự đầu tư về trang phục, sân khấu và thần thái biểu diễn. Mẹ của cô, Rosé nhóm Blackpink và các diễn viên White Lotus 3 - phim đầu tay của Lisa - có mặt tại nhạc hội để cổ vũ cô.\r\nSự xuất hiện của cô trở thành chủ đề nóng trên các nền tảng, truyền thông quốc tế. Nhiều video ghi lại các tiết mục thu hút hàng trăm nghìn lượt xem, yêu thích trên X, Instagram. Phần lớn fan nói tự hào khi thấy thần tượng \"tỏa sáng\", gọi cô là \"biểu tượng Thái Lan tại Coachella (Thaiconic Lisa Coachella)\".\r\n\r\nTuy nhiên, một bộ phận khán giả không đánh giá cao màn trình diễn của Lisa, cho rằng ca sĩ lạm dụng backtrack (bản thu giọng hát trong studio thường cho nhạc đệm) khiến họ không nghe rõ tiếng của cô. Một số tài khoản cho rằng cô hát nhép, bày tỏ bất bình.\r\nBên cạnh chất giọng, vũ đạo của cô cũng thu hút nhiều chú ý. Vài động tác của Lisa bị nhận xét phản cảm, nhất là khi thể hiện bản hit Money với vũ công nam. Trên X, có người nói chúng có yếu tố gợi dục, không phù hợp với lời bài hát \"khoe sự giàu có\" của nhân vật trong bài. Mặt khác, nhiều tài khoản ủng hộ Lisa nhảy sexy dance, cho rằng cô đủ trưởng thành để thực hiện các điệu nhảy táo bạo.\r\n\r\nTheo Kbizroom, việc Lisa có tên trong danh sách tham gia đại nhạc hội ngay sau khi phát hành album là một vinh dự to lớn, nhưng cũng áp lực vì phải giành sự ủng hộ từ khán giả. Trang tin nhận định nhiều người hâm mộ lẫn nhà phê bình trông chờ màn thể hiện của Lisa do cô từng trải qua thời gian đào tạo làm idol khắc nghiệt, có kinh nghiệm biểu diễn tại đây với Blackpink năm 2019. Họ muốn biết cô thể hiện bản thân thế nào với tư cách nghệ sĩ solo. Ngày 18/4, Lisa có buổi diễn thứ hai tại Coachella.\r\nCoachella là lễ hội âm nhạc lớn nhất Bắc Mỹ, diễn ra tháng 4 hàng năm tại Empire Polo Ground ở thành phố Indio, bang California. Năm nay, sự kiện bắt đầu từ ngày 11 đến 20/4, chỉ tổ chức vào bốn ngày cuối tuần. Những ngôi sao nổi bật trong chương trình là Lady Gaga, Post Malone, Keshi. Ngoài Lisa, thành viên khác của Blackpink - Jennie - dự kiến có hai đêm diễn vào ngày 13 và 20/4.\r\n\r\nLisa, 28 tuổi, sinh ở Thái Lan, từng hoạt động chủ yếu ở Hàn Quốc trong nhóm Blackpink. Tài khoản Instagram của cô có hơn 10 triệu người theo dõi. Ca sĩ hiện là CEO của LLOUD - công ty do cô thành lập, hoạt động các lĩnh vực thời trang, điện ảnh, âm nhạc. TC Candler bình chọn cô là Sao nữ đẹp nhất hành tinh 2021.\r\n\r\nTừ khi rời YG, Lisa chuyển hướng phát triển sang thị trường Mỹ, châu Âu. Thời gian qua, cô có nhiều sân khấu tại các sự kiện lớn như lễ trao giải lớn như VMAs 2024, Oscar 2025, gây chú ý khi vừa diện nội y vừa hát tại sàn diễn Victoria\'s Secret năm ngoái.\r\n\r\nNgoài ca hát, Lisa lấn sân đóng phim, giữ vai phụ trong White Lotus 3. Bên cạnh sự nghiệp, chuyện đời tư của cô nhận chú ý. Người đẹp được cho hẹn hò doanh nhân Frédéric Arnault - con trai tỷ phú Bernard Arnault, một trong 10 người giàu nhất thế giới.', '1744476797_h103.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:53:17', 'Giải trí', 0, 3);
INSERT INTO `news` (`id`, `title`, `description`, `content`, `image`, `author`, `created_at`, `category`, `is_featured`, `views`) VALUES
(115, 'Lisa bị nhận xét nhạt nhòa trong lần đầu diễn ở Oscar', 'California - Lisa hát ca khúc \"Live and Let Die\" - nhạc phim James Bond 1973 - ở Oscar 2025, nhưng bị nhận xét giọng hát yếu.', 'Sau khi hạng mục Hóa trang và làm tóc xuất sắc được công bố, Lisa và nhiều nghệ sĩ tri ân loạt phim James Bond bằng những màn biểu diễn sôi động. Nữ diễn viên Margaret Qualley mở đầu bằng tiết mục múa bên dàn vũ công nam diện tuxedo.\r\n\r\nNgay sau đó, Lisa xuất hiện từ trên không bằng dây cáp, mang đến bản cover Live and Let Die - ca khúc chủ đề của bộ phim cùng tên ra mắt năm 1973 do ban nhạc Wings thể hiện. Lisa \"đốt nóng\" khán phòng bằng breakdance, sau đó nhường chỗ cho Doja Cat với giai điệu kinh điển Diamonds Are Forever - ca khúc chủ đề của bản phim James Bond năm 1971.\r\n\r\nSự xuất hiện của Lisa ở Oscar trở thành chủ đề nóng trên X. Nhiều khán giả nhận thấy việc đứng chung sân khấu với những tài năng như Doja Cat và Raye khiến Lisa lộ rõ khả năng biểu diễn kém. Cô bị chê hát yếu, vài đoạn bị phô và hụt hơi, trình diễn nhạt nhòa so với màn mở đầu quyến rũ của Margaret Qually, giọng ca nội lực của Raye với Skyfall.\r\n\r\nTrên X, người xem nhận xét: \"Nếu không có màn đu dây và nhảy, Lisa chẳng còn gì để lôi kéo khán giả\", \"So với Doja Cat hay Raye, giọng Lisa quả là dở\", \"Lisa chỉ nên hát trong phòng thu thôi, so với những nghệ sĩ nổi tiếng quốc tế thì còn thua xa\", \"Lisa chưa đủ tầm để biểu diễn trên sân khấu lớn như Oscar\".\r\n\r\nNhiều người cũng phản đối vì cho rằng buổi lễ nên dành cho các nghệ sĩ được đề cử hơn là ngôi sao nhạc pop. Họ viết: \"Tại sao ban tổ chức lại dành đất cho một nghệ sĩ không liên quan gì đến Oscar như vậy\", \"Lisa được vào đây có lẽ là nhờ thân thiết Frederic Arnault - con trai nhà Arnault, một trong những nhà tài trợ của sự kiện\", \"Chưa hiểu vì sao Lisa lại được chọn hát ca khúc của James Bond?\".\r\n\r\nTrong khi đó, người hâm mộ ca sĩ thể hiện sự phấn khích: \"Tôi hạnh phúc khi được chứng kiến khoảnh khắc cô ấy làm nên lịch sử\", \"Lisa của chúng tôi đã trở thành nghệ sĩ Kpop đầu tiên được hát ở Oscar\", \"Lisa biểu diễn quá đỉnh\". Trước lễ trao giải, fan của ca sĩ bày tỏ trên Instagram: \"Cô ấy xứng đáng với điều đó. Tôi đã hâm mộ từ năm 2016 khi Blackpink ra mắt. Cô gái này sinh ra để làm nghệ sĩ\".\r\nTrang phục của Lisa trong lần đầu dự Oscar cũng được đưa ra bình luận. Váy biểu diễn của cô bị chê là già và kém cuốn hút nhất nhóm. Cô diện đầm xẻ đùi, thân trên bằng nhung thêu hoa. Một khán giả trên X nhận xét: \"Bộ đầm của Lisa khiến tôi nhớ đến chiếc váy cũ mẹ tôi cất trong tủ. Stylist của cô ấy nên học tập stylist của Margaret Qually. Hãy nhìn Margaret với chiếc đầm đỏ tối giản sexy đi\".\r\n\r\nTrên thảm đỏ, Lisa chọn bộ trang phục để tri ân điệp viên 007. Cô diện thiết kế của Markgong, gồm sơ mi dáng váy, áo khoác tuxedo dáng dài, cài áo hoa đỏ gợi nhớ đến phong cách biểu tượng của James Bond do Sean Connery đóng. Variety, India Times khen cô mặc đẹp. Song một số ý kiến cho rằng một bộ đầm mềm mại và quyến rũ sẽ hợp thảm đỏ Oscar hơn là thiết kế có phần \"khô khan và nam tính\".\r\n\r\nLisa, 28 tuổi, sinh ở Thái Lan, hoạt động chủ yếu ở Hàn Quốc trong nhóm Blackpink. Tài khoản Instagram của cô thu hút 105 triệu người theo dõi. Ca sĩ hiện là CEO của LLOUD - công ty do cô thành lập, hoạt động các lĩnh vực thời trang, điện ảnh, âm nhạc. TC Candler bình chọn cô là Sao nữ đẹp nhất hành tinh 2021.\r\n\r\nNgoài ca hát, Lisa lấn sân đóng phim. Cô đang cùng đoàn phim The White Lotus 3 đến nhiều nơi để quảng bá tác phẩm. Bên cạnh sự nghiệp, chuyện đời tư của cô nhận chú ý. Người đẹp được cho hẹn hò doanh nhân Frédéric Arnault - con trai tỷ phú Bernard Arnault, một trong 10 người giàu nhất thế giới.\r\n\r\n', '1744476903_h104.jpg', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:55:03', 'Giải trí', 0, 2),
(116, 'Lisa Blackpink sẽ biểu diễn ở Oscar', 'Lisa là sao K-pop đầu tiên biểu diễn tại lễ trao giải Oscar, diễn ra ngày 2/3 ở Los Angeles (sáng 3/3 giờ Hà Nội).', 'Theo Variety, đại diện ban tổ chức cho biết Lisa và một số nghệ sĩ như Cynthia Erivo, Ariana Grande, Doja Cat, Queen Latifah và Raye tham gia các tiết mục tôn vinh cộng đồng làm phim và huyền thoại điện ảnh. Hiện Viện Hàn lâm chưa công bố bài hát họ trình diễn. Dàn hợp xướng Los Angeles Master Chorale cũng góp mặt.\r\n\r\nSự xuất hiện của Lisa khiến fan phấn khích. Trên Instagram, một tài khoản viết: \"Cô ấy xứng đáng với điều đó. Tôi đã hâm mộ từ năm 2016 khi Blackpink ra mắt. Cô gái này sinh ra để làm nghệ sĩ\". Người khác bình luận: Tôi không thể chờ đợi những ca sĩ này tỏa sáng trên sân khấu\".\r\nLisa, 28 tuổi, sinh ở Thái Lan, hoạt động chủ yếu ở Hàn Quốc trong nhóm Blackpink. Cô là nghệ sĩ châu Á có lượng người theo dõi, số bài viết nhận nhiều like nhất trên Instagram. Ca sĩ hiện là CEO của LLOUD - công ty do cô thành lập, hoạt động các lĩnh vực thời trang, điện ảnh, âm nhạc. Album đầu tay của cô Alter Ego dự kiến phát hành vào ngày 28/2.\r\n\r\nNgoài ca hát, Lisa lấn sân đóng phim, vào vai nhân viên khu nghỉ dưỡng trong The White Lotus 3. Bên cạnh sự nghiệp, chuyện đời tư, phong cách của cô nhận chú ý của hàng triệu fan. Người đẹp được cho hẹn hò doanh nhân Frédéric Arnault - con trai tỷ phú Bernard Arnault. Cô cũng là người lăng xê nhiều xu hướng, trong đó có thú sưu tập đồ chơi nghệ thuật gây sốt gần đây.\r\nOscar là giải thường niên của Viện Hàn lâm Khoa học và Nghệ thuật Điện ảnh Mỹ, một trong các sự kiện phim ảnh nổi tiếng bậc nhất thế giới. Năm ngoái, Oppenheimer của đạo diễn Christopher Nolan đại thắng với bảy giải, gồm Phim hay nhất.\r\n\r\nLễ trao giải Oscar lần thứ 97 do danh hài Conan O\'Brien dẫn chương trình. Năm nay, sự kiện không trình diễn các bài hát được đề cử cho hạng mục Ca khúc gốc hay nhất mà tập trung vào câu chuyện hậu trường từ các nhạc sĩ và đội ngũ sáng tạo đứng sau các ca khúc này.\r\n\r\nNhiều nghệ sĩ nổi tiếng sẽ công bố người chiến thắng tượng vàng, gồm Nick Offerman, Cillian Murphy, Emma Stone, Robert Downey Jr., Da\'Vine Joy Randolph, cùng Joe Alwyn, Ana de Armas, Halle Berry, Penélope Cruz. Lễ trao giải năm nay có phần tôn vinh Los Angeles như \"thành phố của những giấc mơ\", sau khi bị ảnh hưởng do các vụ cháy rừng hồi tháng 1.', '1744476969_h105.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:56:09', 'Giải trí', 0, 1),
(117, 'Thanh Bùi: \'Tôi sẽ đi hát trở lại\'', 'Thanh Bùi nói muốn trở lại ca hát, dự định ra album vào cuối năm nay sau nhiều năm vắng bóng sân khấu.', 'Nhạc sĩ gây chú ý khán giả khi là khách mời trong đêm nhạc Cuộc gọi... thuê bao quý khách của ca sĩ J.ade Bích Ngọc, tối 9/4 tại TP HCM.\r\n\r\nTrong chương trình hơn hai giờ đồng hồ, khán giả liên tục cổ vũ khi Thanh Bùi thể hiện bản hit Tìm về nơi đâu. Nghệ sĩ nhận tham gia chương trình vì tình cảm dành cho học trò. Anh và J.ade Bích Ngọc hòa giọng trong hai ca khúc Show Me Love, Lặng thầm một tình yêu. Dù chỉ luyện tập trước một buổi, họ kết hợp ăn ý.\r\nJ.ade Bích Ngọc cho biết xem Thanh Bùi như người thầy bởi anh luôn có những lời khuyên thiết thực trong nghề, giúp cô theo đuổi cơ hội đứng trên sân khấu chuyên nghiệp.\r\n\r\nỞ phần giao lưu với khán giả, Thanh Bùi nói nhiều năm qua không xuất hiện trên sân khấu vì tập trung cho gia đình, làm giáo dục. Năm 2016, khi ngồi cạnh nhóm nhạc Hàn Quốc BTS ở lễ trao giải Grammy, tại Mỹ, nhạc sĩ biết tin mình sắp làm cha.\r\n\r\n\"Lúc đó, tôi nghĩ mình đã có đạt được đỉnh cao trong âm nhạc và cần dừng lại để ở bên cạnh các con. Nếu tôi muốn chuyên tâm theo hướng biểu diễn và sáng tác, tôi sẽ không đủ thời gian chăm sóc gia đình. Giờ hai con trai Kiến An và Khải An đã lớn, tôi muốn trở lại sân khấu\", anh nói.\r\n\r\nTheo ca sĩ, lần làm khách mời trong show kỷ niệm 15 năm ca hát của Hoàng Thùy Linh năm 2023 và gần nhất là hát sáng tác mới của học trò J.ade Bích Ngọc đã thúc đẩy \"ngọn lửa nghề\" trong anh. \"Hiện tôi không thể yêu nghệ thuật trong thầm lặng nữa. Dưới sự ủng hộ của mọi người, tôi sẽ hoạt động âm nhạc tích cực hơn\", anh cho biết.\r\nSau đêm nhạc cùng học trò, Thanh Bùi sẽ bắt tay thực hiện album với những ca khúc mới. Nhạc sĩ nói mong muốn tạo nguồn cảm hứng cho các lớp trẻ phát triển nghệ thuật qua sản phẩm của anh.\r\n\r\nTrong thời gian im ắng, Thanh Bùi tham gia đầu tư lĩnh vực giáo dục về âm nhạc, nghệ thuật, thể thao, trường mầm non, trường liên cấp theo mô hình NLCS (North London Collegiate School) của Anh. Cuối năm ngoái, ca sĩ ra mắt mô hình tổ hợp nhà hát tại TP HCM.\r\n\r\nNhạc sĩ Thanh Bùi, 43 tuổi, tên thật là Bùi Vũ Thanh, cùng gia đình sang Australia định cư năm 1982. Anh được đào tạo về thanh nhạc và đàn piano lúc 10 tuổi. Thanh Bùi bắt đầu được khán giả chú ý khi vào top 8 Thần tượng âm nhạc Australia 2008. Năm 2010, anh về nước lập nghiệp, cùng nhạc sĩ Dương Khắc Linh phát triển công ty nhạc.\r\n\r\nNgoài ca hát, anh sáng tác nhạc và có nhiều tác phẩm được khán giả yêu thích, hợp tác với nghệ sĩ quốc tế như: Tata Young, BTS, Black Eyed Peas, cũng như gắn với tên tuổi của ca sĩ Hồ Ngọc Hà, Thu Minh. Các bài hát được yêu thích của anh gồm: Tình về nơi đâu, Lặng thầm một tình yêu, Vút bay. Năm 2013, Thanh Bùi làm huấn luyện viên của chương trình The Voice Kids. Nhạc sĩ kết hôn với Trương Huệ Vân (sinh năm 1988) năm 2013 và đón cặp song sinh năm 2016.', '1744477028_h106.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:57:08', 'Giải trí', 0, 0),
(118, 'Phim \'Địa đạo\' vượt 80 tỷ đồng sau bốn ngày', '\"Địa đạo\" - phim chiến tranh lấy bối cảnh Củ Chi - dẫn đầu phòng vé dịp giỗ Tổ Hùng Vương, với 80 tỷ đồng sau bốn ngày.', 'Tối 7/4, phim chạm mốc doanh thu mới, tính cả suất chiếu sớm. Theo Box Office Vietnam - đơn vị quan sát phòng vé độc lập, cuối tuần qua (4-6/4), phim thu hút gần 500.000 lượt khán giả. Thành tích của Địa đạo vượt xa các phim phát hành cùng dịp, như Minecraft (13 tỷ đồng) - bom tấn chuyển thể từ trò chơi kinh điển, Mật vụ phụ hồ - phim hành động có Jason Statham góp mặt (hơn 9 tỷ đồng).\r\nVới tốc độ bán vé hiện tại, Địa đạo được dự đoán sớm vượt mốc 100 tỷ đồng. Theo đại diện Box Office, tác phẩm tiếp tục giữ sức hút thời gian tới do ít đối thủ cạnh tranh. Các phim sắp ra mắt chủ yếu là dự án nước ngoài, còn Lật mặt 8 và Thám tử Kiên - hai phim Việt nhận nhiều quan tâm - được ấn định lịch chiếu vào cuối tháng 4.\r\n\r\nBùi Thạc Chuyên cho biết vui mừng trước sự ủng hộ của khán giả. Ban đầu, anh sợ thời lượng khá dài (128 phút) có thể làm ảnh hưởng đến trải nghiệm của người xem. Bản dựng đầu của phim khoảng 3,5 tiếng, đạo diễn phải cắt bỏ nhiều tuyến nhân vật, trong đó có các cảnh quay tâm đắc của Thái Hòa.\r\nNỗi lo của đạo diễn còn nằm ở khâu tái hiện bối cảnh. Theo anh, địa đạo gồm ba hầm với chức năng riêng biệt như phòng họp, phòng nấu ăn, phòng ngủ. \"Trên màn ảnh, người xem có thể khó phân biệt được hầm nào, dù tôi đã yêu cầu họa sĩ thiết kế từng hầm với chất liệu riêng. Do đó, tôi phải tìm cách dẫn dắt\", anh cho biết. Đơn vị sản xuất dự định phát hành ở nước ngoài sau khi phim kết thúc thời gian chiếu trong nước.\r\nKịch bản xoay quanh nhóm du kích 21 người bám trụ ở Củ Chi, sau trận càn Cedar Falls (năm 1967) của Mỹ. Đội trưởng Bảy Theo (diễn viên Thái Hòa đóng) và các đồng đội được giao nhiệm vụ bảo vệ địa bàn cho nhóm thông tin tình báo chiến lược. Tuy nhiên, các cuộc liên lạc của họ bị quân địch phát hiện. Những chiến sĩ du kích rơi vào tình thế nguy hiểm, đối mặt nhiều trận càn quét. Dự án kỷ niệm 50 năm thống nhất đất nước, là phim chiến tranh được làm từ ngân sách xã hội hóa.\r\n\r\nĐạo diễn - biên kịch Bùi Thạc Chuyên, 57 tuổi, sinh tại Hà Nội. Năm 1995, anh học khoa Diễn viên, Đại học Sân khấu Điện ảnh Hà Nội, trở thành nghệ sĩ của Nhà hát kịch Việt Nam. Năm 1997, anh theo học đạo diễn, gây chú ý với Cuốc xe đêm- tác phẩm Việt đầu tiên đoạt giải ở hạng mục Phim ngắn Cinefondation tại Liên hoan phim quốc tế Cannes. Năm 2005, phim Sống trong sợ hãi của anh giành nhiều giải trong nước và quốc tế. Năm 2022, Tro tàn rực rỡ - phim chuyển thể từ truyện Nguyễn Ngọc Tư - giành nhiều giải trong và ngoài nước, trong đó có Bông Sen Vàng tại Liên hoan phim Việt Nam 2023.', '1744477096_h107.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:58:16', 'Giải trí', 0, 0),
(119, '\'Địa đạo\' - khúc tráng ca dưới lòng đất', '\"Địa đạo\" của đạo diễn Bùi Thạc Chuyên kể về số phận những người lính chiến đấu ở \"đất thép thành đồng\" Củ Chi.', 'Phim lấy bối cảnh sau trận càn Cedar Falls năm 1967 - chiến dịch quân sự của Mỹ nhằm triệt phá căn cứ quân giải phóng miền Nam. Tác phẩm mở đầu bằng một đoạn one-shot (quay không cắt cảnh) miêu tả mảnh đất Bình An Đông, Củ Chi bị tàn phá, những cánh rừng cháy trụi. Nữ chiến sĩ Ba Hương (Hồ Thu Anh đóng) ngụp lặn trên sông tìm đồng đội, nhưng chỉ thấy những thi thể giữa đám lục bình.\r\n\r\nKhung cảnh tang thương đưa người xem vào câu chuyện của nhóm du kích gồm 21 người, do Bảy Theo (Thái Hòa) làm đội trưởng. Theo lệnh của cấp trên, họ bám trụ ở Củ Chi để canh giữ các thiết bị quân y và thuốc men cho bệnh viện dã chiến.\r\n\r\nTuy nhiên, chỉ có Bảy Theo biết nhiệm vụ thực chất của họ là bảo vệ địa bàn cho nhóm tình báo chiến lược của Hai Thưng (Hoàng Minh Triết) truyền đi những tài liệu tối mật bằng sóng vô tuyến. Trọng trách này khiến đội du kích rơi vào thế sống còn, khi quân đội Mỹ định vị được họ, mở một chiến dịch lớn tấn công địa đạo.\r\nVới thời lượng 128 phút, đạo diễn Bùi Thạc Chuyên không đặt tham vọng phác họa toàn bộ bức tranh về địa đạo, mà chỉ tái hiện lát cắt nhỏ với từng thân phận riêng. Anh không mất nhiều thời gian để giới thiệu chân dung, hoàn cảnh đội du kích. Dù đến từ đâu, ở độ tuổi nào, họ có chung lý tưởng: Sẵn sàng cầm súng bảo vệ Tổ quốc.\r\n\r\nNhiều cảnh về cuộc sống sinh hoạt của căn cứ được phác họa bằng mạch phim điềm tĩnh, không xoáy vào những điều bi lụy. Nhiều chi tiết chỉ lướt qua màn ảnh, nhưng đủ để người xem hình dung về chuỗi ngày tháng kham khổ của các chiến sĩ trong lòng đất. Họ chắt chiu từng bát nước đục để nấu nướng, ánh mắt vui mừng khi bữa cơm có thêm khúc cá. Các nữ chiến sĩ sung sướng khi nhận được quà là chiếc lược được cưa từ vỏ bom. Họ chỉ có thể tưởng nhớ đồng đội trong lặng lẽ, di ảnh là những tấm thẻ căn cước của người quá cố.\r\n\r\nĐạo diễn Trịnh Đình Lê Minh - một khán giả của phim - nhận xét tác phẩm khai thác tốt những chi tiết nhỏ về đời thường, với cách lồng ghép hài hước lẫn xúc động.\r\n\r\nĐịa đạo không bám theo một câu chuyện cụ thể hoặc xoay quanh một gương mặt chính, như cấu trúc của đa số phim điện ảnh. Thay vào đó, mỗi vai được xây dựng với tính cách đặc trưng, tạo nên hệ thống nhân vật đa màu sắc. Ba Hương là một người lính gan dạ, cứng rắn nhưng vẫn đủ mềm mỏng để làm chỗ dựa cho các em trong đội. Tư Đạp (Quang Tuấn đóng) đại diện cho những chiến sĩ thích đào sâu, tìm tòi về vũ khí quân sự. Nhân vật được lấy hình mẫu từ Anh hùng Lực lượng vũ trang Tô Văn Đực - người góp phần chế tạo mìn gạt trong cuộc chiến ở Củ Chi.\r\n\r\nTuyến tình cảm của phim được dẫn dắt với nhịp điệu nhẹ nhàng, vừa đủ để khán giả cảm nhận được sự lãng mạn. Chuyện tình Ba Hương - Tư Đạp làm dịu đi những tình huống ngột ngạt. Tác phẩm có nhiều khung hình giàu ngụ ý, gửi gắm về thân phận con người thời chiến.\r\n\r\nTrong số các nhân vật, đội trưởng Bảy Theo nổi bật với sự lý trí, quyết đoán, tính cách có phần thô ráp, qua diễn xuất của Thái Hòa. Dù ít thoại, anh để lại ấn tượng với lối nhấn nhá, gằn giọng tự nhiên, rõ âm điệu. Nhiều câu nói của Bảy Theo gợi tiếng cười nhẹ nhàng mà thấm thía, khi căn dặn các chiến sĩ trẻ về tình thế sống còn. Ở một cảnh biết tin đồng đội hy sinh, nhân vật không khóc nhưng ánh mắt hướng trực diện, xoáy sâu vào ống kính, lột tả nỗi đau.\r\n\r\nThiết kế mỹ thuật cho bối cảnh là một nỗ lực của đoàn phim. Đa phần phân cảnh diễn ra dưới lòng đất, việc quay phim trong không gian chật hẹp là thách thức lớn. Trước đây, hầu như chưa đơn vị nào quay điện ảnh về cuộc chiến ở Củ Chi, do đó êkíp Bùi Thạc Chuyên phải dàn dựng hầu hết nội cảnh (dưới đất).\r\n\r\nKhó khăn lớn nhất với đoàn phim là địa đạo thật có đường kính nhỏ, không thể đặt máy quay, êkíp phải thiết kế một mô hình dài 250 m. Trên màn ảnh, đạo diễn dựng lại địa đạo với ba tầng theo từng chức năng riêng biệt: Hai tầng đầu do nhóm du kích canh giữ để bảo vệ tầng cuối - nơi đội tình báo dò thám sóng vô tuyến. Với các cảnh bom nổ trên mặt đất, dưới hầm chịu rung chấn, êkíp làm một bệ lớn, đặt mô hình lên trên, sau đó sử dụng máy tạo rung động với tần số phù hợp. Ở cảnh mặt đất bị triệt phá, êkíp dùng thuốc nổ để tạo khoảng 50 hố bom.\r\nTác phẩm cũng có nhiều đại cảnh cho thấy quy mô trận chiến, như phân đoạn dàn xe tăng, xe bọc thép, máy bay trực thăng càn quét địa phận. Đạo diễn nhiều lần sử dụng thủ pháp quay tương phản để làm nổi bật sự chênh lệch về lực lượng của nhóm du kích và lính Mỹ. Về cuối, nhịp phim dồn dập hơn khi quân đội Mỹ thâm nhập địa đạo, dồn từng chiến sĩ vào tình thế \"nghìn cân treo sợi tóc\".\r\n\r\nÂm thanh thời chiến được tái hiện chi tiết với tiếng máy bay, xe tăng, bom rơi, đạn nổ, góp phần đẩy không khí khốc liệt. Nhạc phim - với ca khúc chủ đề Mặt trời trong bóng tối (Hứa Kim Tuyền sáng tác) - gợi những quãng lặng về cảm xúc. Đạo diễn tri ân cải lương qua bản vọng cổ Tần Quỳnh khóc bạn (soạn giả Viễn Châu), vang lên với chất giọng Út Khờ, một thành viên trong tiểu đội (Hằng Lamoon đóng).\r\nDo không tập trung vào tuyến nhân vật cụ thể, tác phẩm không đủ kịch tính để đẩy cảm xúc số đông người xem lên cao trào. Bùi Thạc Chuyên cho biết đây là dụng ý của anh bởi tác phẩm được quay theo phong cách phim tài liệu, tái hiện lát cắt cuộc sống, thay vì khai thác các tình tiết lấy nước mắt khán giả.\r\n\r\n\"Với tôi, địa đạo Củ Chi mới là \'gương mặt chính\' của phim, còn các nhân vật xung quanh đại diện cho con người ở vùng đất này. Họ đã anh hùng chiến đấu và lặng lẽ ngã xuống, nhiều người thậm chí không để lại dấu tích gì, chỉ có cái tên\", anh cho biết. Sau hai ngày chiếu sớm, tác phẩm gây chú ý phòng vé, thu 15 tỷ đồng, trước khi ra rạp (ngày 4/4).\r\n\r\nĐịa đạo Củ Chi (huyện Củ Chi) là di tích lịch sử cách mạng nổi tiếng. Cách trung tâm TP HCM khoảng 70 km về hướng Tây Bắc, địa đạo có hệ thống đường hầm dài hơn 200 km, là cứ địa vững chắc của Khu ủy Quân khu, Bộ tư lệnh Sài Gòn - Gia Định, góp phần vào công cuộc thống nhất đất nước.', '1744477185_h108.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 16:59:45', 'Giải trí', 0, 0),
(120, 'Xuân Nghi: \'Tôi từ Mỹ về nước ca hát để thỏa đam mê\'', 'Sau 15 năm định cư ở Mỹ, Xuân Nghi, 31 tuổi, trở về nước lập nghiệp vì thấy có nhiều động lực và cơ hội công việc ở quê nhà.', 'Dịp sắp tham gia Chị đẹp concert vào ngày 12/4 tại TP HCM, Xuân Nghi - ca sĩ nhí nổi tiếng một thời - nói về công việc lẫn cuộc sống riêng.\r\n\r\n- Lý do chị về nước sống sau thời gian dài định cư nước ngoài là gì?\r\n\r\n- Năm 16 tuổi, tôi theo gia đình sang Mỹ, sau đó học ngành Công nghiệp âm nhạc và tiếp thị tại đại học Southern California. Tôi được người thân định hướng làm sản xuất âm nhạc vì cho rằng ngành này bền vững và bớt áp lực. Tuy nhiên, tôi vẫn đam mê ca hát, từng về nước vài lần, ra sản phẩm nhưng thất bại hoàn toàn, không ai nghe.\r\n\r\nKhi tôi mất phương hướng, muốn bỏ nghề, tập trung vào công ty về lĩnh vực tiếp thị do mình tự thành lập, thì lại nhận được lời mời từ nhà sản xuất Chị đẹp đạp gió. Lúc đầu, tôi chỉ nghĩ tham gia cho vui vì \"đâu có gì để mất\", không ngờ lại được khán giả nhớ đến và yêu mến. Ngọn \"lửa\" nghề trong tôi được thắp lại một lần nữa. Tôi nghĩ đây là cơ hội cuối cùng, nếu không nắm bắt sẽ hối tiếc vì đã bước qua tuổi 30. Với tôi, ca hát ở quê nhà là một ước mơ lớn và được bố mẹ ủng hộ. Tôi bay về nước hồi đầu năm nay, đang sửa lại căn hộ ở quận 7 TP HCM để ổn định cuộc sống.\r\n- Thời gian ở Mỹ, chị có những trải nghiệm gì?\r\n\r\n- Tôi được gia đình bồi dưỡng nghệ thuật, làm ca sĩ từ nhỏ và nổi tiếng sớm. Nếu không qua Mỹ, có thể tôi dễ bị cuốn theo guồng quay của danh vọng. Việc thay đổi môi trường giúp tôi sống đơn giản hơn. Tôi hòa nhập khá nhanh, không bị sốc văn hóa. Ở Mỹ, tôi dành phần lớn thời gian để học hành, có được nhiều kỹ năng trong nghề lẫn cuộc sống. Tôi làm nhiều nghề mưu sinh nhưng không thành công. Qua những lần thất bại, tôi chấp nhận mọi thứ và tin rằng cánh cửa này đóng lại, cánh cửa khác sẽ mở ra. Tôi lì lắm, nếu không làm được thì vẫn thử tiếp (cười).\r\n\r\n- Chị gặp khó khăn hay thuận lợi gì khi về nước gây dựng lại tên tuổi?\r\n\r\n- Đúng là vài năm trước tôi không thể vượt qua được cái bóng quá khứ khi người ta chỉ nhớ đến tên \"bé Xuân Nghi\". Giờ tôi tự tin hơn vì ai cũng phải trưởng thành, thay đổi. Tôi gặp nhiều điều thuận lợi hơn là khó khăn. Ngoài những thế hệ 8x, 9x, tôi có thêm lớp khán giả gen Z. Người hâm mộ tự lập fanpage, cập nhật hình ảnh, thông tin của tôi hàng ngày.\r\n\r\nTrước đây, tôi khá chật vật mưu sinh để có vốn đầu tư cho sản phẩm, làm gì cũng cân đo, đong đếm, không được thoải mái sáng tạo. Giờ tôi hạnh phúc khi tài chính vững vàng hơn, có được sự tự do trong âm nhạc, làm những điều bản thân yêu thích.\r\n\r\n- Bố chị - nhạc sĩ Duy Thoán và mẹ - ca sĩ Minh Ngọc động viên chị ra sao trên con đường nghệ thuật?\r\n\r\n- Tôi thấy may mắn khi được thừa hưởng các kỹ năng, chuyên môn của bố mẹ. Họ không chỉ \"truyền lửa\" âm nhạc cho tôi mà còn đồng hành tôi trên từng bước đi.\r\n\r\nBố mẹ cho tôi những bài học quý giá về đạo đức khi làm nghề, trước việc lớn hay nhỏ cũng phải cố gắng hết mình, bằng tất cả nhiệt huyết. Tôi thấy biết ơn họ vì luôn sẵn sàng hỗ trợ tiền cho con gái đầu tư làm nhạc. Ngay cả khi tôi về nước ca hát, mẹ cũng theo cùng để thuận tiện chăm sóc.\r\n\r\nTôi chưa bao giờ thấy áp lực với cụm từ \"con nhà nòi\" bởi bố mẹ không áp đặt con phải thành công. Họ soi đường và may mắn tôi không đi lệch hướng. Giờ đây khi bản thân có chỗ đứng nhất định, tôi cố gắng từng ngày, nhận thức được nền tảng của mình và trân trọng điều này.\r\n\r\n- Chị có kế hoạch gì cho âm nhạc trong thời gian tới?\r\n\r\n- Tôi vẫn đeo đuổi thể loại R&B và soul. Đến giai đoạn này, điều tôi muốn là viết nhạc nhiều hơn, giúp bản thân khai phá thêm khả năng sáng tạo. Ngoài ra tôi cũng phát triển thêm kỹ năng chơi nhạc cụ, biểu diễn đa dạng trên sân khấu hơn là chỉ hát. Sắp tới tôi ra MV mới làm tiền đề cho một E.P (album ít bài hát), hy vọng được mọi người ủng hộ.\r\n- Chuyện tình cảm của chị hiện tại ra sao?\r\n\r\n- Tôi đang hạnh phúc trong tình yêu. Tôi hẹn hò được hai năm, cùng bạn trai tính đến chuyện gắn kết lâu dài. Anh ấy làm trong lĩnh vực công nghệ, là người đàn ông đáng tin cậy, chu đáo, chăm sóc cho tôi từng chút một. Cả hai từng là bạn thời niên thiếu, tình cờ gặp lại và thấy hợp nhau. Bạn trai vun đắp cho tôi nhiều thứ, ủng hộ trong nghệ thuật. Tôi thấy yên tâm vì có người luôn đứng sau làm điểm tựa.\r\n\r\nSuốt thời gian yêu xa khi tôi bận tham gia show truyền hình thực tế, anh luôn gọi điện hỏi han sức khỏe, khiến tôi thấy ấm áp. Tình yêu của bạn trai cho tôi thêm sức mạnh thực hiện những ước mơ trong cuộc sống. Tới thời điểm thích hợp thì cả hai sẽ về chung nhà.\r\n\r\n- Chị đang tìm kiếm điều gì trong âm nhạc và cuộc sống?\r\n\r\n- Tôi không hoạt động âm nhạc để được nổi tiếng. Tôi ít tham vọng, hướng tới cuộc đời bình yên. Tôi hiện có sự nghiệp tươi sáng phía trước, bố mẹ khỏe mạnh, và một tình yêu đẹp, nên không còn tìm kiếm điều gì nữa, chỉ muốn giữ mọi thứ như đang có và thấy đủ đầy.\r\n\r\nXuân Nghi sinh năm 1994 tại Đà Lạt. Cô tham gia ca hát từ năm 5 tuổi. Một trong những album được yêu thích của cô là Xuân Nghi - Tiếng hát thiên thần nhỏ. Ngoài âm nhạc, cô tham gia một số phim như Hoa ngũ sắc, Sơn ca không hát.\r\n\r\nNăm 2010, Xuân Nghi sang Mỹ, học chuyên ngành thanh nhạc, sáng tác, thu âm và kỹ thuật âm nhạc hiện đại. Cô từng sáng tác hai ca khúc Lạc lối và Đổi màu. Năm 2015 cô học Đại học Nam California (University of Southern California), chuyên ngành sản xuất âm nhạc. Cô là một trong 30 nữ nghệ sĩ tham gia show Đạp gió phiên bản Việt mùa hai.', '1744477302_h109.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 17:01:42', 'Giải trí', 0, 0),
(121, 'David Beckham ngăn nắp đến mức \'vợ con phát cáu\'', 'Mỹ - Cựu danh thủ David Beckham nói chứng ám ảnh ngăn nắp do OCD khiến anh mệt mỏi hàng ngày, thỉnh thoảng làm vợ con khó chịu.', 'Theo Page Six ngày 8/4, David Beckham tham dự hội thảo quảng bá sản phẩm tại khách sạn Faena Hotel Miami Beach, Florida. Trong cuộc thảo luận, khán giả thích thú khi nghe anh tiết lộ thói quen ngăn nắp do mắc OCD (rối loạn cưỡng chế). \"Đôi khi việc sắp xếp mọi thứ khiến vợ tôi thấy phiền. Nó cũng làm phiền các con\", David nói.\r\n\r\nDavid Beckham cho biết tuy các thành viên trong nhà khó chịu, anh từng nhắc họ rằng \"nếu không có bố, nhà cửa sẽ không gọn gàng sạch sẽ, mọi người chẳng nhớ đồ đạc nằm ở đâu\". Anh còn khiến khán giả bật cười khi đưa ra ví dụ về chứng OCD bằng cách xếp các sản phẩm theo hình kim tự tháp trên sân khấu.\r\nCũng tại sự kiện, David đề cập lịch trình dày đặc hàng ngày và cách dành thời gian cho gia đình. Theo cựu danh thủ, anh là người dậy sớm nhất nhà, chuẩn bị bữa sáng cho con gái út - Harper, 14 tuổi - và đưa bé đến trường. Sau đó, anh trở về nhà để tập thể dục cùng vợ rồi bắt đầu xử lý công việc tại văn phòng. Anh cũng là người đón Harper tan học, nấu bữa tối cho cả nhà.\r\n\r\n\"Đó là điều hầu hết bậc cha mẹ đều làm. Tôi luôn giữ mình bận rộn để không bao giờ có \'ngày buồn chán\'\", David nói thêm.\r\nRối loạn cưỡng chế (OCD - Obsessive Compulsive Disorder) là tình trạng sức khỏe tâm thần khiến bệnh nhân có những suy nghĩ, ám ảnh, sợ hãi phi lý. Người bị OCD thường liên tục lặp đi lặp lại một số hành động nhằm giảm bớt cảm giác khó chịu dù biết chúng không thực sự cần thiết hoặc có ý nghĩa.\r\n\r\nTrước đây, David Beckham từng bàn về việc mắc hội chứng này trong phim tài liệu Beckham (2023). Anh kể: \"Khi mọi người đi ngủ, tôi thường đi khắp nhà để lau các lọ nến, chỉnh đèn đúng chế độ, đảm bảo mọi thứ đều gọn gàng. Tôi ghét khi xuống giường buổi sáng, gặp cốc và đĩa bẩn. Tôi cắt sáp nến, lau cốc. Đó là nỗi ám ảnh của tôi khi xuất hiện vết bẩn quanh sáp nến. Tôi biết điều này kỳ lạ\". Ngoài ra, anh thường dọn kỹ nhà bếp dù không chắc được vợ đánh giá cao, luôn phân loại quần áo theo màu sắc, kiểu dáng và chuẩn bị trang phục trước một tuần.\r\n\r\nTrên kênh truyền hình ITV năm 2006, Beckham cho biết: \"Tất cả phải được sắp xếp theo đường thẳng hoặc theo cặp. Khi cho nước ngọt vào tủ lạnh, chúng phải là số chẵn. Nếu thừa ra, tôi sẽ bỏ vào tủ khác. Đến khách sạn, trước khi nghỉ ngơi, tôi đặt giấy tờ, sách trong phòng vào ngăn kéo. Mọi thứ phải sạch sẽ\".\r\nDavid Beckham, 50 tuổi, từng thi đấu cho các câu lạc bộ như Manchester United, Real Madrid, Paris Saint-Germain. Anh là đội trưởng đội tuyển quốc gia Anh giai đoạn 2000-2006. Danh thủ giải nghệ năm 2013, ở tuổi 38. David từng đóng vai khách mời trong nhiều phim như Goal, The Man from U.N.C.L.E., King Arthur: Legend of the Sword, Deadpool 2. Ngoài ra, David được đánh giá là một trong những ngôi sao có gu mặc thời trang đẹp nhất nước Anh.\r\n\r\nVợ chồng David Beckham lần đầu gặp nhau trong một bữa tiệc sau trận đấu của đội Manchester United năm 1997. Sau 26 năm kết hôn, cặp sao có bốn con: Brooklyn, Romeo, Cruz, và Harper. Gia đình được khán giả yêu thích vì thường chia sẻ những khoảnh khắc cùng nhau. Beck-Vic được đánh giá là một trong những cặp hạnh phúc bậc nhất làng giải trí.', '1744477379_h110.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 17:02:59', 'Giải trí', 0, 0),
(122, 'Tình bạn 30 năm của nghệ sĩ Tự Long - Xuân Bắc', 'Hai nghệ sĩ Tự Long, Xuân Bắc \"cãi nhau như cơm bữa\" nhưng vẫn gắn bó nhờ chung tình yêu nghề, sự ăn ý trong công việc.', 'Ngày 6/4, diễn viên Tự Long đăng lên trang cá nhân ảnh chụp cùng Xuân Bắc ở một sự kiện, với tấm bảng ghi chức danh mỗi người. Anh viết: \"Nhanh quá đi thôi. 1995, lang thang khu Văn công Mai Dịch. 2025, mỗi đứa có một cái biển to đùng trước mặt\". Nhiều đồng nghiệp khác bình luận ngưỡng mộ tình cảm của cả hai, gọi họ là \"đôi bạn cùng tiến\".\r\n\r\nDiễn viên Xuân Bắc hiện là Cục trưởng Nghệ thuật Biểu diễn, còn Tự Long là Giám đốc Nhà hát Chèo Quân đội. Cả hai đều nhận danh hiệu Nghệ sĩ Nhân dân ở tuổi ngoài 40.\r\nTự Long hơn Xuân Bắc ba tuổi nhưng học cùng khóa ở Đại học Sân khấu Điện ảnh Hà Nội, do trước đó anh từng theo trường cao đẳng nghề. Thời đi học, họ là \"cặp bài trùng\" ở nhiều chương trình văn nghệ, cùng làm thêm việc đóng kịch, dẫn chương trình ở một quán cà phê sinh viên. Những lúc quán không có khách, Tự Long giúp chủ quán chạy bàn, còn Xuân Bắc tắm cho chó nhà chủ nuôi.\r\n\r\nTốt nghiệp, Tự Long về Nhà hát Chèo Quân đội, Xuân Bắc đầu quân cho Nhà hát Kịch Việt Nam. Cứ ngỡ nhóm hài phải \"chia đôi ngả\" nhưng series Gặp nhau cuối tuần một lần nữa kéo họ lại gần nhau, đưa tên tuổi bộ đôi lên cao. Đều hoạt ngôn, trên sân khấu, hai người có nhiều màn đối đáp chọc cười khán giả. Những tiểu phẩm như Con bệnh, Bia ôm, Dấu huyền Xuân Bắc, dấu sắc Tự Long được nhiều khán giả theo dõi Gặp nhau cuối tuần yêu thích.\r\n\r\nỞ Gặp nhau cuối năm, Xuân Bắc nhiều năm gắn vai Nam Tào còn Tự Long đóng Táo Xã hội, Văn hóa, Thể thao... Ngoài ra, họ là bạn diễn trên nhiều sân khấu trong, ngoài nước, các chương trình thiếu nhi. Chuyện cát-xê, Tự Long nói để Xuân Bắc thương lượng, sau đó \"đưa bao nhiêu thì cầm bấy nhiêu, không bao giờ kì kèo\".\r\nHai nghệ sĩ thống nhất không tách rời để tạo thương hiệu nhóm hài Xuân Bắc - Tự Long. Họ từng từ chối nhiều chương trình trả thù lao cao nhưng chỉ mời một người diễn. \"Để mang lại tiếng cười cho khán giả, những sản phẩm chất lượng, nghệ sĩ nào cũng cần một bạn diễn tốt. Chúng tôi gắn bó lâu nên có sự ăn ý, chỉ cần nhìn vào mắt nhau là hiểu người kia muốn dùng chiêu trò gì để còn tung hứng. Tôi cảm thấy khi đóng với Xuân Bắc, cả hai sẽ có sản phẩm tốt nhất nên càng muốn gắn bó\", Tự Long cho biết.\r\n\r\nThông thường, Tự Long viết kịch bản còn Xuân Bắc làm đạo diễn. Khi tập, cả hai hay bất đồng quan điểm. Cùng là nghệ sĩ nổi tiếng, có cái \"tôi\" lớn, họ ví von \"nhịn nhau như nhịn cơm sống\". Tự Long gọi đùa bạn là \"giám đốc nông trường cao su\" vì hay đến muộn, trong khi anh là bộ đội nên luôn đúng giờ.\r\n\r\nTrong 30 năm gắn bó, Xuân Bắc, Tự Long từng suýt \"bỏ nhau\" hai lần. Lần gần nhất vài năm trước, Xuân Bắc nói: \"Tao nghĩ đến lúc phải tách ra thôi\", rồi phân tích một loạt những điều lợi, hại khi diễn riêng. Thế nhưng cuối cùng mọi chuyện lại đâu vào đấy, họ vẫn kề vai sát cánh.\r\n\r\nBộ đôi thừa nhận học hỏi, được truyền cảm hứng từ người kia. Tự Long cho biết điều khiến anh khâm phục nhất ở bạn là tính cầu toàn, tình yêu nghề: \"Có những vở, dù đã diễn hàng trăm lần nhưng Xuân Bắc vẫn bắt tôi tập lại trước khi lên sân khấu. Nhiều lúc, tôi thấy thừa thãi, bực mình vì Bắc cẩn thận quá. Đến lúc ngẫm lại mới thấy đó là điều cần thiết\".\r\nXuân Bắc từng nói về bạn: \"Tự Long chính là nguồn cảm hứng sáng tạo của tôi. Tới thời điểm này, chưa có ai đón ý tôi nhanh hơn anh ấy. Với nhau, chúng tôi có thể bỏ qua mọi thứ, không cố chấp\".\r\n\r\nNgoài công việc, hai nghệ sĩ luôn có mặt trong những cột mốc quan trọng của nhau. Ở lễ cưới của Tự Long, Xuân Bắc làm MC cho bạn. Khi Tự Long vào TP HCM thi Anh trai vượt ngàn chông gai, Xuân Bắc tiễn bạn ra sân bay.\r\n\r\nHiện tại, dù đều ở cương vị quản lý, hai nghệ sĩ vẫn tâm huyết với nghề diễn. Tự Long nói: \"Cái nghiệp đã ăn vào máu, nhìn người khác ở trên sân khấu là không chịu được\". Vì thế, gần đây họ vẫn cùng tham gia một số chương trình như 12 con giáp, Xuân phát tài, được khán giả đón nhận.\r\n\r\nTự Long 52 tuổi, sinh ra trong gia đình có truyền thống nghệ thuật. Bố mẹ anh là cặp vợ chồng nghệ sĩ dân ca Tự Lẫm - Minh Phức, đều được Nhà nước trao danh hiệu Nghệ sĩ Ưu tú. Năm 1998, anh tốt nghiệp, vào Đoàn chèo Tổng cục Hậu cần (tên cũ của Nhà hát Chèo Quân đội), đi lên từ những vai nhỏ. Anh được phong tặng danh hiệu Nghệ sĩ Ưu tú năm 2012 và Nghệ sĩ Nhân dân năm 2015. Năm ngoái, anh gây chú ý khi tham gia Anh trai vượt ngàn chông gai - chương trình ca hát dành cho 33 gương mặt nam hoạt động ở nhiều lĩnh vực trong showbiz.\r\n\r\nXuân Bắc 49 tuổi, nổi tiếng qua các phim 12A và 4H, Chuyện nhà Mộc, Sóng ở đáy sông. Ngoài ra, anh thành công với vai trò MC nhiều chương trình như Đuổi hình bắt chữ, Hỏi xoáy Đáp xoay, Ơn giời cậu đây rồi, Vua tiếng Việt.Anh công tác ở Nhà hát Kịch Việt Nam từ năm 1998, được bổ nhiệm làm giám đốc năm 2021. Năm ngoái, ở tuổi 48, anh nhậm chức Cục trưởng Nghệ thuật Biểu diễn.\r\n\r\n', '1744477449_h111.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 17:04:09', 'Giải trí', 0, 0),
(123, 'Nghệ sĩ Tự Long: \'Không thể thành công nếu không có vợ\'', 'Nghệ sĩ Tự Long cho biết được vợ, giảng viên Minh Nguyệt, giúp quán xuyến nhà cửa, con cái, để anh yên tâm làm nghề.', 'Diễn viên và bà xã kết hôn năm 2015, có ba con. Vợ kém anh 12 tuổi, là giảng viên Đại học Văn hóa Nghệ thuật Quân đội. Anh nói vợ thấu hiểu công việc của chồng, không bao giờ bận tâm chuyện cát-xê của anh. Nghệ sĩ thường xuyên chia sẻ hình ảnh gia đình, làm thơ tặng vợ.\r\nNghệ sĩ Tự Long sinh ra trong gia đình có truyền thống nghệ thuật. Bố mẹ anh là cặp vợ chồng nghệ sĩ dân ca Tự Lẫm - Minh Phức, đều được Nhà nước trao danh hiệu Nghệ sĩ Ưu tú. Từ nhỏ, anh thừa hưởng tình yêu âm nhạc từ bố mẹ. Thế nhưng khi hết phổ thông, người thân khuyên anh theo nghề khác cho đỡ vất vả. Nghệ sĩ từng học nghề mộc, chạy xe ôm, dự định sang nước ngoài làm việc, sau cùng vẫn vào khoa chèo của Đại học Sân khấu Điện ảnh.\r\nNăm 1998, anh tốt nghiệp, vào Đoàn chèo Tổng cục Hậu cần (tên cũ của Nhà hát Chèo Quân đội), đi lên từ những vai nhỏ. Ngoài sân khấu, anh nổi tiếng qua các tiểu phẩm trong chương trình Gặp nhau cuối tuần và Táo quân - Gặp nhau cuối năm. Anh và nghệ sĩ Xuân Bắc là cặp diễn ăn ý, thường cùng nhau xuất hiện trên nhiều sân khấu. Anh được phong tặng danh hiệu Nghệ sĩ Ưu tú năm 2012 và Nghệ sĩ Nhân dân năm 2015. Gần đây, anh gây chú ý khi tham gia Anh trai vượt ngàn chông gai - chương trình ca hát dành cho 33 gương mặt nam hoạt động ở nhiều lĩnh vực trong showbiz.\r\n\r\nNgày 8/3, nghệ sĩ Tự Long cùng ca sĩ Soobin Hoàng Sơn, Tăng Duy Tân, Cường Seven, Rhymastic, Hoàng Dũng sẽ cùng tham gia chương trình mang chủ đề Trạm yêu - Hẹn ước thời không, ở Cung Thể Thao Điền Kinh, Hà Nội.', '1744477596_h112.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 17:06:36', 'Giải trí', 0, 1),
(124, 'Cuộc đời Giáo hoàng Francis', 'Giáo hoàng Francis kể về thời thơ ấu, tiết lộ lý do không xem TV, niềm yêu thích đặc biệt với bóng đá, trong tự truyện \"Hy vọng\".', 'Tác phẩm ghi lại những cột mốc trong cuộc đời Giáo hoàng, Carlo Musso chấp bút. Sách phát hành tại 80 quốc gia hồi tháng 1, ra mắt độc giả Việt trong tháng 3.\r\n\r\nBan đầu, theo nguyện vọng của Giáo hoàng, sách chỉ được công bố sau khi ông qua đời. Tuy nhiên, theo đồng tác giả Carlo Musso, Giáo hoàng quyết định phát hành sách sớm hơn để trùng với Năm Thánh 2025 của Giáo hội Công giáo và phù hợp với nhu cầu của thời đại.\r\n\r\nHồi ký bắt đầu bằng lịch sử gia đình, tuổi thơ của Giáo hoàng đến lúc lên ngôi. Tác phẩm gồm những bức ảnh chưa từng được công bố về cuộc đời ông.\r\nGiáo hoàng Francis có thời thơ ấu êm đềm bên cha mẹ và các em. Dù gia đình khó khăn, ông được giáo dục, sống trong tình yêu thương: \"Đó là một tuổi thơ yên bình. Mọi thứ dường như diễn ra hết sức tự nhiên: vui chơi, đi học, học tập, cũng như việc giáo dục về tôn giáo. Ngay cả những bài học về đức tin cũng được học một các đơn giản tương tự: giống như một thứ ngôn ngữ, bạn học cách nói và học cách tin\". Ông cho rằng việc truyền dạy đức tin phải được thực hiện bằng phương ngữ, cách thức giao tiếp trong gia đình, cuộc sống hàng ngày: \"Chính từ những ký ức thân thương, gốc rễ sâu thẳm ấy, tôi đã hình thành mối gắn kết với đức tin bình dân\".\r\n\r\nNgười đứng đầu tòa thánh Vatican hồi tưởng về tuổi trẻ, ông kể lại những mối tình chớm nở trước khi đi tu, từng xao động trước một \"bóng hồng\", những lần đấu tranh nội tâm, bất chấp sự phản đối của mẹ để chọn con đường phục vụ Thiên Chúa. Ông kể: \"Đó không phải là một cuộc cãi vã, mà là một cuộc đối thoại thẳng thắn và quyết liệt, và dù thế nào mẹ cũng không thay đổi quan điểm. Đến mức sau này bà không đưa tôi đến chủng viện giáo phận, và cũng không có mặt vào ngày tôi được mặc áo chủng sinh\".\r\nTrong hồi ký, Giáo hoàng tiết lộ thích chơi bóng đá, thuở nhỏ, ông hâm mộ San Lorenzo - đội bóng gần nhà ở Buenos Aires. Sau này, dù không xem truyền hình, ông vẫn luôn cập nhật thông tin về môn thể thao vua. Hàng tuần, những người lính cận vệ Thụy Sĩ để kết quả các trận đấu và bảng xếp hạng trên bàn làm việc của ông, kể cả San Lorenzo. Sách có đoạn: \"Với tôi, những trận đấu bóng đá đẹp nhất cho đến hôm nay vẫn là những trận đấu diễn ra nơi góc phố. Và bóng đá vẫn thuộc về dân chúng\".\r\n\r\nTác phẩm hé lộ nguyên nhân Giáo hoàng không xem truyền hình. Ông cho biết đã thề với Đức Mẹ Carmen vào đêm 15/7/1990. Tối đó, khi đang xem TV cùng cộng đồng dòng Tên ở Buenos Aires, một cảnh tượng tồi tệ xuất hiện trên màn hình khiến ông cảm thấy khó chịu, đứng dậy và rời đi. \"Như thể đó là một lời nhắc nhở từ Thiên Chúa rằng truyền hình không dành cho tôi, nó không tốt cho tôi chút nào\". Kể từ đó, trừ vụ khủng bố 11/9 và vụ rơi máy bay ở Buenos Aires năm 1999, ông không xem truyền hình, lời hứa với Đức Mẹ hiếm khi bị vi phạm.\r\n\r\nNgoài chuyện cá nhân, Giáo hoàng thể hiện sự quan tâm về các vấn đề toàn cầu như: Di cư, biến đổi khí hậu, chiến tranh, khủng hoảng kinh tế, lòng trắc ẩn. Ông phản đối mạnh mẽ chiến tranh, cho rằng các cá nhân và quốc gia cần hành động để chấm dứt chạy đua vũ trang và sự lan rộng của vũ khí: \"Với suy nghĩ có thể lấy ác báo ác, cuối cùng sẽ gây ra cái ác còn tồi tệ hơn\".\r\n\r\nÔng cũng nhắc đến vụ bị ám sát hụt năm 2021 tại Iraq. Trong hồi ký, người đứng đầu Vatican tiết lộ ông đã được mật vụ Anh cảnh báo ngay khi máy bay hạ cánh xuống thủ đô Baghdad: có một phụ nữ trẻ mang theo thuốc nổ đang trên đường đến Mosul để tấn công nhằm vào ông. Một chiếc xe tải cũng rời đi với mục đích tương tự. Hôm sau, khi Giáo hoàng hỏi cảnh sát về những kẻ tấn công, viên chỉ huy trả lời ngắn gọn: \"Họ tiêu rồi\". Cảnh sát Iraq đã phát hiện ra họ và tiêu diệt. \"Điều này cũng làm tôi đau đớn. Đây cũng chính là hậu quả độc hại của chiến tranh\", ông viết.\r\n\r\nVới lo ngại về chiến tranh thế giới thứ ba, Giáo hoàng chỉ ra trong các cuộc chiến tranh hiện đại, nạn nhân chủ yếu là dân thường, trong đó trẻ em chiếm phần lớn: \"Trong hầu hết các cuộc xung đột trong 30 năm qua, những người mặc quân phục sống sót dễ dàng hơn nhiều so với cơ hội sống sót của một đứa trẻ mặc áo phông đỏ. Những người bị tàn sát nhiều nhất là những người vô tội: một phần ba trong số đó là trẻ em. Chính đám trẻ con vô tội phải gánh chịu sự điên rồ của chiến tranh. Chẳng có anh hùng nào, chẳng có mỹ từ nào có thể biện minh, chiến tranh là sự hèn nhát và ô nhục đến tột cùng\".\r\n\r\nTự truyện có giọng văn giản dị, xen lẫn hài hước, giúp người đọc dễ dàng tiếp cận. Nguyên Tổng giám mục Hà Nội Giuse Ngô Quang Kiệt cho biết choáng ngợp khi đọc tác phẩm. Ông viết lời tựa sách: \"Không chỉ giúp ta hiểu về con người của vị đại diện Chúa Kitô mà còn khơi lên những tâm tình cao thượng, tác phẩm đồ sộ không phải về số trang sách mà về chất liệu và con người\".\r\nGiáo hoàng Francis (tên thật là Jorge Mario Bergoglio) sinh năm 1936 tại Buenos Aires, Argentina. Ông là con trai cả trong gia đình gốc Italy di cư có năm người con. Sau khi tốt nghiệp chuyên ngành hóa học, ông lấy bằng triết học năm 1963, nhận chức linh mục năm 1969 và bắt đầu phục vụ Thiên Chúa. Năm 2013, ông trở thành Giám mục Roma, là vị Giáo hoàng thứ 266 của Giáo hội Công giáo và là người Mỹ Latin đầu tiên đảm đương trọng trách này.\r\n\r\n', '1744477681_h113.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 17:08:01', 'Giải trí', 0, 0),
(126, 'Tác giả \'Game of Thrones\' khóc khi gặp sói tuyệt chủng 12.500 năm trước', 'George R.R. Martin từng viết về sói trắng tuyệt chủng trong tiểu thuyết gốc series \"Game of Thrones\", khóc khi loài vật này được hồi sinh ngoài đời.', 'Theo Independent, George R.R. Martin là một trong những người đầu tiên gặp ba con non đầu tiên, gồm hai đực một cái, từ dự án hồi sinh chủng sói Aenocyon dirus đã tuyệt chủng khoảng 12.500 năm trước. Đây là thành tựu tái tạo gene của nhóm nhà khoa học thuộc công ty công nghệ sinh học và kỹ thuật di truyền Mỹ -Colossal Biosciences, công bố ngày 7/4.\r\n\r\nTrên trang web Not a Blog ngày 8/4, ông đăng ảnh chụp cùng sói con kèm bài viết về cơ duyên gặp chúng. Tác giả 77 tuổi cho biết: \"Tôi phải giữ bí mật suốt nhiều tháng, thề không hé lộ điều gì nhưng trong lòng chỉ muốn kể cho thế giới biết\". Theo George R.R. Martin, đạo diễn Chúa tể những chiếc nhẫn - Peter Jackson - là người giúp ông tiếp xúc bầy sói ngoài đời.\r\n\"Tôi phải cảm ơn Peter Jackson đã gọi điện thoại cho tôi từ New Zealand, đưa ra lời gợi ý rằng hãy liên hệ một người đàn ông tên Ben Lamm - người có bí mật lớn muốn chia sẻ với tôi. Peter đã thề giữ im lặng nên không tiết lộ cụ thể nhưng tôi cảm nhận sự phấn khích trong giọng nói của anh, và rồi tôi cũng gọi điện. Ơn trời, tôi mừng vì đã làm vậy\", ông viết trên trang cá nhân.\r\n\r\nTheo USA Today, Peter Jackson và George R.R. Martin là những nhà đầu tư của Colossal Biosciences, nhưng Jackson biết về dự án trước Martin. Ben Lamm là đồng sáng lập công ty.\r\n\r\nTháng 2, George R.R. Martin đến nơi nghiên cứu. Khi đó, hai con đực được bốn tháng tuổi còn con cái khoảng một tháng tuổi. Trên Comic Book Resources ngày 8/4, Ben Lamm kể nhà văn đã khóc rất nhiều khi thấy chúng. \"Ông ấy nói đây là một trong những điều tuyệt vời nhất từng gặp trong đời\", Lamm nói thêm.\r\n\r\nNhà văn George R.R. Martin từng viết về sói trắng trong bộ A Song of Fire and Ice (Băng hỏa trường ca) - tiểu thuyết gốc của loạt phim ăn khách Trò chơi vương quyền (Games of Thrones). Chúng là biểu tượng của nhà Stark - một trong những gia tộc nổi bật của series. Cũng trong bài đăng, nhà văn lý giải vì sao loài vật này có ý nghĩa đặc biệt với ông.\r\n\r\nNhiều thập niên trước, tác giả thăm bảo tàng La Brea Tar Pits, Los Angeles, cảm thấy điều gì đó \"bừng tỉnh\" trong người khi quan sát 400 sọ sói xếp trên tường. Năm 1991, ông bỏ dở quyển tiểu thuyết viễn tưởng đang sáng tác để viết A Song of Fire and Ice. Martin nói cảnh tượng các nhân vật phát hiện đám sói con trong rừng thôi thúc ông hoãn tác phẩm khác. Đây cũng là phân cảnh xuất hiện trong tập đầu tiên của Game of Thrones mùa một.\r\n\r\n\"Dù là lý do gì, tôi phải nói rằng sự hồi sinh loài sói trắng đã làm tôi xúc động hơn bất kỳ tin khoa học nào kể từ khi Neil Armstrong đặt chân lên mặt trăng\", ông viết trên blog.\r\nTrò chuyện với Business Wire cùng ngày, nhà văn Martin nhận định: \"Nhiều người xem sói trắng như sinh vật huyền thoại, chỉ xuất hiện ở thế giới kỳ ảo. Trên thực tế, chúng có lịch sử phong phú trong việc đóng góp cho hệ sinh thái nước Mỹ. Tôi có đặc ân viết về những phép màu, nhưng Ben và Colossal mới thực sự tạo ra điều kỳ diệu bằng cách đưa các loài động vật hùng vĩ trở lại thế giới của chúng ta\". Ngoài sói trắng, đơn vị đang tìm cách hồi sinh voi ma mút lông xoăn, hổ Tasmania và chim dodo.\r\nGeorge R.R. Martin sinh năm 1948, là nhà biên kịch, nhà văn người Mỹ nổi tiếng với các tác phẩm giả tưởng hùng tráng lấy cảm hứng từ lịch sử của châu Âu, bàn về các chủ để chính trị, tôn giáo và bản sắc văn hóa. Tác giả từng được vinh danh là \"Toliken người Mỹ\'\'.\r\n\r\nTiểu thuyết sử thi viễn tưởng A Song of Fire and Ice được ông lấy cảm hứng từ các sự kiện lịch sử thời \"Chiến tranh hoa hồng\" của nước Anh, ra mắt tập đầu năm 1996. Khi viết sách, ông chịu ảnh hưởng khá nhiều từ tiểu thuyết gia viễn tưởng gạo cội J. R. R. Tolkien (tác giả Lord of the Rings, The Hobbit). Đến nay, tác giả phát hành năm phần với quyển gần nhất là A Dance of Dragron, dự kiến bộ truyện kết thúc ở cuốn thứ bảy - A Dream of Spring. Hiện hai phần còn lại chưa có ngày ấn hành.\r\n\r\nSeries Trò chơi vương quyền công chiếu lần đầu vào tháng 4/2011, sau đó trở thành hiện tượng toàn cầu. Cùng năm phim ra mắt, George R.R. Martin được tạp chí Times bình chọn là một trong những người ảnh hưởng nhất thế giới.', '1744477817_h115.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 17:10:17', 'Giải trí', 0, 0),
(127, 'Anh hùng du kích Củ Chi hội ngộ ở buổi chiếu \'Địa đạo\'', 'TP HCM - Anh hùng Lực lượng vũ trang Tô Văn Đực, bà Võ Thị Mô - nguyên mẫu phim \"Địa đạo\" - ôn kỷ niệm thời chiến trong buổi giao lưu.', 'Êkíp Địa đạo: Mặt trời trong bóng tối tổ chức buổi chiếu tri ân khán giả ở Củ Chi, tối 10/4. Sự kiện có nhiều cựu du kích như ông Tô Văn Đực (nguyên mẫu nhân vật Tư Đạp - Quang Tuấn đóng - trong phim), trung úy Võ Thị Mô, thiếu úy Cao Thị Hương (đều là nguyên mẫu nhân vật Ba Hương - Hồ Thu Anh thủ vai), Trần Thị Neo và người dân, cán bộ ở Củ Chi.\r\nAnh hùng lực lượng vũ trang Tô Văn Đực, 83 tuổi, cho biết đã xem bản phim hoàn chỉnh ba lần, cùng các đoạn cắt khi phim ở giai đoạn hậu kỳ. Ông nhiều lần cảm ơn đạo diễn Bùi Thạc Chuyên vì thực hiện bộ phim điện ảnh đầu tiên về địa đạo.\r\n\r\nThời chiến, ông dành cả tuổi trẻ bám trụ với nơi này, được đồng đội gọi là \"cỗ máy phá tăng\" vì đã chế tạo ra mìn gạt để phá hủy xe tăng địch. Những ngày làm việc cùng đoàn phim ở hậu trường, ông hỗ trợ cung cấp thông tin cho đoàn, chỉnh sửa một vài chi tiết kịch bản. Ông Tô Văn Đực cũng trực tiếp hướng dẫn diễn viên Quang Tuấn kỹ thuật gài và tháo bom mìn, giúp nghệ sĩ thêm trải nghiệm nhập vai.\r\n\r\nVới ông Đực, địa đạo là biểu tượng của ý chí chiến đấu kiên cường của quân và dân ta trong kháng chiến. Trong buổi chiếu, ông nói đùa với đạo diễn Bùi Thạc Chuyên: \"Sao đầu phim nhìn tôi xấu xí vậy?\".\r\n\r\nÔng cũng hy vọng bộ phim giúp thế hệ trẻ trân trọng những hy sinh của cha ông và tự hào về quê hương. \"Như bao người Củ Chi, chúng tôi chiến đấu được là nhờ vào hệ thống địa đạo. Nếu không có nó, chúng tôi đã không thể tồn tại. Lúc chiến đấu, chúng tôi trạc tuổi đôi mươi như các diễn viên. Bộ phim này đã truyền tải sự ác liệt, cho thấy thế hệ trước dũng cảm, không sờn ý chí\", ông Tô Văn Đực cho biết.\r\nNgồi cạnh ông Tô Văn Đực trong buổi giao lưu là bà Võ Thị Mô, 78 tuổi, được coi là nữ du kích huyền thoại, từng đảm nhận vị trí chỉ huy trưởng của Trung đội nữ du kích Củ Chi. Bà cho biết đã cùng con cháu đến rạp xem phim và mọi người đều ấn tượng với quy mô bối cảnh, tinh thần bất khuất của các nhân vật. Bà Võ Thị Mô nói: \"Từ lúc quay phim, tôi coi các diễn viên là thành viên trong nhà, sẵn sàng chuẩn bị cơm nước tiếp đón mỗi khi các con về chơi. Tôi nôn nóng, muốn xem tác phẩm miêu tả ra sao về con người và vùng đất quê hương mình\".\r\n\r\nTrong tác phẩm, đạo diễn Bùi Thạc Chuyên hư cấu chuyện tình Ba Hương - Tư Đạp để khán giả cảm nhận được sự lãng mạn và tính nhân văn trong mối quan hệ giữa hoàn cảnh khốc liệt của chiến tranh. Ngoài đời, nguyên mẫu hai nhân vật chỉ có tình đồng đội, đồng chí. Bà Võ Thị Mô từng tham gia đánh địch bằng bom mìn do ông Đực chế tạo.\r\n\r\nĐạo diễn cho biết: \"Tôi muốn nhân vật có câu chuyện éo le khi chứng kiến cảnh bom rơi đạn lạc. Cháu xin lỗi chú Tô Văn Đực vì sáng tạo chi tiết không có thật\".\r\n\r\nTrước phim Địa đạo, bà là nguồn cảm hứng cho phim Nữ du kích Củ Chi và kịch Khát vọng hòa bình. Bà nổi tiếng với câu chuyện chiến đấu ở Củ Chi năm 1966 và thả bốn lính Mỹ khi thấy họ cùng nhau đọc thư gia đình, khoe ảnh vợ con rồi ôm nhau khóc. Về sau, một người trong nhóm cựu binh Mỹ trở lại Việt Nam cảm ơn bà.\r\nPhim Địa đạo: Mặt trời trong bóng tối do Bùi Thạc Chuyên đạo diễn, câu chuyện xoay quanh nhóm du kích 21 người bám trụ ở Củ Chi, sau trận càn Cedar Falls (năm 1967) của Mỹ. Đội trưởng Bảy Theo (Thái Hòa đóng) và các đồng đội được giao nhiệm vụ bảo vệ địa bàn cho nhóm thông tin tình báo chiến lược. Tuy nhiên, các cuộc liên lạc của họ bị quân địch phát hiện. Những chiến sĩ du kích rơi vào tình thế nguy hiểm, đối mặt nhiều trận càn quét.\r\n\r\nDự án kỷ niệm 50 năm ngày giải phóng miền Nam, thống nhất đất nước, được làm từ ngân sách xã hội hóa. Tối 10/4, dự án thu hơn 100 tỷ đồng sau một tuần công chiếu, là phim chiến tranh đầu tiên của Việt Nam đạt cột mốc này.\r\n\r\nBùi Thạc Chuyên cho biết tôn trọng tính chân thực của phim vì muốn cùng khán giả suy ngẫm, học những bài học lịch sử qua tác phẩm. Nhiều phân cảnh được anh lấy ý tưởng từ nguyên mẫu có thật. Đoàn phim cũng đầu tư cho phần trang phục - yếu tố quan trọng góp phần phác họa giai đoạn cuối thập niên 1960. Đạo diễn cùng tổ thiết kế tìm hiểu kỹ về lịch sử để tránh các trường hợp gây tranh cãi.', '1744477892_h116.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 17:11:32', 'Giải trí', 0, 0);
INSERT INTO `news` (`id`, `title`, `description`, `content`, `image`, `author`, `created_at`, `category`, `is_featured`, `views`) VALUES
(128, 'Tiến Luật ngâm mình dưới cống đóng phim kinh dị', 'Diễn viên Tiến Luật ngâm mình dưới cống suốt đêm khi tham gia \"Tìm xác\" - phim 18+ anh đóng cùng Ngô Kiến Huy.', 'Trong tác phẩm kinh dị do đạo diễn Bùi Văn Hải thực hiện, anh đóng vai Tiến - một nhân viên nhà xác. Tiến Luật cho biết dự án là một trong những phim thử thách nhất về thể lực anh từng tham gia. Ở phân cảnh nhân vật truy tìm tung tích một thi thể, anh phải chịu lạnh suốt đêm khi ngâm dưới cống. Phim quay ở một vùng quê của Long Khánh, Đồng Nai, nhiệt độ về đêm xuống còn 15-16 độ C. \"Dù vất vả, khi xem lại, tôi và êkíp thấy công sức bỏ ra xứng đáng\", anh cho biết.\r\n\r\nAnh lần đầu diễn chung với nghệ sĩ Hồng Vân (vai Hồng - mẹ của Tiến). Trong gia đình chỉ có hai người, họ sống nương tựa, hy sinh vì nhau. Bà Hồng vốn mắc bệnh tâm lý, thường đãng trí. Người con túc trực bên mẹ chăm sóc, làm chỗ dựa cho bà. Trên trường quay, Hồng Vân hỗ trợ Tiến Luật trong các cảnh đòi hỏi kỹ thuật diễn phức tạp, giúp anh \"mồi\" cảm xúc. \"Vì quen biết lâu, ban đầu, lối diễn của tôi và chị Vân giống chị em hơn là mẹ con. May mắn, chị biết cách điều tiết, tạo không gian để dẫn dắt bạn diễn\", anh nói.\r\nTiến Luật, 43 tuổi, bắt đầu sự nghiệp với vai trò diễn viên hài. Năm 2020, Chị Mười Ba phần 2 - phim vợ chồng Thu Trang, Tiến Luật sản xuất - vượt mốc 100 tỷ đồng. Năm 2022, Nghề siêu dễ - phim Tiến Luật góp mặt - đoạt doanh thu 73 tỷ đồng, là một trong những dự án ăn khách của năm. Năm ngoái, anh tham gia show Anh trai vượt ngàn chông gai, gây chú ý với các tiết mục như: Tình anh là đại dương (Duy Mạnh sáng tác), Thu hoài (Hoài Thanh, 14 Casper sáng tác). Anh kết hôn năm 2011, có một con trai.\r\n\r\nPhim xoay quanh hai nhân vật chính - Tiến và tài xế xe cứu thương Thành (Ngô Kiến Huy). Một lần, vận chuyển một thi thể, họ vướng vào một sự cố, từ đó lần ra thân phận của nạn nhân. Khi sắp tìm ra lời giải, biến cố bắt đầu ập đến gia đình của Tiến, đe dọa mẹ anh. Tiến phải đứng trước lựa chọn: Im lặng trước tội ác hoặc tiếp tục đi tìm sự thật.\r\nKịch bản lấy ý tưởng từ một vụ án có thật thập niên 1980, nhà sản xuất Nhất Trung (phim Quỷ nhập tràng, Ma da) là biên kịch. Đạo diễn Bùi Văn Hải từng thực hiện phim Vú em tập sự do Johnny Trí Nguyễn, Tiến Luật đóng chính. Anh xuất thân từ nghề cascadeur trước khi theo học làm phim với Charlie Nguyễn.', '1744478062_h117.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 17:14:22', 'Giải trí', 0, 0),
(129, 'Sài Gòn thời say mê cải lương', 'Không còn thời vàng son nhưng cải lương vẫn giữ mạch nguồn văn hóa, ghi dấu qua các ngôi sao như Phùng Há, Thanh Nga, Minh Vương, Lệ Thủy...', 'Một ngày tháng 3, trong căn nhà hai tầng ở trung tâm TP HCM, Nghệ sĩ Nhân dân Minh Vương đón nhà báo đến phỏng vấn theo cung cách của kép hát lừng danh một thời. Trong câu chuyện về cải lương, ánh mắt, cử chỉ của nghệ sĩ gần 80 tuổi trở nên tinh anh. \"Cái thật và đẹp của cải lương, tình cảm của khán giả luôn khiến tôi xúc động, biết ơn những gì tổ nghề ban tặng\", ông nói.\r\n\r\nMinh Vương có hơn 60 năm gắn bó TP HCM, khi ông còn là cậu bé 12 tuổi quê Long An lên Sài Gòn, mê vọng cổ nên được một thầy đàn dưới chân cầu chữ Y, quận 8, truyền nghề ca hát. Từ thành phố này, nhiều thế hệ nghệ sĩ trước và cùng thời ông trải qua những thập niên vàng, chứng kiến giai đoạn rực rỡ của nghệ thuật cổ truyền.\r\n\r\nSài Gòn - trung tâm sân khấu cải lương miền Nam\r\n\r\nNét \"thật và đẹp\" nghệ sĩ Minh Vương nhắc đến chính là tuyên ngôn nghệ thuật nổi tiếng một thời mà NSND Năm Châu (Nguyễn Thành Châu) dành tâm huyết xây dựng bản sắc cho cải lương. Cải lương có tuổi đời non trẻ hơn tuồng (hát bội), chèo, quan họ hay đờn ca tài tử, nhưng bộ môn này, như Giáo sư Trần Văn Khê từng nói: \"Động mà không tĩnh, phá chấp, không theo một khuôn khổ nào, từ đó phát triển thêm ra\".\r\n\r\nKhoảng 120 năm trước, hát bội là loại hình chủ yếu trong đời sống sân khấu người miền Nam, với nhiều ban nhóm đình đám tụ hội ở Sài Gòn. Những vở tuồng thường dựa theo điển tích, câu chuyện xưa cũ trong và ngoài nước. Sự du nhập của văn hóa phương Tây vào đời sống từ tầng lớp trí thức đến bình dân khiến sân khấu có nhu cầu phải thay đổi, là tiền đề cải lương tuồng cổ ra đời: Một loại hình ca kịch kết hợp nhạc tài tử với lối diễn xuất cách tân nghệ thuật hát bội.\r\nSài Gòn không phải là nơi sinh ra môn nghệ thuật này. Các tỉnh Nam kỳ xưa như Vĩnh Long, Mỹ Tho, Bạc Liêu là chiếc nôi của cải lương nhờ nền tảng của đờn ca tài tử. Nhưng với vị thế là trung tâm kinh tế, xã hội và văn hóa - nghệ thuật, cùng sự phát triển mạnh mẽ của báo chí chữ quốc ngữ, Sài Gòn mau chóng trở thành \"vùng đất của cải lương\", sôi động cùng hàng trăm đoàn hát. Rạp Cô Tám (ở đường rue des Marins, đường Trần Hưng Đạo ngày nay) là nơi trình làng tuồng Vì nghĩa quên tình - được xem là một trong những vở cải lương đầu tiên ở miền Nam, năm 1917.\r\n\r\nNhững năm 1920, nhiều gánh như Tái Đồng Ban, Tân Thịnh, Văn Hí Ban nở rộ, hoặc những gánh hát ở các tình miền Tây cũng tập trung về, gây dựng tiếng tăm rồi tỏa nhiều tỉnh thành, tiếp cận sâu rộng vào đời sống.\r\n\r\nDo những biểu hiện bên ngoài của loại hình ca nhạc kịch - thể thức biểu diễn dựa trên âm nhạc cổ truyền, cải lương có thể được cho là gắn với nội dung bi lụy và \"sến\". Nhưng ngay từ bối cảnh ra đời bộ môn này, cải lương chính là \"một sự cải tiến, cách tân và tiến bộ\" (reformist), với những tác phẩm phản ánh hiện thực, tâm tư tình cảm của nhiều người.\r\n\r\nVăn hóa thưởng thức cải lương\r\n\r\nThập niên 1960, Sài Gòn - Chợ Lớn có gần 40 rạp hát cải lương và liên tục sáng đèn để phục vụ nhu cầu giải trí của khán giả. Người xem đến rạp vào những tối cuối tuần, quây kín các sân khấu lớn lẫn điểm diễn ở vùng ven. Các suất diễn tại các rạp Hưng Đạo, Quốc Thanh, Nguyễn Văn Hảo, Hào Huê, Lao Động luôn \"cháy\" vé. Nghệ sĩ Bảo Quốc nhớ lại cuộc sống của mọi người lúc này còn khó khăn, thậm chí ăn cơm độn, nhưng vẫn ùn ùn đi xem cải lương.\r\n\r\n\"Vé vừa bán một tiếng sau đã hết sạch. Nhiều người sẵn sàng bỏ tiền ra mua vé chợ đen giá gấp ba, bốn lần. Có fan chấp nhận ngồi ghế đẩu để được vào rạp xem vở diễn yêu thích, ngắm nhìn thần tượng\", Bảo Quốc cho biết.\r\n\r\nSau mỗi suất diễn, khán giả không chịu về, chen chân khu vực cửa sau rạp hát để chờ được nhìn tận mặt nghệ sĩ mình yêu thích. Họ mong được nắm tay, ôm hôn nghệ sĩ để thể hiện tình cảm.\r\n\r\nVới đoàn Thanh Minh - Thanh Nga của bà bầu Thơ, khán giả đi xem đoàn hát ăn mặc rất lịch sự. \"Đàn ông mặc vest lịch lãm, phụ nữ mặc áo dài thướt tha nhìn rất đẹp mắt\", nghệ sĩ Bảo Quốc nói.\r\nThời hát cho đoàn Minh Tơ, nghệ sĩ Bạch Long vẫn còn nhớ có nhiều người mê cải lương đến mức đặt chỗ ngồi nguyên tuần cho các suất hát. Khi nghệ sĩ vừa đến, họ reo hò: Vũ Linh, Tài Linh, Minh Vương, Lệ Thủy vang cả một góc phố.\r\n\r\nKhông ồn ào, náo nhiệt như bên ngoài rạp, khi màn nhung sân khấu mở ra, mọi người chăm chú nghe các nghệ sĩ ca. Nhất là lúc diễn viên trên sân khấu vô câu vọng cổ, lập tức, khán giả im phăng phắc để thưởng thức từng lời, cách nhả chữ, luyến láy. Khi nghệ sĩ xuống câu vọng cổ, khán phòng rào rào vỗ tay tán thưởng. Mỗi đoàn thường có cặp đào, kép chánh, như đoàn Minh Tơ có Điền Thanh - Bạch Lê, Sài Gòn 2 là Minh Vương - Lệ Thủy. Khán giả thích nghệ sĩ nào sẽ theo người đó trên từng sân khấu và vào tận hậu trường chăm sóc, động viên.\r\n\r\nSinh thời, nghệ sĩ cải lương Vũ Linh từng có những đêm diễn thu hút chục nghìn khán giả, tiền thù lao đựng trong bao tải chưa hết. Vũ Linh hát ở Đầm Môn (Khánh Hòa) đón 12.000 khán giả. Mọi người đốt đuốc, lội suối đến điểm diễn để xem trực tiếp nghệ sĩ.\r\nNghệ sĩ Lệ Thủy kể hồi xưa đi hát, khoảng 200 vé là diễn viên chê, bán cả nghìn vé trở lên thì đêm hát mới diễn ra. Từ sáng sớm, các xe đi phát tờ bướm đông vui, rôm rả. Băng rôn quảng cáo về các suất diễn treo rợp khắp các tuyến đường.\r\n\r\n\"Không chỉ khán giả ở Sài Gòn, khán giả ở miền Tây, miền Trung thời đó rất ái mộ nghệ sĩ cải lương. Đoàn diễn xong, họ mời đến nhà chơi, đãi tiệc, tặng trái cây, thức ăn. Họ coi nghệ sĩ như người trong gia đình\", nghệ sĩ Bạch Long nhớ lại.\r\n\r\nNhững giai đoạn thăng hoa\r\n\r\nHơn một thế kỷ, sự phát triển của cải lương gắn với các dấu mốc quan trọng ở giai đoạn 1955-1975 hay 1980-1990. Trước biến động của xã hội, thời cuộc và nhu cầu thưởng thức của khán giả, cải lương định hình là bộ môn hoàn thiện, chuyên nghiệp trong các khía cạnh: Tác phẩm (kịch bản, bài ca), nghệ sĩ, sân khấu.\r\n\r\nKhông chỉ ở Sài Gòn, cải lương còn lan tỏa trên cả nước. Những năm 1940, công tử Trần Viết Long (Bầu Long) phải lòng cô đào cải lương Kim Chung nên lập gánh Kim Chung tại Hà Nội. Danh cầm đất Bắc Trần Văn Vân và vợ là danh hài Vân Quý cũng từng hát cho Kim Chung ở Hà Nội. Đôi vợ chồng có con đều theo nghiệp cải lương, sau 1975, họ vào Nam đóng góp cho cải lương tại TP HCM những tên tiêu biểu như: NSƯT Thanh Dậu (nguyên giám đốc Nhà hát Cải lương Trung Ương), NSND Thanh Vy (Nàng Xê Đa lừng danh), nhạc Văn Hai, nhạc sĩ Văn Thắng. Sui gia của đôi vợ chồng nhạc sĩ, danh hài này có cô Ái Liên, một nghệ sĩ cải lương lừng danh trong Nam ngoài Bắc ở thập niên 1940-1950.\r\n\r\nTại Sài Gòn, nhiều gia tộc cả đời với nghiệp diễn, như gia tộc Huỳnh Long, Minh Tơ, Đoàn cải lương Thanh Minh - Thanh Nga, gánh hát Phước Cương. Trong đó, đoàn Thanh Minh - Thanh Nga với 30 năm phát triển vang dội (khoảng 1950-1980) góp cho nghệ thuật cải lương hàng loạt ngôi sao: Út Trà Ôn, Hoàng Giang, Thanh Nga, Thành Được, Út Bạch Lan, Bảo Quốc, Thành Được. Những vở tuồng của đoàn này, như Tiếng trống Mê Linh hay Sân khấu về khuya (Năm Châu) là minh chứng cho khái niệm \"thánh đường nghệ thuật\" trong lòng công chúng.\r\nThời kỳ vàng son của cải lương còn kéo dài sang 50 năm sau ngày giải phóng miền Nam, thống nhất đất nước. NSND Thanh Tòng, con của ông bầu Minh Tơ và chú ruột là nhạc sĩ Đức Phú có công lớn trong việc thay đổi mạnh mẽ cải lương tuồng cổ, từ cải cách dàn nhạc dân tộc đến tạo những nội dung thuần Việt cho sân khấu.\r\n\r\nTrước năm 1975, miền Nam có khoảng 60 đoàn cải lương, sau năm này, riêng ở TP HCM có khoảng 22 đoàn, trong đó, có hai đoàn quốc doanh được nhà nước bao cấp. Vốn quý của sân khấu là thể loại cải lương tuồng cổ, hình thành thêm nhiều nghệ nhân, đạo diễn tên tuổi như Bích Lâm, Bá Huỳnh, Huỳnh Nga, Minh Trị.\r\n\r\nTừ loại hình kịch hát dân tộc mang tính vùng miền, cải lương còn Bắc tiến, cũng như chinh phục khán giả cả nước. Đạo diễn Đoàn Bá dựng vở Người trong cõi nhớ của Lưu Quang Vũ, tạo dấu ấn ở Hội diễn sân khấu toàn quốc năm 1985. Nhiều vở diễn hay, như Tiếng hò sông Hậu, quy tụ dàn diễn viên thanh sắc, kịch bản trau chuốt, sân khấu được đầu tư kỹ càng.\r\n\r\nCuối 1980 và đầu 1990, do quy luật tất yếu của sự chuyển mình trong đời sống kinh tế - xã hội, cải lương dần thoái trào. Các loại hình nghệ thuật - giải trí nghe nhìn, phim ảnh, nhạc trẻ lên ngôi. Nghệ thuật truyền thống đối diện thách thức về kịch bản khan hiếm, sân khấu cũ kỹ, chưa có thế hệ lứa nghệ sĩ trẻ kế thừa.\r\n\r\nTừ một thời kỳ huy hoàng với hàng chục sân khấu liên tục sáng đèn, hiện tại nhiều sân khấu cải lương xưa ở thành phố đóng cửa. Dẫu còn nhiều khó khăn, sức sống của cải lương vẫn như mạch ngầm len lỏi trong đời sống phố thị náo nhiệt. Các đơn vị nghệ thuật công lập và xã hội hóa bám trụ, tổ chức nhiều buổi biểu diễn để đưa loại hình này đến lớp khán giả mới. Các nhà hát vẫn nỗ lực sáng đèn, như nhà hát Cải lương Trần Hữu Trang, rạp Hồng Liên (quận ), Sân khấu Sen Việt (Hội Sân khấu TP HCM).\r\nTiến sĩ Trần Thị Minh Thu - trưởng ban Nghiên cứu Nghệ thuật, Viện Văn hóa, Nghệ thuật, Thể thao và Du lịch Việt Nam - cho biết trong một lần dẫn sinh viên khoa Du lịch, Đại học Văn hóa Hà Nội, đi xem vở Người đi tìm minh chủ (2018) của Nhà hát Cải lương Việt Nam, một sinh viên bật khóc nói: \"Cô ơi, em từng nghĩ cải lương chỉ có ủy mị, sướt mướt, tình yêu nhạt nhẽo. Nhưng sau khi xem vở diễn, em thấy mình đã hiểu sai. Em không nghĩ cải lương lại hay đến thế\".\r\n\r\nNhờ sự năng động, dung nạp và tiếp biến văn hóa để chọn lọc nét hay của nhiều loại hình biểu diễn, cải lương một thời không chỉ là một hình thức giải trí mà còn là dòng chủ lưu của nghệ thuật sân khấu. Kho tàng hàng trăm bản vọng cổ, cải lương còn được lưu giữ đến hôm nay chứa đựng ngôn ngữ giàu và đẹp, văn hóa dân gian, nếp sống, lối ứng xử, phong tục và phẩm chất của người Việt.\r\n\r\nNghệ sĩ Minh Vương khép lại buổi trò chuyện với nụ cười ấm áp khi nhắc về bạn diễn vàng - nghệ sĩ Lệ Thủy. Ông cho biết vừa được lưu diễn ở Hà Nội và chờ Lệ Thủy từ Australia về để tiếp tục \"chạy show\". Với \"Ông hoàng của sân khấu cải lương\", được hát đến cuối cuộc đời và còn khán giả nghe hát là hạnh phúc.', '1744478152_h118.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 17:15:52', 'Giải trí', 0, 0),
(130, 'Vì sao sân khấu cải lương xuống dốc ba thập kỷ qua?', 'Nghệ sĩ - soạn giả giỏi ngày càng ít, sàn diễn xuống cấp, nhiều loại hình giải trí trỗi dậy... khiến dòng cổ nhạc rơi vào bế tắc.', 'Đầu những năm 1990, sự bùng nổ của làn sóng băng video, phim nhựa và truyền hình khiến cải lương mất thế thượng phong. Sân khấu ca cổ không còn nhiều đất sống, các chủ đoàn hát tư nhân rút vốn đầu tư. Giữa thập niên 1990, sàn diễn dần thoái trào, khán giả đến rạp ít hơn vì chuộng xem băng video. Cải lương nguyên tuồng cũng bị \"bức tử\", nhiều rạp chỉ lấy trích đoạn vở cũ hay nhất ra biểu diễn. Sân khấu rơi vào cảnh đìu hiu vì không còn cảnh khán giả rần rần xếp hàng đi xem cải lương. Đến đầu thế kỷ 21, nhiều loại hình giải trí nghe nhìn, game show, Internet phát triển mạnh mẽ khiến sân khấu truyền thống khốn đốn. NSND Lệ Thủy chia sẻ, vào thời hoàng kim, một suất nếu bán chỉ vài trăm vé nghệ sĩ đã chê. \"Giờ chỉ mong mỗi suất có vài trăm khán giả đến xem là mừng lắm rồi\", bà kể.\r\nNSND - đạo diễn Ngọc Giàu, NSND Bạch Tuyết cho rằng cải lương hiện thiếu toàn diện những yếu tố từng làm nên thời vàng son như: soạn giả giỏi, thế hệ nghệ sĩ kế thừa xứng đáng, những ông bầu lành nghề và sàn diễn ổn định.\r\n\r\nNhững soạn giả có tiếng hiện nay chỉ đếm trên đầu ngón tay như Hoàng Song Việt, Lê Duy Hạnh, Triệu Quang Vinh, Triệu Trung Kiên... Trong Liên hoan cải lương toàn quốc tại Long An hồi tháng 9, NSƯT Lê Chức - Chủ tịch Hội đồng Nghệ thuật của liên hoan - nhận định các tác phẩm dự thi đa số đều phục dựng từ kịch bản cũ hoặc vay mượn từ kịch nói. Hàng loạt vở tuồng về đề tài lịch sử, dã sử, hoặc mang chủ đề gia đình, xã hội vốn ngày trước rất ăn khách, là \"gà đẻ trứng vàng\" cho nhiều sân khấu cũ, nay cứ tái diễn nhiều lần khiến cải lương bị bào mòn về nội dung, độ sáng tạo.\r\n\r\nSự thiếu hụt đội ngũ kế thừa thế hệ nghệ sĩ vàng cũng khiến bộ môn nghệ thuật này không còn gây ấn tượng với lớp khán giả cũ lẫn khán giả trẻ. Trên VnExpress, khán giả có nickname cangvt46 chia sẻ: \"Cải lương xưa đi vào lòng người nhờ hai yếu tố: giọng ca hay và soạn giả giỏi. Những yếu tố mà cải lương ngày nay không có\". Còn khán giả Anh Tuấn Nguyễn Lê bày tỏ: \"Nghệ sĩ bây giờ ca vẫn hay nhưng không có hồn\".\r\n\r\nNgày trước, hàng loạt nghệ sĩ cải lương, mỗi người là một vẻ riêng biệt, độc đáo về thanh lẫn sắc, in đậm dấu ấn trong lòng khán giả mộ điệu bằng tài năng ca diễn lẫn thần thái, phong cách như: Nghệ sĩ Ưu tú Thanh Nga, Thanh Sang, Minh Cảnh, Thanh Kim Huệ, Lệ Thủy, Minh Vương, Minh Phụng, Mỹ Châu, Vũ Linh, Tài Linh, Bạch Tuyết... Thế hệ này được lớp soạn giả lừng danh một thời như: Viễn Châu, Hà Triều - Hoa Phượng, Kiên Giang, Thu An, Yên Lang... \"chắp cánh\" bằng những vở tuồng đặc sắc, chạm sâu vào tâm hồn, cảm xúc của nhiều thế hệ khán giả như: Tuyệt tình ca, Nửa đời hương phấn, Cô gái đồ long, Áo cưới trước cổng chùa, Người vợ không bao giờ cưới, Lan và Điệp, Đường gươm Nguyên Bá...\r\n\r\nKhi thời của lớp trước đã lùi xa, lớp trẻ - vốn được bổ sung từ nhiều cuộc thi cải lương như Triển vọng Trần Hữu Trang, Tài năng trẻ cải lương toàn quốc, Chuông vàng vọng cổ... -  lại khó bám trụ với nghề và khẳng định chỗ đứng. Bởi họ thiếu những yếu tố cần và đủ để tạo bản sắc riêng: thiếu sàn diễn, thiếu tác phẩm hay, ít cơ hội để rèn chuyên môn và thiếu một lớp khán giả trung thành - những người vốn không đủ kiên nhẫn trước một loại hình trình diễn thiếu sự đầu tư về nhiều mặt.\r\nCâu chuyện về đào tạo là chủ đề thường trực trong các buổi hội thảo chuyên đề vực dậy cải lương. Các lò đào tạo diễn viên cải lương một thời như Nhà hát Trần Hữu Trang, Đồng Ấu Bạch Long - nơi khai sinh ra các tên tuổi trẻ như Quế Trân, Tâm Tâm, Vũ Luân, Tú Sương - không còn hoạt động như trước. Đầu vào ở các trường, khoa chuyên đào tạo nghệ sĩ cải lương ở TP HCM cũng ít ỏi, thiếu các gương mặt tiềm năng.\r\n\r\nNgoài ra, theo NSND Huỳnh Nga - đạo diễn vở Đời cô Lựu, việc cải lương xuống dốc còn nằm ở khâu chọn lựa tác phẩm của các đoàn. Ông cho rằng những năm gần đây, các đoàn cải lương thường chọn bừa kịch bản, tập hợp diễn viên làm vở mà không xác định thời điểm, xu hướng và chiến lược lâu dài.\r\n\r\nĐiều kiện thiếu thốn, hạn chế của các điểm diễn hiện tại cũng là trở ngại lớn. NSƯT Hoa Hạ cho biết bà từng đi nhiều nước, nhận thấy sàn diễn ở nhiều nơi đã phát triển \"đến nóc nhà hát\", sân khấu xoay chuyển, biến mất trong tích tắc trước sự hoa mắt, choáng ngợp của khán giả. Đạo diễn nhận định mặt bằng chung sân khấu cải lương hiện vẫn giữ mức như thập niên 1980, đạo cụ vẫn sản xuất thủ công, cơ sở còn thiếu an toàn... Chẳng hạn sân khấu Nhà hát cải lương Trần Hữu Trang (quận 1) - tiền thân là rạp Hưng Đạo, nơi từng được xem là \"thánh địa\" của các đoàn cải lương Sài Gòn xưa. Sau khi được xây mới với kinh phí 132 tỷ đồng, sân khấu nơi đây lại không sáng đèn thường xuyên vì hạn chế nhiều mặt về yếu tố kỹ thuật. Sinh thời, cố nghệ sĩ Út Bạch Lan buồn bã khi thấy rạp Hưng Đạo sửa mới không đáp ứng được nguyện vọng của giới nghệ sĩ, khán giả mộ điệu trong việc vực dậy cổ nhạc. Theo nhiều đạo diễn, hiện chỉ có sân khấu Bến Thành (quận 1) mới đáp ứng hầu hết điều kiện cho các tác phẩm cải lương, song giá thuê còn khá đắt.\r\nTrong nỗ lực khôi phục cải lương, nhiều đoàn hát tư nhân đề ra nhiều cách thức thu hút khán giả. Sân khấu Lê Hoàng ở Trung tâm Văn hóa quận Bình Thạnh từng dàn dựng lại các vở cải lương kinh điển của tuồng cổ như Chung Vô Diệm, Ngọc Kỳ Lân, Xử bá đao Từ Hải Thọ..., thu hút nhiều tên tuổi hợp tác như NSND Bạch Tuyết, Ngọc Giàu, Lệ Thủy, Bạch Mai, Trường Sơn, Kim Tử Long... Họ kêu gọi các nghệ sĩ quảng bá vở diễn trên mạng xã hội, nhờ các diễn đàn cải lương thu hút khán giả thích xem vở nguyên tuồng đến thưởng thức. Sân khấu Thầy Năm Tú của nghệ sĩ Nguyễn Quang (tại Nhà văn hóa Lao động quận Bình Tân) từng diễn miễn phí trong tuồng đầu tiên để đo lường mức độ yêu thích của công chúng. Tuy nhiên, các điểm diễn này đều gặp khó khăn khi tiếp cận các khán giả vãng lai.\r\n\r\nTheo bước chân của kịch nói, nhiều đơn vị nỗ lực xã hội hóa cải lương nhưng chưa thành công. Các câu lạc bộ như Cải lương Tinh Hoa, Hương Xưa, Sân khấu thể nghiệm... ra đời nhưng sớm đi vào quên lãng. Năm 2008, chương trình Sân khấu vàng của NSND Lệ Thủy - NSƯT Minh Vương được xây dựng với mục địch khôi phục các vở kinh điển và quyên góp quỹ thiện nguyện. Tuy nhiên, do thiếu kinh phí từ các mạnh thường quân, chương trình này phải kết thúc sau vài năm khởi xướng. Nhóm xã hội hóa của nghệ sĩ Vũ Luân cũng rơi vào cảnh đìu hiu, từ rạp Thủ Đô chuyển sang sân khấu trong công viên Lê Thị Riêng, dù trước đó đầu tư hàng tỷ đồng. Năm 2015, ông bầu Huỳnh Anh Tuấn của sân khấu Idecaf xây dựng một sàn diễn cải lương định kỳ ở Nhà hát Nón Lá, Cung văn hóa Lao động (quận 1) nhưng bỏ cuộc vì không có nghệ sĩ chung lưng đấu cật.\r\nNhững năm gần đây, NSƯT Kim Tử Long là một trong những nghệ sĩ đi theo mô hình đổi mới. Ban đầu, họ kêu gọi các nhà tài trợ ủng hộ. Sau đó, khi có được nguồn thu, nghệ sĩ đóng góp cát-xê của họ, và chia ra từng mảng để phụ trách như: cảnh trí, phục trang, dàn nhạc... cho một vở diễn. Kim Tử Long kể anh nhận được chia sẻ của nhiều nghệ sĩ tâm huyết để chung tay với mong muốn hồi sinh cải lương. Ấp ủ dự định khôi phục Sân khấu vàng, nghệ sĩ Minh Vương cho rằng trên chặng đường đi tìm ánh hào quang xưa, cải lương rất cần sự quan tâm của các nhà quản lý văn hóa, sự đồng lòng giữa các nghệ sĩ nhiều thế hệ.', '1744478233_h119.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 17:17:13', 'Giải trí', 0, 0),
(131, 'Bí quyết giúp Mariah Carey trông trẻ so với tuổi', 'Ca sĩ Mariah Carey cho biết lười trang điểm, ăn nhiều cá hồi, tập đấm bốc để giữ sắc vóc.', 'Hôm 28/3, trên trang cá nhân, danh ca Mỹ chia sẻ khoảnh khắc đón tuổi mới trong bộ đầm sequin quây ngực kèm dòng viết: \"Những khoảnh khắc đáng nhớ ngày 27/3\". Dưới bức ảnh, khán giả khen cô rạng rỡ, trẻ trung.\r\nỞ tuổi 56, ngôi sao giữ được nhan sắc, đường cong gợi cảm, nhờ chế độ ăn kiêng và tập luyện nghiêm túc hơn 20 năm qua. Trả lời phỏng vấn Hollywood Life, Mariah Carey cho biết từ năm 30 tuổi đã không ăn đồ nhiều calo. Ca sĩ ưu tiên chọn thực phẩm hữu cơ, nguyên chất và không dùng đồ chế biến sẵn, tập trung vào rau, hải sản, cá hồi, nụ bạch hoa, táo, xoài, việt quất. Những thực phẩm này góp phần giúp bổ sung nguồn dinh dưỡng, nhiều vitamin, chất xơ, chất chống lão hóa.\r\n\r\nTừ năm 2018 đến nay, Mariah Carey hạn chế đường khỏi thực đơn dù trước đó, cô rất thích các món tráng miệng làm từ chocolate. Ca sĩ cho biết e ngại ăn nhiều đồ ngọt là nguồn cơn của béo phì và lão hóa nhanh. Một quy tắc mà Mariah Carey áp dụng hàng ngày là ăn chậm và nhai kỹ, giúp bản thân nhận biết cảm giác no và dừng lại đúng lúc, đồng thời giúp thức ăn tiêu hóa tốt hơn, cơ thể hấp thụ được tối đa dinh dưỡng từ thức ăn.\r\n\r\nNgoài ăn uống, giọng ca Without You chăm tập aerobic và bơi lội. Trên Marie Claire, cô cho biết nếu chế độ ăn quyết định tới 70% trong quá trình giảm và giữ cân, tập thể dục đều đặn mỗi ngày sẽ chiếm 30% còn lại. Cô tập luyện ba lần một tuần, ưu tiên những bài tập cho tim mạch, cơ bụng, kết hợp tập cardio, chạy nước rút lên dốc, đấm bốc, đi bộ chậm với tạ.\r\n\r\nTrên Instyle, tiến sĩ Valter Longo, người đứng đầu Viện nghiên cứu tuổi thọ tại Đại học Nam California, cho biết tập thể dục, trong đó có đi bộ thường xuyên, có tác dụng \"duy trì tuổi trẻ\" vì các tế bào được kích hoạt thường xuyên, làm chậm tiến trình lão hóa. Ngoài ra, hoạt động này còn kích thích sản sinh collagen, máu huyết lưu thông, giúp da mịn, hồng hào và tươi tắn.\r\n\r\nĐể có làn da ít nếp nhăn, Mariah Carey chăm sóc đều đặn ở các spa, ưu tiên chu trình làm sạch và cấp nước đơn giản tại nhà. Trên Elle, cô cho biết hạn chế trang điểm tối đa, thường xuyên để mặt mộc để da được \"thở\" và tái tạo một cách tự nhiên. \"Bạn càng ít trang điểm thì càng đỡ phải tẩy trang. Tẩy trang quá nhiều sẽ khiến da có thể bị khô và quá tải với hóa chất, trừ phi bạn dùng các loại dầu thiên nhiên để tẩy\", cô nói.\r\nMariah Carey là một trong những diva hàng đầu của làng nhạc thế giới. Trong 37 năm sự nghiệp, cô giành năm giải Grammy và bán hơn 200 triệu bản thu. Cô nổi tiếng với giọng hát năm quãng tám và phong cách biểu diễn, thời trang quyến rũ, có nhiều bản hit kinh điển. Tổ chức Guinness trao cô danh hiệu \"Songbird Supreme\", dành cho những người có chất giọng cao nhất thế giới.', '1744478365_h120.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 17:19:25', 'Giải trí', 0, 0),
(132, 'Tùng Dương hát về tình cha con', 'Ca sĩ Tùng Dương ra ca khúc \"Lời nói dối của cha\", bày tỏ tình cảm với người bố đã thầm lặng hy sinh cho gia đình.', 'Nhạc sĩ Đông Thiên Đức viết bài hát sau khi nghe câu chuyện tuổi thơ của Tùng Dương. Lúc nhỏ, ca sĩ sống xa gia đình hơn mười năm khi bố mẹ anh sang Nga làm việc. Trước khi đi, bố anh dặn: \"Bố mẹ bôn ba thế này cũng chỉ để lo cho tương lai của con\". Mỗi lần nhận thư họ, Tùng Dương đều khóc. \"Tôi từng ấm ức, không hiểu vì sao bố mẹ bỏ mình đi xa như vậy. Khi lớn lên, tôi hiểu và thương họ nhiều hơn\", ca sĩ nói.\r\n\r\nNhạc phẩm có một số câu như: \"Cha nói dối, cha nói cha vui khi con ở xa\", \"Cha nói dối, cha nói con là tuổi xuân của cha. Cha sẽ bên con như Trái Đất tròn, cha không bao giờ già\". Nhạc sĩ Nguyễn Hữu Vượng, người phối khí, dùng tiếng cello để tạo cảm xúc lắng đọng, trầm buồn và đẩy cao trào ở phần cuối bài hát.\r\nTác giả Đông Thiên Đức và Tùng Dương đồng cảm bởi đều đã làm bố, hiểu vai trò và trách nhiệm của một người cha. Hiện ngoài chăm sóc con trai Voi, chín tuổi, Tùng Dương mới nhận nuôi bé Sóc, hai tuổi. Vợ chồng anh biết Sóc thông qua một người bạn trong nhóm thiện nguyện, đón bé từ khi con vài tháng tuổi.\r\nTùng Dương, 42 tuổi, là một trong những giọng ca trưởng thành từ Sao Mai Điểm hẹn 2004. Ca sĩ được giới chuyên môn đánh giá có giọng hát nội lực, phong cách âm nhạc độc đáo. Sau cuộc thi, anh bền bỉ hoạt động nghệ thuật, từng ra sáu album phòng thu, tổ chức 11 liveshow, giành nhiều giải thưởng uy tín. Anh kết hôn với Giang Phạm năm 2010, có con trai Mạc Lam năm 2015.\r\n\r\nCuối năm ngoái, anh gây chú ý với ca khúc Tái sinh, Tăng Duy Tân sáng tác, thắng Bài hát của năm ở giải Cống hiến.\r\n\r\nĐông Thiên Đức, 38 tuổi, là tác giả nhiều ca khúc được khán giả yêu thích như Ai chung tình được mãi, Ngày mai người ta lấy chồng, Một vòng Việt Nam', '1744478449_h121.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 17:20:49', 'Giải trí', 0, 2),
(133, 'Tùng Dương: \'Biết đâu tôi sẽ vừa hát vừa nhảy như Soobin\'', 'Tùng Dương nói sắp học nhảy để có thể vừa hát vừa khoe vũ đạo như các đàn em Soobin, Mono.', 'Ca sĩ nói về công việc, cuộc sống, những dự định với âm nhạc, dịp được Bộ Văn hóa, Thể thao và Du lịch vinh danh là Ca sĩ nổi bật năm 2024.\r\n\r\n- Hầu hết giải thưởng anh đạt từ trước đến nay do giới chuyên môn trao. Nếu tranh tài ở các sự kiện do khán giả bình chọn, anh tự tin mình có bao nhiêu phần trăm chiến thắng?\r\n\r\n- Tôi hạnh phúc, tự hào vì những nỗ lực năm qua được ghi nhận. Tôi ý thức giải thưởng như một sự nhắc nhở phải luôn kiên định sáng tạo, chinh phục những dấu mốc mới và không ngủ quên trên chiến thắng.\r\n\r\nSo với một Tùng Dương cực đoan trước đây, tôi thấy hiện tại mình được công chúng yêu mến, dành nhiều tình cảm hơn. Khi đi diễn, thậm chí ra đường, tôi được các học sinh đến chụp ảnh, xin chữ ký. Tôi nghĩ \"đội quân\" gen Z, gen Alpha rất hùng hậu. Các em thường múa hát Một vòng Việt Nam ở các buổi văn nghệ. Tôi chưa bao giờ nghĩ một Tùng Dương rất \"quái\" sẽ có ngày được như vậy.\r\n\r\nGần đây, khi Tái sinh gây sốt, tôi cũng có thêm một fan qua các nền tảng mạng xã hội. Vậy nên tôi rất tự tin nếu tham gia các giải thưởng cần đến sự bình chọn, bởi được cả khán giả cũ lẫn mới ủng hộ.\r\n- Anh cảm thấy thế nào khi \"Tái sinh\" vang lên ở nhiều nơi, đạt top 1 trending trên YouTube trong ba tuần?\r\n\r\n- Ca khúc là sự kết hợp của hai anh em họ trong nhà, tôi và Tăng Duy Tân, nhưng đã phổ biến \"ngoài ngõ\". Nhiều em nhỏ cho đến người trung niên đều biết. Đây là niềm vui của chúng tôi. Khi thu âm, chúng tôi không đoán trước nhạc phẩm sẽ hot đến vậy. Lúc đó, tôi chỉ nghĩ: \"Bên cạnh những ca khúc gai góc, đây là bài hát dễ chịu, nhẹ nhõm, nhất của Tùng Dương\".\r\n\r\nTái sinh cho tôi nguồn năng lượng tích cực, tươi mới, mở ra cơ hội cộng tác với những nghệ sĩ gen Z. Qua ca khúc, mọi người đều nhận ra giọng hát của tôi không bị giới hạn bởi điều gì, khá phù hợp với nhạc trẻ. Tôi nghĩ âm nhạc của mình đang rút ngắn khoảng cách thế hệ, đó là hướng đi đúng đắn.\r\n\r\n- Anh nghĩ gì trước ý kiến nhờ hợp tác người trẻ như Tăng Duy Tân, anh mới có được sức hút hiện tại?\r\n\r\n- Điều này không sai. Tôi rất trân trọng Tân, một \"hit maker\" thực thụ, nhanh nhạy và thức thời. Vài năm gần đây, mỗi năm cậu ấy đều có ca khúc thành công, từ Bên trên tầng lầu, Cắt đôi nỗi sầu, Cô đơn trên sofa đến Tái sinh. Nhiều người sáng tác lâu năm nhưng không có được cái duyên như vậy. Sự cộng hưởng giữa bài hát của Tăng Duy Tân và giọng hát của Tùng Dương đã tạo ra sức hút. Tân cũng nói với tôi: \"Nếu em hát Tái sinh, có lẽ không thể ảnh hưởng như bác\". Tôi tin mỗi bài hát luôn có số phận của nó.\r\n- Anh nói gì trước nhận định Tùng Dương khôn ngoan, thức thời trong âm nhạc?\r\n\r\n- Tôi chỉ tự nhủ rằng đã đến lúc cần thay đổi bởi tôi cũng chán mình lắm rồi. Tôi nhiều năm sáng tạo những ca khúc khù khoằm, muốn thử thách tai nghe khán giả, cứ mãi như vậy cũng không phải cái hay. Tôi lì lợm và quyết liệt, khao khát bứt phá. Nhưng tôi không bao giờ cố chấp giữ cái \"tôi\" mà luôn biết lắng nghe, khéo léo dung hòa trong sáng tạo nghệ thuật. Tôi cởi mở, suy nghĩ hiện đại, mới mẻ.\r\n\r\n- Gần đây, anh theo dõi hai show âm nhạc được quan tâm - Anh trai vượt ngàn chông gai và Anh trai say hi - thế nào?\r\n\r\n- Tôi nhận được lời mời thi Anh trai vượt ngàn chông gai nhưng không sắp xếp được thời gian. Trong liveshow của tôi, Soobin còn hướng dẫn tôi nhảy. Nhìn cậu ấy di chuyển điệu nghệ, mình cũng tự dưng thèm (cười). Tôi sẽ học nhảy. Biết đâu vài hôm nữa, khán giả sẽ thấy tôi vừa hát vừa nhảy như Soobin hay Mono. Tôi vẫn thấy mình thiếu sót, còn nhiều khía cạnh để khai thác.\r\n\r\nVới tôi, hai show trên quy tụ nhiều nghệ sĩ tài năng, có cả tính nghệ thuật và giải trí, đáp ứng được nhu cầu của khán giả. Không thể phủ nhận là năm qua các \"anh trai\" đã lên ngôi. Tôi cũng phải cạnh tranh với họ (cười). Đó là sự thi đua tích cực, lành mạnh trong nghệ thuật.\r\n- Anh nghĩ sao nếu nói Tùng Dương hát thêm nhạc trẻ \"đắt\" show hơn nhưng cũng mất chất?\r\n\r\n- Nếu không hát nhạc trẻ, khán giả của tôi chỉ có những người cùng thế hệ hoặc lớn tuổi hơn. Nhưng tôi lại muốn chinh phục thêm nhiều đối tượng. Khi làm việc cùng các nghệ sĩ gen Z, họ mang lại cho tôi nguồn năng lượng tích cực, khiến tôi \"thấy mình như đang đôi mươi\".\r\n\r\nGiống chúng tôi trước kia, các em đang ở giai đoạn mộng mơ, có ngôn ngữ và thế giới quan riêng, táo bạo và ngang tàng. Các em quá giỏi khi tự sáng tác, sản xuất, biểu diễn. Kết hợp chung, tôi lắng nghe họ, cởi mở tiếp thu để có những sản phẩm sáng tạo. Mời Ngọt, Bùi Lan Hương, Soobin, Tăng Duy Tân hát trong concert, tôi không ép họ hát hit của mình mà thoải mái \"phiêu\" với nhạc của các em.\r\n\r\nTôi nghĩ rằng Tùng Dương vẫn luôn là một hình ảnh khác biệt, một cá tính mạnh trong âm nhạc, kiên định đi trên \"độc đạo\" của riêng mình. Nhưng điều khác biệt là giờ đây tôi không cảm thấy cô đơn nữa, vì có nhiều bạn đồng hành. Khán giả của mình ngày một đông hơn, đa dạng và đại chúng hơn.\r\n\r\n- Biến chuyển trong âm nhạc của anh liên quan gì với những thay đổi trong cuộc sống?\r\n\r\n- Tâm thế trước cuộc sống quyết định dáng hình âm nhạc, phản ánh cái \"tôi\" ở hiện tại. Thời trẻ, tôi hiếu thắng, thích thể hiện, lại không biết trân trọng sức khỏe, chạy \"sô\" liên tục, từng nhiều lần mất giọng trước liveshow lớn. Ở tuổi trung niên, tôi làm việc bài bản, khoa học hơn.\r\n\r\nVới album Multiverse, tôi hát thảnh thơi, tĩnh tại, không còn gào thét nhiều. Tôi phát hiện ra đỉnh cao của nghệ thuật là sự tinh tế, biết tiết chế. Tôi nghĩ đây cũng là một sự giác ngộ. Ở tuổi 42, tôi vẫn giữ hoài bão, nhưng không còn nhiều áp lực, tham vọng.\r\n- Đến nay, vợ hỗ trợ anh thế nào trong sự nghiệp và cuộc sống?\r\n\r\n- Bạn đời luôn là người hiểu rõ tôi nhất, chia sẻ và đóng góp nhiều ý tưởng trong mỗi dự án. Cô ấy luôn thẳng thắn và công tâm. Tôi cũng thích như vậy, chứ không thể chồng hát vợ khen hay. Cô ấy luôn nói: \"Nếu anh dễ dãi với bản thân thì sớm muộn sẽ lụi tàn. Nếu muốn đi xa trong nghệ thuật, hãy luôn khắt khe với chính mình\".\r\n\r\n- Trút bỏ những bộ cánh lấp lánh trên sân khấu, anh là người đàn ông thế nào trong gia đình?\r\n\r\n- Tôi có trách nhiệm, yêu thương vợ con. Là trụ cột, tôi hiểu vai trò của một người cha, người chồng. Về tới tổ ấm của mình, ngoài những lúc giúp vợ chăm con, dạy con học, tôi vẫn là \"người đàn ông hát\". Tôi hát suốt ngày mà không biết chán, mọi lúc mọi nơi, cho vợ con nghe (cười).\r\n\r\n- Anh có dự định gì trong năm mới?\r\n\r\n- Tôi vẫn muốn làm một concert. Ngoài ra, tôi sẽ phát hành thêm đĩa than và một EP khoảng năm bài. Khán giả sẽ bất ngờ vì năm sau tôi sẽ trẻ trung, cộng tác với những gương mặt mới hơn nữa.\r\n\r\nĐể chuẩn bị sức khỏe, tinh thần cho những dự án dài hơi, tôi sinh hoạt khoa học, tập gym và cardio mỗi ngày. Các hoạt động này giúp tôi ổn định hơi thở, yếu tố quan trọng nhất trong thanh nhạc.\r\n\r\nTùng Dương là một trong những giọng ca trưởng thành từ Sao Mai Điểm hẹn 2004. Nghệ sĩ được giới chuyên môn đánh giá có giọng hát nội lực, phong cách âm nhạc độc đáo. Sau cuộc thi, anh bền bỉ hoạt động nghệ thuật, từng ra sáu album phòng thu, tổ chức 11 liveshow, giành nhiều giải thưởng uy tín. Anh kết hôn với vợ - Giang Phạm - năm 2010, có con trai Mạc Lam năm 2015.', '1744478534_h122.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 17:22:14', 'Giải trí', 0, 1),
(134, '\'Trò chuyện với hoa thủy tiên\' - sứ mệnh của văn chương', 'Cố nhà văn Nguyễn Huy Thiệp từng nhận định văn học Việt Nam đang thiếu tham vọng lớn, trong sách \'\'Trò chuyện với hoa thủy tiên\'\'.', 'Ấn phẩm ra mắt sau hơn ba năm nhà văn qua đời, tập hợp những bài tiểu luận được Nguyễn Huy Thiệp viết trong khoảng thời gian văn đàn diễn ra nhiều cuộc tranh luận về ông (1988-1992). Ngoài ra sách có loạt bài tạp văn ký tên Dương Thị Nhã, đăng trên các báo từ năm 2000 trở đi, những bài giới thiệu bạn bè văn nghệ như nhà thơ Trần Đăng Khoa, nhà nghiên cứu Hoàng Ngọc Hiến và quan điểm của Nguyễn Huy Thiệp về các cây bút trẻ đương thời.\r\n\r\nTrong sách, tác giả bàn nhiều vấn đề trong lý luận, phê bình, sáng tác văn học, như sứ mệnh của tác giả, cái đẹp trong văn chương. Nhà văn không coi những ý kiến của mình là chân lý mà chỉ \'\'nêu ra những suy nghĩ \'nhầm lẫn\' để mọi người trong giới văn học xem xét\'\'.\r\nNguyễn Huy Thiệp nhận định \'\'văn học là khoa học về con người\'\', hướng họ vươn đến những điều tốt đẹp. Ở hoàn cảnh éo le nhất, văn chương đánh thức tất cả tiềm năng trong mỗi người. Ông tin bằng cách riêng, văn học góp phần tiến bộ xã hội. Trong bài Khoảng trống ai lấp được trong tư tưởng nhà văn, ông viết: \'\'Gạt sang bên sự nhếch nhác của hiện thực, nhặt ra những mảnh vụn sang trọng trong tư tưởng nhà văn, cưu mang nó, đấy chính là tiến bộ. Loài người đã nhích từng tí một đến chân lý nhờ những hạt vàng tư tưởng ấy\'\'.\r\n\r\nĐể thực hiện nhiệm vụ ấy, Nguyễn Huy Thiệp cho rằng mỗi cây bút cần \'\'rắc chút muối\'\' trên trang giấy, cảm nhận nỗi đau, sống với số phận nhân vật. Ở bài Nhà văn và bốn trùm Mafia, tác giả khẳng định: \'\'Văn chương hay bởi có \'lời đạt\', \'lời đạt\' là bởi có \'đạo\', \'đạo\' xuất phát từ \'tâm\' tức là trái tim người viết. Đã không viết thì thôi, chứ viết ắt phải có tâm, phải có tấm lòng\'\'.\r\n\r\nTheo Nguyễn Huy Thiệp, nếu muốn tạo nên những tác phẩm mang tư tưởng, tình cảm lớn, người viết phải làm việc nghiêm túc, biết tích lũy chất liệu sáng tác từ thực tế. Điều khiến cho các nhà văn, nhà biên kịch \'\'không nhạt\'\' là họ phải đọc, học hỏi, \'\'tự làm khó mình\'\'. Công việc của các tác giả phải bắt đầu từ nghiên cứu bạn đọc, đúng hơn là tìm hiểu tâm lý dân tộc trong khoảng thời gian dài. Từ đó, họ có thể mang đến những \'\'món ăn tinh thần\'\' cho cả thời đại.\r\n\r\nBằng cách viết thẳng thắn, Nguyễn Huy Thiệp đưa ra nhiều nhận xét về văn học Việt Nam hiện đại như thiếu vắng những tham vọng lớn, mất đi khả năng tưởng tượng, lãng mạn và nhiệt huyết sống. Ông cũng bàn luận việc đào tạo các tác giả, cách tổ chức hội nhóm nhà văn, thái độ của xã hội với văn học, sự đánh giá của cơ chế thị trường đương đại dành cho nghệ thuật.\r\n\r\nQua đó, nhà văn đưa một số ý kiến để nền văn học nước nhà phát triển. Tác giả đề xuất nâng cấp trường Viết văn Nguyễn Du nhằm gần gũi thiên nhiên, gắn bó xã hội hơn, không phải nằm tại không gian chật hẹp giữa thủ đô. Hay ông cho rằng đã đến lúc người ta phải nghĩ đến một thế hệ nhà văn viết có bài bản, đủ lý lẽ chứ không mò mẫm, tự phát.\r\nNguyễn Huy Thiệp sinh năm 1950 tại Thái Nguyên, quê gốc ở Hà Nội, tốt nghiệp khoa Lịch sử, trường Đại học Sư phạm Hà Nội. Ông xuất hiện ở văn đàn Việt Nam từ năm 1986, với một số truyện ngắn đề tài nông thôn, đăng trên báo Văn nghệ. Nhà văn còn viết 10 vở kịch, bốn tiểu thuyết cùng nhiều tiểu luận, phê bình văn học gây chú ý, được xem là \"hiện tượng hiếm\" của văn đàn trong nước.\r\n\r\nÔng từng nhận huân chương Văn học Nghệ thuật Pháp (2007), giải thưởng Premio Nonino (Italy, 2008). Một số tác phẩm nổi bật của Nguyễn Huy Thiệp gồm truyện ngắn Tướng về hưu, chuyển thể thành phim điện ảnh cùng tên năm 1988, Những ngọn gió Hua Tát (tập truyện ngắn và kịch, 1989), Tiểu Long Nữ (tiểu thuyết, 1996), Tuổi 20 yêu dấu (tiểu thuyết, xuất bản ở Pháp năm 2002). Nhà văn mất ngày 20/3/2021 sau thời gian dài tai biến, thọ 71 tuổi.', '1744478603_h123.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-12 17:23:23', 'Giải trí', 0, 2),
(135, 'Miền Bắc nắng nóng diện rộng trong tuần tới', 'Đợt nắng nóng diện rộng đầu tiên ở miền Bắc dự báo xảy ra vào cuối tuần tới, nhiệt độ tại Hà Nội cao nhất lên mức 36 độ C.', 'Từ chiều qua, miền Bắc có mưa, nhiệt độ thấp nhất về đêm và sáng ở mức 17-20 độ, ngày mai 25-28 độ do ảnh hưởng không khí lạnh. Trung tâm Dự báo Khí tượng Thủy văn quốc gia nhận định, thời tiết mát mẻ ở Bắc Bộ duy trì đến hết ngày 16/4.\r\n\r\nTừ 17/4, không khí lạnh suy yếu, vùng áp thấp phía tây có xu hướng phát triển và mở rộng về phía đông khiến cho nền nhiệt miền Bắc tăng nhanh. Dự báo khoảng ngày 18/4 trở đi, Bắc Bộ khả năng xảy ra nắng nóng diện rộng. Đây sẽ là đợt nắng nóng diện rộng đầu tiên trong năm nay thay vì chỉ cục bộ ở Tây Bắc Bộ.\r\nTrang Accuweather của Mỹ dự báo, nhiệt độ Hà Nội đầu tuần 19-27 độ, sau đó tăng dần đến cuối tuần sau ở mức 28-36 độ. Điểm cao trên 1.500 m như Sa Pa (Lào Cai) cũng tăng từ 13-20 độ lên 18-26 độ C vào cuối tuần tới.\r\n\r\nMiền Trung duy trì tình trạng mây nhiều đến hết ngày mai. Từ 16/4, khi không khí lạnh yếu và tan hẳn, vùng áp thấp phía tây hoạt động trở lại, nền nhiệt ở Trung Bộ cũng sẽ tăng nhanh. Từ 17/4, vùng núi phía tây của các tỉnh Trung Bộ xảy ra nắng nóng cục bộ.\r\n\r\nTừ ngày 18/4, nắng nóng mở rộng ra từ Thanh Hóa đến Quảng Ngãi.\r\n\r\nNam Bộ và Tây Nguyên, từ nay đến ngày 16/4 chịu ảnh hưởng của rãnh thấp xích đạo có trục vào khoảng 5-7 độ vĩ bắc nên có mưa rào, giông về chiều tối. Địa bàn Nam Bộ mưa sẽ nhiều hơn Tây Nguyên. Cơ quan khí tượng cảnh báo trong mưa giông có thể xảy ra lốc, mưa đá và gió giật. Do mưa, cả hai khu vực ít xảy ra nắng nóng diện rộng.\r\n\r\nTrong một tháng tới, không khí lạnh hoạt động yếu dần thay vào đó là xu hướng tăng nhiệt khi bước vào mùa hè. Dự báo, nhiệt độ trung bình trên cả nước phổ biến cao hơn trung bình nhiều năm trước 0,5-1 độ C, riêng Bắc Trung Bộ và Nam Bộ xấp xỉ.', '1744545091_h124.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 11:51:31', 'Thời sự', 0, 0),
(136, 'Miền Bắc và Thanh Hóa rét đậm', 'Đợt không khí lạnh cuối mùa đang gây rét đậm diện rộng tại 25 tỉnh thành miền Bắc và tỉnh Thanh Hóa, với nhiệt độ thấp nhất dưới 15 độ C.', 'Ngày 30/3, đỉnh Mẫu Sơn (Lạng Sơn) cao khoảng 1.600 m ghi nhận mức nhiệt thấp nhất cả nước với hơn 3 độ C. Các đỉnh núi khác như Đồng Văn (Hà Giang), Tam Đảo (Vĩnh Phúc) và Sa Pa (Lào Cai) cũng rét buốt với nhiệt độ lần lượt là 7 và 9 độ C.\r\n\r\nTại đồng bằng, Chí Linh (Hải Dương) hơn 11 độ C. Thủ đô Hà Nội cũng chìm trong không khí lạnh, trạm đo tại Ba Vì ghi nhận 12 độ C, các trạm Láng, Hoài Đức, Hà Đông, Sơn Tây đều 13 độ C. Cao nhất ngày 18 độ C, tức trong ngưỡng rét đậm.\r\n\r\nNgoài ra, các huyện Yên Định, Sầm Sơn, Bái Thượng, Như Xuân, Tĩnh Gia của tỉnh Thanh Hóa cũng ghi nhận nhiệt độ dưới 15 độ C. Các tỉnh lân cận như Nghệ An, Hà Tĩnh dao động 15-17 độ, trong khi Quảng Bình và TP Huế ấm hơn, khoảng 17-20 độ C.\r\nTrung tâm Dự báo Khí tượng Thủy văn quốc gia cho biết không khí lạnh sẽ tiếp tục ảnh hưởng đến các tỉnh Trung và Nam Trung Bộ trong ngày và đêm nay. Nhiệt độ thấp nhất ở miền Bắc phổ biến 12-15 độ, vùng núi cao có nơi dưới 10 độ C. Dự kiến nền nhiệt tăng không đáng kể vào ngày 31/3. Khu vực Thanh Hóa - Hà Tĩnh thấp nhất 14-17 độ C, còn Quảng Bình - TP Huế 17-19 độ C.\r\n\r\nDo ảnh hưởng của không khí lạnh, vịnh Bắc Bộ có gió đông bắc mạnh cấp 6, sóng biển cao 1,5-2,5 m. Bắc Biển Đông (bao gồm vùng biển quần đảo Hoàng Sa) có gió đông bắc mạnh cấp 6, riêng vùng biển phía bắc có lúc cấp 7, sóng biển cao 3-5 m. Vùng biển phía tây khu vực giữa Biển Đông và vùng biển Quảng Trị - Quảng Ngãi có gió đông bắc mạnh cấp 5-6, sóng biển cao 2-3 m.\r\n\r\nTrước đó ngày 19/3, đỉnh núi Tà Xùa (Yên Bái) cao hơn 2.600 m bất ngờ xuất hiện băng giá. Đây là lần đầu tiên nơi này có băng trong tháng 3. Dự báo từ nay đến nửa đầu tháng 4, không khí lạnh sẽ tiếp tục hoạt động mạnh hơn trung bình nhiều năm, sau đó mới có xu hướng yếu dần.', '1744545150_h125.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 11:52:30', 'Thời sự', 0, 0),
(137, 'Trung ương thảo luận phương hướng nhân sự Đại hội 14', 'Hà Nội - Trong ngày làm việc thứ hai, hội nghị Trung ương lần thứ 11 thảo luận 7 đề án và báo cáo, trong đó có phương hướng công tác nhân sự Đại hội 14 của Đảng.', 'Theo thông cáo Văn phòng Trung ương Đảng phát chiều 11/4, sáng nay Trung ương làm việc tại hội trường dưới sự điều hành của Thủ tướng Phạm Minh Chính.\r\n\r\nCùng với phương hướng công tác nhân sự Đại hội 14 của Đảng, các đề án và báo cáo được Trung ương thảo luận sáng nay còn có:\r\n\r\nDự thảo Báo cáo chính trị của Ban Chấp hành Trung ương Đảng khóa 13 tại Đại hội đại biểu toàn quốc lần thứ 14 của Đảng.\r\n\r\nDự thảo Báo cáo Tổng kết một số vấn đề lý luận và thực tiễn về công cuộc đổi mới theo định hướng xã hội chủ nghĩa trong 40 năm qua ở Việt Nam.\r\n\r\nDự thảo Báo cáo đánh giá 5 năm thực hiện Chiến lược phát triển kinh tế xã hội 10 năm 2021-2030; phương hướng, nhiệm vụ phát triển kinh tế xã hội 5 năm 2026-2030.\r\n\r\nDự thảo Báo cáo của Ban Chấp hành Trung ương Đảng khóa 13 tổng kết công tác xây dựng Đảng và thi hành Điều lệ Đảng trình Đại hội đại biểu toàn quốc lần thứ 14 của Đảng.\r\n\r\nSửa đổi, bổ sung, thay thế Chỉ thị số 35, ngày 14/6/2024 của Bộ Chính trị về đại hội đảng bộ các cấp tiến tới Đại hội 14 của Đảng.\r\n\r\nĐề án phương hướng bầu cử đại biểu Quốc hội khóa 16 và đại biểu HĐND các cấp nhiệm kỳ 2026-2031.\r\nChiều cùng ngày, Trung ương thảo luận tổ về nhóm vấn đề công tác chuẩn bị Đại hội 14 và bầu cử Quốc hội khóa 16, bầu cử HĐND các cấp nhiệm kỳ 2026-2031.\r\n\r\nSau đó, Bộ Chính trị họp cho ý kiến về tiếp thu, giải trình ý kiến Trung ương đã thảo luận hôm qua và sáng nay, gồm nội dung về tiếp tục sắp xếp tổ chức bộ máy của hệ thống chính trị, sắp xếp đơn vị hành chính và tổ chức chính quyền địa phương 2 cấp. Đó là đề án sắp xếp đơn vị hành chính cấp tỉnh; phương án sắp xếp, tổ chức lại đơn vị hành chính cấp xã; chức năng, nhiệm vụ, quyền hạn, tổ chức bộ máy của chính quyền cấp tỉnh và cấp xã; về hệ thống tổ chức đảng, đoàn thể, TAND, VKSND; về bố trí, sắp xếp cán bộ và thực hiện chế độ, chính sách đối với cán bộ; về quản lý, sử dụng tài sản công sau sắp xếp.\r\n\r\nPhát biểu khai mạc hội nghị Trung ương 11 sáng 10/4, Tổng Bí thư Tô Lâm nhấn mạnh công tác nhân sự là vấn đề then chốt của then chốt để hiện thực hóa các mục tiêu, tầm nhìn của Đại hội 14. Yêu cầu nhiệm vụ cao hơn đòi hỏi mặt bằng đội ngũ cán bộ cao hơn, đặc biệt là cán bộ cấp chiến lược. Công tác nhân sự phải được chuẩn bị từ sớm và sẽ được tiếp tục bổ sung, hoàn thiện cho đến Đại hội 14.\r\n\r\nÔng cho biết Bộ Chính trị đã chỉ đạo rà soát, bổ sung dự thảo phương hướng công tác nhân sự Ban Chấp hành Trung ương khóa 14 phù hợp với cơ cấu của mô hình tổ chức mới; rà soát đề xuất Trung ương bổ sung quy hoạch cán bộ cấp chiến lược; dự thảo sửa đổi, bổ sung một số nội dung Chỉ thị 35 của Bộ Chính trị về tổ chức Đại hội đảng các cấp tiến tới Đại hội 14 của Đảng.\r\n\r\nTheo Tổng Bí thư, tất cả công việc quan trọng này đều hướng đến mục tiêu tổ chức thành công Đại hội Đảng các cấp và tổ chức Đại hội 14 vào quý I/2026 theo đúng kế hoạch đề ra.', '1744545216_h126.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 11:53:36', 'Thời sự', 0, 0);
INSERT INTO `news` (`id`, `title`, `description`, `content`, `image`, `author`, `created_at`, `category`, `is_featured`, `views`) VALUES
(138, 'Tổng Bí thư: Sắp xếp đơn vị hành chính với tầm nhìn ít nhất 100 năm', 'Tổng Bí thư Tô Lâm khẳng định sắp xếp đơn vị hành chính các cấp nhằm xây dựng chính quyền gần dân và mở ra cục diện mới phát triển đất nước với tầm nhìn ít nhất 100 năm.\r\n\r\n', 'Phát biểu khai mạc hội nghị Trung ương 11 khóa 13 sáng 10/4, Tổng Bí thư Tô Lâm cho biết hội nghị diễn ra sớm hơn một tháng so với kế hoạch ban đầu. Do khối lượng công việc cần giải quyết lớn, phạm vi rộng, nhiều nội dung nhạy cảm liên quan đến \"quốc kế dân sinh\", các tờ trình sẽ không được trình bày lại tại hội nghị để dành thời gian cho các đại biểu nghiên cứu, đóng góp ý kiến.\r\n\r\nVề nhóm vấn đề tiếp tục sắp xếp tổ chức bộ máy hệ thống chính trị, sắp xếp đơn vị hành chính và tổ chức chính quyền địa phương 2 cấp, Tổng Bí thư cho biết trong hơn 4 tháng qua, thực hiện các kết luận của Trung ương, Bộ Chính trị, Ban Bí thư đã chỉ đạo cơ bản hoàn thành việc sắp xếp, tinh gọn tổ chức bộ máy các cơ quan Đảng, Chính phủ, Quốc hội, Mặt trận ở cấp Trung ương.\r\n\r\nNhững con số về tinh giảm đầu mối, hiệu quả công việc, tiết kiệm chi phí đã \"thể hiện rõ tính cách mạng\" trong việc tinh gọn này. Dù vậy, mô hình tổ chức bộ máy của hệ thống chính trị chưa thật hoàn thiện, nhất là ở địa phương. Vì vậy, để tiếp tục cuộc cách mạng tinh giản tổ chức bộ máy, Bộ Chính trị, Ban Bí thư đã họp nhiều phiên, \"bàn bạc thấu đáo nhiều khía cạnh và thống nhất cao\" trình Trung ương Đề án sắp xếp, tổ chức lại đơn vị hành chính các cấp và xây dựng mô hình tổ chức chính quyền địa phương 2 cấp.\r\n\r\nBộ Chính trị và Ban Bí thư cũng thống nhất trình Trung ương Đề án về hệ thống tổ chức Đảng ở địa phương; Đề án về sắp xếp, tinh gọn cơ quan Mặt trận Tổ quốc, các tổ chức chính trị - xã hội, các hội quần chúng được Đảng và Nhà nước giao nhiệm vụ; các Đề án về sắp xếp các cơ quan Tòa án, Viện Kiểm sát; và các Đề án về sửa đổi, bổ sung Hiến pháp, pháp luật của Nhà nước, sửa đổi, bổ sung các quy định thi hành điều lệ Đảng, các quy định về kiểm tra, giám sát Đảng để bảo đảm triển khai đồng bộ.\r\n\r\nNgười đứng đầu Đảng đánh giá đề án về sắp xếp, tổ chức lại đơn vị hành chính các cấp và xây dựng mô hình tổ chức chính quyền địa phương 2 cấp cùng với các Đề án kèm theo là những vấn đề \"rất hệ trọng, có tính lịch sử\". Đây không chỉ sắp xếp về tổ chức, bộ máy, cán bộ; mà còn phân cấp về thẩm quyền; bố trí lại đơn vị hành chính; phân bổ về nguồn lực; tạo không gian phát triển. Mục tiêu là xây dựng Chính quyền theo hướng gần dân, sát dân, phục vụ nhân dân tốt hơn; đồng thời, mở ra cục diện mới trong phát triển đất nước với tầm nhìn lâu dài, ít nhất là cho 100 năm tới.\r\n\r\nTổng Bí thư thông báo về kế hoạch tái cấu trúc hành chính quy mô lớn, dự kiến giảm số tỉnh, thành xuống 34; không tổ chức cấp huyện và giảm một nửa đơn vị hành chính cấp xã, áp dụng mô hình chính quyền địa phương hai cấp từ 1/7. Song song đó, hệ thống Tòa án và Viện Kiểm sát sẽ được tổ chức lại theo mô hình ba cấp, đi kèm điều chỉnh thẩm quyền và sửa đổi Hiến pháp cùng các quy định liên quan.\r\nPhương hướng nhân sự khóa 14 phù hợp với mô hình tổ chức mới\r\n\r\nVề nhóm vấn đề tiếp tục chuẩn bị cho Đại hội 14 của Đảng và Bầu cử đại biểu Quốc hội khóa 16, đại biểu HĐND các cấp nhiệm kỳ 2026-2031, Tổng Bí thư cho biết từ sau Hội nghị Trung ương 10, Bộ Chính trị đã quyết liệt chỉ đạo chuẩn bị các công việc, trọng tâm là hai nhóm vấn đề văn kiện và nhân sự.\r\n\r\nVề văn kiện, dự thảo tóm tắt đã được gửi lấy ý kiến cấp cơ sở. Bộ Chính trị cũng chỉ đạo bổ sung nhiều nội dung mới, mang tính đột phá. Văn kiện được xây dựng theo phương châm Báo cáo chính trị là \"ngọn đuốc soi đường\", các báo cáo khác là \"cẩm nang hành động\".\r\n\r\nNội dung xuyên suốt của các văn kiện thể hiện sự kiên trì không thay đổi con đường xã hội chủ nghĩa; giải quyết tốt mối quan hệ biện chứng giữa ổn định và phát triển, bám sát mục tiêu lớn là \"đáp ứng ngày càng tốt hơn nhu cầu của nhân dân\"; quyết tâm hiện thực hóa 2 mục tiêu 100 năm của đất nước.\r\n\r\nTổng Bí thư cũng bày tỏ mong muốn được nghe kiến nghị, giải pháp để Việt Nam đạt mục tiêu tăng trưởng GDP từ 8% trở lên trong năm 2025 và tăng trưởng liên tục hai con số những năm tiếp theo. Bối cảnh là cả nước tiến hành cuộc cách mạng về tổ chức bộ máy, sắp xếp lại đơn vị hành chính các cấp cũng như trong tình hình chiến tranh thương mại trên phạm vi toàn cầu.\r\nVề nhân sự, Tổng Bí thư nhấn mạnh đây là vấn đề then chốt của then chốt để hiện thực hóa các mục tiêu, tầm nhìn của Đại hội 14. Yêu cầu nhiệm vụ cao hơn đòi hỏi mặt bằng đội ngũ cán bộ cao hơn, đặc biệt là cán bộ cấp chiến lược. Công tác nhân sự phải được chuẩn bị từ sớm và sẽ được tiếp tục bổ sung, hoàn thiện cho đến Đại hội 14.\r\n\r\nÔng cho biết Bộ Chính trị đã chỉ đạo rà soát, bổ sung dự thảo Phương hướng công tác nhân sự Ban Chấp hành Trung ương khóa 14 phù hợp với cơ cấu của mô hình tổ chức mới; rà soát đề xuất Trung ương bổ sung quy hoạch cán bộ cấp chiến lược; dự thảo sửa đổi, bổ sung một số nội dung Chỉ thị 35 của Bộ Chính trị về tổ chức Đại hội đảng các cấp tiến tới Đại hội 14 của Đảng.\r\n\r\nTheo Tổng Bí thư, tất cả công việc quan trọng này đều hướng đến mục tiêu tổ chức thành công Đại hội Đảng các cấp và tổ chức Đại hội 14 vào quý I/2026 theo đúng kế hoạch đề ra.\r\n\r\nGóp phần đưa Nghị quyết Đại hội 14 đi ngay vào cuộc sống, Bộ Chính trị trình Trung ương Đề án về \"Phương hướng Bầu cử đại biểu Quốc hội khóa 16, đại biểu HĐND các cấp nhiệm kỳ 2026-2031\", với chủ trương là tổ chức bầu cử sớm hơn và cải cách, đổi mới, nâng cao hiệu quả hoạt động của Quốc hội và HĐND. Mục tiêu là góp phần phát huy đầy đủ quyền làm chủ của nhân dân đối với công việc của đất nước.\r\n\r\nTheo chương trình dự kiến, Trung ương sẽ họp trong ba ngày để thảo luận, cho ý kiến đối với 15 nội dung, tập trung là hai nhóm vấn đề chính. Đó là nhóm vấn đề tiếp tục sắp xếp tổ chức bộ máy của hệ thống chính trị, sắp xếp đơn vị hành chính và tổ chức chính quyền địa phương 2 cấp; và tiếp tục chuẩn bị cho Đại hội 14 của Đảng và Bầu cử đại biểu Quốc hội khóa 16, đại biểu Hội đồng nhân dân các cấp nhiệm kỳ 2026-2031.\r\n\r\nBộ Chính trị cũng sẽ báo cáo Trung ương xem xét, quyết định một số nội dung công tác cán bộ theo thẩm quyền; báo cáo Trung ương về tình hình đất nước; tình hình thế giới, khu vực; những công việc quan trọng Bộ Chính trị, Ban Bí thư đã giải quyết từ sau Hội nghị Trung ương 10 đến nay và các chuyên đề về hoàn thiện thể chế và đột phá phát triển khoa học, công nghệ, đổi mới sáng tạo, chuyển đổi số quốc gia.', '1744545324_h127.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 11:55:24', 'Thời sự', 0, 0),
(139, 'Khai mạc Hội nghị Trung ương 11 khóa 13', 'Hà Nội - Hội nghị Trung ương lần thứ 11 khóa 13 khai mạc sáng 10/4, dự kiến kéo dài ba ngày để thảo luận hai nhóm nội dung về sắp xếp đơn vị hành chính và chuẩn bị đại hội 14.', 'Hội nghị diễn ra tại trụ sở Trung ương Đảng. Tổng Bí thư Tô Lâm phát biểu khai mạc và Chủ tịch nước Lương Cường điều hành phiên khai mạc.\r\nHội nghị Trung ương lần này sẽ tập trung thảo luận và cho ý kiến về 15 nội dung, xoay quanh hai nhóm vấn đề chính. Nhóm thứ nhất tập trung vào việc tiếp tục sắp xếp tổ chức bộ máy của hệ thống chính trị, sắp xếp đơn vị hành chính và tổ chức chính quyền địa phương hai cấp. Nhóm thứ hai liên quan đến việc tiếp tục chuẩn bị cho Đại hội lần thứ 14 của Đảng và công tác bầu cử đại biểu Quốc hội khóa 16, đại biểu HĐND các cấp nhiệm kỳ 2026-2031.\r\n\r\nCũng tại hội nghị này, Bộ Chính trị sẽ báo cáo Trung ương xem xét và quyết định một số nội dung quan trọng về công tác cán bộ theo thẩm quyền. Trung ương cũng sẽ được báo cáo về tình hình đất nước, thế giới và khu vực, cũng như những công việc quan trọng mà Bộ Chính trị, Ban Bí thư đã giải quyết từ sau Hội nghị Trung ương 10 đến nay và các chuyên đề về hoàn thiện thể chế, đột phá phát triển khoa học, công nghệ, đổi mới sáng tạo và chuyển đổi số quốc gia.\r\n\r\nTheo chương trình làm việc, Hội nghị Trung ương 11 khóa 13 dự kiến kéo dài đến ngày 12/4.\r\nTrước đó ngày 28/3 tại Đà Nẵng, Tổng Bí thư Tô Lâm thông tin về việc Trung ương sẽ họp và tính toán các phương án sắp xếp tinh gọn bộ máy vào đầu tháng 4. Theo dự kiến, sau quá trình sắp xếp, cả nước sẽ còn khoảng 34 tỉnh, thành phố so với 63 tỉnh, thành hiện tại; không còn đơn vị hành chính cấp huyện; và sáp nhập còn khoảng 5.000 đơn vị hành chính cấp xã.', '1744545385_h128.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 11:56:25', 'Thời sự', 0, 0),
(140, 'Lập đề án sáp nhập tỉnh, xã trước 1/5', 'Chính phủ giao UBND các tỉnh, thành lập hồ sơ đề án sắp xếp đơn vị hành chính cấp tỉnh và xã trước 1/5 để gửi Ủy ban Thường vụ Quốc hội và Quốc hội.', 'Ngày 7/4, Chính phủ ban hành nghị quyết về kế hoạch thực hiện sắp xếp đơn vị hành chính và xây dựng mô hình tổ chức chính quyền địa phương hai cấp.\r\n\r\nTheo kế hoạch, Bộ Nội vụ sẽ thẩm định và lập hồ sơ đề án sáp nhập xã, phường để trình Chính phủ trước ngày 30/5, sau đó Chính phủ sẽ trình Ủy ban Thường vụ Quốc hội xem xét. Cùng thời điểm, Bộ Nội vụ cũng hoàn tất hồ sơ đề án sáp nhập đơn vị hành chính cấp tỉnh để trình Chính phủ, trước khi trình Quốc hội. Dự kiến, Ủy ban Pháp luật và Tư pháp sẽ thẩm tra và Quốc hội sẽ xem xét, thông qua đề án này trước ngày 20/6.\r\n\r\nĐể đảm bảo tiến độ, Bộ Nội vụ sẽ tổ chức hội nghị toàn quốc trước ngày 18/4 nhằm triển khai kế hoạch đến các bộ, ngành trung ương và địa phương, ngay sau hội nghị quán triệt của Bộ Chính trị. Công tác tổng kết việc sắp xếp đơn vị hành chính các cấp dự kiến hoàn thành trước ngày 20/9.\r\n\r\nBộ Nội vụ đang gấp rút trình Chính phủ để trình Quốc hội ban hành Luật mới thay thế Luật Tổ chức chính quyền địa phương năm 2025 và Luật Cán bộ, Công chức năm 2008 (sửa đổi, bổ sung năm 2019), thời hạn hoàn thành trước ngày 30/4. Song song đó, Bộ cũng tham mưu, trình Chính phủ để trình Ủy ban Thường vụ Quốc hội dự thảo Nghị quyết về việc sắp xếp đơn vị hành chính, dự kiến hoàn thành trước ngày hôm nay, 8/4.\r\n\r\nNgoài ra, một loạt nghị định quan trọng liên quan đến số phó chủ tịch UBND các cấp, quy trình bầu cử, miễn nhiệm, bãi nhiệm, điều động, cách chức thành viên UBND, giao quyền chủ tịch UBND; tổ chức cơ quan chuyên môn cấp xã, phường, đặc khu; vị trí việc làm và biên chế; phụ cấp chức vụ lãnh đạo; chế độ tiền lương cho cán bộ, công chức, viên chức và lực lượng vũ trang cũng đang được khẩn trương xây dựng để hoàn thành trước 30/6.\r\nTrước 10/4, nhiều bộ ngành phải hoàn thành nhiệm vụ được giao để đảm bảo quá trình sáp nhập diễn ra đồng bộ. Theo đó, Bộ Nội vụ sẽ tham mưu Ban Chỉ đạo của Chính phủ chỉ đạo Bộ Tài chính, Ngân hàng Nhà nước Việt Nam và các cơ quan liên quan nghiên cứu phương án sắp xếp các cơ quan thuế, kho bạc nhà nước, hải quan, bảo hiểm xã hội, thống kê, ngân hàng... phù hợp với việc sáp nhập cấp tỉnh và mô hình chính quyền địa phương hai cấp.\r\n\r\nBộ Tư pháp có trách nhiệm ban hành hướng dẫn xử lý các vấn đề phát sinh trong lĩnh vực hộ tịch và đăng ký biện pháp bảo đảm. Bộ Nông nghiệp và Môi trường sẽ hướng dẫn chỉnh lý hồ sơ địa chính, giấy chứng nhận quyền sử dụng đất và cơ sở dữ liệu đất đai.\r\n\r\nBộ Công an được giao nhiệm vụ hướng dẫn về thu hồi, khắc mới con dấu và thay đổi địa chỉ cư trú trên cơ sở dữ liệu quốc gia về dân cư, căn cước công dân và định danh điện tử. Bộ Quốc phòng sẽ hướng dẫn xác định đơn vị hành chính khu vực biên giới, trọng điểm quốc phòng, xây dựng khu vực phòng thủ quân sự và lực lượng dân quân tự vệ.\r\n\r\nBộ Xây dựng có trách nhiệm hướng dẫn đánh số và gắn biển số nhà, công trình xây dựng. Bộ Tài chính hướng dẫn thực hiện vốn đầu tư công và thay đổi thông tin đăng ký doanh nghiệp, hợp tác xã, hộ kinh doanh, giấy phép kinh doanh. Bộ Khoa học và Công nghệ hướng dẫn về chính quyền điện tử, chính quyền số và xây dựng hệ thống dữ liệu kết nối giữa chính quyền địa phương các cấp với trung ương.\r\n\r\nChính phủ cũng yêu cầu các bộ ngành trước 30/6 rà soát quyền hạn của cấp huyện và nội dung có thể phân cấp cho cấp tỉnh, xã.\r\n\r\nNgày 28/3 tại Đà Nẵng, Tổng Bí thư Tô Lâm đã thông tin việc Trung ương sẽ họp và tính toán các phương án sắp xếp tinh gọn bộ máy vào đầu tháng 4. Theo dự kiến ban đầu, cả nước sẽ còn khoảng 34 tỉnh, thành phố sau khi sắp xếp từ 63 tỉnh, thành hiện tại; không còn đơn vị hành chính cấp huyện; và sáp nhập còn khoảng 5.000 đơn vị hành chính cấp xã.', '1744545456_h129.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 11:57:36', 'Thời sự', 0, 0),
(141, 'Phó thủ tướng: Nhiều tỉnh miền núi sẽ có biển, tỉnh miền biển có núi', 'Phó thủ tướng Nguyễn Hòa Bình cho biết sau sáp nhập, nhiều tỉnh miền núi sẽ có biển và tỉnh miền biển sẽ có núi để phát huy mạnh mẽ tiềm năng các địa phương.', 'Làm việc với lãnh đạo tỉnh Quảng Ninh chiều 2/4, Phó thủ tướng thường trực Nguyễn Hòa Bình cho biết công tác tinh gọn và sắp xếp lại bộ máy hướng tới ba mục tiêu lớn. Thứ nhất là xây dựng được bộ máy hành chính có cơ cấu khoa học, hợp lý, tinh gọn, hoạt động hiệu lực, hiệu quả; đội ngũ cán bộ có năng lực, đủ tâm, đủ tầm đảm nhận các nhiệm vụ công tác.\r\n\r\nMục tiêu thứ hai là mở rộng không gian phát triển cho các địa phương, phát huy các tiềm năng, thế mạnh cho các địa phương.\r\n\r\nCuối cùng là có sự phân cấp, phân quyền mạnh mẽ trong hệ thống các cơ quan. \"Qua sắp xếp, nhiều tỉnh miền núi sẽ có biển và tỉnh miền biển sẽ có núi, thế mạnh của các địa phương sẽ được phát huy mạnh mẽ, đủ tiềm năng phát triển\", ông Bình nói.\r\n\r\nVới những kết quả đạt được trong triển khai nghị quyết 18 thời gian qua, Phó thủ tướng đề nghị tỉnh Quảng Ninh chủ động thực hiện các nhiệm vụ theo hướng dẫn của Trung ương. Tinh thần là \"vừa chạy vừa xếp hàng\" trong công tác tinh gọn, sắp xếp bộ máy. Địa phương đồng thời tập trung xây dựng dự thảo văn kiện của đại hội cấp xã, tỉnh sát với thực tiễn.\r\nCuộc cách mạng tinh gọn bộ máy đang được triển khai gấp rút trên toàn quốc. Tuy nhiên, phương án cụ thể sáp nhập các tỉnh thành chưa được công bố.\r\n\r\nTheo dự thảo Nghị quyết của Ủy ban Thường vụ Quốc hội do Bộ Nội vụ chủ trì soạn thảo, 11 đơn vị hành chính cấp tỉnh dự kiến giữ nguyên hiện trạng, bao gồm Hà Nội, Huế, Lai Châu, Điện Biên, Sơn La, Cao Bằng, Lạng Sơn, Quảng Ninh, Thanh Hóa, Nghệ An và Hà Tĩnh. 52 địa phương còn lại, gồm cả 4 thành phố trực thuộc Trung ương là TP HCM, Hải Phòng, Đà Nẵng và Cần Thơ, thuộc diện phải sắp xếp.\r\n\r\nTổng Bí thư Tô Lâm trước đó cho biết dự kiến cả nước còn khoảng 34 tỉnh, thành phố sau khi sắp xếp 63 tỉnh, thành hiện tại; không còn cấp huyện; và cả nước còn khoảng 5.000 đơn vị hành chính cấp xã.\r\n\r\nViệt Nam hiện có 28 tỉnh, thành phố giáp biển, trải dọc theo bờ biển dài hơn 3.260 km, kéo dài từ Quảng Ninh đến Kiên Giang. Các tỉnh, thành giáp biển gồm: Quảng Ninh, Hải Phòng, Thái Bình, Nam Định, Ninh Bình, Thanh Hóa, Nghệ An, Hà Tĩnh, Quảng Bình, Quảng Trị, Thừa Thiên Huế, Đà Nẵng, Quảng Nam, Quảng Ngãi, Bình Định, Phú Yên, Khánh Hòa, Ninh Thuận, Bình Thuận, Bà Rịa - Vũng Tàu, TP HCM, Tiền Giang, Bến Tre, Trà Vinh, Sóc Trăng, Bạc Liêu, Cà Mau và Kiên Giang.', '1744545516_h130.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 11:58:36', 'Thời sự', 0, 1),
(142, 'Sáp nhập tỉnh thành nên dựa vào nguyên tắc nào?', 'Chuyên gia đề xuất nguyên tắc sáp nhập tỉnh thành bao gồm: Tăng tối đa số địa phương có biển, tăng cường sức mạnh kinh tế và đảm bảo quốc phòng an ninh.', 'Cả nước đang triển khai chủ trương sáp nhập tỉnh thành - một phần của quá trình tinh gọn bộ máy hành chính với mục tiêu giảm từ 63 đơn vị hành chính cấp tỉnh xuống còn khoảng một nửa.\r\n\r\nKết luận 127 của Bộ Chính trị, Ban Bí thư đã nêu việc sáp nhập không chỉ dựa trên các yếu tố truyền thống như quy mô dân số và diện tích mà cần có nghiên cứu kỹ lưỡng về quy hoạch tổng thể quốc gia, quy hoạch vùng, quy hoạch địa phương, chiến lược phát triển kinh tế xã hội và các ngành kinh tế. Một mục tiêu quan trọng được nhấn mạnh là việc sáp nhập phải hướng tới mở rộng không gian phát triển cho các địa phương và quốc gia.\r\n\r\nCác tiêu chí cụ thể hơn đã được Thủ tướng Phạm Minh Chính công bố vào ngày 5/3, bao gồm diện tích tự nhiên, quy mô dân số, tình hình phát triển kinh tế, đặc trưng văn hóa và khả năng bổ sung, hỗ trợ lẫn nhau giữa các địa phương dự kiến sáp nhập.\r\n\r\nBộ trưởng Nội vụ Phạm Thị Thanh Trà ngày 11/3 cũng nhấn mạnh việc sắp xếp đơn vị hành chính không phải là giải pháp tình thế hay thay đổi ngắn hạn mà là bước đi chiến lược nhằm mở rộng không gian để phát triển ổn định trong hàng trăm năm tới.\r\n\r\nSự chú trọng vào yếu tố kinh tế tiếp tục được thể hiện trong phát biểu của Tổng Bí thư Tô Lâm tại buổi làm việc ngày 17/3 với Tiểu ban Kinh tế - Xã hội Đại hội 14 của Đảng. Ông khẳng định việc sắp xếp tỉnh thành không chỉ đơn thuần là thay đổi về mặt địa lý hành chính mà còn là sự điều chỉnh sâu sắc đối với không gian kinh tế và việc phân bổ nguồn lực.\r\n\r\nTăng tối đa số tỉnh có biển\r\n\r\nĐại biểu Quốc hội Nguyễn Chu Hồi, Phó chủ tịch thường trực Hội Thủy sản Việt Nam, cho rằng giảm số tỉnh từ 63 xuống còn hơn 30 là bước đi cần thiết và hợp lý trong bối cảnh phát triển hiện nay của Việt Nam. Nguyên tắc bao trùm cho quá trình sắp xếp là tạo động lực phát triển mạnh mẽ và bền vững cho địa phương.\r\nViệc sáp nhập tỉnh thành sẽ tạo ra không gian và dư địa phát triển mới, đồng thời giải quyết nhiều vấn đề tồn tại trong liên kết vùng và sự liên thông tự nhiên giữa các khu vực. Một trong những mục tiêu quan trọng của việc sáp nhập là phải xóa bỏ được thực trạng phân mảnh lãnh thổ tự nhiên bởi đây chính là nền tảng vững chắc để duy trì sự phát triển bền vững về kinh tế và xã hội.\r\n\r\nÔng dẫn chứng cụ thể trường hợp vịnh Cam Ranh, khu vực có tiềm năng to lớn nhưng hiện thuộc địa phận của hai tỉnh Khánh Hòa và Ninh Thuận, gây ra những thách thức không nhỏ trong quản lý và khai thác hiệu quả nguồn lực. Vì vậy, bên cạnh các tiêu chí truyền thống về điều kiện tự nhiên và quy mô dân số theo quy định của Ủy ban Thường vụ Quốc hội, nguyên tắc sáp nhập tỉnh thành lần này cần được tiếp cận dựa trên tư duy tổ chức lại lãnh thổ. \"Đây là cuộc cách mạng tổ chức lại lãnh thổ quốc gia, tạo điều kiện cho đất nước phát triển nhanh, bền vững\", ông nói.\r\n\r\nViệt Nam có tiềm năng biển rất lớn với bờ biển dài và vùng đặc quyền kinh tế rộng, bên cạnh phần diện tích đất liền với đa dạng địa hình núi rừng và đồng bằng, ven biển. Ông Hồi cho rằng sự tương hỗ giữa các vùng biển, đồng bằng và núi rừng này cần được khai thác trong quá trình sáp nhập tỉnh thành, nhằm tạo \"thế và lực mới\" cho sự phát triển toàn diện của các địa phương.\r\n\r\nNhìn theo chiều dọc lãnh thổ, Việt Nam trải dài trên 13 vĩ tuyến và sở hữu bờ biển dài hơn 3.260 km. Hình thái lãnh thổ với \"mặt tiền\" hướng ra biển mang lại nhiều cơ hội phát triển, tạo lợi thế trong quá trình hội nhập quốc tế. Tuy nhiên, đặc điểm này cũng đặt ra những thách thức không nhỏ về an ninh quốc phòng, đòi hỏi phải có sự tính toán kỹ lưỡng về sự liên kết và hỗ trợ lẫn nhau giữa đất liền và vùng biển.\r\n\r\nDải ven biển Việt Nam được xác định là vùng kinh tế động lực, không chỉ đóng vai trò là hậu phương vững chắc cho việc vươn ra biển khơi, mở rộng không gian kinh tế biển đảo mà còn có tác động lan tỏa sâu rộng vào khu vực nội địa. Cả vùng núi rừng sẽ được đánh thức tiềm năng phát triển khi kinh tế biển phát triển mạnh mẽ.\r\n\r\nThấm nhuần quan niệm \"trăm sông đổ về biển cả\", ông Nguyễn Chu Hồi đề xuất nguyên tắc then chốt cho sáp nhập tỉnh thành là ưu tiên tối đa việc hình thành các tỉnh có biển, đồng thời thiết lập mối liên kết chiến lược giữa biển và đất liền - tỉnh biển sáp nhập với tỉnh núi rừng hoặc tỉnh duyên hải với nội địa. Đây được xem là chìa khóa để khai thác triệt để sức mạnh biển, mở ra kỷ nguyên phát triển cân bằng và bền vững cho Việt Nam.\r\nĐồng quan điểm, ông Trần Ngọc Chính, Chủ tịch Hội Quy hoạch và Phát triển đô thị Việt Nam, chỉ rõ Việt Nam là quốc gia biển với 28 tỉnh thành có biển, cùng nhiều vùng núi rừng, đồng bằng. \"Vậy cần sáp nhập sao cho tỉnh thành sau sắp xếp phải có biển, trừ các tỉnh quá sâu trong đất liền\", ông đề xuất, cho rằng điều này mở ra không gian phát triển mới, tạo sự tự chủ trong hoạt động xuất nhập khẩu qua đường biển cho các địa phương.\r\n\r\nTheo ông với nguyên tắc này, Hải Phòng có thể kết hợp với Hải Dương, Hưng Yên với Thái Bình để hình thành các tỉnh mới có bờ biển. Hoặc các tỉnh miền núi như Kon Tum sáp nhập với Quảng Ngãi, Gia Lai với Bình Định, Lâm Đồng với Bình Thuận sẽ giúp các tỉnh Tây Nguyên từ trước đến nay chỉ có núi rừng vươn được ra biển. Hàng loạt tỉnh trước đây không có \"lợi thế biển\" nay được kết nối với biển sẽ tạo nên sức mạnh mới, đánh thức tiềm năng vốn có của vùng miền.\r\n\r\nÔng Chính cũng gợi ý việc TP HCM có thể sáp nhập với Bình Dương và Bà Rịa - Vũng Tàu để có thêm không gian phát triển với hệ thống cảng biển phong phú. Đây là phương án tốt nhất vì đơn vị hành chính mới sẽ hội tụ cả biển, núi rừng và trung du.\r\n\r\n\"Cần tính đến nhập tỉnh có núi với tỉnh có biển để hài hòa các lợi thế trong phát triển kinh tế, du lịch và văn hóa\", thiếu tướng Trần Đức Thuận, chuyên trách Ủy ban Quốc phòng - An ninh và Đối ngoại của Quốc hội, góp ý.\r\n\r\nTạo sức mạnh kinh tế mới\r\n\r\nChuyên gia kinh tế Phạm Chi Lan nhấn mạnh mục tiêu then chốt của việc sáp nhập tỉnh thành là kiến tạo sức mạnh kinh tế mới cho cả địa phương và quốc gia. Do đó cấp có thẩm quyền cần tính toán để chủ trương sáp nhập tỉnh thành mang lại hiệu quả kinh tế, đồng thời bổ sung nguồn lực và lợi thế giữa các địa phương.\r\n\r\nTheo bà, Đồng bằng sông Cửu Long với 13 tỉnh là \"quá nhỏ và manh mún\", gây trở ngại cho các dự án quy mô lớn như xây dựng đê chống lũ. Do đó, việc sáp nhập các tỉnh trong khu vực này cần hướng đến phát triển nông nghiệp hàng hóa tập trung, quy mô lớn.\r\n\r\nĐối với các đô thị lớn như TP HCM, Hà Nội, Hải Phòng, Đà Nẵng, bà Chi Lan đề xuất nguyên tắc sáp nhập tập trung vào việc mở rộng động lực tăng trưởng kinh tế cho cả nước. Việc sáp nhập các đô thị không đơn thuần tạo ra một đô thị lớn hơn về diện tích và dân số mà phải hình thành một mô hình tăng trưởng mới dựa trên chất lượng với các ngành kinh tế mới và giá trị gia tăng cao. \"Đô thị mới này phải đóng vai trò là đầu tàu, thúc đẩy sự phát triển của các vùng lân cận\", chuyên gia Phạm Chi Lan hiến kế.\r\nCác đô thị lớn sau sáp nhập cần chuyển đổi mô hình phát triển, từ bỏ các ngành gia công thâm dụng lao động giá rẻ từ nông thôn, miền núi để tập trung vào các ngành công nghệ và dịch vụ có giá trị gia tăng cao. Mục tiêu là xây dựng được các doanh nghiệp lớn mạnh, có khả năng tham gia sâu vào chuỗi cung ứng toàn cầu.\r\n\r\nBà Lan phân tích với quy mô hiện tại, TP HCM là đô thị lớn nhất cả nước về diện tích, dân số và kinh tế, nếu được sáp nhập thêm Bình Dương và Bà Rịa - Vũng Tàu sẽ tạo thành một chuỗi kinh tế biển và khu công nghiệp hùng mạnh. Trong kịch bản đó, TP HCM sẽ vươn lên trở thành siêu đô thị với tiềm lực kinh tế tương đương Thượng Hải (Trung Quốc) hoặc Singapore.\r\n\r\n\"Nếu TP HCM mở rộng và học hỏi được các mô hình phát triển tiên tiến, thành phố sẽ đủ sức trở thành đầu tàu dẫn dắt nền kinh tế Việt Nam phát triển\", bà khẳng định.\r\n\r\nPGS Nguyễn Chu Hồi thì đề xuất tối ưu hóa việc sử dụng lãnh thổ. Việc gắn kết tỉnh ven biển với địa phương lưu vực sông tạo liên kết vùng tự nhiên, mở ra không gian phát triển \"mở và tự do\". Ví dụ, nếu TP HCM sáp nhập Bình Dương và Bà Rịa - Vũng Tàu thì phải cân nhắc lại quy hoạch cảng Cần Giờ. Cần Giờ có thể trở thành đô thị du lịch thông minh với cảng biển quốc tế, bảo tồn khu dự trữ sinh quyển. Tương tự, nếu Khánh Hòa và Ninh Thuận sáp nhập, vịnh Cam Ranh sẽ do một đầu mối quản lý, thuận lợi cho phát triển kinh tế, bảo tồn và an ninh quốc phòng.\r\n\r\nÔng Trần Ngọc Chính thêm rằng sáp nhập cần tận dụng tối đa hạ tầng sẵn có để bổ sung cho nhau; trong đó tính toán để ít nhất mỗi tỉnh có một sân bay hoặc cảng biển, tạo điều kiện giao thương, đi lại trong nước và quốc tế. Hệ thống cao tốc cũng cần tăng cường liên kết nội tỉnh và liên vùng.\r\nĐảm bảo quốc phòng an ninh\r\n\r\nMặc dù đồng tình với các tiêu chí chung về kinh tế, lãnh thổ, văn hóa, thiếu tướng Trần Đức Thuận nhấn mạnh sự cần thiết phải \"tính toán thận trọng\" yếu tố an ninh quốc phòng khi tiến hành sáp nhập tỉnh thành, đặc biệt ở các địa bàn chiến lược như biên giới, hải đảo, vùng sâu.\r\n\r\n\"Những khu vực này cần có tiêu chí sắp xếp đặc biệt, không thể đơn thuần theo quy mô dân số và diện tích vì liên quan đến khả năng phòng thủ và đảm bảo an ninh\", ông nói, giải thích rằng các tiêu chí kinh tế và dân số có thể ưu tiên sự tập trung và hiệu quả quy mô lớn, nhưng yếu tố an ninh đòi hỏi sự hiện diện và khả năng quản lý sâu sát tại các vùng nhạy cảm.\r\n\r\nÔng Thuận phân tích một số tỉnh biên giới có thể dân số dưới một triệu, nhưng lại đóng vai trò \"phên giậu\" quốc gia nên cần chính sách đặc thù để bảo vệ chủ quyền và an ninh. Việc sáp nhập cơ học các tỉnh này vào các địa phương lớn hơn có thể dẫn đến nguy cơ loãng lực lượng quản lý giám sát và giảm hiệu quả đảm bảo an ninh quốc phòng. Sự tập trung hóa có thể tạo ra khoảng trống và làm suy yếu khả năng ứng phó nhanh chóng với các tình huống phức tạp ở biên giới và vùng sâu.\r\n\r\nĐể giải quyết vấn đề này, PGS Nguyễn Chu Hồi cho rằng với các tỉnh biên giới, việc sáp nhập cần hướng đến sự bù đắp và tăng cường khả năng hỗ trợ lẫn nhau, tạo lợi thế chung cho cả khu vực theo nguyên tắc \"bốn tại chỗ\" (chỉ huy tại chỗ, lực lượng tại chỗ, phương tiện tại chỗ và hậu cần tại chỗ). Nếu các tỉnh này có diện tích đủ rộng để đảm bảo không gian hoạt động và phòng thủ thì việc có dân số thấp hơn tiêu chuẩn vẫn có thể chấp nhận được và ưu tiên hàng đầu là đảm bảo vững chắc quốc phòng an ninh.\r\n\r\nĐể \"ổn định trăm năm\", chuyên gia Phạm Chi Lan lưu ý quá trình sáp nhập tỉnh cần tính đến yếu tố văn hóa, tập quán, hướng tới sự hòa hợp giữa cộng đồng các vùng miền và ngăn ngừa xung đột.\r\n\r\n', '1744545603_h131.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:00:03', 'Thời sự', 0, 0),
(143, 'Cả nước sẽ còn 28 tỉnh và 6 thành phố trực thuộc Trung ương', 'Ban Chấp hành Trung ương thống nhất giảm số đơn vị hành chính cấp tỉnh trên cả nước từ 63 xuống 34, trong đó có 28 tỉnh và 6 thành phố trực thuộc Trung ương.', 'Phát biểu bế mạc hội nghị Trung ương 11 khóa 13 chiều 12/4, Tổng Bí thư Tô Lâm nhấn mạnh việc sắp xếp này được thực hiện với tinh thần \"khoa học, đột phá, sáng tạo, bám sát thực tiễn và có tầm nhìn 100 năm\", nhằm mở rộng không gian phát triển kinh tế, xã hội, văn hóa cho đất nước.\r\n\r\nMục tiêu của việc sắp xếp là xây dựng chính quyền địa phương tinh gọn, hiệu quả, gần dân, đáp ứng yêu cầu quản trị hiện đại, thúc đẩy tăng trưởng nhanh, bền vững, tăng cường quốc phòng, an ninh, đối ngoại, phát triển kinh tế (chú trọng kinh tế tư nhân), khoa học công nghệ, đổi mới sáng tạo và nâng cao đời sống nhân dân.\r\n\r\n\"Nhiều đồng chí băn khoăn vấn đề sắp xếp nhân sự khi sáp nhập, hợp nhất và sắp xếp nhân sự Đại hội. Các cơ quan Trung ương sẽ có hướng dẫn cụ thể về tiêu chí, tiêu chuẩn. Tôi đề nghị phải lấy tiêu chuẩn cao nhất là vì yêu cầu công việc, sau đó mới đến các tiêu chí khác\", Tổng Bí thư nói.\r\n\r\nÔng yêu cầu Ban Thường vụ cấp tỉnh (có sáp nhập, hợp nhất) phải bàn kỹ với nhau vấn đề này để tạo sự thống nhất cao trong thực hiện, nhất là bố trí người đứng đầu các cơ quan sau khi sáp nhập. Những vấn đề chưa thống nhất thì Ủy viên Bộ Chính trị, Ban Bí thư được phân công phụ trách địa bàn sẽ hướng dẫn, chỉ đạo. Các tỉnh cũng cần phân công thành viên cấp ủy tỉnh hướng dẫn chỉ đạo Đại hội cấp xã.\r\n\r\nSau khi Trung ương thống nhất, Chính phủ và địa phương sẽ xây dựng phương án chi tiết việc sáp nhập tỉnh, thành, xác định tên gọi, trung tâm hành chính mới, để trình Quốc hội xem xét thông qua tại kỳ họp tháng 5-6.\r\nCấp huyện sắp chấm dứt hoạt động\r\n\r\nTheo chủ trương đã được Trung ương thống nhất cao, chính quyền địa phương sẽ được tổ chức theo 2 cấp: cấp tỉnh (tỉnh, thành phố trực thuộc Trung ương) và cấp xã (xã, phường, đặc khu). Cấp huyện sẽ chấm dứt hoạt động sau khi Quốc hội thông qua việc sửa đổi, bổ sung Hiến pháp 2013 và Luật Tổ chức chính quyền địa phương 2025. Dự kiến, cả nước giảm khoảng 60-70% số lượng đơn vị hành chính cấp xã hiện nay.\r\n\r\nTrung ương cũng đồng ý chủ trương lập tổ chức đảng ở địa phương tương ứng với hệ thống hành chính cấp tỉnh, cấp xã; kết thúc hoạt động của các đảng bộ cấp huyện.\r\n\r\nVới mô hình hành chính mới này, cấp tỉnh sẽ có vai trò vừa thực hiện các chủ trương, chính sách từ Trung ương, vừa ban hành các chính sách phù hợp với tình hình địa phương và trực tiếp chỉ đạo, quản lý các hoạt động của cấp xã trên địa bàn tỉnh, thành phố.\r\n\r\nTrong khi đó, cấp xã sẽ tập trung thực hiện các chính sách từ Trung ương và cấp tỉnh ban hành. Đồng thời, cấp xã sẽ được tăng cường phân cấp, phân quyền và có thẩm quyền ban hành văn bản quy phạm pháp luật để quyết định việc tổ chức thi hành pháp luật và các vấn đề thuộc thẩm quyền của cấp mình.\r\nSáp nhập Mặt trận Tổ quốc, tổ chức chính trị - xã hội và hội quần chúng\r\n\r\nTrung ương đã thống nhất chủ trương sắp xếp, tinh gọn và hợp nhất cơ quan Mặt trận Tổ quốc Việt Nam, các tổ chức chính trị - xã hội, các hội quần chúng do Đảng, Nhà nước giao nhiệm vụ ở cả cấp Trung ương, cấp tỉnh và cấp xã. Đồng thời, Trung ương cũng chủ trương kết thúc hoạt động của công đoàn viên chức, công đoàn lực lượng vũ trang, giảm mức đóng góp công đoàn phí của cán bộ, công chức, viên chức và người lao động.\r\n\r\nBan Chấp hành Trung ương cũng nhất trí về việc sửa đổi, bổ sung Hiến pháp và pháp luật liên quan đến chính quyền địa phương, Mặt trận Tổ quốc Việt Nam và các tổ chức chính trị - xã hội để phục vụ việc sắp xếp bộ máy. Các văn bản pháp luật này cần hoàn thành trước ngày 30/6 và có hiệu lực từ ngày 1/7.\r\n\r\nCác quy định mới sẽ có thời gian chuyển tiếp để đảm bảo hoạt động thông suốt, không bị gián đoạn, phù hợp với lộ trình sắp xếp, sáp nhập. Trung ương yêu cầu Quốc hội, Chính phủ và các cơ quan liên quan phối hợp chặt chẽ để triển khai hiệu quả việc hoàn thiện thể chế.\r\n\r\nTheo dự thảo Nghị quyết của Ủy ban Thường vụ Quốc hội do Bộ Nội vụ chủ trì soạn thảo, có 11 đơn vị hành chính cấp tỉnh giữ nguyên hiện trạng, bao gồm Hà Nội, Huế, Lai Châu, Điện Biên, Sơn La, Cao Bằng, Lạng Sơn, Quảng Ninh, Thanh Hóa, Nghệ An và Hà Tĩnh. 52 địa phương còn lại thuộc diện sắp xếp, trong đó có 4 thành phố trực thuộc Trung ương là TP HCM, Hải Phòng, Đà Nẵng và Cần Thơ.\r\n\r\nMột số phương án sáp nhập đang được nghiên cứu như Đà Nẵng và Quảng Nam; Bắc Giang và Bắc Ninh; Hải Phòng và Hải Dương; Lào Cai và Yên Bái...', '1744545698_h132.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:01:38', 'Thời sự', 0, 0),
(144, 'Tổng Bí thư: Sắp xếp đơn vị hành chính với tầm nhìn ít nhất 100 năm', 'Tổng Bí thư Tô Lâm khẳng định sắp xếp đơn vị hành chính các cấp nhằm xây dựng chính quyền gần dân và mở ra cục diện mới phát triển đất nước với tầm nhìn ít nhất 100 năm.', 'Phát biểu khai mạc hội nghị Trung ương 11 khóa 13 sáng 10/4, Tổng Bí thư Tô Lâm cho biết hội nghị diễn ra sớm hơn một tháng so với kế hoạch ban đầu. Do khối lượng công việc cần giải quyết lớn, phạm vi rộng, nhiều nội dung nhạy cảm liên quan đến \"quốc kế dân sinh\", các tờ trình sẽ không được trình bày lại tại hội nghị để dành thời gian cho các đại biểu nghiên cứu, đóng góp ý kiến.\r\n\r\nVề nhóm vấn đề tiếp tục sắp xếp tổ chức bộ máy hệ thống chính trị, sắp xếp đơn vị hành chính và tổ chức chính quyền địa phương 2 cấp, Tổng Bí thư cho biết trong hơn 4 tháng qua, thực hiện các kết luận của Trung ương, Bộ Chính trị, Ban Bí thư đã chỉ đạo cơ bản hoàn thành việc sắp xếp, tinh gọn tổ chức bộ máy các cơ quan Đảng, Chính phủ, Quốc hội, Mặt trận ở cấp Trung ương.\r\n\r\nNhững con số về tinh giảm đầu mối, hiệu quả công việc, tiết kiệm chi phí đã \"thể hiện rõ tính cách mạng\" trong việc tinh gọn này. Dù vậy, mô hình tổ chức bộ máy của hệ thống chính trị chưa thật hoàn thiện, nhất là ở địa phương. Vì vậy, để tiếp tục cuộc cách mạng tinh giản tổ chức bộ máy, Bộ Chính trị, Ban Bí thư đã họp nhiều phiên, \"bàn bạc thấu đáo nhiều khía cạnh và thống nhất cao\" trình Trung ương Đề án sắp xếp, tổ chức lại đơn vị hành chính các cấp và xây dựng mô hình tổ chức chính quyền địa phương 2 cấp.\r\n\r\nBộ Chính trị và Ban Bí thư cũng thống nhất trình Trung ương Đề án về hệ thống tổ chức Đảng ở địa phương; Đề án về sắp xếp, tinh gọn cơ quan Mặt trận Tổ quốc, các tổ chức chính trị - xã hội, các hội quần chúng được Đảng và Nhà nước giao nhiệm vụ; các Đề án về sắp xếp các cơ quan Tòa án, Viện Kiểm sát; và các Đề án về sửa đổi, bổ sung Hiến pháp, pháp luật của Nhà nước, sửa đổi, bổ sung các quy định thi hành điều lệ Đảng, các quy định về kiểm tra, giám sát Đảng để bảo đảm triển khai đồng bộ.\r\n\r\nNgười đứng đầu Đảng đánh giá đề án về sắp xếp, tổ chức lại đơn vị hành chính các cấp và xây dựng mô hình tổ chức chính quyền địa phương 2 cấp cùng với các Đề án kèm theo là những vấn đề \"rất hệ trọng, có tính lịch sử\". Đây không chỉ sắp xếp về tổ chức, bộ máy, cán bộ; mà còn phân cấp về thẩm quyền; bố trí lại đơn vị hành chính; phân bổ về nguồn lực; tạo không gian phát triển. Mục tiêu là xây dựng Chính quyền theo hướng gần dân, sát dân, phục vụ nhân dân tốt hơn; đồng thời, mở ra cục diện mới trong phát triển đất nước với tầm nhìn lâu dài, ít nhất là cho 100 năm tới.\r\n\r\nTổng Bí thư thông báo về kế hoạch tái cấu trúc hành chính quy mô lớn, dự kiến giảm số tỉnh, thành xuống 34; không tổ chức cấp huyện và giảm một nửa đơn vị hành chính cấp xã, áp dụng mô hình chính quyền địa phương hai cấp từ 1/7. Song song đó, hệ thống Tòa án và Viện Kiểm sát sẽ được tổ chức lại theo mô hình ba cấp, đi kèm điều chỉnh thẩm quyền và sửa đổi Hiến pháp cùng các quy định liên quan.\r\nPhương hướng nhân sự khóa 14 phù hợp với mô hình tổ chức mới\r\n\r\nVề nhóm vấn đề tiếp tục chuẩn bị cho Đại hội 14 của Đảng và Bầu cử đại biểu Quốc hội khóa 16, đại biểu HĐND các cấp nhiệm kỳ 2026-2031, Tổng Bí thư cho biết từ sau Hội nghị Trung ương 10, Bộ Chính trị đã quyết liệt chỉ đạo chuẩn bị các công việc, trọng tâm là hai nhóm vấn đề văn kiện và nhân sự.\r\n\r\nVề văn kiện, dự thảo tóm tắt đã được gửi lấy ý kiến cấp cơ sở. Bộ Chính trị cũng chỉ đạo bổ sung nhiều nội dung mới, mang tính đột phá. Văn kiện được xây dựng theo phương châm Báo cáo chính trị là \"ngọn đuốc soi đường\", các báo cáo khác là \"cẩm nang hành động\".\r\n\r\nNội dung xuyên suốt của các văn kiện thể hiện sự kiên trì không thay đổi con đường xã hội chủ nghĩa; giải quyết tốt mối quan hệ biện chứng giữa ổn định và phát triển, bám sát mục tiêu lớn là \"đáp ứng ngày càng tốt hơn nhu cầu của nhân dân\"; quyết tâm hiện thực hóa 2 mục tiêu 100 năm của đất nước.\r\n\r\nTổng Bí thư cũng bày tỏ mong muốn được nghe kiến nghị, giải pháp để Việt Nam đạt mục tiêu tăng trưởng GDP từ 8% trở lên trong năm 2025 và tăng trưởng liên tục hai con số những năm tiếp theo. Bối cảnh là cả nước tiến hành cuộc cách mạng về tổ chức bộ máy, sắp xếp lại đơn vị hành chính các cấp cũng như trong tình hình chiến tranh thương mại trên phạm vi toàn cầu.\r\nVề nhân sự, Tổng Bí thư nhấn mạnh đây là vấn đề then chốt của then chốt để hiện thực hóa các mục tiêu, tầm nhìn của Đại hội 14. Yêu cầu nhiệm vụ cao hơn đòi hỏi mặt bằng đội ngũ cán bộ cao hơn, đặc biệt là cán bộ cấp chiến lược. Công tác nhân sự phải được chuẩn bị từ sớm và sẽ được tiếp tục bổ sung, hoàn thiện cho đến Đại hội 14.\r\n\r\nÔng cho biết Bộ Chính trị đã chỉ đạo rà soát, bổ sung dự thảo Phương hướng công tác nhân sự Ban Chấp hành Trung ương khóa 14 phù hợp với cơ cấu của mô hình tổ chức mới; rà soát đề xuất Trung ương bổ sung quy hoạch cán bộ cấp chiến lược; dự thảo sửa đổi, bổ sung một số nội dung Chỉ thị 35 của Bộ Chính trị về tổ chức Đại hội đảng các cấp tiến tới Đại hội 14 của Đảng.\r\n\r\nTheo Tổng Bí thư, tất cả công việc quan trọng này đều hướng đến mục tiêu tổ chức thành công Đại hội Đảng các cấp và tổ chức Đại hội 14 vào quý I/2026 theo đúng kế hoạch đề ra.\r\n\r\nGóp phần đưa Nghị quyết Đại hội 14 đi ngay vào cuộc sống, Bộ Chính trị trình Trung ương Đề án về \"Phương hướng Bầu cử đại biểu Quốc hội khóa 16, đại biểu HĐND các cấp nhiệm kỳ 2026-2031\", với chủ trương là tổ chức bầu cử sớm hơn và cải cách, đổi mới, nâng cao hiệu quả hoạt động của Quốc hội và HĐND. Mục tiêu là góp phần phát huy đầy đủ quyền làm chủ của nhân dân đối với công việc của đất nước.\r\n\r\nTheo chương trình dự kiến, Trung ương sẽ họp trong ba ngày để thảo luận, cho ý kiến đối với 15 nội dung, tập trung là hai nhóm vấn đề chính. Đó là nhóm vấn đề tiếp tục sắp xếp tổ chức bộ máy của hệ thống chính trị, sắp xếp đơn vị hành chính và tổ chức chính quyền địa phương 2 cấp; và tiếp tục chuẩn bị cho Đại hội 14 của Đảng và Bầu cử đại biểu Quốc hội khóa 16, đại biểu Hội đồng nhân dân các cấp nhiệm kỳ 2026-2031.\r\n\r\nBộ Chính trị cũng sẽ báo cáo Trung ương xem xét, quyết định một số nội dung công tác cán bộ theo thẩm quyền; báo cáo Trung ương về tình hình đất nước; tình hình thế giới, khu vực; những công việc quan trọng Bộ Chính trị, Ban Bí thư đã giải quyết từ sau Hội nghị Trung ương 10 đến nay và các chuyên đề về hoàn thiện thể chế và đột phá phát triển khoa học, công nghệ, đổi mới sáng tạo, chuyển đổi số quốc gia.', '1744545773_h133.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:02:53', 'Thời sự', 0, 0),
(145, 'Hơn 10% thanh niên không việc làm, không được đào tạo', 'Cả nước còn 1,35 triệu thanh niên 15-24 tuổi không có việc làm, không tham gia học tập, đào tạo, chiếm 10,4% tổng số thanh niên cả nước, theo Cục Thống kê.', 'Cục Thống kê ngày 6/4 công bố tình hình lao động việc làm quý I/2025, cho biết con số trên đã tăng 84.400 người so với quý trước và giảm 66.900 so với cùng kỳ năm ngoái. Tỷ lệ thanh niên \"hai không\" ở khu vực nông thôn cao hơn thành thị, lần lượt 11,7% và 8,2%; nữ 11,5% trong khi nam 9,3%.\r\n\r\nTỷ lệ thất nghiệp của thanh niên độ tuổi này là 7,93%, Cục Thống kê đánh giá \"có giảm so với quý trước và cùng kỳ năm 2024 nhưng không đáng kể\".\r\nTình hình lao động, việc làm ba tháng đầu năm nhìn chung duy trì quy luật của quý có Tết Nguyên đán khi lực lượng lao động, tỷ lệ thất nghiệp đều giảm so với quý IV/2024 và tăng so với cùng kỳ năm trước. Lao động từ 15 tuổi trở lên đạt 52,9 triệu, tăng 532.000 người so với cùng kỳ năm trước.\r\n\r\nLao động có việc làm ước tính 51,9 triệu, giảm 234.000 người so với quý IV/2024. Xét theo khu vực kinh tế, khu vực dịch vụ chiếm tỷ trọng cao nhất 40,7%, tương đương 21,1 triệu người có việc làm và tăng gần 0,6 triệu người so với cùng kỳ năm trước; trong khi đó hai khu vực còn lại đều giảm là công nghiệp xây dựng 33,3%, tương đương 17,3 triệu người; nông lâm thủy sản chiếm tỷ trọng thấp nhất 26%, tức 13,5 triệu người.\r\n\r\nQuý I/2025, thu nhập bình quân lao động có cải thiện, tăng 131.000 đồng (1,6%) so với quý trước, đạt 8,3 triệu đồng mỗi tháng. Mức thu nhập trên tăng 720.000 đồng so với cùng kỳ năm ngoái. Thu nhập bình quân lao động thành thị gấp gần 1,4 lần so với nông thôn, đạt 10,1 triệu so với 7,2 triệu đồng. Đây là khoản tiền công hoặc tiền lương, lợi nhuận lao động nhận được tính cả tiền làm thêm giờ, tiền thưởng, phụ cấp nghề và các khoản phúc lợi khác trong tháng trước thời điểm điều tra.\r\nXét theo vùng kinh tế xã hội, thu nhập bình quân lao động đều tăng ở các vùng. Riêng Đồng bằng sông Hồng, Bắc Trung Bộ và duyên hải miền Trung tăng cao nhất, đạt 7,3 triệu đồng mỗi tháng, tăng 11,7% so với cùng kỳ năm trước.\r\n\r\nMột số địa phương ghi nhận mức tăng cao như Vĩnh Phúc 9,9 triệu đồng mỗi tháng, tăng 1 triệu; Hưng Yên 9,2 triệu, tăng 949.000 đồng; Hải Dương 8,8 triệu, tăng 841.000 đồng; Thanh Hóa 7,8 triệu, thêm 1,1 triệu đồng; Nghệ An 7,2 triệu, tăng 1,4 triệu; Quảng Bình 7 triệu, tăng 871.000 đồng.\r\n\r\nTheo khu vực kinh tế, lao động dịch vụ vẫn có thu nhập cao nhất, đạt 9,9 triệu đồng mỗi tháng, tăng 832.000 đồng so với cùng kỳ năm ngoái; nông lâm thủy sản 4,9 triệu đồng, tăng 434.000 đồng; công nghiệp và xây dựng 9,1 triệu đồng, tăng 690.000 đồng.\r\n\r\nMột số ngành kinh tế ghi nhận mức thu nhập khá so với cùng kỳ năm ngoái, như sản xuất và phân phối điện, khí đốt, nước nóng, hơi nước đạt 12,3 triệu đồng, tăng 1,2 triệu; công nghiệp chế biến, chế tạo 9,1 triệu, tăng 667.000 nghìn đồng; hoạt động tài chính, ngân hàng và bảo hiểm 13,9 triệu, tăng 800 nghìn đồng.', '1744545867_h134.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:04:27', 'Thời sự', 0, 0),
(146, 'Hàng nghìn người nộp hồ sơ dự thi tiếng Hàn', 'Hơn 1.500 người Nghệ An, Hà Tĩnh tới Trung tâm Dịch vụ việc làm đăng ký dự thi tiếng Hàn Quốc để xuất khẩu theo chương trình EPS.', '31/3 là ngày đầu tiên tỉnh Nghệ An tiếp nhận hồ sơ đăng ký tham gia dự thi tiếng Hàn theo chương trình cấp phép cho lao động người nước ngoài sang làm việc tại Hàn Quốc (chương trình EPS) đợt một năm 2025.\r\n\r\nTừ 6h, hàng trăm người tuổi 18-39 đã đến Trung tâm Dịch vụ việc làm của tỉnh đóng trên đường Phong Định Cảng, TP Vinh chờ làm thủ tục. Năm nay nhà chức trách sẽ tuyển lao động làm việc trong lĩnh vực sản xuất chế tạo và nông nghiệp. Để ra nước ngoài làm việc, lao động phải vượt qua kỳ thi tiếng Hàn.\r\n\r\nAnh Trần Văn Vũ, 18 tuổi, cho biết do lo sợ quá tải như năm trước nên đã dậy từ lúc 3h30, lái xe máy vượt hơn 70 km vào TP Vinh để tránh phải chờ lâu. Tuy nhiên, đến nơi đã có hàng trăm người xếp hàng làm thủ tục ở sảnh.\r\n\"Em đến sớm nhưng số thứ tự đã là 293, dự kiến chiều nay mới hoàn tất hồ sơ\", Vũ nói. Tốt nghiệp lớp 12, Vũ chia sẻ không đăng ký xét tuyển vào các trường đại học hoặc cao đẳng vì gia cảnh khó khăn, sợ lúc học xong khó xin việc. Nam thanh niên đặt mục tiêu đỗ kỳ thi lần này để sang Hàn Quốc làm việc, kiếm tiền phụng dưỡng bố mẹ và tích góp một khoản sau này lập gia đình.\r\n\r\nNhiều thanh niên ở các huyện vùng cao như Quỳ Hợp, Quỳ Châu cũng bắt xe khách vượt 100-150 km đến TP Vinh nộp hồ sơ thi tiếng Hàn. Một số người tới muộn nên nhận số thứ tự trên 500. Họ cho hay trường hợp chưa đến lượt sẽ thuê nhà nghỉ gần trung tâm để ở qua đêm, sáng hôm sau tới sớm hoàn tất thủ tục.\r\n\r\nBùi Đức Duy, 21 tuổi, cho biết đăng ký học tiếng Hàn từ năm 2023, hoàn thành cuối năm 2024. Năm nay kỳ thi tiếng Hàn sẽ tổ chức theo bộ đề mới, Duy khá lo lắng bởi trước đây học theo chương trình cũ, sợ khó đáp ứng được yêu cầu.\r\n\r\n\"Nhưng cũng phải cố gắng hết sức vì tương lai của bản thân và gia đình thôi. Nếu không đỗ, em dự định ra Hà Nội ôn luyện để sang năm thi tiếp\", Duy nói.\r\nTrong sáng nay, Trung tâm Dịch vụ việc làm của tỉnh Nghệ An tiếp nhận khoảng 1.000 hồ sơ đăng ký. Đơn vị đã phân vùng theo từng cụm địa phương để thí sinh làm thủ tục theo từng ngày, vì thế không xảy ra tình trạng chen lấn như mọi năm. Dự kiến số người nộp hồ sơ khoảng 3.000, giảm một nửa so với năm ngoái. Nguyên nhân là chương trình EPS giảm ngành nghề và chỉ tiêu.\r\n\r\nLịch đăng ký hồ sơ thi tiếng Hàn của tỉnh Hà Tĩnh giống Nghệ An, đến ngày 2/4 kết thúc. Sáng nay, Trung tâm Dịch vụ việc làm tỉnh đón khoảng 500 người.\r\n\r\nHọ được bố trí ngồi trong các phòng, chờ cán bộ gọi tên theo thứ tự để đến bàn trình căn cước công dân, đăng ký qua hệ thống trực tuyến và kiểm tra thông tin bằng mã QR. Trung bình mỗi người mất khoảng 7-10 phút để hoàn thành thủ tục.\r\n\r\nAnh Bùi Văn Hải, 32 tuổi, trú huyện Hương Khê, cho biết học tiếng Hàn một năm nay để thi EPS sang Hàn Quốc làm nông nghiệp. \"Nếu trúng tuyển thì đó là niềm vui lớn. Qua tìm hiểu thì tôi thấy lương theo chương trình rất cao, khoảng 40 triệu đồng một tháng, gấp hơn ba lần so với đi làm thuê ở quê\", Hải nói.\r\n\r\nBà Nguyễn Thị Thanh Hương, Giám đốc Trung tâm Dịch vụ việc làm tỉnh Hà Tĩnh, cho biết sáng nay khoảng 500 thí sinh đến đăng ký sơ tuyển, dự kiến đến hết đợt nhận khoảng 1.000 hồ sơ, trong khi năm ngoái là 3.500.\r\nKỳ thi EPS năm nay diễn ra ở Đà Nẵng vào giữa tháng 4, công bố kết quả đầu tháng 8. Dự kiến phía Hàn lấy 3.300 lao động trúng tuyển làm việc hai ngành nghề sản xuất chế tạo và nông nghiệp. Trong đó, sản xuất chỉ tiêu 3.000, nông nghiệp 300.\r\n\r\nChương trình EPS có đặc thù là chủ doanh nghiệp Hàn Quốc sẽ chọn lao động dựa trên hồ sơ được giới thiệu ngẫu nhiên mà không chỉ định, không ai tác động được tới quy trình ký kết hợp đồng. Lao động vượt qua hai vòng thi, nộp xong hồ sơ cũng không chắc chắn sẽ được chọn đi Hàn, không biết trước thời gian xuất cảnh. Nhà chức trách khuyến cáo thí sinh nộp xong hồ sơ vẫn nên duy trì công việc bình thường, không nên ở nhà chờ đợi trong căng thẳng, lãng phí thời gian.\r\n\r\nNghệ An và Hà Tĩnh có hàng chục nghìn lao động làm việc tại Hàn Quốc, song có hàng nghìn trường hợp bỏ trốn ra ngoài làm, cư trú bất hợp pháp. Một số huyện từng bị nhà chức trách cấm thi EPS như huyện Nghi Lộc, Hưng Nguyên, TP Vinh (Nghệ An) và huyện Cẩm Xuyên, Nghi Xuân (Hà Tĩnh). Đầu năm 2024, Trung tâm Lao động ngoài nước có công văn tạm gỡ lệnh cấm trên, cho phép người tại 8 huyện ở Hải Dương, Thanh Hóa, Nghệ An và Hà Tĩnh được dự thi.\r\n\r\n\r\n', '1744545949_h135.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:05:49', 'Thời sự', 0, 0),
(147, 'Cơ hội và thách thức thị trường việc làm 2025', 'Thị trường lao động Việt Nam năm nay đối mặt với biến động lớn do sự phát triển của AI và nhu cầu tuyển dụng nhân sự có kinh nghiệm, theo thống kê từ Joboko.', 'Khảo sát mới nhất từ nền tảng công nghệ tuyển dụng Joboko chỉ ra rằng thị trường lao động hiện đang biến động khi các doanh nghiệp điều chỉnh chiến lược tuyển dụng, ưu tiên nhân sự có kinh nghiệm. Sự phát triển của trí tuệ nhân tạo (AI) đang tái định hình nhiều ngành nghề, thu hẹp cơ hội việc làm cho người trẻ, đặc biệt là sinh viên mới tốt nghiệp.\r\nThống kê trước đó, tổng số lượng việc làm trong năm 2024 giảm 5,2% so với năm 2023. Những ngành chịu ảnh hưởng lớn nhất gồm: kế toán (giảm 23,89%), hành chính - nhân sự (giảm 23,59%) và giáo dục - đào tạo (giảm 13,71%). Một trong những nguyên nhân của sự cắt giảm này xuất phát từ sự phát triển mạnh mẽ của công nghệ, đặc biệt là AI. Nhiều công việc như xử lý số liệu, nhập liệu, quản lý hồ sơ và hỗ trợ khách hàng dần được tự động hóa trong bối cảnh doanh nghiệp gặp khó khăn nên cần tối ưu lại hoạt động toàn bộ máy nhân sự.\r\n\r\nKhông chỉ bị ảnh hưởng bởi AI, ứng viên trẻ còn đối mặt với xu hướng tuyển dụng khắt khe hơn. Báo cáo thị trường từ Joboko cho thấy số lượng việc làm dành cho ứng viên fresher (sinh viên mới ra trường) giảm mạnh tới 27,6%. Trong khi đó, nhu cầu tuyển dụng nhân sự có từ một đến ba năm kinh nghiệm lại tăng nhẹ. Điều này phản ánh rõ rệt xu hướng doanh nghiệp cắt giảm nhân sự chưa có kinh nghiệm để tập trung vào nhóm đã có kinh nghiệm, khả năng làm việc ổn định và nhanh chóng hòa nhập với công việc.\r\nĐơn vị này cũng chỉ ra vài điểm khởi sắc khi có hơn 78,4% doanh nghiệp có kế hoạch mở rộng tuyển dụng trong năm 2025. Tuy nhiên, nhu cầu vẫn nghiêng về nhóm ứng viên có từ một đến ba năm kinh nghiệm (64%), trong khi cơ hội cho những người dưới một năm kinh nghiệm vẫn bị hạn chế.\r\n\r\nTheo Báo cáo Tương lai việc làm 2025 của Diễn đàn Kinh tế Thế giới, quá trình chuyển đổi cơ cấu sẽ tác động mạnh mẽ đến thị trường lao động giai đoạn 2025-2030. Dự kiến khoảng 8% tổng số việc làm hiện tại - tương đương 92 triệu vị trí - sẽ biến mất nhưng cũng có các vị trí mới được tạo ra.\r\n\r\nXu hướng này buộc người lao động phải chuyển nghề nếu không muốn bị thất nghiệp. Tuy vậy thách thức lớn nhất là doanh nghiệp đòi hỏi ứng viên phải có nhiều kinh nghiệm và kỹ năng hơn. Hầu hết đều là kỹ năng cao cấp như: sử dụng AI, công nghệ số, tư duy phân tích, khả năng thích ứng linh hoạt và năng lực lãnh đạo. Những kỹ năng này sẽ trở thành yếu tố cốt lõi giúp ứng viên tạo lợi thế cạnh tranh.\r\nÔng Phạm Tuấn Anh, Giám đốc nền tảng công nghệ tuyển dụng Joboko cho biết năm nay dự báo sẽ có sự phục hồi về tổng số việc làm, đặc biệt trong các ngành: kinh doanh - bán hàng, IT - phần mềm, marketing - truyền thông, giao nhận - vận chuyển, bất động sản, xây dựng và sản xuất. Tuy nhiên, các doanh nghiệp vẫn thận trọng trong quyết định tuyển dụng ứng viên trẻ. Để gia tăng cạnh tranh, ứng viên trẻ nên điều chỉnh hạ thấp tiêu chí tìm việc, giữ tư duy tích cực và chủ động trau dồi kỹ năng để tránh rơi vào khoảng trống nghề nghiệp ngay khi mới gia nhập thị trường.', '1744546023_h136.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:07:03', 'Thời sự', 0, 0);
INSERT INTO `news` (`id`, `title`, `description`, `content`, `image`, `author`, `created_at`, `category`, `is_featured`, `views`) VALUES
(148, 'Xếp hàng từ nửa đêm xin visa sang Hàn Quốc làm việc thời vụ', 'Nhu cầu sang Hàn Quốc làm thời vụ tăng cao, lao động các tỉnh đổ về TP HCM xin visa phải xếp hàng từ đêm hôm trước mới lấy được số thứ tự.', '0h ngày 26/3, trước cổng trung tâm đăng ký visa Hàn Quốc trên đường Điện Biên Phủ (quận 3), hàng chục người miền Tây, Đông Nam Bộ đến chờ bốc số làm visa đi lao động thời vụ dù nơi này tới 8h30 mới làm việc.\r\nCó mặt ở điểm bốc số thứ tự lúc 23h30, anh Nguyễn Quốc Sơn, 37 tuổi, cho biết sẽ qua tỉnh Sangju trồng nho, nhân sâm theo diện em gái bảo lãnh. Năm ngoái, anh nghỉ làm công nhân để đi lao động ở Hàn Quốc 8 tháng với mức lương hơn 40 triệu đồng. Thấy có dư so với công việc ở Việt Nam, lần này anh và vợ cùng xuất ngoại.\r\n\r\n\"Nhiều người cũng xin visa đợt này nên vợ chồng tôi phải đến sớm, đợi đến sáng là không kịp\", anh Sơn nói.\r\n\r\nNgồi gần đó, bà Nguyễn Thị Thương, 50 tuổi, cho biết đã từ Bạc Liêu lên TP HCM. Bà được người cháu gái lấy chồng ở Hàn Quốc bảo lãnh sang làm việc tại trang trại trồng rau. Hôm trước, bà đến từ sáng nhưng chờ đến chiều vẫn chưa tới lượt làm hồ sơ, nên lần này đi sớm hơn.\r\n\r\nBà Thương cho biết từng sang Hàn Quốc để làm nông nghiệp thời vụ 5 tháng, thu nhập ổn định nên nay đi tiếp. \"Bên đó thời tiết rất lạnh, ban đầu tôi chưa quen, nhưng bù lại công việc thu nhập dư dả hơn\", bà nói, cho biết được ở cùng nhà người thân nên không tốn nhiều chi phí sinh hoạt.\r\n\r\nĐến 1h30, bảo vệ bắt đầu phát số thứ tự. Mỗi đêm trung tâm cấp 180 số, song không đáp ứng đủ nhu cầu do người làm visa quá đông.\r\nLao động Việt Nam sang Hàn Quốc làm thời vụ có từ năm 2018. Ông Phạm Viết Hương, Cục phó Quản lý lao động ngoài nước (Bộ Nội vụ), cho biết đến nay có 17 tỉnh, thành ký kết với nước bạn để lao động sang làm việc từ 3 đến 11 tháng, chủ yếu trong lĩnh vực nông nghiệp. Số lượng lao động diện này tăng dần, năm 2022 có 433 người, đến 2023 tăng lên 1.840 và năm ngoái là 2.157.\r\n\r\nPhân tích nguyên nhân, ông Hương cho rằng phía Hàn Quốc rất cần nhân công trong khi đó nhu cầu đi làm của lao động Việt Nam rất lớn. Công việc trong lĩnh vực nông nghiệp không cần ngoại ngữ như lao động dài hạn hoặc các kỹ năng làm việc khá đơn giản nhưng thu nhập tốt, độ tuổi cũng mở từ 30-55. Bên cạnh diện được các địa phương đưa đi, hiện phía Hàn Quốc cho phép cô dâu, lao động có visa cư trú dài hạn bảo lãnh người thân sang làm việc.\r\n\r\nĐồng Tháp là một trong 4 địa phương đầu tiên ký kết đưa lao động thời vụ sang Hàn Quốc. Bà Nguyễn Thị Minh Tuyết, Phó giám đốc Sở Nội vụ tỉnh, cho biết ban đầu lao động chỉ sang làm được 3 tháng (visa C4) nhưng đến nay tăng lên 5, 8, 11 tháng (visa E8). Địa phương đã ký kết với hai tỉnh là Gyeonggi, Gangwon và đang xúc tiến ký tiếp với một địa phương khác.\r\n\r\nLãnh đạo Sở Nội vụ Đồng Tháp cho biết lao động ở tỉnh đi Hàn làm việc thời vụ tăng cao do từ năm 2023 đến nay, số lượng người mất việc trên địa bàn tăng, đặc biệt là công nhân từ các tỉnh Đông Nam Bộ trở về. Những người độ tuổi 35-45 được Trung tâm dịch vụ việc làm tỉnh giới thiệu sang Hàn làm việc.\r\n\r\nThu nhập bình quân mỗi tháng của lao động thời vụ ở Hàn Quốc khoảng 40 triệu đồng. Bên sử dụng lao động hỗ trợ chỗ ở, phương tiện sinh hoạt, lao động bỏ chi phí mua thực phẩm. Sau khi trừ các chi phí, mỗi tháng dư 30 triệu đồng. Nhiều trường hợp đi cả vợ chồng, sau 8 tháng để dành gần nửa tỷ đồng.\r\nNhiều năm nghiên cứu thị trường và lao động Việt Nam sang Hàn Quốc, thạc sĩ Nguyễn Nam Cường, Nghiên cứu sinh Địa lý Nhân văn tại Học viện Hàn Quốc học AKS, cho biết một lý do lao động Việt sang Hàn tăng cao do nước này thiếu lao động. Năm 2021, Bộ An ninh Hành chính Hàn Quốc công bố 89 vùng trong nước suy giảm dân số, tức 20% ở độ tuổi trên 65. Những cô dâu ở các vùng này được bảo lãnh người thân sang đây làm việc.\r\n\r\nÔng Nguyễn Như Tuấn, Phó phòng Thị trường châu Á - châu Phi (Cục Quản lý lao động ngoài nước, Bộ Nội vụ), khuyến cáo lao động muốn sang Hàn Quốc làm việc cần tìm hiểu kỹ danh sách 17 tỉnh được phép đưa đi. Ngoài ra, nhiều trường hợp lợi dụng chính sách cho phép người thân bảo lãnh đã lừa người muốn đi Hàn, đóng hàng trăm triệu đồng nhưng không xuất cảnh được.\r\n\r\n\"Lao động làm thời vụ chỉ tốn tiền vé máy bay, phí làm visa, bảo hiểm du lịch, nếu cao hơn cần cảnh giác, liên hệ ngành chức năng để được tư vấn\", ông Tuấn khuyến cáo.', '1744546093_h137.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:08:13', 'Thời sự', 0, 0),
(149, 'Cán bộ diện tinh giản tập quen môi trường tư nhân', 'TP HCM - Dù chuẩn bị tâm lý và kỹ năng để nghỉ việc nhà nước, khi chuyển sang môi trường tư nhân, chị Tuyền bất ngờ vì tiêu chí tuyển dụng thay đổi quá nhanh.', 'Chị Nguyễn Thanh Tuyền 34 tuổi, là viên chức văn phòng đại diện tại TP HCM của một cơ quan trực thuộc Chính phủ. Thực hiện Nghị quyết 18 về tinh gọn bộ máy, cơ quan chị sắp xếp lại, cắt giảm một số người. Chị có trình độ thạc sĩ, ngoại ngữ tiếng Anh, nhiều năm liền là chiến sĩ thi đua nhưng vẫn bất an. Nữ viên chức nhận thấy tính ổn định, lợi thế lớn nhất của khu vực công không còn. Nếu cố ở lại, vài năm nữa có một đợt tinh giản, chị hết cơ hội ở thị trường lao động.\r\n\r\n\"Tôi mất gần hai tháng suy nghĩ để quyết định tự nguyện nghỉ việc\", chị Tuyền nói. Đầu tiên, chị khảo sát lại thị trường lao động, tìm hiểu các vị trí tuyển dụng cần chuyên môn hành chính, nhân sự. Tiếp đến chị làm công tác tư tưởng với bố mẹ ở quê về lý do rời nhà nước và tính lại cách thức trả nợ vay ngân hàng mua căn hộ trong trường hợp không sớm tìm được việc làm mới nhưng tiền hỗ trợ chưa về tài khoản.\r\nDù làm nhà nước, hàng năm chị tạo CV ứng tuyển vào các vị trí của doanh nghiệp cần trình độ chuyên môn phù hợp bản thân. Nữ viên chức nói rằng chưa từng có ý định bỏ công việc đang làm nhưng những lần \"đi phỏng vấn thử\" giúp chị biết doanh nghiệp cần gì, thị trường thay đổi ra sao để chủ động nâng cao trình độ, kỹ năng. Hơn một năm qua, chị đã đầu tư học tiếng Trung trước làn sóng đầu tư từ Trung Quốc sang Việt Nam. Với sự chủ động lâu nay, chị tin rằng cơ hội việc làm sẽ mở ra khi chị rời nhà nước.\r\n\r\n\"Tôi không nhớ mình đã rải bao nhiêu CV, chỉ biết là rất nhiều\", chị Tuyền nói, thêm rằng đã không bỏ qua bất kỳ cơ hội nào dù vị trí tuyển dụng chỉ dính dáng chút ít đến chuyên môn. Tuy nhiên, khác với lúc giả người tìm việc \"gửi hồ sơ có doanh nghiệp gọi ngay\", lần này mỗi ngày trôi qua những nơi ứng tuyển không có phản hồi trong khi cơ quan trao quyết định nghỉ việc vào cuối tháng 2.\r\n\r\nSau hai tuần, chị nhận được lời mời phỏng vấn của doanh nghiệp cho vị trí nhân viên nhân sự. Dù luôn chủ động nâng cao kỹ năng, chị vẫn bất ngờ với những yêu cầu mới của nhà tuyển dụng. Ví dụ, người làm nhân sự giờ đây phải biết phân tích dữ liệu, đánh giá thị trường để có cách thức giữ được người tài mà lương, thưởng phải đảm bảo. Sau nhiều lần bị từ chối, chị được một công ty tiếp nhận với mức lương khởi điểm 15 triệu đồng và sẽ đi làm vào đầu tháng 4.\r\n\r\n\"Tôi sẽ trân trọng mọi cơ hội đến với mình nên sẵn sàng nhận việc và sẽ học hỏi thêm\", chị Tuyền nói. Hiện tại chị theo học các lớp online về kỹ năng phân tích công việc, văn phòng, tiền lương và tìm kiếm các môi trường sử dụng tiếng Anh, Trung. Cựu viên chức xác định sẽ mất ít nhất hai năm để bản thân thích ứng với thị trường ngoài nhà nước.\r\nKhác với Thanh Tuyền, chị Bùi Xuân Triều, Phó đại diện Văn phòng Bảo hiểm xã hội tại TP HCM, chọn làm đối tác phân phối sản phẩm của Tập đoàn Amway sau khi nghỉ việc do cơ quan tinh gọn theo Nghị quyết 18. \"Đây là cơ hội để tôi có bứt phá về thu nhập\", cựu viên chức vừa bước vào tuổi 40 nói. Từ giữa năm ngoái, chị đã bắt đầu tìm hiểu, sử dụng các sản phẩm và cách thức bán hàng của công ty.\r\n\r\nSau giờ làm hoặc tranh thủ nghỉ trưa, chị hẹn khách hàng tư vấn. Công việc bán thời gian mang lại trung bình trên dưới 10 triệu đồng mỗi tháng càng giúp chị tin rằng nếu chuyên tâm thu nhập không dừng lại ở con số này. Đặc biệt, công việc tiếp xúc nhiều giúp chị vui vẻ, cuộc sống thú vị. Hơn một năm qua, chị bị giằng co giữa nơi đã giúp mình trưởng thành, có cuộc sống ổn định nhưng cũng muốn thoát ra để đến với công việc thu nhập tốt, chủ động về thời gian.\r\n\r\nNữ viên chức cho rằng trước đây bản thân lưỡng lự, không dám rời bỏ khu vực công nhưng khi Nghị quyết 18 và chính sách hỗ trợ của Nghị định 178 ra đời khiến chị hạ quyết tâm. \"16 năm làm nhà nước cho tôi nhiều thứ nhưng đã đến lúc tôi phải rời đi\", chị Triều nói.\r\n\r\nChị Triều, Thanh Tuyền thuộc nhóm đầu tiên rời khu vực công khi khối này thực hiện sắp xếp lại bộ máy theo Nghị quyết 18. Để hỗ trợ người nghỉ việc, Chính phủ ban hành Nghị định 178 hỗ trợ người nghỉ việc do tinh gọn với nhiều chính sách về tài chính vượt trội. Tuy nhiên, mới đây Nghị định 67 điều chỉnh một số điều của Nghị định 178, hiệu lực từ 15/3, trong đó có sửa đổi điều khoản quan trọng là bãi bỏ chính sách hỗ trợ tăng thêm từ địa phương.\r\nBà Thanh Nguyễn, Giám đốc điều hành Anphabe, cho rằng những người đã và sắp rời khu vực công cần nhìn nhận thực tế không chỉ đơn vị nhà nước tinh giản mà khối tư nhân cũng đang thực hiện mạnh mẽ sắp xếp. Tinh gọn bộ máy, lựa chọn nhân sự ưu tú với các kỹ năng mới là yêu cầu hàng đầu của tất cả tổ chức và quá trình này sẽ tiếp tục diễn ra. Do đó, để thích ứng, người làm khu vực công cần cởi bỏ tư duy biên chế suốt đời mà phải sẵn sàng cho sự thay đổi.\r\n\r\nCEO Anphabe cho rằng trước hết cán bộ, công chức cần định nghĩa lại sự ổn định, an toàn. Trước đây, định nghĩa ổn định là làm một công việc đến khi nghỉ hưu nhưng giờ đây nên hiểu là dù có bất kỳ chuyện gì xảy ra với tổ chức, nhân viên vẫn luôn có cơ hội. Do đó, người đi làm phải tự gia tăng năng lực mới của mình để phù hợp với sự phát triển, yêu cầu của công việc.\r\n\r\n\"Làm được điều này, người đi làm sẽ mở rộng vùng an toàn và dễ tìm cơ hội mới dù tổ chức thay đổi\", bà Thanh nói, thêm rằng ngoài kỹ năng, ứng viên chủ động, sẵn sàng thay đổi luôn được mọi tổ chức chào đón, coi trọng.\r\n\r\nBà Thanh Nguyễn cho biết trong bối cảnh khắp nơi đều tinh giản thì cơ hội việc làm của người vừa rời khu vực công không cao. Vì vậy giai đoạn này những người rời khu vực công phải chấp nhận thực tế muốn tìm công việc với mức lương như cũ là không thể. \"Cán bộ, công chức có thể chấp nhận lùi một bước để nhìn nhận lại thị trường, điều chỉnh kỳ vọng, kiến tạo lại bản thân để rẽ ngang hoặc tiến nhanh hơn\", CEO Anphabe nói.\r\n\r\nTuy nhiên, chuyên gia cũng có rằng những người vừa mất việc không nên quá lo lắng vì thực tế qua khảo sát của Anphabe năm ngoái đã chỉ ra những nhân sự bị cắt giảm năm 2023, sau 6 tháng đã có việc mới. 1/3 trong số đó tìm được môi trường làm việc, mức lương tốt hơn chỗ cũ.\r\n\r\nNhiều năm làm nhà nước rồi chuyển ra khu vực tư nhân, ông Trần Ngọc Minh, Giám đốc Kinh doanh Tập đoàn Rockwool tại Việt Nam, cho rằng lo lắng khó hòa nhập là tâm lý của hầu hết nhân sự khi đổi chỗ làm. Chưa kể từ khu vực công ra bên ngoài có nhiều khác biệt về môi trường, quy trình, văn hóa làm việc... Để thích nghi và phát triển tốt, cán bộ, công chức cần chuẩn bị kỹ về tâm lý, đặc biệt làm việc cường độ cao, sẵn sàng học hỏi, chủ động thể hiện bản thân qua tinh thần trách nhiệm, kết quả công việc. Sự động viên từ gia đình, bạn bè cũng giúp người rời khu vực công có năng lượng tích cực, thích ứng môi trường mới.', '1744546165_h138.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:09:25', 'Thời sự', 0, 0),
(150, 'Từ 1/7, tiền lương đóng BHXH dự kiến gồm những khoản nào?', 'Tiền lương làm căn cứ đóng bảo hiểm xã hội (BHXH) bắt buộc của lao động trong doanh nghiệp là tiền lương tháng, phụ cấp lương và các khoản bổ sung khác.', 'Bộ Tư pháp vừa công bố hồ sơ thẩm định dự thảo Nghị định quy định chi tiết hướng dẫn thi hành một số điều Luật Bảo hiểm xã hội về BHXH bắt buộc. Nghị định do Bộ Lao động Thương binh và Xã hội xây dựng - hiện chuyển về Bộ Nội vụ phụ trách sau khi sáp nhập.\r\n\r\nDự thảo nêu rõ với lao động trong doanh nghiệp, tiền lương làm căn cứ đóng BHXH bắt buộc là tiền lương tháng gồm mức lương theo công việc hoặc chức danh, phụ cấp lương và các khoản bổ sung khác được thỏa thuận trả thường xuyên, ổn định trong mỗi kỳ chi trả.\r\nTiền lương làm căn cứ đóng BHXH không bao gồm chế độ và phúc lợi khác như: Thưởng sáng kiến; tiền ăn giữa ca; hỗ trợ xăng xe, điện thoại, đi lại, tiền nhà ở, tiền giữ trẻ, nuôi con nhỏ; hỗ trợ khi người lao động có thân nhân bị chết, có người thân kết hôn, sinh nhật, trợ cấp cho lao động gặp hoàn cảnh khó khăn khi bị tai nạn lao động, bệnh nghề nghiệp và các khoản hỗ trợ ghi thành mục riêng trong hợp đồng.\r\n\r\nSo với dự thảo trước đây, Ban soạn thảo đã tiếp thu góp ý và nêu rõ những khoản phụ cấp không tính đóng BHXH trên cơ sở kế thừa quy định hiện hành. Trên thực tế, cơ quan quản lý từng ghi nhận nhiều doanh nghiệp chẻ thu nhập lao động thành nhiều khoản để giảm chi phí đóng BHXH. Lao động thu nhập mỗi tháng hàng chục triệu đồng song tiền đóng BHXH bình quân chỉ nhỉnh hơn lương tối thiểu vùng.\r\n\r\nDự thảo nghị định quy định rõ tiền lương làm căn cứ đóng bắt buộc với một số nhóm thuộc diện mở rộng từ ngày 1/7 khi luật sửa đổi có hiệu lực.\r\n\r\nVới lao động làm việc không trọn thời gian, căn cứ đóng BHXH bắt buộc là tiền lương tính trong tháng theo thỏa thuận tại hợp đồng. Khoản lương tháng được tính tùy thuộc vào việc hai bên thỏa thuận trả lương theo giờ hay theo ngày.\r\n\r\nVới người hoạt động không chuyên trách cấp xã, thôn, tổ dân phố, tiền lương làm căn cứ đóng BHXH là mức phụ cấp hàng tháng. Nếu phụ cấp thấp hơn mức tham chiếu thì căn cứ đóng bằng mức này.\r\n\r\nVới nhóm dân quân thường trực, căn cứ đóng BHXH bắt buộc bằng với mức đóng của hạ sĩ quan, binh sĩ quân đội, chiến sĩ nghĩa vụ trong quân đội, công an, cơ yếu đang theo học được hưởng sinh hoạt phí.\r\n\r\nQuản lý doanh nghiệp, kiểm soát viên, người đại diện phần vốn nhà nước, đại diện phần vốn của doanh nghiệp theo quy định của pháp luật; thành viên hội đồng quản trị, tổng giám đốc, giám đốc, thành viên ban kiểm soát hoặc kiểm soát viên; quản lý hợp tác xã có hưởng tiền lương cũng sẽ tham gia BHXH bắt buộc từ ngày 1/7. Căn cứ đóng của nhóm này chính là tiền lương mà họ được hưởng.\r\n\r\nNếu lương ghi trong hợp đồng và trả cho lao động là ngoại tệ thì căn cứ đóng bắt buộc được tính bằng Việt Nam đồng trên cơ sở chuyển đổi theo tỷ giá do Ngân hàng Nhà nước công bố tại ngày 2/1 cho sáu tháng đầu năm và 1/7 cho nửa năm còn lại.\r\nDự thảo nêu rõ lộ trình đóng BHXH bắt buộc với chủ hộ kinh doanh của hộ kinh doanh có đăng ký nhằm giảm áp lực phải tham gia ngay với nhóm nhỏ lẻ, giúp ngành bảo hiểm xã hội có thời gian quản lý.\r\n\r\nCụ thể, từ 1/7/2025 áp dụng với chủ hộ của hộ có đăng ký kinh doanh nộp thuế theo phương pháp kê khai. Từ 1/7/2027 áp dụng với chủ hộ của hộ kinh doanh đăng ký nộp thuế theo phương pháp khoán. Từ 1/7/2029 áp dụng với chủ hộ của hộ kinh doanh có đăng ký không thuộc hai nhóm trên.\r\n\r\nLuật sửa đổi quy định chủ hộ được lựa chọn tiền lương làm căn cứ đóng BHXH bắt buộc nhưng thấp nhất bằng mức tham chiếu và cao nhất bằng 20 lần khoản trên tại thời điểm đóng, tức dao động 2,34-46,8 triệu đồng. Sau 12 tháng đóng, họ được chọn lại tiền lương làm căn cứ tham gia.\r\n\r\nCả nước có khoảng 5 triệu hộ kinh doanh chia làm hai nhóm: Hơn 2 triệu hộ có đăng ký kinh doanh với doanh thu trên 100 triệu đồng mỗi năm và đang đóng thuế; gần 3 triệu hộ còn lại không đăng ký, có doanh thu thấp như các hộ nông, lâm nghiệp, buôn bán tự do.\r\n\r\nThống kê của ngành thuế cuối năm 2024, hơn 80.000 hộ kinh doanh theo phương pháp kê khai, hơn 2 triệu hộ theo hình thức khoán. Nhiều chủ hộ kinh doanh hết tuổi lao động đang hưởng lương hưu, trợ cấp BHXH, trợ cấp hàng tháng hoặc đóng BHXH bắt buộc theo nhóm khác.\r\n\r\nCuối năm 2024, kết dư Quỹ Bảo hiểm xã hội là 1,29 triệu tỷ đồng với tăng trưởng bình quân 9% mỗi năm. Dự kiến năm nay, số thu BHXH đạt hơn 391.200 tỷ đồng. Lao động tham gia BHXH đạt 20 triệu người, bao phủ gần 43% lực lượng lao động trong độ tuổi. Trong số này, khu vực bắt buộc 17,7 triệu và 2,3 triệu người tham gia tự nguyện.', '1744546227_h139.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:10:27', 'Thời sự', 0, 0),
(151, 'TP HCM sẽ giới thiệu việc làm cho cán bộ bị tinh giản', 'Cán bộ bị tinh giản do sắp xếp bộ máy muốn làm việc sẽ được TP HCM giới thiệu đến doanh nghiệp nhà nước, hỗ trợ khởi nghiệp hoặc kết nối với khối tư nhân.', 'Thông tin được ông Nguyễn Bắc Nam, Phó giám đốc Sở Nội vụ TP HCM, cho biết tại chương trình Dân hỏi - Chính quyền trả lời sáng 16/3 với chủ đề tinh gọn bộ máy và nâng cao chất lượng phục vụ người dân, doanh nghiệp.\r\n\r\nTheo ông Nam, Sở Nội vụ tham mưu cho UBND TP HCM tiếp tục thực hiện chế độ chính sách có liên quan Nghị định 178 của Chính phủ và Nghị quyết 01 của HĐND thành phố về chính sách hỗ trợ tăng thêm đối với cán bộ thuộc diện tinh giản khi sắp xếp bộ máy. Bên cạnh đó, thành phố đang triển khai một số giải pháp giúp người lao động dôi dư được tiếp tục làm việc.\r\nTP HCM huy động doanh nghiệp nhà nước trên địa bàn sẽ tham gia tuyển dụng cán bộ, người lao động dôi dư, tạo điều kiện để lực lượng này tiếp tục làm việc. Đây cũng là cách khai thác hiệu quả nguồn lực của thành phố.\r\n\r\nVới người muốn tự khởi nghiệp, chuyển đổi công việc, thành phố sẽ thông tin và hỗ trợ tiếp cận các chương trình hỗ trợ thúc đẩy vay vốn ưu đãi, hướng dẫn lập kế hoạch kinh doanh kết nối với các quỹ hỗ trợ doanh nghiệp trên địa bàn.\r\n\r\nThông qua Trung tâm dịch vụ việc, TP HCM kết nối với các khu công nghiệp, công nghệ cao, khu vực tư nhân để giới thiệu cán bộ, công chức, người lao động dôi dư đến ứng tuyển, làm việc.\r\n\r\n\"Thành phố sẽ thực hiện đồng bộ nhiều giải pháp hỗ trợ cán bộ dôi dư khi sắp xếp bộ máy nhưng muốn tiếp tục làm việc có cơ hội cống hiến\", ông Nam nói.\r\n\r\nHiện, TP HCM đã hoàn thành giai đoạn xếp các cơ quan chuyên môn, hành chính, đơn vị sự nghiệp trực thuộc UBND TP HCM, TP Thủ Đức và các quận, huyện. Thành phố đang triển khai thực hiện sắp xếp tỉnh, không tổ chức thực hiện chính quyền cấp huyện và sắp xếp chính quyền cấp xã theo chỉ đạo của Trung ương.\r\n\r\nỞ giai đoạn 1, thành phố tính toán có 7.159 nhân sự bị ảnh hưởng. Trong đó 521 cán bộ, công chức, viên chức khối Đảng; cán bộ, công chức (không bao gồm cấp xã) là 2.015 người; viên chức hưởng lương từ ngân sách nhà nước là 2.767 người. Cán bộ, công chức dôi dư do sắp xếp đơn vị hành chính cấp xã theo Nghị quyết 1278 của Ủy ban Thường vụ Quốc hội là 988 người; 418 người phụ trách công tác đảng tại các doanh nghiệp nhà nước bị dôi dư sau sắp xếp và 450 trường hợp không đủ điều kiện tái cử, bổ nhiệm.\r\n\r\nĐể hỗ trợ cho cán bộ dôi dư, TP HCM dự trù 17.000 tỷ đồng để chi trả theo Nghị định 178 và Nghị quyết 01 của HĐND thành phố, trong đó người nhận số tiền cao nhất có thể gần 2,7 tỷ đồng.\r\n\r\nVới nhóm ở lại làm việc trong hệ thống chính quyền, thành phố thực hiện nhiều giải pháp để cán bộ đáp ứng yêu cầu công việc mới. Năm 2025 chính quyền tổ chức 55 lớp cho 32.000 cán bộ, công chức học tập, bồi dưỡng trong và ngoài nước.\r\n\r\n', '1744546298_h140.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:11:38', 'Thời sự', 0, 0),
(152, 'Gần 32% lao động TP HCM tìm việc lương trên 20 triệu đồng mỗi tháng', 'Lao động thành phố tìm việc có lương trên 20 triệu đồng mỗi tháng chiếm 31,76% nhưng chưa đến 10% vị trí tuyển dụng ở doanh nghiệp trả mức này, theo báo cáo của UBND TP HCM.', 'Đó là một phần trong báo cáo của UBND TP HCM với Đoàn đại biểu quốc hội thành phố giám sát thực hiện chính sách pháp luật về phát triển và sử dụng nguồn nhân lực trên địa bàn giai đoạn 2021-2024, chiều 14/3.\r\n\r\nTừ năm 2021 đến 2024, bình quân mỗi năm, Trung tâm Dự báo nhu cầu nhân lực và Thông tin thị trường lao động thành phố (Falmi) khảo sát thông tin hơn 144.000 ứng viên tìm kiếm việc làm. Theo đó, khi xét đến tiêu chí tiền lương khi tìm việc, có 31,76% lao động muốn lương trên 20 triệu đồng mỗi tháng - mức lương có đông người tìm việc mong muốn đạt được nhất. Tiếp theo, mức lương được nhiều lao động lựa chọn là 5-10 triệu đồng mỗi tháng chiếm 25,83%, chỉ 3,64% chấp nhận lương dưới 5 triệu đồng mỗi tháng.\r\nNgược lại, khi khảo sát hơn 285.000 vị trí cần tuyển người ở doanh nghiệp, chỉ 9,77% vị trí có lương trên 20 triệu đồng mỗi tháng, đây cũng là vị trí có nhu cầu tuyển ít nhất trên thị trường. Mức lương 5-10 triệu đồng chiếm đến 44,47% tổng nhu cầu tuyển dụng và có đến 11,37% công việc chỉ trả lương dưới 5 triệu đồng mỗi tháng.\r\n\r\nGS Nguyễn Thiện Nhân (Đoàn đại biểu quốc hội thành phố) cho biết lao động có mong muốn tiền lương cao, thu nhập đủ sống là hoàn toàn chính đáng khi đặt trong bối cảnh của TP HCM và nhà nước, doanh nghiệp cần phải quan tâm.\r\n\r\nTheo ông Nhân, nhiều năm qua tỷ suất sinh của TP HCM thấp nhất cả nước, lao động tại chỗ không đáp ứng được nhu cầu phát triển kinh tế mà phụ thuộc vào lao động nhập cư. Tuy nhiên, đang có xu hướng người di cư không chọn thành phố là điểm đến như trước do chi phí đắt đỏ, tiền lương không kham nổi.\r\n\"Từ những cơ sở này, thành phố cần có những chính sách phù hợp để thu hút doanh nghiệp đến đầu tư, ngành nghề mang đến giá trị gia tăng cao, chi trả tiền lương hợp lý cho lao động\", ông Nhân nói. Về lâu dài để giải quyết bài toán nhân lực, TP HCM cần cùng lúc thực hiện nhiều vấn đề bao gồm tăng tỷ suất sinh, chăm sóc cho người nhập cư và giữ chân sinh viên đến thành phố học.\r\n\r\nTS Lê Đình Kha, Hiệu trưởng Trường cao đẳng kỹ thuật Cao Thắng, cho rằng cần nhìn nhận nhu cầu tuyển dụng của doanh nghiệp. Nhu cầu tuyển dụng trên địa bàn đang tập trung nhiều vào lao động sản xuất, có tay nghề do đó mức lương đưa ra phù hợp với nhu cầu, khả năng chi trả của doanh nghiệp và trình độ tương ứng.\r\n\r\n\"Doanh nghiệp cần chuyên gia không nhiều, cái họ cần rất nhiều là lao động trực tiếp sản xuất\", ông Kha nói.\r\n\r\nTheo báo cáo của UBND TP HCM, lao động tìm kiếm việc làm chiếm số lượng lớn ở khối ngành dịch vụ - thương mại như kinh doanh thương mại, hành chính - văn phòng - biên dịch, kế toán - kiểm toán, quản lý điều hành...\r\n\r\nỞ phía doanh nghiệp, ngành công nghiệp chế biến chế tạo đứng đầu nhu cầu tuyển dụng, tiếp đến các ngành buôn bán, sửa chữa ô tô, cơ khí, khoa học công nghệ...\r\n\r\nVề trình độ, nhóm đi tìm việc và nhu cầu tuyển dụng doanh nghiệp đang có sự chênh lệch. Cụ thể gần 68% lao động tìm việc có trình độ đại học trở lên, cao đẳng gần 15%, còn lại trung cấp, sơ cấp, lao động phổ thông chiếm khoảng 17%\r\n\r\nTrong khi đó, nhu cầu tuyển dụng ở phía doanh nghiệp, chỉ 21% vị trí việc làm cần trình độ đại học trở lên, tỷ lệ này ở trình độ cao đẳng là trên 20%; trung cấp, sơ cấp, lao động phổ thông chiếm gần 59%.\r\nTS Kha cho rằng cần đánh giá sát nhu cầu của doanh nghiệp để có chính sách hỗ trợ đào tạo nhân lực. Ví dụ, không chỉ ở đại học mà ngay ở hệ cao đẳng, những ngành thành phố đang muốn đầu tư cần hỗ trợ học phí cho người học, bất kể đến từ địa phương nào để thu hút sinh viên theo học, tạo nguồn nhân lực cho thành phố.\r\n\r\nNăm 2024, lực lượng lao động tại thành phố là gần 4,9 triệu người, chiếm 51,2% tổng dân số. Lao động làm việc tập trung chủ yếu ở khu vực dịch vụ với gần 66%; khu vực công nghiệp và xây dựng chiếm trên 33%; nông, lâm nghiệp và thủy sản chiếm 1,22%. Tiền lương bình quân mỗi tháng doanh nghiệp chi trả cho lao động (khảo sát 1.750 doanh nghiệp sử dụng 310.444 lao động) là 12,4 triệu đồng.', '1744546371_h141.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:12:51', 'Thời sự', 0, 0),
(153, 'Doanh nghiệp dần cởi mở tuyển dụng lao động trung niên', 'Mất việc khi đã 55 tuổi, ông Vũ, hơn chục năm gắn bó với ngành may, không dám tin sẽ có nơi nhận mình.', 'Ông Nguyễn Hoàng Vũ vốn là quản lý xưởng của một công ty may ở quận Tân Phú. Từ sau Covid-19, đơn hàng của nhà máy sụt giảm nghiêm trọng. Suốt hai năm, mỗi ngày công nhân chỉ làm việc 2-3 giờ, thu nhập giảm mạnh nhưng rất ít người dám nghỉ. Theo ông Vũ, nhà máy hoạt động lâu năm, lao động có thâm niên, phần lớn đã ngoài 40 nên sợ nghỉ ra ngoài không kiếm được việc.\r\nĐể xoay xở, ông Vũ nghỉ việc, nhận trợ cấp thất nghiệp chờ công ty phục hồi nhưng cuối cùng phải rời đi để tìm chỗ mới. Lo lắng lớn nhất của ông khi nghỉ việc là lớn tuổi, khó đáp ứng được tiêu chí \"trẻ, khỏe, nhanh\" của doanh nghiệp sản xuất. Do đó, ông không dám nộp hồ sơ vào các tin tuyển dụng có nội dung \"thường xuyên tăng ca\".\r\n\r\nMấy tháng trước, ông Vũ thấy mẫu tuyển lao động của Công ty TNHH vật tư thiết bị y tế Polaris không giới hạn tuổi, làm giờ hành chính, tiêu chí có kinh nghiệm nên nộp đơn. Sau buổi phỏng vấn, ông được nhận vào vị trí phụ trách nhân sự, chấm công và kiểm tra hàng xuất xưởng. Không chỉ ông, đồng nghiệp ở công ty cũ được giới thiệu sang cũng được giám đốc Polaris tiếp nhận.\r\n\r\nÔng Đinh Doãn Phi Hải, Giám đốc Polaris, cho biết dù hoạt động trong ngành may mặc nhưng các sản phẩm của doanh nghiệp khá đặc thù. Công ty sản xuất 5 dòng sản phẩm liên quan chăm sóc sức khỏe như bó khuỷu tay, cổ tay, gối, đai lưng... có đường may khó vì thiết kế uốn lượn, vòng tròn. Do đó, khi tuyển dụng ông ưu tiên công nhân có kinh nghiệm, tỉ mỉ, cẩn thận. Doanh nghiệp không đặt yêu cầu số lượng mà quan trọng chất lượng nên chấp nhận trả lương theo thời gian, lao động làm giờ hành chính.\r\n\r\n\"Trẻ thì ai cũng thích nhưng với Polaris không phải quan trọng hàng đầu. Do đó, chúng tôi không giới hạn tuổi khi tuyển dụng\", ông Hải nói, thêm rằng hơn 50% lao động ở xưởng sản xuất có độ tuổi ngoài 40.\r\n\r\nKhông chỉ Polaris, nhiều doanh nghiệp đang dần cởi mở với lao động trung niên, tức sẵn sàng tuyển dụng những người ở độ tuổi ngoài 40, theo bà Nguyễn Văn Hạnh Thục, Giám đốc Trung tâm dịch vụ việc làm TP HCM.\r\n\r\nTheo bà Thục, trước đây, các doanh nghiệp đăng tuyển công nhân sẽ ghi rõ độ tuổi từ 18 đến dưới 35. Tuy nhiên, qua ghi nhận ở các sàn giao dịch việc làm trực tiếp lẫn online thời gian qua, khoảng 50% các nhà máy sản xuất, một số ngành dịch vụ, du lịch, bán hàng, nhân viên kho... đã bỏ giới hạn độ tuổi khi đăng tuyển, giúp mở ra cơ hội cho lao động trung niên tìm việc.\r\nPhân tích nguyên nhân, bà Thục cho rằng có nhiều yếu tố khiến doanh nghiệp cởi mở hơn với lao động trung niên. Đầu tiên, lao động trẻ không muốn vào các ngành sản xuất mà mong muốn làm các công việc linh động, tự do, thoải mái về thời gian, địa điểm làm việc. Điều này khiến các doanh nghiệp khó tìm được nguồn nhân lực trẻ để bổ sung dẫn đến khó khăn trong kiếm người. Do đó, doanh nghiệp phải nới độ tuổi tuyển dụng.\r\n\r\n\"Nới độ tuổi hay cởi mở với lao động trung niên là cách doanh nghiệp thích ứng với thị trường. Xu hướng này đang bắt đầu ở những ngành nghề ngày càng khan hiếm nhân sự trẻ\", bà Thục nói.\r\n\r\nTrong khi đó, bà Cao Lê Thanh Loan, Giám đốc cấp cao dịch vụ tư vấn lao động của ManpowerGroup Việt Nam, cho rằng tình hình kinh tế có tác động đến các tiêu chí tuyển dụng của doanh nghiệp, trong đó có độ tuổi. Các khảo sát của ManpowerGroup doanh nghiệp ở các trung tâm sản xuất lớn như TP HCM, Bình Dương, những địa phương mới mở khu công nghiệp, nhà máy mới đầu tư đã dần cởi mở với lao động lớn tuổi hơn.\r\n\r\nNhiều năm làm tư vấn và cung cấp dịch vụ lao động cho doanh nghiệp, đại diện ManpowerGroup Việt Nam cho rằng biểu hiện rõ nhất trong ngành sản xuất, logistics khi nhiều đối tác sẵn sàng tiếp nhận ứng viên đến 50-55 tuổi, tùy vào vị trí công việc.\r\n\r\nTheo bà Loan, lao động trung niên có những rào cản nhất định như tiếp cận công nghệ, học hỏi cái mới nhưng bù lại nhóm này có kỹ năng hồi phục tốt nhất. Đây là kỹ năng cần phải được rèn luyện qua thời gian, biến động của thị trường, doanh nghiệp. Điều này giúp lao động đồng hành với doanh nghiệp khi khó khăn, từ những kinh nghiệm sẵn có họ dễ dàng tìm ra cách làm mới và đặc biệt ít nhảy việc.\r\n\r\nNgoài ra, đại diện ManpowerGroup Việt Nam cho rằng quy định pháp luật, tiêu chí nhãn hàng cũng phần nào tác động đến doanh nghiệp. Doanh nghiệp tuyển dụng dựa vào tuổi, giới tính cũng là biểu hiện của phân biệt đối xử lao động.\r\nTheo bà Loan, Việt Nam bước vào giai đoạn già hóa dân số, người lớn tuổi sẽ chiếm số lượng đáng kể trong lực lượng lao động. Vì vậy không chỉ doanh nghiệp mà chính sách, bản thân người lao động phải có cách thích ứng. Có thể học cách làm của Nhật Bản, khi doanh nghiệp nhận lao động lớn tuổi sẽ được nhà nước hỗ trợ thuế, trợ cấp tái tuyển dụng hoặc kinh phí để cải thiện môi trường làm việc. Nhà nước cũng có các chương trình đào tạo kỹ năng cho lao động để thích nghi công việc mới. Một điều quan trọng là người lao động phải thay đổi, học thêm kỹ năng, chấp nhận những công việc mới phù hợp sức khỏe, độ tuổi và thị trường đang cần.\r\n\r\nHơn 20 năm đeo đuổi ngành may mặc, ông Phi Hải chứng kiến thế hệ công nhân cùng thời \"già theo năm tháng\", nhiều người phải nghỉ việc vì khó theo được yêu cầu sản xuất. Tuy nhiên, ông cho rằng thời \"trẻ, khỏe, nhanh\" không thể kéo dài mãi nên cả xã hội phải tìm cách thích ứng. \"Ngoài phát triển các ngành nghề phù hợp, doanh nghiệp hoàn toàn có thể sắp xếp công đoạn, phân bổ công đoạn phù hợp với sức khỏe, độ tuổi để không lãng phí nguồn lao động\", ông Hải nói.\r\n\r\n', '1744546446_h142.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:14:06', 'Thời sự', 0, 0),
(154, 'U50 ứng tuyển vào công ty điện tử', 'Bắc Giang - Gửi căn cước qua môi giới làm hồ sơ, nửa ngày sau chị Hương, 46 tuổi, đã chốt được lịch phỏng vấn vào làm việc tại nhà máy điện tử ở Bắc Giang.', 'Giữa tháng 2, mất hơn ba tiếng chạy xe máy từ huyện Văn Quan, Lạng Sơn xuống Bắc Giang trong giá rét 13 độ C, chị Nông Thị Hương run vì lạnh. Hai túi quần áo chằng sau yên, trước yếm xe treo lủng lẳng túi đựng mấy bắp ngô luộc. Khăn, mũ trùm kín mít để lộ đôi mắt đỏ ngầu vì gió bụi, chị gọi điện báo nhà cung ứng biết mình và người em họ đã xuống tới nơi, chờ dẫn vào phỏng vấn.\r\n\r\nChị Hương quyết định ứng tuyển công ty điện tử ở Bắc Giang chỉ sau một lần nghe tư vấn từ môi giới tuyển dụng việc làm. Buổi tối rảnh, chị lướt xem điện thoại thấy nhóm môi giới livestream trên mạng xã hội liền vào hỏi thăm cho vui, lập tức có nhân viên nhắn tin tư vấn tận tình về công việc. Hồ sơ nhận việc chỉ cần căn cước công chứng, không mất chi phí. Thu nhập mỗi tháng 9-12 triệu đồng, có thêm tiền thưởng cho nhân viên mới nếu làm đủ ba tháng.\r\n\"Ở quê chăm 6 sào lúa mỗi vụ mới được ngần ấy tiền\", chị Hương làm phép so sánh. Nhìn quanh làng, lớp trung niên tuổi chị trước đây chỉ ở nhà làm ruộng, mấy năm nay đã xuống Bắc Giang, Bắc Ninh làm công nhân. Thanh niên tuổi 18 xuôi Hà Nội học, ngược cửa khẩu buôn bán hoặc chọn ngành dịch vụ nhiều hơn là vào nhà máy. Mất một đêm suy nghĩ, chị quyết định xuống Bắc Giang.\r\n\r\nTrước khi đi, chị Hương chụp ảnh căn cước gửi cho môi giới, chỉ vài tiếng sau đã được xếp lịch hẹn phỏng vấn vào chiều cùng ngày. Gần 12h, chị chằng túi xách quần áo phía sau xe cùng người em họ đi thẳng xuống Bắc Giang. Vốn chỉ quen cầm liềm cầm cuốc, chị lo không biết thao tác máy móc, nhưng được môi giới trấn an \"hướng dẫn vài buổi là biết\".\r\n\r\nCác nhà máy FDI mở rộng sản xuất khiến nhu cầu tuyển dụng năm 2025 của Bắc Giang tăng gần 17% so với năm ngoái, tới 120.000 lao động. Riêng ba tháng đầu năm cần 30.000 người, phần lớn công nhân phổ thông. Doanh nghiệp ứng dụng mọi kênh, một số nới rộng tuổi ứng viên \"tới hết tuổi lao động\".\r\n\r\nÔng Nguyễn Xuân Sơn, Giám đốc Trung tâm Dịch vụ Việc làm tỉnh Bắc Giang, đánh giá nhu cầu về nhân lực lớn khiến nhiều nhà máy mở rộng độ tuổi tuyển dụng 18-45 thay vì giới hạn 18-25 như trước đây. Nhiều người trong số này từng làm công nhân, từ miền Nam về hoặc tham gia thị trường từ trước, có kinh nghiệm nên dễ đáp ứng yêu cầu, tạo thêm việc làm cho lao động địa phương.\r\nMột môi giới lao động cho loạt nhà máy ở Bắc Giang ví von \"trước đây tuyển dụng còn giới hạn độ tuổi, giờ còn thở là nhận\" để nói về mức độ khát công nhân phổ thông vào đầu năm. Khoảng 20% hồ sơ người này giới thiệu vào nhà máy là lao động 35 đến dưới 50 tuổi. Họ cư trú ở các huyện xa của Bắc Giang hoặc tỉnh lân cận, đáp ứng được nhu cầu cấp bách về nhân lực của nhà máy mùa cao điểm, lại có thêm thu nhập lúc nông nhàn mà không phải di cư quá xa như trước.\r\n\r\nKhi dây chuyền sản xuất chuyên môn hóa cao, lao động trung niên vẫn đảm nhận công đoạn chạy máy, kiểm hàng. \"Về tinh thần làm việc, nhóm lớn tuổi thậm chí kiên trì, tỉ mỉ, tính tình dễ chịu hơn nhóm thanh niên\", một môi giới nhận xét. Ra giêng, anh thường nhận tin nhắn hỏi thăm nhà máy nào tuyển, chỗ nào thu nhập tốt hơn từ lao động lớn tuổi từng giới thiệu. Hầu hết họ được tuyển dụng vì biết việc lại không bỏ ngang.\r\n\r\nMôi giới này nhận định lao động U50 sẽ là lực lượng cung ứng thường xuyên cho các nhà máy vào đầu hoặc cuối năm, bởi họ đã ổn định gia đình, cần tiền cho con cái đi học, không chán là nhảy việc như người trẻ. Trước đại dịch Covid, nhà máy còn kén chọn công nhân tuổi đôi mươi, nhưng xu hướng người trẻ dần ngại việc chân tay trong nhà máy mà chuộng các ngành dịch vụ hơn. Vì thế bộ phận tuyển dụng đi khắp các bản làng \"vét\" lao động tuổi 40-45. Với xu hướng này, mấy năm tới lao động U40, U50 còn khó tìm.\r\nChiều cuối tháng 2, nhiều lao động trung niên xếp hàng cùng thanh niên trước cổng một nhà máy điện tử KCN Đình Trám đợi dẫn vào phỏng vấn. Chị Hoài cùng bạn đi xe máy hơn 100 km từ huyện Lục Ngạn xuống, cầm sẵn căn cước công dân. Người phụ nữ 40 tuổi để con út đang học mẫu giáo với đồi vải cho chồng chăm, để đi làm cho nhà máy điện tử.\r\n\r\nĐi làm ở công ty là cách duy nhất để chị có thể chu cấp mỗi tháng 5 triệu đồng sinh hoạt, chưa tính học phí cho con trai đang học đại học dưới Hà Nội. Chị Hoài kêu bói không ra mỗi tháng ngần ấy tiền nếu chỉ trông vào vụ vải thiều. Năm ngoái vải mất mùa, những nhà thu hoạch tốt chỉ được 40%. Chị trừ các khoản, phải vay mượn thêm mới đủ đóng học phí cho con. Trạc tuổi chị trong làng đi hết, chị Hoài và bạn cũng rủ nhau quay lại khu công nghiệp.\r\n\r\n\"Ổn thì cố vài năm tới khi con học xong, còn không chịu được thì nghỉ\", chị nói, không kỳ vọng công việc cho thăng tiến hay củng cố tay nghề, chỉ cần có tiền nuôi con. Cả hai phỏng vấn xong sẽ lại về Lục Ngạn, được nhận thì sáng đi chiều về bằng xe ghép tuyến huyện như công nhân trong làng. Các nhà máy đều hỗ trợ đi lại khoảng 300.000 đồng mỗi tháng, chị Hoài chấp nhận bỏ thêm vài trăm nghìn mua vé tháng như xe buýt để không mất khoản ăn uống, thuê trọ.\r\n\r\nKhông thuận tiện như chị Hoài, chị Nông Thị Hương cùng em họ sẽ phải thuê phòng trọ gần công ty. Cả hai dự định làm đến khi nào khu công nghiệp trên quê tuyển dụng sẽ xin về gần nhà.', '1744546517_h143.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:15:17', 'Thời sự', 0, 0),
(155, 'Nghề môi giới tìm việc cho công nhân', 'Bắc Giang - Cổng nhà máy sau Tết tấp nập khi lao động quanh vùng về tìm việc, Hào cầm xấp thẻ ra vào cùng giấy tờ in sẵn, bấm số gọi người đã hẹn trước.', 'Nhận ra cô gái 20 tuổi quê Cao Bằng cầm điện thoại đang vẫy vẫy tay, Lâm Hào (tên đã thay đổi) đối chiếu thông tin căn cước, dặn dò mấy điều cơ bản rồi đưa thẻ khách. \"Đội mũ vào, chỉnh gương xe lại\", anh gọi theo khi cô gái phóng xe qua cổng nhà máy chuyên sản xuất thiết bị điện tử ở huyện Việt Yên.\r\n\r\nXong xuôi, nam thanh niên lại dúi mắt vào điện thoại gửi thông tin tuyển dụng, gửi thông báo mới lên nhóm: \"Các bạn đã ký hợp đồng chiều nay, 7h30 ngày mai có mặt ở phòng đào tạo số 2 nhà máy để nhận diện khuôn mặt làm thẻ. Khi tới nhớ đi giày hoặc dép quai hậu, bút viết, xe máy, đội mũ bảo hiểm có gương\". Một quy trình gọi điện - nhận diện - đưa thẻ lặp lại trước cổng nhà máy.\r\nBắc Giang ba năm liền dẫn đầu cả nước về tốc độ tăng trưởng kinh tế, đạt bình quân 14% giai đoạn 2017-2024. Dòng vốn FDI năm ngoái đổ về 2,23 tỷ USD, đứng thứ 11 toàn quốc. Các nhà máy đua nhau mở rộng sản xuất khiến nhu cầu tuyển dụng tăng cao, riêng năm nay tăng gần 17% so với cùng kỳ 2024, lên mức 120.000 lao động phần lớn lĩnh vực điện tử.\r\n\r\nĐể mở rộng nguồn tuyển, doanh nghiệp đồng loạt áp dụng nhiều kênh, trong đó có thưởng nhân viên nội bộ giới thiệu người quen vào làm. Khoản này bình quân 2-3 triệu đồng, có thể tăng gấp đôi vào thời điểm cần gấp nhân công như sau Tết hoặc cuối năm.\r\n\r\nChính sách tạo ra các \"nhà cung ứng\" nghiệp dư từ hệ thống nhân sự nội bộ, hình thành mạng lưới chuyên tìm kiếm và tuyển được số lượng đáng kể lao động vào nhà máy. Họ phần lớn là nhân viên tuyển dụng chính thức của doanh nghiệp - bộ phận nắm rõ chính sách và có thẻ \"thông hành\" qua cổng.\r\n\r\nHào là nhân sự phòng tuyển dụng của doanh nghiệp điện tử đóng ở huyện Việt Yên, có gần sáu năm làm môi giới lao động. Trước khi vào công ty, thanh niên quê Bắc Giang đã làm môi giới công nhân từ năm 2019, sớm nhìn ra tiềm năng của thị trường khi các nhà máy FDI lần lượt mọc lên trên quê mình.\r\n\r\n\"Bắc Giang giờ chỗ nào cũng thấy công ty, quanh năm không lo thiếu việc\", Hào tự hào nói. Ngoài công ty chính thức, anh còn là đầu mối cung ứng nhân công cho một số nhà máy khác tùy lĩnh vực và vị trí cần người.\r\n\r\nXong việc văn phòng, thời gian còn lại trong ngày Hào dành chăm sóc các hội nhóm, nơi có hàng trăm thành viên tìm việc và đăng tin khi có vị trí tuyển công nhân. Lao động chỉ cần để lại bình luận lập tức có cộng tác viên liên hệ hỏi thông tin và hướng dẫn cụ thể. Người hỏi phần lớn cần công việc lâu dài, đi làm gấp nên thường chỉ nửa buổi đã chốt xong. Môi giới tính toán mỗi nhóm 400-500 người, \"cứ có tin tuyển dụng là đăng, kiểu gì cũng tìm được 4-5 người cần việc\".\r\n\r\nCó lịch phỏng vấn, Hào trực tiếp đón người và giao thẻ qua cửa nhà máy. Lao động chỉ cần cầm theo căn cước công dân, một số giấy tờ photo theo yêu cầu của doanh nghiệp. Người trúng tuyển cùng ngày được gom vào nhóm chat riêng, đặt tên bằng chữ cái viết tắt của nhà máy kèm thời gian để nhận những thông báo tiếp theo. Chỉ trưởng, phó nhóm mới có quyền gửi tin cho các thành viên.\r\n\r\nMỗi lao động ký hợp đồng đi làm chính thức, môi giới nhận được thưởng nóng. Tiền thưởng thường được chia theo tỷ lệ 70/30, tức đầu mối trực tiếp trong doanh nghiệp hưởng 70%, phần còn lại thuộc về đại lý cấp một như Hào cùng các cộng tác viên phía dưới.\r\n\r\nHoa hồng 30% dao động 0,5- 0,7 triệu đồng lúc thấp điểm và lên 2 triệu đồng mùa cao điểm. Hào nhận 100.000-150.000 đồng mỗi đơn hàng thành công, còn lại chia cho cộng tác viên phía dưới. Anh tính toán \"không cần ăn dày, chỉ cần lấy số đông để bù lại\".\r\n\r\nMùa cao điểm, mỗi ngày cộng tác viên có thể giới thiệu 20-30 ứng viên. Một nửa này đậu thì khoản hoa hồng Hào nhận được khoảng 1-1,5 triệu đồng. Cộng tác viên nhận mức 300.000- 400.000 đồng, nhưng cần bỏ công chăm sóc, hướng dẫn tốn thời gian hơn.\r\nNăm đầu tiên, Hào ôm hết các khâu để hưởng trọn hoa hồng. Khi nguồn tuyển mở rộng, công việc quá tải, bữa trưa chỉ kịp gặm bánh mì, anh đổi chiến thuật, tạo mạng lưới cộng tác viên còn mình quản lý. Nghề tay trái mang về cho anh thu nhập gấp đôi, ba lần vị trí chính thức trong văn phòng nhân sự.\r\n\r\nMỗi lao động vào nhà máy đều có thể trở thành cộng tác viên cho Hào, giúp mở rộng mạng lưới. Mùa cao điểm, đại lý này có thể tìm kiếm khoảng 1.000 công nhân. Nguồn tuyển mở rộng theo anh \"công ty chỉ có lợi mà không mất gì, mình cũng có thêm thu nhập chân chính\".\r\n\r\nMôi giới tuyển dụng vì thế cạnh tranh khá gay gắt. Một số nhóm chuyên nghiệp hàng chục thành viên, có đồng phục nhận diện và làm toàn thời gian. Để chủ động nguồn tuyển, một số công ty tự đào tạo bộ phận nhân sự chuyên nghiệp, thậm chí thành các streamer để quảng bá. Nhưng dù hoạt động hết công suất cũng không thể nào đáp ứng số lượng tuyển dụng lớn. Các nhà cung ứng nghiệp dư vì thế vẫn là kênh tìm người hiệu quả.\r\n\r\nNhưng thị trường này cũng có nguyên tắc riêng. \"Lao động đến cổng nhà máy tức là đã liên hệ trước với đại lý, hẹn với ai người đó dẫn vào nhà máy, không tranh giành\", anh kể. Đôi lúc vẫn có sự cố như bị nói xấu để hạ uy tín, chểnh mảng là bị \"hớt tay trên\" nguồn cộng tác viên bằng cách chia hoa hồng cao hơn. Ngón tay Hào vì thế gần như không rời bàn phím điện thoại, kể cả những khi tranh thủ về thăm nhà.\r\n\r\n\"Việc gì kiếm ra tiền mà không phạm pháp thì cứ làm chứ\", Hào nói, khẳng định nhà máy còn tuyển thì anh vẫn còn nghề.', '1744546636_h144.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:17:16', 'Thời sự', 0, 0),
(156, 'TP HCM ra mắt thẻ thanh toán liên thông buýt và metro', 'Thành phố ra mắt thẻ MultiPass cho phép thanh toán liên thông giữa buýt và metro, giúp khách thuận tiện di chuyển giữa các loại hình giao thông công cộng, ngày 11/4.', 'Bà Nguyễn Thị Dạ Thảo, Giám đốc trung tâm quản lý giao thông công cộng TP HCM, cho biết đây là loại thẻ do Sở Giao thông công chánh TP HCM phối hợp ngân hàng Sacombank và Mastercard phát hành, áp dụng cho phương tiện giao thông công cộng lắp máy POS.\r\n\r\nViệc thiết kế thẻ MultiPass được hướng đến dành cho những người thường xuyên sử dụng xe buýt và metro ở thành phố, như học sinh, sinh viên, người đi làm hàng ngày...\r\nVới khả năng liên thông giữa các loại hình, khách có thể dùng một thẻ để thanh toán mà không cần trả tiền mặt hay các loại thẻ khác nhau. Giải pháp này sắp tới sẽ mở rộng với toàn bộ các loại hình giao thông cộng ở TP HCM, như buýt sông, xe đạp công cộng. Đây cũng nằm trong lộ trình triển khai các chính sách giá vé liên thông, ưu đãi linh hoạt đối với các nhóm khách khác nhau, khuyến khích người dân sử dụng giao thông công cộng.\r\n\r\nCùng với loại thẻ trên, ngành giao thông TP HCM mở rộng phương thức thanh toán không tiền mặt trên xe buýt, metro, theo công nghệ Open loop. Hệ thống này cho phép hành khách lựa chọn các hình thức thanh toán như: thẻ tín dụng, ghi nợ, thẻ trả trước EMV, ví điện tử... Hiện, ngoài metro Bến Thành - Suối Tiên, gần 1.000 xe buýt ở thành phố đã áp dụng mô hình trên và dự kiến tăng lên hơn 2.000 xe trong tháng 5 tới. Song song hình thức này, khách vẫn có thể dùng thẻ, vé, tiền mặt như trước để thanh toán.\r\n\r\nTheo Giám đốc Sở Giao thông công chánh TP HCM Trần Quang Lâm, trong các mục tiêu phát triển giao thông công cộng trên địa bàn, ngoài xây dựng và mở rộng mạng lưới, thành phố là nơi tiên phong áp dụng các mô hình thẻ vé thông minh. Giải pháp này không chỉ tạo thuận lợi cho hành khách, phù hợp với xu hướng chung, còn giúp hình thành cơ sở dữ liệu, số hóa thông tin. Đây là cơ sở để cơ quan quản lý xác định rõ hơn thói quen, nhu cầu đi lại của người dân, từ đó có những điều chỉnh nếu chưa phù hợp.\r\n\r\nTP HCM hiện có 138 tuyến xe buýt với hơn 2.200 đầu xe, bình quân mỗi ngày khai thác gần 13.000 chuyến, phục vụ hơn 350.000 lượt khách. Cuối năm 2024, metro Bến Thành - Suối Tiên, tuyến tàu điện đầu tiên ở TP HCM vận hành, đã áp dụng nhiều phương thức thanh toán. Tuy nhiên, nhiều người vẫn đang sử dụng tiền mặt, chưa quen dùng các loại thẻ vé thông minh.', '1744546690_h145.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:18:10', 'Thời sự', 0, 0),
(157, 'Cầu chờ 2-3 năm mới khai thác do chưa có đường kết nối', 'Cầu vượt sông Đáy nối Ninh Bình với Nam Định đã thông xe kỹ thuật song phải chờ 2-3 năm để đấu nối với đường còn lại thuộc cao tốc Ninh Bình - Hải Phòng.', 'Dự án cầu vượt sông Đáy nối huyện Nghĩa Hưng, tỉnh Nam Định với huyện Yên Khánh, tỉnh Ninh Bình có tổng vốn 1.450 tỷ đồng, do Ban Quản lý dự án đầu tư xây dựng tỉnh Nam Định làm chủ đầu tư.\r\nCông trình quy mô 4 làn xe, vận tốc thiết kế 100 km/h gồm phần cầu vượt sông dài 1,36 km và đường dẫn dài 0,64 km, bề rộng nền đường 19 m. Cầu chính có 29 nhịp, kết cấu dầm bêtông cốt thép dự ứng lực. Ngoài ra, dự án còn gồm các tuyến đường nhánh, nút giao và công trình phụ trợ.\r\n\r\nCầu vượt sông Đáy nhằm kết nối các tuyến giao thông của hai tỉnh Ninh Bình và Nam Định, từng bước hoàn thiện tuyến đường bộ cao tốc Ninh Bình - Hải Phòng, tăng cường tính kết nối liên vùng đồng bằng sông Hồng, giảm tải cho hệ thống giao thông hiện hữu và thúc đẩy phát triển kinh tế xã hội.\r\nSau gần một năm rưỡi thi công, đến nay cầu vượt đã hoàn thành song chưa thể khai thác đúng thiết kế. Hiện hai đầu cầu được rào chắn và cắm biển cảnh báo do chưa đấu nối với các đoạn đường còn lại thuộc cao tốc Ninh Bình - Hải Phòng.\r\n\r\nTheo kế hoạch của UBND tỉnh Ninh Bình, đoạn cao tốc qua địa bàn tỉnh (nơi kết nối trực tiếp với cầu vượt sông Đáy) dài hơn 25 km, tổng vốn gần 6.900 tỷ đồng được đầu tư bằng ngân sách, dự kiến khởi công trong tháng 4, hoàn thành vào năm 2026.\r\n\r\nDự án cao tốc Ninh Bình - Hải Phòng (CT 08), đoạn qua Nam Định và Thái Bình, dài gần 61 km được đầu tư theo hình thức PPP với tổng vốn gần 19.800 tỷ đồng, dự kiến hoàn thành vào năm 2027.\r\nToàn tuyến cao tốc Ninh Bình - Hải Phòng sẽ được khai thác đồng bộ năm 2028. Như vậy, cầu vượt sông Đáy dù đã hoàn thành, nhưng cần chờ thêm 2-3 năm mới khai thác đúng thiết kế khi toàn tuyến cao tốc liên quan hoàn tất.\r\n\r\nTheo đại diện chủ đầu tư, người dân hai tỉnh Ninh Bình, Nam Định tạm thời có thể lưu thông qua cầu trong một số trường hợp, chủ yếu là phục vụ nhu cầu nội vùng hoặc dân sinh.', '1744546769_h146.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:19:29', 'Thời sự', 0, 0),
(158, 'Giá vé máy bay dịp 30/4 cao nhất 4,5 triệu đồng', 'Cục Hàng không Việt Nam ghi nhận giá vé cao nhất chặng Hà Nội - Phú Quốc khoảng 4,5 triệu đồng một lượt, đã bao gồm thuế, phí.\r\n\r\n', 'Ngày 10/4, Cục Hàng không Việt Nam đã khảo sát giá vé trên một số đường bay. Kết quả cho thấy trong ngày đầu nghỉ lễ (29/4), giá vé hạng phổ thông chặng Hà Nội - TP HCM được niêm yết từ 3,4 đến 3,74 triệu đồng/lượt, đã bao gồm các loại thuế, phí.\r\n\r\nCụ thể, hãng Bamboo Airways và Vietnam Airlines niêm yết giá cao nhất 3,6-3,7 triệu, trong khi Vietjet Air cung cấp mức giá từ 2,9 đến 3,68 triệu, Vietravel Airlines là 3,41 triệu đồng. Các mức giá này tương đương dịp Tết Nguyên đán 2025.\r\n\r\nTrên chặng TP HCM - Đà Nẵng, giá vé chiều đi ngày 29/4 ghi nhận ở mức cao, từ 2,5 triệu đồng với Vietnam Airlines, trong khi Vietjet Air và Bamboo Airways trong khoảng 1,5 đến 2,2 triệu đồng.\r\n\r\nChặng Hà Nội - Đà Nẵng, giá vé cao hơn, từ 2,3 tới 3,5 triệu đồng với Vietnam Airlines, các hãng còn lại 1,9-2,4 triệu đồng.\r\nChặng Hà Nội - Phú Quốc, Vietnam Airlines có giá cao nhất tới 4,5 triệu đồng, còn Vietjet Air dao động 2,7-4,3 triệu; chặng TP HCM - Phú Quốc từ 1,6 tới 2,7 triệu đồng.\r\n\r\nChặng Hà Nội - Cam Ranh, các hãng đưa ra mức giá từ 3,3 tới 3,7 triệu đồng.\r\n\r\nỞ chiều ngược lại, từ các thành phố du lịch trở lại Hà Nội và TP HCM giá vé tăng dần trong các ngày cuối của kỳ nghỉ lễ. Ngày 4/5, giá vé chặng Nha Trang -Hà Nội cao nhất của VietJet Air là 3,68 triệu đồng, trong khi các hãng còn lại hiện tại đã hết vé hạng phổ thông.\r\n\r\nCòn từ Nha Trang về TP HCM, Vietjet Air ghi nhận mức giá 1,8 triệu đồng, trong khi Vietnam Airlines cao nhất lên tới 2,7 triệu đồng.\r\n\r\nChặng Phú Quốc - Hà Nội, ngày 4/5 đều đã hết vé hạng phổ thông, trong khi chặng về TP HCM giá vé của Vietnam Airlines từ 1,9 tới 2,7 triệu đồng. Các hãng còn lại đều ghi nhận mức giá trung bình là 1,8 triệu đồng.\r\n\r\nNhiều chuyến bay đã kín chỗ trong dịp cao điểm 30/4. Theo Cục Hàng không Việt Nam, tỷ lệ đặt chỗ từ Hà Nội, TP HCM đến các địa phương đa phần đã đạt trên 50% trong các ngày đầu nghỉ lễ (ngày 29-30/4).\r\n\r\nMột số đường bay đã đạt từ 85 đến 100% tỷ lệ đặt chỗ trong ngày 30/4, như các chặng Hà Nội - Huế (89%), Hà Nội - Tuy Hòa (94%), Hà Nội - Chu Lai (95%), Hà Nội - Đồng Hới (103%), TP HCM - Phú Quốc (87%%), TP HCM - Côn Đảo (92,7%), TP HCM - Tuy Hòa (100%)...\r\n\r\nVào cuối kỳ nghỉ lễ (3-4/5), tỷ lệ đặt chỗ tăng cao trên các đường bay từ thành phố du lịch về lại TP HCM và Hà Nội, như: Nha Trang - TP HCM (98%), Tuy Hòa - TP HCM (97%), Phú Quốc - Hà Nội (100%), Tuy Hòa - Hà Nội (99%), Đồng Hới - Hà Nội (103%)...\r\n\r\nĐối với các đường bay trục như TP HCM đi Đà Nẵng, Hà Nội; Hà Nội đi Đà Nẵng, TP HCM, tỷ lệ lấp đầy đạt từ 30 đến 60%.\r\n\r\nDịp nghỉ lễ 30/4-1/5, các hãng trong nước dự kiến thực hiện 7.536 chuyến bay, cung ứng khoảng 1,5 triệu ghế, trung bình 685 chuyến mỗi ngày, tăng hơn 20% so với ngày thường và cùng kỳ năm trước. Số chuyến bay được tăng thêm do các hãng bổ sung gần 10 máy bay so với cùng kỳ năm trước, nâng tổng số lên hơn 200 chiếc.\r\n\r\nNhà chức trách hàng không khuyến cáo hành khách mua vé qua các kênh bán chính thức, theo dõi kế hoạch khai thác của các hãng trong dịp cao điểm.\r\n\r\nNgày giải phóng miền Nam, thống nhất đất nước 30/4 và quốc tế lao động 1/5, công chức, viên chức và người lao động được nghỉ 5 ngày liên tiếp, từ thứ tư đến hết chủ nhật (30/4-4/5), do đã hoán đổi ngày làm việc thứ sáu 2/5 sang thứ bảy 26/4 của tuần trước đó.', '1744546828_h147.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:20:28', 'Thời sự', 0, 0);
INSERT INTO `news` (`id`, `title`, `description`, `content`, `image`, `author`, `created_at`, `category`, `is_featured`, `views`) VALUES
(159, 'Bình Dương muốn dùng chung depot metro với TP HCM', 'Bình Dương đề nghị dùng chung depot Long Bình của metro Bến Thành - Suối Tiên, cho dự án tàu điện đầu tiên của tỉnh kết nối TP HCM về Thủ Dầu Một.', 'Đề nghị được UBND tỉnh Bình Dương nêu trong công văn vừa gửi TP HCM. Việc sử dụng hạ tầng ở Depot Long Bình chỉ mang tính tạm thời trong giai đoạn đầu tuyến metro của tỉnh đầu tư hoàn thành. Sau đó, địa phương sẽ xây một depot mới để phục vụ tuyến.\r\nTheo UBND tỉnh Bình Dương, tuyến metro đầu tiên của tỉnh được nghiên cứu với tổng chiều dài khoảng 32,5 km, qua 4 thành phố: Tân Uyên, Thủ Dầu Một, Thuận An và Dĩ An. Tổng mức đầu tư dự án hiện ước tính sơ bộ khoảng 64.370 tỷ đồng.\r\n\r\nĐiểm đầu tuyến (ga S1) tại phường Phú Hòa, TP Thủ Dầu Một; điểm cuối ở ga bến xe Suối Tiên (gần bến xe Miền Đông mới), thuộc metro Bến Thành - Suối Tiên tại TP HCM. Đây cũng là nhà ga cuối của tuyến metro tại thành phố, dẫn vào depot Long Bình.\r\nDepot nêu trên được quy hoạch tổng diện tích 26 ha, trong đó giai đoạn một của metro Bến Thành - Suối Tiên đã xây dựng 20 ha với 30 đường đỗ tàu. Cùng với 17 đoàn tàu thuộc tuyến metro này đang khai thác, Bình Dương dự kiến vận hành 10 tàu giai đoạn đầu dự án của tỉnh đưa vào vận hành.\r\n\r\nTrước đó, các đơn vị chức năng của hai địa phương cùng với đơn vị vấn làm việc cũng đã thống nhất phương án sử dụng chung depot Long Bình trong giai đoạn đầu. Sau này, Bình Dương dự kiến đầu tư depot mới tại TP Tân Uyên.\r\nTheo quy hoạch được Thủ tướng phê duyệt năm 2021, hệ thống metro kết nối vùng sẽ có 8 tuyến. Đó là tuyến Trảng Bom - Hòa Hưng, Biên Hòa - Vũng Tàu, Dĩ An - Lộc Ninh, TP HCM - Cần Thơ, TP HCM - Nha Trang, Thủ Thiêm - Long Thành, TP HCM - Tây Ninh và các tuyến đường sắt chuyên dụng nối cảng Hiệp Phước.\r\n\r\n', '1744546886_h148.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:21:26', 'Thời sự', 0, 0),
(160, 'Khởi công cầu Mã Đà nối Đồng Nai và Bình Phước vào tháng 6', 'Cầu Mã Đà nối Đồng Nai với Bình Phước dự kiến được khởi công vào tháng 6 bằng vốn ngân sách, hoàn thành trong năm 2025.', 'Theo kế hoạch xây dựng cầu Mã Đà của tỉnh Đồng Nai vừa ban hành, sau khi xây xong cầu, hai tỉnh sẽ đầu tư nâng cấp ĐT 753 (phía Bình Phước) và ĐT 761, 767 (phía Đồng Nai), xây mới đường kết nối Vành đai 4 TP HCM với quy mô 8 làn xe. Thời gian thực hiện từ tháng 6/2026 đến năm 2028.\r\nTheo phương án được Đồng Nai và Bình Phước thống nhất, đề xuất Thủ tướng trước đó, đường kết nối hai tỉnh giai đoạn 1 sẽ có 4 làn xe. Trong đó, cầu Mã Đà được đầu tư khoảng 220 tỷ đồng và đường kết nối từ khu vực giáp ranh đến đường Vành đai 4 TP HCM dài 44 km khoảng 10.800 tỷ đồng (riêng chi phí giải phóng mặt bằng khoảng 2.000 tỷ đồng).\r\n\r\nĐể thực hiện tuyến đường, Đồng Nai sẽ thu hồi, chuyển mục đích sử dụng đất rừng đặc dụng khoảng 45 ha. Giai đoạn 2, dự án sẽ đầu tư đồng bộ thêm 4 làn xe còn lại ở cầu Mã Đà và tuyến đường kết nối. Tỉnh cũng dự kiến sẽ xây 5 km cầu cạn qua khu vực rừng tự nhiên thuộc Khu bảo tồn thiên nhiên văn hóa Đồng Nai.\r\n\r\nĐoạn tuyến từ ngoài ranh khu bảo tồn đến giao với đường Vành đai 4 TP HCM sẽ mở mới 17 km song song với đường ĐT 767 hiện hữu (cách khoảng 3,5 km về phía tây). Ngoài ra, Đồng Nai dự kiến sẽ xây dựng thêm nhiều tuyến đường kết nối giữa 2 tỉnh trong tương lai.\r\nTrước đó, Bình Phước nhiều lần đề xuất làm đường giao thương hai tỉnh, kết nối sân bay và cảng biển, rút ngắn 60 km so với đi đường hiện tại. Tuy nhiên phía Đồng Nai không đồng ý do lo ngại đường xuyên qua rừng sẽ ảnh hưởng Khu bảo tồn thiên nhiên văn hóa Đồng Nai được UNESCO công nhận là khu dự trữ sinh quyển thế giới.\r\n\r\nSau đó, lãnh đạo hai tỉnh đã có các buổi khảo sát và họp bàn thống nhất phương án xây đường kết nối, giao Đồng Nai chủ trì.', '1744546955_h149.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:22:35', 'Thời sự', 0, 0),
(161, 'Cao tốc Hà Nội - Thái Nguyên có thể được mở rộng 6 làn xe', 'Tuyến cao tốc Hà Nội - Thái Nguyên hiện có 4 làn xe, được nhà đầu tư đề xuất mở rộng 6 làn xe theo quy hoạch trước năm 2030.', 'Công ty CP Đầu tư và Xây dựng giao thông Phương Thành (Phương Thành Tranconsin) vừa đề xuất Bộ Xây dựng đầu tư mở rộng hoàn chỉnh tuyến cao tốc Hà Nội - Thái Nguyên theo phương thức đối tác công tư (PPP).\r\n\r\nNhà đầu tư cam kết hoàn thành hồ sơ nghiên cứu dự án trong 4 tháng, trình cơ quan có thẩm quyền xem xét, phê duyệt chủ trương đầu tư và sẽ tham gia đấu thầu lựa chọn nhà đầu tư theo quy định.\r\n\r\nTuyến cao tốc CT07 Hà Nội - Thái Nguyên dài gần 71 km, đi qua Hà Nội, Bắc Ninh và Thái Nguyên, gồm hai đoạn. Đoạn một từ nút giao Ninh Hiệp (huyện Gia Lâm, Hà Nội) đến nút giao Tân Lập (Thái Nguyên) được đầu tư từ nguồn vốn vay ODA của Chính phủ Nhật Bản. Quy mô cao tốc trên địa bàn Hà Nội và Bắc Ninh là 2-4 làn xe, vận tốc thiết kế 80-100 km/h.\r\n\r\nĐoạn hai từ nút giao Tân Lập đến nút giao Tân Long (Thái Nguyên) đã được đầu tư từ nguồn vốn trái phiếu Chính phủ, có 4 làn xe hoàn chỉnh, vận tốc thiết kế 100 km/h.\r\nTheo Quy hoạch phát triển mạng lưới đường bộ thời kỳ 2021-2030, tầm nhìn đến năm 2050, tuyến cao tốc Hà Nội - Thái Nguyên - Bắc Kạn - Cao Bằng quy mô 4-6 làn xe hoàn chỉnh. Trong đó, đoạn Hà Nội - Thái Nguyên 6 làn xe và đầu tư trước năm 2030.\r\n\r\nQuy hoạch thời kỳ 2021-2030, tầm nhìn đến năm 2050 xác định Thái Nguyên là một trong những trung tâm công nghiệp phát triển hiện đại, bao gồm 11 khu công nghiệp và một khu công nghệ thông tin tập trung, 41 cụm công nghiệp.\r\n\r\nTỉnh Thái Nguyên còn tập trung phát triển du lịch dựa trên thế mạnh về du lịch nghỉ dưỡng, sinh thái gắn với lịch sử, văn hóa, tâm linh. Do đó, việc đầu tư xây dựng tuyến Hà Nội - Thái Nguyên thành cao tốc hoàn chỉnh được đánh giá là cần thiết.', '1744547023_h150.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:23:43', 'Thời sự', 0, 0),
(162, 'Ôtô liên tiếp đâm hỏng gác chắn đường sắt', 'Từ ngày 5 đến 9/4, trên một số đường ngang xảy ra bốn vụ tài xế ôtô đâm hỏng gác chắn tự động của ngành đường sắt, suýt va chạm với tàu hỏa.\r\n\r\n', 'Ngày 5/4, Tổng công ty Đường sắt Việt Nam (VNR) đã phối hợp với công an tỉnh Bình Định xử phạt 11 triệu đồng, tước giấy phép lái xe 2 tháng với một lái xe tải không chấp hành tín hiệu cảnh báo đường ngang, cố tình vượt qua đường sắt khi tàu sắp đến, làm gãy gác chắn dài 3,3 m.\r\n\r\nBa ngày sau, tối 8/4, tại đường ngang có gác chắn tự động ở xã Lê Thiện, huyện An Dương, TP Hải Phòng, khi đoàn tàu chuẩn bị chạy qua, tín hiệu cảnh báo được kích hoạt, chuông kêu, đèn đỏ nhấp nháy và gác chắn tự động hạ xuống, nhưng tài xế xe tải vẫn cố tình vượt qua, đâm vào gác và mắc kẹt trên đường ray. May mắn tài xế kịp lùi ra khỏi đường ray trước khi tàu đến.\r\n\r\nTheo VNR, sự việc không xảy ra tai nạn đường sắt song một bên gác chắn tại đường ngang bị cong vênh.\r\nChỉ sau 60 phút, lúc 20h tại đường ngang ở thị trấn Cẩm Giàng, tỉnh Hải Dương, khi chuẩn bị có tàu chạy qua, tín hiệu cảnh báo tự động được bật, nhưng một tài xế xe tải cố tình vượt qua, đâm vào chắn. Hậu quả gác chắn hư hỏng, xoay vào trong lòng đường sắt. Đơn vị thông tin tín hiệu đường sắt đã sửa chữa gác chắn và cử người cảnh giới, đảm bảo an toàn cho các đoàn tàu đi qua.\r\n\r\nSáng 9/4, tại đường ngang xã Hố Nai 3, huyện Trảng Bom, tỉnh Đồng Nai, đoàn tàu SE3 chuẩn bị thông qua, tín hiệu cảnh báo tự động đã bật, gác chắn đang hạ xuống thì một xe tải cố vượt qua làm hỏng gác.\r\n\r\nTheo đại diện VNR, gần đây vi phạm Luật Trật tự an toàn giao thông đường bộ và Luật Đường sắt có chiều hướng gia tăng, đặc biệt là tại các đường ngang có gác chắn tự động, gây thiệt hại tài sản và uy hiếp an toàn giao thông đường sắt. Riêng trong quý I đã xảy ra 116 vụ tài xế cố tình vượt đường ngang.', '1744547095_h151.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:24:55', 'Thời sự', 0, 1),
(163, 'Nhiều dự án giao thông trọng điểm sẽ khởi công trong tháng 5', 'Bộ trưởng Xây dựng Trần Hồng Minh yêu cầu các đơn vị dự án hoàn tất thủ tục để khởi công các dự án La Sơn - Hòa Liên, Dầu Giây - Tân Phú và Mỹ An - Cao Lãnh trong tháng 5.', 'Bộ trưởng Xây dựng Trần Hồng Minh vừa có công điện yêu cầu các ban quản lý dự án khẩn trương hoàn thiện thủ tục đối với dự án đã được giao kế hoạch vốn năm 2025 nhằm đảm bảo tiến độ giải ngân và sớm khởi công công trình trọng điểm. Các đơn vị phải hoàn tất lựa chọn nhà thầu để khởi công trong tháng 5; sang tháng 6 khởi công dự án cầu Ninh Cường, dự án cầu yếu trên các quốc lộ giai đoạn 2.\r\n\r\nCao tốc La Sơn - Hòa Liên qua TP Huế, TP Đà Nẵng sẽ được mở rộng từ 2 lên 4 làn xe với tổng mức đầu tư 3.010 tỷ đồng. Tuyến đường dài 65 km, điểm đầu nối với cao tốc Cam Lộ - La Sơn, thuộc thị trấn La Sơn, huyện Phú Lộc, TP Huế; điểm cuối tại nút giao Hòa Liên, thuộc huyện Hòa Vang, TP Đà Nẵng. Dự án sẽ tận dụng nguyên trạng 12 cầu trên tuyến đã đảm bảo 4 làn xe; xây dựng thêm cầu mới hoặc mở rộng 50 cầu trên tuyến quy mô 4 làn xe.\r\nDự án cao tốc Dầu Giây - Tân Phú có điểm đầu tại nút giao với quốc lộ 1, kết nối với cao tốc TP HCM - Long Thành - Dầu Giây, thuộc thị trấn Dầu Giây, huyện Thống Nhất, tỉnh Đồng Nai; điểm cuối tại xã Phú Trung, huyện Tân Phú, tỉnh Đồng Nai.\r\n\r\nGiai đoạn 1, cao tốc được đầu tư xây dựng 4 làn xe, bề rộng nền đường 17 m. Tại đoạn dừng xe khẩn cấp, công trình cầu theo quy mô giai đoạn hoàn chỉnh với bề rộng nền đường 24 m. Công tác giải phóng mặt bằng theo quy mô giai đoạn hoàn chỉnh 4 làn xe, nền đường rộng 24 m. Dự án có tổng mức đầu tư hơn 8.980 tỷ đồng theo hình thức đối tác công tư (PPP).\r\n\r\nCao tốc Cao Lãnh - Mỹ An dài gần 27 km nằm tại tỉnh Đồng Tháp; điểm đầu kết nối tuyến N2 tại thị trấn Mỹ An, huyện Tháp Mười, điểm cuối tại nút giao An Bình (đầu cầu Cao Lãnh).\r\n\r\nGiai đoạn 1, dự án được phân kỳ đầu tư với nền đường 17 m, 4 làn xe, vận tốc thiết kế 100 km/h, các đoạn dừng xe khẩn cấp bố trí không liên tục. Dự án sẽ giải phóng mặt bằng quy mô 6 làn xe để phục vụ mở rộng theo quy hoạch. Công trình có tổng mức đầu tư giai đoạn 1 là gần 6.130 tỷ đồng, trong đó vốn vay ODA của Chính phủ Hàn Quốc khoảng 4.460 tỷ đồng và vốn đối ứng từ ngân sách nhà nước khoảng 1.665 tỷ đồng\r\n\r\nDự án cầu Ninh Cường vượt sông Ninh Cơ trên quốc lộ 37B dài 1,65 km. Cầu chính 2 làn xe cơ giới, 2 làn xe hỗn hợp bề rộng cầu 12 m. Đường dẫn hai đầu cầu có quy mô đường cấp 3 đồng bằng, 2 làn xe, bề rộng nền đường 12 m, vận tốc thiết kế 80 km/h. Dự án được phê duyệt tổng mức đầu tư hơn 581 tỷ đồng, sử dụng vốn vay ODA của Chính phủ Hàn Quốc.\r\n\r\nBộ trưởng Xây dựng đề nghị chủ đầu tư hoàn thiện thủ tục phê duyệt dự án mở rộng cao tốc Cam Lộ - La Sơn trong tháng 4; chuẩn bị thủ tục mở rộng cao tốc TP HCM - Trung Lương - Mỹ Thuận trong tháng 5, để khởi công vào đầu quý III.', '1744547147_h152.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:25:47', 'Thời sự', 0, 1),
(164, 'Quân đội Lebanon \'tiếp quản hầu hết cơ sở của Hezbollah\'', 'Quân đội Lebanon được cho là đã kiểm soát và phá dỡ hầu hết cơ sở quân sự của Hezbollah ở miền nam nước này, cho thấy lệnh ngừng bắn đang được thực thi.', 'Các nguồn tin thân cận với Tổng thống và Thủ tướng Lebanon ngày 12/4 cho biết quân đội chính phủ nước này đã bắt đầu tiến vào các cơ sở quân sự của Hezbollah ở phía bắc sông Litani mà không xảy ra bất cứ sự cố nào.\r\n\r\nĐiều này cho thấy quá trình Hezbollah chuyển giao các căn cứ cho quân đội Lebanon diễn ra hòa bình và hai bên có thể đang tiến hành thêm các thỏa thuận để chính thức hóa quy trình bàn giao.\r\n\r\nMột nguồn tin thân cận với Hezbollah cùng ngày xác nhận 190 trong tổng số 256 căn cứ của nhóm ở phía nam sông Litani đã được chuyển giao cho quân đội Lebanon. Truyền thông Lebanon cho hay quân đội đã gần hoàn tất phá dỡ các căn cứ, cơ sở hạ tầng của Hezbollah ở miền nam đất nước.\r\n\r\nTheo lệnh ngừng bắn đạt được ngày 27/11/2024 sau hơn một năm xung đột giữa Hezbollah và Israel, chỉ có lực lượng gìn giữ hòa bình Liên Hợp Quốc và quân đội Lebanon được phép triển khai ở miền nam nước này.\r\n\r\nThỏa thuận yêu cầu Hezbollah, nhóm vũ trang do Iran hậu thuẫn, phải tháo dỡ các cơ sở hạ tầng quân sự của họ ở miền nam Lebanon và di chuyển lực lượng về phía bắc sông Litani, cách biên giới Israel khoảng 30 km.\r\nIsrael trong khi đó cũng phải rút quân hoàn toàn khỏi Lebanon vào ngày 18/2 sau khi bỏ lỡ thời hạn vào tháng 1, song Tel Aviv đã tuyên bố duy trì lực lượng ở 5 địa điểm mà họ xem là chiến lược. Israel vẫn tấn công những nơi mà họ cho là cơ sở hạ tầng của Hezbollah hoặc có các thành viên của nhóm ở Lebanon.\r\n\r\nTrong bài phát biểu kỷ niệm ngày nội chiến năm 1975-1990 bùng nổ ở Lebanon, Tổng thống Joseph Aoun đã nhắc lại lời kêu gọi Hezbollah hạ vũ khí.\r\n\r\n\"Tất cả chúng ta đều nhất trí rằng bất kỳ hành vi mang vũ khí nào ngoài thẩm quyền của nhà nước đều sẽ gây nguy hiểm cho lợi ích của Lebanon. Đã đến lúc chúng ta phải nói rằng Lebanon chỉ có thể được bảo vệ bởi nhà nước, quân đội và lực lượng an ninh quốc gia\", ông nói.\r\n\r\nMorgan Ortagus, phó đặc phái viên Mỹ tại Trung Đông, đã thảo luận về việc giải giáp Hezbollah với các quan chức cấp cao của Lebanon trong chuyến thăm Beirut cuối tuần trước, theo nguồn tin am hiểu vấn đề. Mỹ là chủ tịch ủy ban, trong đó có Pháp, giám sát lệnh ngừng bắn.\r\n\r\n\"Chúng tôi tiếp tục gây sức ép với chính phủ nước này để thực thi lệnh ngừng bắn đầy đủ, gồm cả việc giải giáp Hezbollah và lực lượng dân quân\", bà nói, thêm rằng điều này phải diễn ra \"càng sớm càng tốt\".\r\nSau khi xung đột ở Dải Gaza giữa Hamas và Israel bùng phát tháng 10/2023, nhóm vũ trang Hezbollah bắt đầu bắn phá miền bắc Israel để bày tỏ ủng hộ với người Palestine.\r\n\r\nNhiều tháng giao tranh căng thẳng đã biến thành chiến tranh toàn diện ở miền nam Lebanon vào tháng 9 năm ngoái, khiến Hezbollah bị giáng đòn và suy yếu nghiêm trọng. Thủ lĩnh Hezbollah Hassan Nasrallah đã thiệt mạng trong cuộc không kích của Israel vào Beirut cuối tháng đó.\r\n\r\nHơn 4.000 người đã thiệt mạng trong hơn một năm giao tranh giữa Israel và Hezbollah, theo chính phủ Lebanon.', '1744547349_h153.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:29:09', 'Thế giới', 0, 0),
(165, 'Chính quyền Trump nói người nhập cư bị trục xuất nhầm \'vẫn còn sống\'', 'Bộ Ngoại giao Mỹ cho hay Abrego Garcia, người nhập cư bị trục xuất nhầm sang El Salvador, vẫn \"còn sống và an toàn\" trong siêu nhà tù CECOT.', '\"Theo báo cáo chính thức từ đại sứ quán của chúng tôi tại San Salvador, Abrego Garcia hiện bị giam tại Trung tâm Chống Khủng bố (CECOT) ở El Salvador. Anh ấy còn sống và an toàn tại nhà tù đó. Anh ấy bị giam theo thẩm quyền của El Salvador\", Michael Kozak, quan chức cấp cao của Bộ Ngoại giao Mỹ, tuyên bố về tình trạng của Kilmar Abrego Garcia, người nhập cư 29 tuổi bị giới chức trục xuất nhầm.\r\n\r\nTuy nhiên, Kozak không cung cấp thông tin về kế hoạch đưa Garcia trở về Mỹ theo lệnh của thẩm phán liên bang Paula Xinis, người đã yêu cầu chính phủ tiết lộ vị trí giam và tình trạng hiện tại, cũng như cách thức và thời điểm dự kiến đưa người này về Mỹ.\r\n\r\nTòa án Tối cao Mỹ ngày 10/4 giữ nguyên phán quyết của thẩm phán Xinis, yêu cầu chính quyền Tổng thống Donald Trump \"tạo điều kiện\" cho Garcia trở về Mỹ và cần cập nhật thông tin hàng ngày.\r\nÔng Trump hôm 11/4 cho biết đã cân nhắc về lệnh của Tòa án Tối cao, tái khẳng định sự tôn trọng đối với tòa. \"Nếu Tòa án Tối cao nói đưa ai đó trở về, tôi sẽ làm như vậy. Tôi tôn trọng tòa án\", ông nói.\r\n\r\nKilmar Armando Abrego Garcia là người gốc El Salvador, rời khỏi quê nhà hồi năm 2011, khi mới 16 tuổi, vì bị các băng đảng tội phạm đe dọa, sau đó nhập cư trái phép vào Mỹ 10 năm trước và sống ở bang Maryland.\r\n\r\nAnh kết hôn với người Mỹ, có một người con 5 tuổi bị khuyết tật. Garcia hồi năm 2019 được một thẩm phán di trú tại Mỹ cấp quy chế bảo vệ, trong đó cấm chính quyền liên bang trục xuất anh về quê nhà.\r\n\r\nTuy nhiên, hôm 12/3, Abrego Garcia bị các đặc vụ kiểm soát nhập cư ở bang Maryland chặn xe và bắt khi đang đưa con trai về nhà, với cáo buộc anh là thành viên băng nhóm tội phạm MS-13, dù Garcia không có tiền án tại Mỹ. Ba ngày sau, anh bị trục xuất đến siêu nhà tù CECOT ở El Salvador.\r\n\r\nChính quyền ông Trump sau đó thừa nhận đã bắt giam và trục xuất nhầm Garcia đến El Salvador. Tuy nhiên, giới chức Mỹ cho hay họ không kiểm soát được việc giam giữ anh ở El Salvador dù có thỏa thuận trị giá 6 triệu USD với nước này để giam giữ hàng trăm người nhập cư bị trục xuất từ Mỹ.\r\n\r\nTổng thống El Salvador Nayib Bukele dự kiến thăm Nhà Trắng ngày 14/4 theo lời mời của ông Trump. Nhà Trắng bác khả năng Garcia sẽ đi cùng chuyên cơ với ông Bukele về Mỹ.', '1744547404_h154.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:30:04', 'Thế giới', 0, 0),
(166, 'Bên trong thủ phủ hàng hóa Trung Quốc giữa bão thuế với Mỹ', 'Các thương nhân tại \"thủ phủ hàng hóa\" Nghĩa Ô bị ảnh hưởng lớn khi Mỹ áp thuế 145% với Trung Quốc, nhưng tin rằng Bắc Kinh sẽ chiến thắng.', 'Thành phố Nghĩa Ô, tỉnh miền đông Chiết Giang, được xem là thủ phủ hàng tiêu dùng nhỏ của Trung Quốc. Trung tâm thương mại quốc tế Nghĩa Ô là siêu thị bán buôn lớn nhất thế giới, tập trung hàng triệu mặt hàng lớn nhỏ để bán trong nước cũng như xuất khẩu.\r\n\r\nMỹ là thị trường xuất khẩu lớn nhất của Nghĩa Ô, với giá trị xuất khẩu hơn 116 tỷ USD, chiếm 14,2% kim ngạch xuất khẩu của thành phố. Các mặt hàng tiêu dùng như đồ lưu niệm, đồ chơi, đồ trang trí, gia dụng cỡ nhỏ là những nhóm hàng hóa chính mà các thương nhân ở thành phố này bán tới Mỹ.\r\n\r\nNhưng hoạt động này đang bị ảnh hưởng nghiêm trọng bởi cuộc chiến thuế quan giữa Mỹ và Trung Quốc. Hàng hóa Trung Quốc xuất khẩu sang Mỹ đang chịu thuế suất tổng cộng 145%, khi Tổng thống Donald Trump không ngừng tăng thuế với nước này. Bắc Kinh đáp trả bằng mức thuế bổ sung 125% lên toàn bộ hàng hóa nhập khẩu từ nền kinh tế số một thế giới, hiệu lực từ ngày 12/4.\r\n\r\nTrong bối cảnh Mỹ - Trung Quốc \"ăn miếng, trả miếng\" trong cuộc đấu thuế quan, các nhà xuất khẩu ở Nghĩa Ô được xem là những doanh nhân ở tuyến đầu cuộc chiến thương mại.\r\n\r\n\"Mỹ muốn lấy đi một phần chiếc bánh của Trung Quốc\", thương nhân Kenny Qi nói từ quầy hàng treo đầy áo in khẩu hiệu \"Làm nước Mỹ vĩ đại trở lại\" tại một trung tâm triển lãm thương mại ở Nghĩa Ô.\r\nÔng Kenny tin rằng Tổng thống Trump đã bị sốc khi Trung Quốc áp thuế bổ sung 125% lên toàn bộ hàng hóa nhập khẩu từ Mỹ. Ông tin rằng Tổng thống Mỹ sẽ lùi bước \"trong tối đa hai tuần\".\r\n\r\n\"Truyền thông cho hay người Mỹ đang phải lùng mua trứng, bột mì, dầu ăn\", Nie Ziqin, chủ cơ sở xuất khẩu đồ trang trí Halloween ở Nghĩa Ô, nói.\r\n\r\nBà Nie, đến Nghĩa Ô làm công nhân 20 năm trước, hiện điều hành nhà máy sản xuất riêng với hơn 100 nhân công. Bà thừa nhận cuộc chiến thuế quan giữa hai nước khiến bà sốc và thất vọng sau nhiều thập kỷ làm ăn với Mỹ.\r\n\r\nBà đã từ chối yêu cầu giảm giá của đối tác Mỹ sau khi cuộc chiến thuế nổ ra, dù buộc phải sa thải 10% nhân công để bù đắp cho các đơn hàng bị hủy.\r\n\r\n\"Người Trung Quốc có cách nghĩ khác người nước ngoài. Chúng tôi tiết kiệm và có thể bám trụ trong 1-2 năm, thậm chí ba năm, còn người nước ngoài thường tiêu hết những gì họ kiếm được. Chúng tôi sẽ thắng trong bất kỳ cuộc chiến thương mại trường kỳ nào\", bà Nie nói.\r\nCác nhà sản xuất ở Nghĩa Ô cũng xuất khẩu sang châu Âu và các nước đang phát triển, giúp họ có vị thế tương đối tốt để vượt qua cuộc chiến thương mại với Mỹ. Tuy nhiên, nhiều nhà sản xuất lớn hơn ở các vùng khác của Trung Quốc có thể dễ bị tổn thương hơn.\r\n\r\nTại thành phố miền trung Trịnh Châu, nơi Apple sản xuất hầu hết iPhone, nhiều công nhân đang lo lắng. \"Mọi thứ hiện tại bình thường, nhưng Trung Quốc đang chịu áp lực rất lớn\", một công nhân nói trong giờ giải lao tại dây chuyền lắp ráp iPhone do nhà thầu Foxconn của Apple sở hữu.\r\n\r\nApple được cho là đã vận chuyển 600 tấn iPhone bằng đường hàng không từ Ấn Độ về Mỹ để tránh đòn thuế quan. Các công nhân tại nhà máy Foxconn Trịnh Châu lo ngại giờ làm sắp tới sẽ ít hơn.\r\n\r\n\"Công ty sẽ cắt giảm sản lượng, có thể dẫn đến ít việc hơn\", công nhân này giải thích.\r\n\r\nZhu Yuelai, nhà xuất khẩu đồ cắm trại ở Nghĩa Ô, cho biết hiệp hội ngành này đang cố gắng giúp đỡ các nhà sản xuất lớn ở những khu vực khác phụ thuộc nhiều hơn vào thị trường Mỹ.\r\n\r\nCác nhà sản xuất vừa và nhỏ ở Nghĩa Ô đang giới thiệu mạng lưới khách hàng, trong đó có các nước đang phát triển, cho những công ty lớn hơn, dù trước đây không được họ đánh giá cao, ông Zhu nói.\r\n\r\nKenny, nhà sản xuất hàng hóa chủ đề MAGA (Làm nước Mỹ vĩ đại trở lại), khẳng định sẽ không bị ảnh hưởng, bởi người ủng hộ ông Trump sẵn sàng trả giá cao hơn để có các sản phẩm mang hình ảnh Tổng thống mà họ ngưỡng mộ.\r\n\r\n\"Mũ lưỡi trai MAGA chỉ tốn 1 USD để sản xuất ở đây. Thuế có thể khiến chi phí đội thêm gần 3 USD, nhưng những chiếc mũ này được bán với giá tới 50 USD tại Mỹ. Và những người bán hàng Mỹ có thể lấy cớ thuế cao để tăng giá lên 60 USD. Chi phí phát sinh vẫn do người tiêu dùng Mỹ chịu\", ông nói.', '1744547468_h155.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:31:08', 'Thế giới', 0, 0),
(167, 'Nhật tổ chức triển lãm quốc tế để \'đoàn kết thế giới\'', 'Thủ tướng Nhật Ishiba khai mạc Triển lãm Osaka, tin rằng nó sẽ giúp \"đoàn kết thế giới\" đang bị bủa vây bởi xung đột và căng thẳng thương mại.', '\"Sau khi vượt qua đại dịch Covid-19, thế giới hiện bị đe dọa bởi loạt chia rẽ. Việc mọi người từ khắp nơi trên thế giới tụ họp lại đây và thảo luận về chủ đề cuộc sống, trải nghiệm công nghệ tiên tiến cùng các ý tưởng và nền văn hóa đa dạng là vô cùng ý nghĩa\", Thủ tướng Nhật Bản Shigeru Ishiba phát biểu khai mạc Triển lãm Expo 2025 Osaka ngày 12/4.\r\n\r\nExpo 2025 Osaka được tổ chức với chủ đề về cuộc sống, thế giới và tương lai. Nhật Bản hy vọng sự kiện sẽ mang lại sự đoàn kết cho thế giới và giúp những người tham dự hình dung được về xã hội tương lai.\r\n\r\n\"Tôi thực sự hy vọng thế giới sẽ đoàn kết trở lại thông qua các tương tác giữa những quốc gia và du khách trong và ngoài Grand Ring\", ông Ishiba nói, đề cập tới công trình mang tính biểu tượng của sự kiện.\r\n\r\nGrand Ring là công trình vòng tròn bằng gỗ lớn nhất thế giới với chiều cao 20 m và chu vi 2 km.\r\nTrong sự kiện kéo dài 6 tháng trên đảo nhân tạo Yumeshima ở Vịnh Osaka, khoảng 180 quốc gia, khu vực và tổ chức sẽ giới thiệu các triển lãm mang tính tương lai trong khoảng 80 gian hàng với thiết kế độc đáo. Đây là hội chợ quốc tế thứ hai của Osaka sau sự kiện năm 1970 thành công vang dội thu hút 64 triệu du khách, được ghi nhận là mức kỷ lục cho tới khi hội chợ ở Thượng Hải tổ chức năm 2010.\r\n\r\nTuy nhiên, sự kiện năm nay của Nhật Bản đối mặt nhiều thách thức khi mức độ quan tâm của công chúng suy giảm, chi phí xây dựng tăng cao gần gấp đôi so với ước tính 235 tỷ yên (1,64 tỷ USD) ban đầu. Chi phí tăng cũng khiến nhiều người Nhật chỉ trích cách chính phủ sử dụng tiền thuế của họ.\r\n\r\nDoanh số bán vé cũng không như kỳ vọng, khi đến nay chỉ có 9 triệu vé được bán, thấp hơn mục tiêu 14 triệu vé. Ban tổ chức hy vọng sẽ đón hơn 28 triệu du khách tới sự kiện từ nay đến hết ngày 13/10.\r\nExpo 2025 Osaka diễn ra giữa lúc thế giới căng thẳng vì đòn thuế quan của chính quyền Tổng thống Mỹ Donald Trump. Dù ông Trump đã tuyên bố hoãn áp thuế đối ứng với các đối tác trong 90 ngày, tình trạng bất ổn vẫn chưa qua đi khi hai nền kinh tế lớn nhất thế giới Mỹ và Trung Quốc liên tục áp thuế trả đũa nhau. Trong khi đó, xung đột Ukraine và Trung Đông vẫn chưa thấy triển vọng chấm dứt.', '1744547536_h156.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:32:16', 'Thế giới', 0, 1),
(168, 'Tuần sóng gió trong \'thành trì\' của ông Trump ở Hạ viện', 'Dự luật ngân sách được ông Trump hậu thuẫn trải qua tuần tranh luận nảy lửa ở Hạ viện, hé lộ những rạn nứt sâu sắc trong đảng Cộng hòa.', 'Chủ tịch Hạ viện Mỹ Mike Johnson nở nụ cười rạng rỡ khi trêu chọc các phóng viên tại Đồi Capitol sáng 10/4: \"Tôi đã bảo đừng nghi ngờ chúng tôi mà\".\r\n\r\nChỉ vài phút trước đó, Hạ viện vừa thông qua nghị quyết từ Thượng viện về đề xuất soạn thảo ngân sách liên bang. Động thái mở đường cho đảng Cộng hòa thông qua dự luật then chốt đối với cương lĩnh hành động \"Làm nước Mỹ vĩ đại trở lại\" (MAGA) của Tổng thống Donald Trump, mà theo lời ông mô tả là \"to đẹp tuyệt vời\" và có thể giúp đảng Cộng hòa giành chiến thắng áp đảo ở Hạ viện trong bầu cử giữa kỳ năm 2026.\r\n\r\nNhưng thông qua nghị quyết này mới là bước dễ nhất trên hành trình nhiều thử thách của dự luật ngân sách mà ông Trump mong muốn. Văn bản dài 70 trang mà Hạ viện và Thượng viện đã phê chuẩn hầu như không có chi tiết chính sách cụ thể.\r\n\r\nMục tiêu chính của nghị quyết này mang tính thủ tục chứ không phải nội dung: đảng Cộng hòa cần thông qua nghị quyết của Thượng viện để kích hoạt quy trình hòa giải, một cơ chế đàm phán dự luật lưỡng viện và cho phép Thượng viện chỉ cần 51 phiếu thay vì 60 phiếu để thông qua dự luật ngân sách liên bang.\r\n\r\nĐảng Cộng hòa đang nắm 53 ghế tại Thượng viện, trong khi đảng Dân chủ có 45 ghế cộng với hai nghị sĩ độc lập thường bỏ phiếu theo đảng này. Cơ chế hòa giải lưỡng viện sẽ giúp phe Tổng thống Trump lách qua khe cửa hẹp, thông qua dự luật ngân sách mà không cần đa số áp đảo, tương đương 60 phiếu, ở Thượng viện.\r\nTuy nhiên, ngay cả bước khởi đầu này cũng suýt thất bại tại Hạ viện. Khi đảng Cộng hòa tại Thượng viện hoàn tất dự thảo ngân sách vào cuối tuần trước, phe bảo thủ cực hữu trong Hạ viện đã lập tức phản đối, bất chấp mọi lời kêu gọi từ Tổng thống Trump.\r\n\r\nCác thượng nghị sĩ Cộng hòa chưa thống nhất sẽ cắt giảm chương trình nào, giảm loại thuế nào và mức độ ra sao để cân bằng ngân sách. Vấn đề nhạy cảm nhất là chương trình bảo hiểm y tế Medicaid, nhưng từ này chỉ xuất hiện đúng một lần trong tài liệu.\r\n\r\nƯu tiên của phe Cộng hòa tại Thượng viện khi soạn dự luật không phải giảm chi tiêu, mà là duy trì vô thời hạn các khoản cắt giảm thuế theo đạo luật được thông qua năm 2017, trong nhiệm kỳ đầu của ông Trump.\r\n\r\nNhóm nghị sĩ ủng hộ kỷ luật tài khóa tại Hạ viện nổi giận ngay lập tức với ý tưởng này. Họ không hài lòng khi nghị quyết ở Thượng viện không chỉ ra phương án bù đắp khoản thâm hụt ước tính 5 nghìn tỷ USD vì cắt giảm thuế, mà chỉ điều chỉnh số liệu cơ bản để tạo ấn tượng rằng việc gia hạn không làm nợ công tăng vọt và đặt con số tối thiểu là 4 tỷ USD.\r\n\r\nĐầu tuần này, Chủ tịch Hạ viện Johnson đã phải đau đầu với hơn 10 thành viên bất đồng trong đảng, trong khi ông chỉ được phép mất tối đa ba phiếu Cộng hòa để thông qua nghị quyết mà không có phiếu Dân chủ.\r\n\r\nTrước đây, trong các bất đồng nội bộ, kể cả cuộc bỏ phiếu chọn Chủ tịch Hạ viện hồi tháng 1, ông thường dựa vào Tổng thống Trump để gây áp lực lên các thành viên \"cứng đầu\". Nhưng lần này, ngay cả ông Trump cũng không thuyết phục nổi nhóm nghị sĩ bảo thủ, để rồi ông Johnson phải hoãn cuộc bỏ phiếu vào đêm 9/4 khi nhận ra nắm chắc phần thua.\r\n\r\nHạ nghị sĩ Chip Roy từ Texas, lãnh đạo nhóm nghị sĩ bảo thủ, gọi đề xuất của Thượng viện là \"cợt nhả\" và \"làm toán bịp\". Trong phiên làm việc sáng 9/4 ở Hạ viện Mỹ, ông Roy còn chỉ trích gay gắt từ đồng nghiệp ở lưỡng viện quốc hội đến Nhà Trắng.\r\n\r\n\"Đừng đánh lừa người dân Mỹ nữa. Các ông bà không thể viết đại vài thứ lên bảng rồi tuyên bố: \'Phương án này tự sinh ra tiền nuôi nó\'. Không phải thế. Hãy mang tẩy và bút đến trước mặt tôi, viết rõ mọi thứ lên giấy rồi chứng minh cho tôi thấy đi\", Roy bức xúc.\r\n\r\n\"Thông điệp này, tôi gửi thẳng cho Nhà Trắng, những đồng nghiệp của tôi thuộc đảng Cộng hòa ở Thượng viện và các lãnh đạo Hạ viện ở cả hai đảng. Tôi sẽ không bỏ phiếu ủng hộ vì đây là một văn kiện vô trách nhiệm\", ông nhấn mạnh.\r\nPhe bảo thủ vốn là \"thành trì\" ủng hộ Tổng thống Trump ở Hạ viện, nhưng họ không tin vào cam kết giảm thâm hụt ngân sách của phe Cộng hòa ở Thượng viện, từ lâu luôn phản đối các biện pháp cắt giảm chi tiêu quyết liệt mà họ đòi hỏi. Giờ đây, với các động thái mạnh mẽ từ Elon Musk tại Ban Hiệu suất Chính phủ (DOGE), họ càng tin rằng thời cơ đã chín muồi để giảm núi nợ công 36.600 tỷ USD của Mỹ.\r\n\r\nPhe của ông Roy nêu yêu sách Thượng viện phải cam kết cụ thể cắt giảm ít nhất 1.500 tỷ USD trong dự thảo cuối cùng cho dự luật ngân sách liên bang. Đến sáng 10/4, lãnh đạo Cộng hòa ở cả hai viện chấp nhận nhượng bộ.\r\n\r\nChủ tịch Hạ viện Johnson và lãnh đạo phe đa số Cộng hòa ở Thượng viện John Thune cùng đứng trước ống kính tuyên bố: \"Chúng tôi cam kết rà soát ít nhất 1.500 tỷ USD để tiết kiệm cho người dân Mỹ mà vẫn giữ các chương trình thiết yếu\", ông Johnson nói.\r\n\r\nÔng Thune tiếp lời một cách mập mờ, nhưng cũng đề cập đến con số mà phe bảo thủ mong muốn: \"Chúng tôi đồng thuận với Hạ viện. Chủ tịch đã nói về 1.500 tỷ USD và nhiều thượng nghị sĩ cho rằng đó là mức tối thiểu\".\r\n\r\nTrên thực tế, phần lớn thượng nghị sĩ Cộng hòa cho rằng cắt giảm ngân sách đến mức đó là bất khả thi nếu không động đến các chương trình y tế và phúc lợi như Medicaid, Medicare hoặc An sinh Xã hội. Ngay cả những đồng minh thân cận của Tổng thống Trump như thượng nghị sĩ Josh Hawley cũng phản đối cắt giảm Medicaid.\r\n\r\nDù vậy, những cam kết từ ông Johnson lẫn ông Thune đã đủ thuyết phục phe bảo thủ, vốn là nhóm ít đổi ý nhất Hạ viện.\r\n\r\nĐến cuộc bỏ phiếu, ngoại trừ hai người thuộc nhóm bảo thủ Cộng hòa, tất cả những người còn lại đều ủng hộ nghị quyết mà vài ngày trước họ còn gọi là \"cợt nhả\" chỉ dựa trên lời hứa miệng từ lãnh đạo đảng. Chủ tịch Hạ viện Johnson lại một lần nữa lách qua khe cửa hẹp, với tổng cộng 216 phiếu thuận và 214 phiếu chống cho nghị quyết ngân sách.\r\n\r\n\"Vì tinh thần đồng thuận, tôi sẽ tin họ\", nghị sĩ Roy nói sau cuộc bỏ phiếu, song không quên cảnh báo phe bảo thủ sẽ bác dự luật nếu nó không đề ra các biện pháp cắt giảm ngân sách cụ thể, bao gồm cải tổ Medicaid.', '1744547617_h157.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:33:37', 'Thế giới', 0, 0),
(169, 'Phi công F-16 Ukraine thiệt mạng trong chiến đấu', 'Không quân Ukraine thông báo một phi công tiêm kích F-16 đã thiệt mạng trong chiến đấu, song không công bố chi tiết.', '\"Phi công Pavlo Ivanov, 26 tuổi, đã thiệt mạng khi thực hiện nhiệm vụ chiến đấu trên tiêm kích F-16 vào ngày 12/4. Một ủy ban liên ngành đang xác định tất cả tình tiết liên quan đến thảm kịch\", Bộ tư lệnh không quân Ukraine thông báo hôm nay.\r\n\r\nGiới chức Ukraine chưa công bố chi tiết về sự việc, cũng như tình trạng của chiếc F-16. Bộ tư lệnh không quân Ukraine khẳng định các phi công F-16 đang thực hiện hàng loạt nhiệm vụ trong điều kiện cực kỳ phức tạp ở tiền tuyến, trong đó có yểm trợ đồng đội và tấn công mục tiêu mặt đất.\r\n\r\nTổng thống Ukraine Volodymyr Zelensky sau đó gửi lời chia buồn đến gia đình phi công thiệt mạng.\r\n\r\nBộ Quốc phòng Nga chưa bình luận về thông tin.\r\nTiêm kích F-16 được đánh giá là có năng lực vượt trội so với các dòng chiến đấu cơ thời Liên Xô trong biên chế không quân Ukraine, đặc biệt là trong nhiệm vụ đánh chặn tên lửa hành trình, thậm chí hiệu quả hơn cả các hệ thống phòng không mặt đất trong vai trò này.\r\n\r\nPhi đội F-16 Ukraine ban đầu chỉ làm nhiệm vụ phòng không ở tuyến sau, chuyên đánh chặn tên lửa hành trình và máy bay không người lái (UAV) tự sát của Nga. Chiến đấu cơ này lần đầu xuất hiện với vũ khí tấn công mặt đất hồi đầu năm, nhưng không rõ địa điểm tham chiến.\r\n\r\nUkraine mất tiêm kích F-16 đầu tiên khi ứng phó đợt tập kích của Nga ngày 26/8/2024, chưa đầy một tháng sau khi tiếp nhận máy bay. Trung tá Oleksiy Mes, người điều khiển chiếc F-16 và là một trong những phi công tiêm kích giỏi nhất Ukraine, thiệt mạng trong sự việc.\r\n\r\nMariana Bezugla, phó chủ tịch Ủy ban Quốc phòng Quốc hội Ukraine, khi đó tuyên bố chiếc F-16 đã bị tên lửa phòng không Patriot bắn nhầm do thiếu hiệp đồng giữa các đơn vị. Tờ Wall Street Journal ngày 14/2 dẫn các nguồn tin giấu tên am hiểu vấn đề đưa ra thông tin tương tự, cho rằng \"sự việc xảy ra một phần vì Mỹ không cung cấp công nghệ thiết yếu để bảo đảm an toàn tác chiến\".\r\n\r\nTruyền thông Nga cuối tháng 3 cho biết tổ hợp S-400 đã hiệp đồng với Su-35S để phục kích F-16 Ukraine. Sonyashnik, tài khoản Telegram do các binh sĩ không quân Ukraine vận hành, thừa nhận một máy bay F-16 đã bị tên lửa Nga khóa mục tiêu nhưng phi công tránh né được quả đạn.\r\n\r\nCác tài khoản ủng hộ quân đội Nga nhận định những chiếc F-16 Ukraine bắt đầu làm nhiệm vụ ở Sumy từ tháng 2, thời điểm Moskva bắt đầu chiến dịch phản công tại Kursk, nhằm yểm trợ các biên đội MiG-29 ném bom dẫn đường vào lãnh thổ Nga.', '1744547694_h158.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:34:54', 'Thế giới', 0, 0),
(170, 'UAV Nga tập kích, gây cháy lớn tại Kiev', 'Ukraine tuyên bố ngăn chặn 80 UAV Nga, nhưng dường như để lọt một số phi cơ, khiến nhiều khu vực chịu thiệt hại, trong đó có thủ đô Kiev.', 'Bộ tư lệnh không quân Ukraine hôm nay cho biết Nga đã tập kích nước này bằng 88 máy bay không người lái (UAV) tự sát tầm xa Geran-2 và phi cơ mồi bẫy. \"Các đơn vị phòng không đã bắn hạ 56 UAV tự sát, trong khi 24 phi cơ mồi bẫy lạc đường và không gây hậu quả tiêu cực. Đòn tấn công đã gây ảnh hưởng đến tỉnh Kharkov, Odessa, Dnipro và Donetsk\", cơ quan này cho hay.\r\n\r\nKhông quân Ukraine không đề cập tới 8 UAV còn lại, dường như ngầm thừa nhận rằng đã để lọt số phi cơ này.\r\n\r\nVideo do hãng tin Reuters quay tại thủ đô Kiev cho thấy nhiều khẩu đội pháo phòng không Ukraine khai hỏa để chặn UAV Nga, nhưng không rõ có trúng mục tiêu hay không. Trong hình ảnh sau đó, đám cháy lớn bùng phát và tạo ra cột khói bao trùm một phần thành phố. Một đám khói nhỏ hơn bốc lên ở gần đó sau vài giây, nhiều khả năng là do đầu nổ của UAV Nga.\r\nThị trưởng Kiev Vitali Klitchko cho biết cuộc tập kích đã khiến 3 người bị thương. Quan chức thành phố nói rằng các mảnh vỡ UAV đã phá hủy một nhà dân và làm hư hại một số tòa nhà thương mại, đồng thời gây ra những đám cháy lớn tại nhiều khu vực ở Kiev.\r\n\r\nThị trưởng Kharkov Ihor Terekhov xác nhận một người bị thương do đòn tập kích của Nga. Thiệt hại về các tòa nhà dân cư và thương mại cũng được ghi nhận ở tỉnh Dnipro và Odessa.\r\n\r\nBộ Quốc phòng Nga cùng ngày thông báo đã \"làm hư hại một doanh nghiệp hàng không thuộc tổ hợp công nghiệp quốc phòng Ukraine\", cũng như tập kích cơ sở hạ tầng tại một sân bay quân sự và cảng biển, nhà xưởng lắp ráp UAV và sửa chữa xe thiết giáp, kho đạn và tên lửa, điểm tập kết binh lực của đối phương tại nhiều khu vực.\r\n\r\nTài khoản The_Wrong_Side trên Telegram chuyên đăng tư liệu về đòn tập kích của Nga nói rằng mục tiêu trong cuộc tấn công nhằm vào Kiev là nhà máy thuộc tập đoàn hàng không Antonov, cùng tòa nhà từng được sử dụng bởi Viện Nghiên cứu Thiết bị Cơ điện Ukraine. \"Nhà máy Antonov bắt đầu sản xuất các loại UAV cho quân đội Ukraine từ năm 2023\", tài khoản này cho hay.\r\n\r\nQuân đội Nga nhiều lần khẳng định các đòn tập kích chỉ nhắm vào cơ sở quân sự và hạ tầng phục vụ ngành công nghiệp quốc phòng Ukraine. Giới chức Ukraine cũng từng thừa nhận nhiều cơ sở quân sự nằm trong số mục tiêu bị Nga tập kích, song thường không nêu chi tiết khi đề cập đến chúng.\r\n\r\nNga gần đây thường xuyên triển khai UAV tự sát dòng Geran-2 cùng máy bay mồi nhử để làm tiêu hao đạn phòng không của Ukraine, mở đường cho đòn tập kích tên lửa. Quân đội Nga thậm chí còn trang bị đầu đạn cho máy bay mồi nhử, biến chúng thành UAV tự sát với chi phí thấp.\r\n\r\nChiến thuật tập kích mới của Nga khiến quân đội Ukraine liên tục đối mặt với tình trạng thiếu đạn phòng không. Tổng thống Volodymyr Zelensky hôm 11/4 kêu gọi các đồng minh phương Tây tập trung cải thiện mạng lưới phòng không cho Ukraine và cung cấp thêm ít nhất 10 hệ thống tên lửa tầm xa Patriot.', '1744547787_h159.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:36:27', 'Thế giới', 0, 2),
(171, 'Loạt học viện quân sự hàng đầu Mỹ ngừng xét chủng tộc khi tuyển sinh', 'Học viện West Point và Học viện Không quân Mỹ ngừng cân nhắc yếu tố chủng tộc khi tuyển sinh, thay đổi so với dưới thời chính quyền Biden.', 'Tài liệu do Bộ Tư pháp Mỹ nộp lên tòa án liên bang ở New York và Colorado hôm 11/4 cho thấy Học viện Lục quân West Point và Học viện Không quân không còn coi chủng tộc là một trong các tiêu chí tuyển sinh, điều từng được áp dụng dưới thời chính quyền Joe Biden nhằm tăng tỷ lệ nhập học của người da màu, người gốc Latin và các cộng đồng thiểu số khác.\r\n\r\nTài liệu được nộp liên quan hai vụ kiện do nhóm Sinh viên vì Tuyển sinh Công bằng (SFFA) đệ đơn. SFFA được thành lập bởi Edward Blum, người phản đối chính sách tuyển sinh nhằm tăng số lượng học viên thuộc cộng đồng thiểu số, và hồi năm 2023 từng thuyết phục được Tòa án Tối cao Mỹ cấm các trường đại học dân sự xem xét chủng tộc khi tuyển sinh.\r\n\r\nTòa án Tối cao Mỹ khi đó phán quyết rằng chính sách của Đại học Havard và Đại học Bắc Carolina là vi hiến. Dù vậy, phán quyết không áp dụng với các học viện quân sự do những cơ sở này \"có thể có lợi ích khác biệt\", theo Chánh án Tòa án Tối cao Mỹ John Roberts.\r\n\r\nĐiều đó khiến SFFA tiếp tục nộp đơn kiện nhằm hủy bỏ chính sách tuyển sinh dựa trên chủng tộc tại các cơ sở đào tạo sĩ quan hàng đầu của Mỹ gồm Học viện Lục quân West Point ở bang New York, Học viện Không quân ở bang Colorado và Học viện Hải quân tại bang Maryland.\r\nChính quyền Biden bảo vệ chính sách tuyển sinh dựa trên yếu tố chủng tộc, cho biết giới lãnh đạo quân sự Mỹ từ lâu đã nhận thức được rằng tình trạng thiếu các sĩ quan đến từ cộng đồng thiểu số có thể gây ra sự ngờ vực trong quân đội.\r\n\r\nTòa án Tối cao Mỹ tháng 2/2024 đứng về phía chính quyền ông Biden và từ chối chặn chính sách xem xét chủng tộc khi tuyển sinh tại Học viện Lục quân và Học viện Không quân. SFFA tiếp tục nộp đơn kiện Học viện Không quân sau khi ông Donald Trump đắc cử tổng thống tháng 11/2024.\r\n\r\nTổng thống Trump hôm 27/1 ký sắc lệnh hành pháp nhằm chấm dứt các chính sách về đa dạng, công bằng và hòa nhập (DEI) trong quân đội.\r\n\r\nHọc viện Lục quân West Point sau đó thay đổi chính sách tuyển sinh nhằm tuân thủ sắc lệnh do ông Trump ban hành. Một quan chức cấp cao của không quân Mỹ hôm 6/2 cũng ban hành bản ghi nhớ nhằm thực thi sắc lệnh của Tổng thống Trump, qua đó chấm dứt chỉ tiêu hoặc mục tiêu tuyển sinh dựa trên giới tính, chủng tộc hoặc sắc tộc.\r\n\r\nBộ Tư pháp Mỹ cho rằng những động thái trên khiến đơn kiện của SFFA trở nên vô nghĩa. Cơ quan này tháng trước cũng thông báo Học viện Hải quân đã ngừng cân nhắc yếu tố chủng tộc khi tuyển sinh.', '1744547840_h160.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:37:20', 'Thế giới', 0, 6),
(172, 'Ông Trump cấm người chuyển giới tham gia thể thao nữ', 'Ông Trump ký sắc lệnh cấm vận động viên chuyển giới tham gia môn thể thao nữ, đồng thời yêu cầu ủy ban Olympic điều chỉnh quy định liên quan.', '\"Từ nay trở đi, thể thao nữ sẽ chỉ dành cho nữ giới\", Tổng thống Mỹ Donald Trump nói trước khi ký sắc lệnh tại Nhà Trắng, vây quanh là hàng chục trẻ em và vận động viên nữ. \"Với sắc lệnh hành pháp này, cuộc chiến chống lại các môn thể thao nữ đã kết thúc\".\r\n\r\nChủ tịch Hạ viện Mike Johnson và hạ nghị sĩ Marjorie Green cũng có mặt để theo dõi Tổng thống ký sắc lệnh.\r\n\r\n\"Chúng tôi sẽ bảo vệ truyền thống đáng tự hào của các vận động viên nữ. Chúng tôi không cho phép đàn ông tham gia các môn thể thao của họ để áp đảo, làm bị thương và lừa dối phụ nữ cũng như bé gái\", ông Trump nói thêm trong tiếng vỗ tay, reo hò.\r\nTheo sắc lệnh, cơ quan chính phủ Mỹ được phép từ chối tài trợ cho các trường học để vận động viên chuyển giới tham gia đội tuyển nữ. \"Nếu để đàn ông tham gia các đội tuyển thể thao nữ hoặc xâm phạm phòng thay đồ, các bạn sẽ bị điều tra và có nguy cơ mất nguồn tài trợ liên bang\", Tổng thống Mỹ cho hay.\r\n\r\nÔng Trump cho biết sẽ hối thúc Ủy ban Olympic quốc tế (IOC) thay đổi quy định về vận động viên chuyển giới trước Thế vận hội Los Angeles 2028. Ông cũng đã chỉ thị Ngoại trưởng Marco Rubio \"làm rõ\" với IOC rằng \"chúng tôi muốn họ thay đổi mọi thứ liên quan đến Thế vận hội cũng như chủ đề hoàn toàn vô lý này\".\r\n\r\nTổng thống Mỹ đồng thời chỉ đạo Bộ trưởng An ninh Nội địa Kristi Noem từ chối đơn xin thị thực \"của những người đàn ông tự nhận là vận động viên nữ và cố gian lận nhập cảnh vào Mỹ để tham gia Thế vận hội\".\r\n\r\nTrong bài phát biểu nhậm chức ngày 20/1, ông Trump tuyên bố chính sách của chính phủ Mỹ sẽ chỉ công nhận hai giới tính, nam và nữ, chấm dứt tùy chọn giới tính thứ ba. Vài ngày sau, ông ký sắc lệnh xóa bỏ \"hệ tư tưởng chuyển giới\" trong quân đội và cấm người chuyển giới tham gia quân đội. Ông cũng ban hành lệnh hạn chế các thủ tục chuyển đổi giới tính đối với người dưới 19 tuổi.\r\n\r\nĐộng thái mới nhất của ông diễn ra sau khi Hạ viện do đảng Cộng hòa lãnh đạo hồi tháng 1 thông qua dự luật hạn chế nghiêm ngặt vận động viên chuyển giới tham gia các môn thể thao dành cho nữ.', '1744547886_h161.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:38:06', 'Thế giới', 0, 12),
(173, 'Ông Trump đấu khẩu với Thống đốc về lệnh cấm người chuyển giới trong thể thao nữ', 'Ông Trump và Thống đốc bang Maine trao đổi nảy lửa khi họp tại Nhà Trắng, liên quan quy định cấm vận động viên chuyển giới tham gia thể thao nữ.', 'Tổng thống Mỹ Donald Trump hôm 21/2 họp cùng thống đốc các bang tại Nhà Trắng, đề cập sắc lệnh hành pháp cấm vận động viên chuyển giới tham gia các môn thể thao nữ mà ông đã ký vào đầu tháng này.\r\n\r\n\"Hai tuần trước, tôi đã ký sắc lệnh hành pháp cấm nam giới tham gia thể thao nữ. Nhiều người đảng Dân chủ đang phản đối tôi vì việc này. Tôi hy vọng họ sẽ tiếp tục làm như vậy vì họ sẽ không bao giờ giành chiến thắng trong cuộc đua nào nữa. Lệnh cấm này là để bảo vệ phụ nữ\", ông Trump tuyên bố.\r\nÔng quay sang Thống đốc bang Maine Janet Mills, người đảng Dân chủ, và hỏi: \"Bà sẽ không tuân thủ lệnh cấm sao\".\r\n\r\n\"Tôi tuân thủ luật bang và liên bang\", bà Mills trả lời.\r\n\r\n\"Chúng tôi chính là luật liên bang. Tốt hơn hết là bà nên tuân thủ, nếu không bà sẽ không nhận được bất kỳ khoản tài trợ liên bang nào nữa\", người đứng đầu Nhà Trắng tuyên bố.\r\n\r\nThống đốc Maine đáp lời: \"Hẹn gặp ngài tại tòa\".\r\n\r\nÔng Trump tỏ ra tức giận và nói: \"Tốt, tôi sẽ gặp bà tại tòa. Tôi mong chờ điều đó đấy, hẳn là chuyện dễ dàng. Hãy tận hưởng cuộc sống của bà sau khi kết thúc nhiệm kỳ thống đốc, vì tôi nghĩ bà sẽ không được bầu tham gia chính trường nữa đâu\".\r\nTheo sắc lệnh, các cơ quan chính phủ Mỹ được phép từ chối tài trợ cho các trường học để vận động viên chuyển giới tham gia đội tuyển nữ. \"Nếu để đàn ông tham gia các đội tuyển thể thao nữ hoặc xâm phạm phòng thay đồ, các bạn sẽ bị điều tra và có nguy cơ mất nguồn tài trợ liên bang\", Tổng thống Mỹ cho hay.\r\n\r\nÔng Trump cho biết sẽ hối thúc Ủy ban Olympic quốc tế (IOC) thay đổi quy định về vận động viên chuyển giới trước Thế vận hội Los Angeles 2028, đồng thời chỉ đạo Bộ trưởng An ninh Nội địa Kristi Noem từ chối đơn xin thị thực \"của những người đàn ông tự nhận là vận động viên nữ và cố gian lận nhập cảnh vào Mỹ để tham gia Thế vận hội\".\r\nMột số quan chức Maine cho biết họ không có ý định thay đổi chính sách cho phép học sinh chuyển giới được chọn đội chơi, trích dẫn Đạo luật Nhân quyền Maine của bang. Bà Mills và tổng chưởng lý bang đã tuyên thệ sẽ chống lại bất kỳ động thái nào nhằm từ chối cung cấp tài trợ liên bang cho bang.\r\n\r\nSắc lệnh của ông Trump đã bị tổ chức nhân quyền Amnesty International chỉ trích, gọi đó là \"cuộc tấn công tàn nhẫn vào những người chuyển giới\".\r\n\r\nTrong bài phát biểu nhậm chức ngày 20/1, ông Trump tuyên bố chính sách của chính phủ Mỹ sẽ chỉ công nhận hai giới tính, nam và nữ, chấm dứt tùy chọn giới tính thứ ba. Vài ngày sau, ông ký sắc lệnh xóa bỏ \"hệ tư tưởng chuyển giới\" trong quân đội và cấm người chuyển giới tham gia quân đội. Ông cũng ban hành lệnh hạn chế các thủ tục chuyển đổi giới tính đối với người dưới 19 tuổi.', '1744547953_h162.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:39:13', 'Thế giới', 0, 17),
(174, 'Ông Trump cho phép quân đội Mỹ kiểm soát đất công dọc biên giới Mexico', 'Tổng thống Trump chỉ thị các cơ quan liên bang cho phép quân đội Mỹ toàn quyền kiểm soát các khu đất công dọc biên giới với Mexico để đối phó người nhập cư.', 'Trong bản ghi nhớ gửi Bộ trưởng Nội vụ Doug Burgum, Bộ trưởng An ninh Nội địa Kristi Noem, Bộ trưởng Quốc phòng Pete Hegseth và Bộ trưởng Nông nghiệp Brooke Rollins ngày 11/4, Tổng thống Donald Trump cho biết quân đội Mỹ phải đóng vai trò trực tiếp hơn trong nỗ lực bảo vệ biên giới.\r\n\r\nÔng Trump yêu cầu các cơ quan liên quan cho phép quân đội Mỹ toàn quyền \"sử dụng và có quyền tài phán\" dải đất công dọc biên giới cho mục đích quân sự, trong đó có xây dựng tường biên giới và lắp thiết bị giám sát.\r\nTrong số các khu đất công mà ông Trump muốn quân đội Mỹ tiếp quản có Khu bảo tồn Roosevelt, nằm trên ba bang California, Arizona và New Mexico. Khu vực bảo tồn trên được cựu tổng thống Theodore Roosevelt thành lập năm 1907 nhằm chống nạn buôn lậu giữa Mỹ và Mexico.\r\n\r\nBiên giới Mỹ - Mexico dài khoảng 3.200 km, trong đó khoảng 2.200 km ở bang Texas. Đường biên giới tại ba bang California, Arizona và New Mexico có tổng chiều dài hơn 1.000 km, đi qua Khu bảo tồn Roosevelt và Khu bảo tồn Quốc gia Tohono Oodhaa, thuộc hệ thống khu bảo tồn thổ dân liên bang. Bản ghi nhớ ngày 11/4 của ông Trump không nhắc đến khu Tohono Oodhaa.\r\n\r\nTheo bản ghi nhớ, do Tổng thống Trump đã tuyên bố tình trạng khẩn cấp tại biên giới phía nam, Bộ trưởng Burgum có thể ra quy định tại các khu đất công dọc biên giới để Lầu Năm Góc sử dụng. Bộ trưởng Hegseth có thể mở rộng phạm vi sang hoạt động sang các vùng đất dọc biên giới bổ sung bất cứ lúc nào.\r\nTài liệu này mở đường để các cơ quan chính phủ Mỹ coi đất công dọc biên giới là cơ sở quân sự. Bất cứ người nhập cư nào vượt qua khu vực này đều sẽ bị bắt vì hành vi \"xâm phạm khu vực quân sự\". Khi bị binh sĩ Mỹ bắt, người vượt biên sẽ bị giữ tại khu vực đó, chờ Bộ An ninh Nội địa điều lực lượng tới tiếp nhận và trục xuất.\r\n\r\nNgay sau khi nhậm chức, Tổng thống Trump lệnh cho quân đội Mỹ tăng cường hiện diện dọc biên giới phía nam để đối phó làn sóng người nhập cư. Hàng nghìn binh sĩ Mỹ đã tới biên giới với Mexico, chủ yếu hỗ trợ Cơ quan Hải quan và Biên phòng Mỹ (CBP) phát hiện và bắt những người di cư tìm cách nhập cảnh bất hợp pháp vào nước này.', '1744548007_h163.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:40:07', 'Thế giới', 0, 0),
(175, 'Vũ khí Đức khó phát huy hiệu quả ở Ukraine vì \'quá phức tạp\'', 'Các đơn vị tiền tuyến Ukraine đang gặp khó khăn với một số hệ thống vũ khí do Đức cung cấp, do mức độ phức tạp và tinh vi của chúng.', 'Trong cuộc trò chuyện với khoảng 200 quân nhân Đức hồi tháng 1, phó tùy viên quân sự nước này tại Kiev cho hay trong khi một số hệ thống vũ khí của Đức được khen ngợi ở Ukraine, số khác tỏ ra không phù hợp với cường độ khốc liệt của chiến sự, tờ Spiegel ngày 10/4 dẫn các tài liệu quân sự nội bộ cho biết.\r\n\r\nTheo tài liệu, các tổ hợp lựu pháo tự hành Panzerhaubitze 2000 (PzH 2000), được mô tả mạnh hàng đầu thế giới và \"vượt trội\" về hiệu suất, nhưng lại quá phức tạp về mặt kỹ thuật, gây hoài nghi về mức độ phù hợp với khả năng tác chiến.\r\n\r\nLoạt xe tăng Leopard 1A5, từng được đánh giá \"đáng tin cậy\", nay được sử dụng chủ yếu như những tổ hợp pháo di động do lớp giáp quá mỏng. Trong khi đó, mẫu Leopard 2A6 hiện đại hơn không thể sửa chữa trên chiến trường do quy trình bảo trì tốn kém và đòi hỏi cao về hậu cần.\r\nLực lượng Ukraine cũng gặp khó với các hệ thống phòng không Đức. Các tổ hợp IRIS-T có mức độ hiệu quả trong đánh chặn đòn tập kích của Nga, nhưng lại hạn chế về số lượng, cùng chi phí đạn dược cao.\r\n\r\nHệ thống tên lửa Patriot do Mỹ sản xuất, thành phần quan trọng trong lưới phòng không nhiều lớp của Ukraine chủ yếu do Đức cung cấp, cũng gặp khó khăn trên chiến trường do các xe vận tải MAN của Đức quá cũ, khó tìm phụ tùng thay thế.\r\n\r\nHệ thống phòng không Gepard được đánh giá là vũ khí thành công nhất của Đức tại Ukraine, \"phổ biến, hiệu quả, đáng tin cậy nhất\" với binh sĩ nước này.\r\n\r\nTờ Spiegel dẫn nguồn tin trong quân đội thừa nhận các phát biểu của phó tùy viên quân sự Đức tại đại sứ quán ở Kiev phản ánh thực tế chiến trường, nơi vũ khí Đức \"hiện đại, song dễ xuống cấp khi hoạt động trong điều kiện khắc nghiệt ở Ukraine\".\r\n\r\nCác khóa huấn luyện cấp tốc dành cho quân đội Ukraine tại Đức cũng không đủ thời gian để hướng dẫn kỹ thuật chuyên sâu. Các cơ sở bảo trì nằm xa tiền tuyến làm trầm trọng thêm thách thức hậu cần.\r\n\r\nĐức là bên tài trợ quân sự hàng đầu của Ukraine tại châu Âu. Berlin cuối tháng 3 thông qua khoản viện trợ hơn 3 tỷ USD cho Kiev sau nhiều tháng trì hoãn, cung cấp thêm nhiều thiết bị quân sự, gồm đạn dược, thiết giáp, hệ thống phòng không, drone.', '1744548064_h164.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:41:04', 'Thế giới', 0, 0);
INSERT INTO `news` (`id`, `title`, `description`, `content`, `image`, `author`, `created_at`, `category`, `is_featured`, `views`) VALUES
(176, 'Hơn 10 năm xung đột ám ảnh trẻ em miền đông Ukraine', 'Một vụ nổ làm cửa sổ trung tâm cộng đồng ở Sloviansk rung lên, phá vỡ bầu không khí tĩnh lặng của nhóm thiếu niên đang ngồi học.\r\n', '\"Đấy là một quả bom lượn\", Bogdan Masliy, 15 tuổi, nói mà không thèm quay ra nhìn.\r\n\r\nBa năm từ khi xung đột Nga - Ukraine bùng nổ, Masliy bây giờ dễ dàng phân biệt được nhiều loại vũ khí đang trút xuống quê hương Sloviansk, thành phố ở miền đông Ukraine.\r\nNgồi thành vòng tròn trên sàn nhà trung tâm Giấc mơ Sloviansk một ngày tháng 3, nơi nhóm thiếu niên đang giao lưu và hướng dẫn các em nhỏ, Masliy và bạn bè kể về những năm tháng xung đột đã tác động tới trẻ em ở Sloviansk như thế nào.\r\n\r\n\"Bây giờ chúng cháu không còn là thiếu niên nữa\", Masliy nói, tỏ vẻ buồn bã.\r\n\r\n\"Cháu muốn cảm nhận mình là trẻ con trong một giây, nhưng không thể\", Anna, 14 tuổi, quê ở tỉnh Donetsk mà Nga sáp nhập năm 2022, cho hay.\r\n\r\nGiao tranh đã nổ ra tại thành phố khoảng 100.000 dân này từ năm 2014, khi phe ly khai được Nga hậu thuẫn nổi dậy ở các thành phố miền đông Ukraine. Masliy cho hay vẫn nhớ những tay súng ly khai và \"những người đội mũ trùm đầu màu đen\" đã kiểm soát thành phố trong ba tháng trước khi bị quân đội Ukraine đẩy lùi.\r\n\r\nXung đột giữa phe ly khai và quân đội chính phủ tiếp diễn ở miền đông cho tới tháng 2/2022, khi Nga đưa quân sang Ukraine. Sloviansk kể từ đó thường xuyên bị tập kích. 11 người, trong đó có một em bé hai tuổi, đã thiệt mạng trong một vụ không kích hồi tháng 4/2023.\r\n\r\nXung đột triền miên làm đảo lộn cuộc sống, buộc hàng triệu người Ukraine phải sơ tán và không ai có thể lên kế hoạch cho cuộc sống tương lai.\r\n\r\n\"Sáng mai cháu vẫn có thể tỉnh dậy\", là câu trả lời của Anna khi được hỏi về hy vọng vào tương lai. \"Bởi vì ở đất nước chúng cháu, không ai có thể biết được sáng mai mình còn thức dậy được hay không\".\r\n\r\nQuân đội Nga hiện chỉ còn cách Sloviansk khoảng 10 km và đang duy trì đà tiến lên khắp tiền tuyến.\r\n\r\nYevgeniia Alfimova, 53 tuổi, người sáng lập kiêm giám đốc Giấc mơ Sloviansk, lấy làm tiếc về tuổi thơ bị đánh cắp của trẻ em trong thành phố, nhưng cũng ngưỡng mộ sự trưởng thành và tự do của những người trẻ ngày nay so với thế hệ trước.\r\n\r\n\"Các cháu khác với chúng tôi. Chúng ta không thể nhốt thế hệ trẻ trong lồng\", bà nói.\r\n\r\nNhiều năm xung đột khiến Sloviansk hoang tàn, dân số chỉ còn một nửa so với thời điểm trước xung đột. Chính quyền thành phố cho biết khoảng 5.500 trẻ em ở đây đang phải học trực tuyến.\r\n\r\nNgoài giờ học, Masliy còn tham dự các khóa sơ cứu và tập đánh dao. \"Mặt trận không còn xa xôi nữa, phải luôn trong tư thế sẵn sàng\", cậu nói. Khi có thời gian, cậu tập luyện cùng ban nhạc rock để có thể \"phiêu theo âm nhạc\".\r\nMasliy cho hay âm nhạc giúp bản thân ngừng suy nghĩ về chiến tranh và những ngày đi sơ tán ở Vinnytsia, miền trung đất nước, nơi cậu bị gọi là \"kẻ ly khai\" và bị đánh giữa phố.\r\n\r\nNhững ký ức này khiến cậu quyết tâm trở thành một người theo chủ nghĩa nhân đạo, người sẽ giảng giải cho người dân cả nước biết về tình hình xung đột ở miền đông Ukraine. Mẹ của Masliy muốn rời khỏi Sloviansk nhưng với cậu, \"mọi thứ của cháu đều ở đây, cuộc sống, bạn bè\".', '1744548133_h165.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:42:13', 'Thế giới', 0, 0),
(177, 'Valentine của những góa phụ Ukraine', 'Natalia cầm theo quyển sách bìa tím, đến trước mộ chồng đọc thơ cho anh nghe trong ngày lễ Tình nhân.', '\"Tôi tặng anh ấy cuốn sách này làm quà kỷ niệm ngày cưới. Một tháng sau, anh ấy tử trận\", Natalia nói, nước mắt chảy dài trên gương mặt trong lễ Tình nhân 14/2 khi đứng trước mộ chồng, Vassyl, binh sĩ Ukraine thiệt mạng ở tiền tuyến và được chôn cất tại thành phố Lviv, miền tây nước này.\r\nNatalia và Vassyl bên nhau 21 năm, có ba con và con út mới 6 tuổi. Vassyl là nhà văn, say mê văn học. Vì anh không có thời gian để đọc quyển thơ vợ tặng, Natalia mang tới nghĩa trang \"để đọc cho anh ấy nghe\".\r\n\r\nGóa phụ mặc áo khoác phao màu đen, mắt đỏ hoe vì xúc động, khẽ đọc bài thơ \"Chẳng ai đã từng yêu\" mà cô thuộc lòng. Cô kẹp đóa hồng vàng ép khô vào giữa cuốn sách, cùng màu với hoa hồng đặt trên mộ Vassyl.\r\n\r\nNatalia không phải người duy nhất có chồng là quân nhân được chôn cất tại nghĩa trang ở Lviv, nơi những quả bóng bay hình trái tim màu đỏ, thú nhồi bông và quốc kỳ được đặt trên các ngôi mộ trong ngày lễ Tình nhân.\r\n\r\nMaria mất chồng, anh Andrey, vào đêm Giáng sinh năm ngoái. Họ chưa bao giờ tổ chức lễ Tình nhân bởi Maria cho rằng \"đây là chiêu tiếp thị\" của những người bán vật phẩm Valentine.\r\n\r\n\"Nhưng không hiểu sao hôm nay tôi vẫn muốn đến\", cô nói. \"Nỗi đau thấu tâm can và thực sự bất công. Thay vì có một cuộc sống tốt đẹp như chúng tôi từng có trước chiến tranh, bây giờ chỉ còn một ngôi mộ trong nghĩa trang\".\r\nMột góa phụ khác cũng tên là Natalia đang chăm chú ghim một trái tim nhỏ lên hoa đặt trên mộ chồng, người tử trận vì một thiết bị bay không người lái tấn công chiếc xe anh điều khiển.\r\n\r\n\"Tôi vẫn chưa chấp nhận được sự thật anh ấy đã không còn trên cõi đời này nữa, tôi sẽ không bao giờ nghe thấy tiếng anh ấy nói, không bao giờ được nhìn thấy anh nữa\", cô nói. \"Chồng tôi rất yêu tôi. Anh ấy hay gọi điện tâm sự, nói yêu tôi. Nếu còn sống, ngày hôm nay anh ấy sẽ chúc mừng tôi\".\r\n\r\nTại Kramatorsk, gần tiền tuyến ở Donetsk, bác sĩ quân y Yaroslav, 30 tuổi, đang chuẩn bị đón lễ Tình nhân năm thứ ba liên tục thiếu bóng vợ. Bất chấp xa xôi cách trở, anh vẫn giữ niềm tin.\r\n\r\n\"Hãy coi đây là một ngày lễ, thế thôi. Chiến tranh là thế. Cuộc sống luôn có lúc khó khăn\", Yaroslav nói.\r\n\r\nAnh khoe bánh macaron socola vợ gửi, cho biết đây là món ưa thích của mình. Yaroslav và đồng đội gửi hoa, kẹo cho vợ hoặc người yêu qua đường bưu điện hoặc chuyển phát nhanh. Anh đã không gặp vợ trong ba tháng và có lẽ phải chờ thêm ba tháng nữa mới được nghỉ phép về nhà.\r\n\r\n\"Tôi rất buồn khi phải xa cô ấy. Thật buồn khi quay lại đây sau kỳ nghỉ phép\", anh khẽ nói, đôi mắt màu xanh dương cụp xuống. Nếu được ở bên nhau trong ngày lễ Tình nhân, \"chúng tôi sẽ không nói chuyện, chỉ ôm nhau thôi\".\r\nCách chỗ Yaroslav không xa là Olga Volodiuk, người bán hoa, đang đợi các đôi tình nhân tới mua hàng nhưng không ai xuất hiện. \"Chợ vắng lắm\", Volodiuk nói, co ro trong chiếc áo phao màu hồng.\r\n\r\nCô cho hay năm nay vắng khách vì chiến sự gia tăng ở Kramatorsk, một trong số ít thành phố còn lại ở miền đông vẫn nằm dưới sự kiểm soát của quân đội Ukraine.\r\n\r\nCác cửa hàng bày bán đầy gấu bông và những món quà lưu niệm sặc sỡ trong ngày lễ Tình nhân nhưng gần như không có người mua. \"Hôm nay tiếng nổ vang lên liên tục\", cô nói. \"Không ai xếp hàng mua bánh mì, người xếp hàng mua hoa càng không có\".\r\n\r\n', '1744548198_h166.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:43:18', 'Thế giới', 0, 0),
(178, 'Đi hay ở - trăn trở của những chàng trai Ukraine sắp 18 tuổi', 'Một tháng trước khi tròn 18 tuổi, Roman Biletskyi quyết định rời nhà lên chuyến tàu về phía tây để ra khỏi Ukraine, tránh nhập ngũ.', '\"Tôi trì hoãn cho tới phút chót\", Biletskyi, chàng trai người Kiev nói từ ký túc xá đại học ở Slovakia, nơi cậu đến hồi tháng 2. \"Đó là chuyến đi một chiều\".\r\n\r\nKhông phải nam thanh niên Ukraine nào cũng đưa ra quyết định tương tự. Andriy Kotyk nhập ngũ ngay khi tròn 18 tuổi, lúc cuộc chiến mới bắt đầu năm 2022.\r\n\r\n\"Tôi đã suy nghĩ thấu đáo mọi thứ và quyết định nhập ngũ\", Kotyk, mặc áo chống đạn và ôm súng trường, nói từ vị trí đóng quân ở vùng Kharkov, đông bắc Ukraine trong lúc đang chờ sửa xe sau khi thoát chết khỏi một cuộc tập kích UAV.\r\n\r\n\"Tôi phải chiến đấu để bảo vệ quê hương\", anh nói. \"Nhập ngũ còn hơn là chạy trốn\".\r\nUkraine cấm đàn ông thành niên ra nước ngoài sau khi Nga phát động cuộc chiến vào tháng 2/2022. Những người mà Reuters phỏng vấn đều nói về tình thế tiến thoái lưỡng nan là ra đi hay ở lại khi sắp đến tuổi thành niên.\r\n\r\nMặc dù đa số chọn ở lại, một số người như Biletskyi đã chọn ra nước ngoài để tránh viễn cảnh thương vong trên chiến trường. Cuộc chiến sắp kéo dài được ba năm, Nga đang chiếm ưu thế và Ukraine đang cố gắng cầm cự trong tình thế suy yếu.\r\n\r\nTheo dữ liệu của Liên minh châu Âu (EU), hơn 190.000 thiếu niên Ukraine trong độ tuổi 14-17 đã đăng ký tình trạng yêu cầu bảo vệ tạm thời ở các nước EU từ khi cuộc xung đột bắt đầu, cùng với hàng triệu người đã rời khỏi Ukraine.\r\n\r\nUkraine đã hạ tuổi nhập ngũ từ 27 xuống 25 vào đầu năm, nhưng đồng minh vẫn liên tục gây sức ép tuyển thêm binh sĩ trẻ dù Kiev phản đối. Ngoại trưởng Mỹ Antony Blinken ngày 4/12 nhận định Ukraine rất khó đưa ra quyết định. \"Nhiều bên cho rằng tuyển thêm thanh niên nhập ngũ là cần thiết. Hiện tại, thanh niên trong độ tuổi 18-25 không phải đi chiến đấu\", ông nói.\r\n\r\nCả Biletskyi lẫn Kotyk đều không hối tiếc về quyết định của mình.\r\n\r\n\"Tôi nghĩ mình sẽ hối hận nếu không ra đi\", Biletskyi nói, kể về tâm trạng phập phồng bất an khi sinh nhật 18 tuổi đến gần và người nhà đã lo lắng thế nào khi chuẩn bị hành lý đưa cậu rời đi.\r\n\r\n\"Đồng hồ điểm từng tích tắc\", Biletskyi nhớ lại. Cậu đang học ngành quản trị kinh doanh tại một trường đại học ở Bratislava, thủ đô Slovakia. \"Chúng tôi hành động theo lý trí. Ai cũng hiểu là tôi phải đi\".\r\n\r\nKotyk tốt nghiệp trường âm nhạc trước khi cuộc chiến nổ ra. Chiến tranh khiến cậu cảm thấy có nghĩa vụ nhập ngũ cùng 4 người bạn. Kotyk đánh dấu tuổi thành niên trong lúc tham gia chiến dịch giành lại thành phố Kherson ở phía nam Ukraine cuối năm 2022.\r\n\r\n\"Hai trận đánh đầu vô cùng đáng sợ\", chàng lính bộ binh nay đã 21 tuổi, nói. \"Sau này tôi đã quen\".\r\n\r\nKotyk thừa nhận cuộc chiến đã thay đổi bản thân sâu sắc. \"Tôi đã hết suy nghĩ trẻ con\", cậu nói, dù vẫn nuôi hy vọng quay lại với đam mê ca hát và lập gia đình trong tương lai.\r\n\r\nKotyk hiểu tại sao nhiều người quyết định rời Ukraine và không muốn phán xét họ, dù việc họ ra đi tăng thêm gánh nặng cho những người lựa chọn ở lại chiến đấu. \"Ai cũng mệt mỏi, cần thay lính mới\", cậu nói.\r\n\r\nMột số quan chức cấp cao, trong đó có cựu ngoại trưởng Dmytro Kuleba, đã công khai chỉ trích đàn ông Ukraine trong độ tuổi nghĩa vụ quân sự đang sống ở nước ngoài khi những người khác chiến đấu và hy sinh vì tổ quốc. Nỗi tức giận này phản ánh cuộc tranh luận gay gắt trong xã hội Ukraine về việc rời khỏi đất nước thời chiến là đúng hay sai, đặt ra vấn đề thù ghét và chia rẽ khi cuộc chiến kết thúc và công dân từ nước ngoài trở về.\r\n\r\nĐộ tuổi trung bình của binh lính Ukraine là 40, theo đại sứ Canada tại Ukraine Natalka Cmoc, dù Kiev không công khai dữ liệu này.\r\n\r\nVolodymyr Davydiuk, sĩ quan tuyển quân cho Lữ đoàn Tấn công số 3 ở Kiev, cho hay quân đội cần nhiều lính trẻ hơn, để mang tới động lực và sức bền cho cuộc chiến. \"Sự chênh lệch của một binh sĩ 40 tuổi với một binh sĩ 20 tuổi khi ra trận là rất lớn\", ông nói.\r\n\r\nLữ đoàn Khartia của Kotyk đặt mục tiêu thu hút những nam thanh niên đang đứng trước \"ngã ba\" cuộc đời như tốt nghiệp cấp ba hay tốt nghiệp đại học. Danylo Velychko, người chiêu binh cho Khartia, cho hay nam thanh niên ít tuổi chiếm một phần nhỏ trong lữ đoàn, trong khi độ tuổi trung bình của người nộp đơn nhập ngũ là trên 32.\r\n\r\nNhu cầu tuyển thêm người không giới hạn trong quân đội ở Ukraine, quốc gia có 41 triệu người sinh sống trước khi hàng chục nghìn người thiệt mạng trong chiến tranh. Nền kinh tế bị ảnh hưởng nghiêm trọng, các ngành nghề đều thiếu hụt lao động khi công dân ra tiền tuyến, tỷ lệ sinh giảm mạnh và người dân ra nước ngoài tránh xung đột.\r\n\r\nCó hơn 87.600 trẻ em chào đời ở Ukraine trong 6 tháng đầu năm 2024, giảm một phần ba so với con số 132.500 trẻ ra đời trong nửa đầu năm 2021. Trong khi đó, Liên Hợp Quốc cho hay gần 7 triệu người Ukraine ở đủ lứa tuổi đã rời khỏi đất nước từ khi cuộc chiến nổ ra. Gần 4,2 triệu người đăng ký ở lại EU tính đến cuối tháng 9.\r\nKiev đang cố ngăn người dân rời đi và khuyến khích người ở nước ngoài quay về. Quốc hội Ukraine ngày 3/12 phê chuẩn bổ nhiệm phó thủ tướng đứng đầu một bộ mới phụ trách đoàn kết dân tộc, chịu trách nhiệm đưa công dân về nước.\r\n\r\nViệc này không dễ dàng, khi Nga đang chiếm ưu thế, hệ thống điện của Ukraine bị tên lửa phá hủy và sự bất định của viện trợ phương Tây trong tương lai sau khi ông Trump đắc cử tổng thống Mỹ.\r\n\r\nBiletskyi đã sống một mình ở Slovakia. Svitlana, mẹ của Biletskyi, hồi tháng 10, đến Slovakia thăm con. Bà rơm rớm khi nhớ về thời khắc vẫy tay tạm biệt con trai trên chuyến tàu rời sân ga Kiev hồi tháng 2.\r\n\r\n\"Tôi đã rất khó khăn để đưa ra quyết định này, nhưng tôi chắc chắn đây là quyết định đúng vì tương lai của con trai\", bà nói.', '1744548253_h167.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:44:13', 'Thế giới', 0, 0),
(179, 'Người Việt kể khoảnh khắc động đất rung chuyển Thái Lan', 'Rung chấn xảy ra khi Minh Hải đang trên tàu điện ngầm Sukhumvik di chuyển về Asok, anh ban đầu ngỡ do mình bị đau đầu, chóng mặt', '\"Sau đó tôi nhận ra tàu rung lắc mạnh rồi dừng hẳn. Bảo vệ yêu cầu toàn bộ hành khách sơ tán khỏi tàu. Tôi cùng mọi người chạy lên mặt đất qua lối khẩn cấp thì thấy người dân đã tràn ra đường\", Minh Hải, 25 tuổi, người làm kinh doanh tại Bangkok, thủ đô Thái Lan kể lại, cho biết rung lắc kéo dài khoảng 3-4 phút.\r\n\r\nĐây là rung chấn xuất phát từ trận động đất mạnh 7,7 độ xảy ra gần thành phố Sagaing ở miền trung Myanmar, quốc gia láng giềng với Thái Lan. Rung chấn từ trận động đất tàn phá nhiều khu vực ở Myanmar và làm rung chuyển nhiều khu vực ở Thái Lan, Trung Quốc, Việt Nam.\r\n\r\nTại thủ đô Bangkok, hàng trăm người đã chạy khỏi các tòa nhà khi chúng rung lắc dữ dội. Tòa nhà Tổng Kiểm toán 34 tầng đang thi công ở quận Bang Sue, phía bắc thủ đô Bangkok đổ sập, khiến 8 người chết, hơn 100 người mất tích.\r\n\"Khi đến kiểm tra hiện trường, tôi nghe thấy mọi người kêu cứu, cầu xin giúp đỡ\", Worapat Sukthai, phó cảnh sát trưởng quận Bang Sue, nói. \"Chúng tôi ước tính có hàng trăm người bị thương nhưng chúng tôi vẫn đang xác định số thương vong chính xác\", ông cho biết.\r\n\r\nJay, 30 tuổi, đang lái xe đi gặp đối tác trên đường Rama II thì mặt đường bất ngờ rung lắc dữ dội. \"Rung chấn kéo dài khoảng 5 giây, khiến tôi cứ ngỡ là do ổ gà. Hai bên đường, mọi người bắt đầu đổ ra ngoài\", Jay, doanh nhân ngành dệt ở Bangkok, kể lại.\r\n\r\n\"Mọi người hoảng loạn chạy khỏi nơi làm việc. Tàu điện ngầm và tàu điện trên cao đều ngừng hoạt động, nhiều người chưa về được nhà\", Somchai Araya, nhân viên văn phòng ở Bangkok, nói.\r\n\r\nHà My, 27 tuổi, đang cùng chồng đến điểm tham quan trong tuần trăng mật ở Bangkok thì rung lắc dữ dội xảy ra, khiến xe taxi chở hai vợ chồng phải cài số lùi.\r\n\r\n\"Tôi đang ngủ trên xe thì nghe tiếng mọi người kêu la. Ai nấy đều tháo chạy, sơ tán hết\", Hà My kể giây phút rung chấn lan đến thủ đô Thái Lan.\r\n\r\nAnh Sáng Trần, doanh nhân ở Thái Lan, đang làm việc ở trung tâm thương mại Sathorn-Narathiwat, Bangkok thì cảm nhận rất rõ rung chấn truyền đến, khiến hàng hóa bên trong đổ ầm ầm và điện bị cắt. \"Nhân viên, khách hàng bỏ chạy tán loạn, cả thành phố rung chuyển\", anh Sáng kể.\r\nNhững người Việt sống nhiều năm ở Thái Lan cho biết đây là lần đầu tiên nước này hứng rung chấn mạnh như vậy. \"Tôi sang Bangkok học tập và làm việc đã được 5 năm, nhưng đây là lần đầu tiên chứng kiến cảnh hỗn loạn như thế\", anh Hải nói.\r\n\r\nChị Nguyễn Thị Hà chiều nay đã không thể tìm được xe đưa đón tới sân bay quốc tế Suvarnabhumi để lên chuyến bay về TP HCM. \"Tôi nghe nói nhiều sân bay hủy chuyến. Tôi đang cố gắng liên hệ với hãng bay để cập nhật tình hình\", chị Hà nói.\r\n', '1744548329_h168.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:45:29', 'Thế giới', 0, 0),
(180, 'Yêu cầu Myanmar đảm bảo an toàn cho người Việt bị tạm giữ', 'Bộ Ngoại giao cho biết một số công dân Việt Nam bị phía Myanmar giữ sau đợt truy quét các cơ sở lừa đảo, yêu cầu giới chức sở tại đảm bảo an toàn cho họ.', '\"Cơ quan chức năng Myanmar đã thông báo về một số công dân Việt Nam đang bị tạm giữ do vi phạm quy định về xuất nhập cảnh vào nước này, sau các đợt truy quét, triệt phá cơ sở lừa đảo trực tuyến tại khu vực Myawaddy gần biên giới giữa Myanmar và Thái Lan\", người phát ngôn Bộ Ngoại giao Phạm Thu Hằng cho biết trong cuộc họp báo hôm nay.\r\n\r\nBà Hằng đưa ra tuyên bố khi được đề nghị cập nhập thông tin về công tác bảo hộ công dân Việt Nam được giải cứu ở khu vực biên giới Thái Lan - Myanmar, sau khi giới chức Myanmar hồi cuối tháng 2 tiến hành đợt truy quét các cơ sở lừa đảo trực tuyến ở Myawaddy, giải cứu khoảng 7.000 người. Hiện chưa rõ có bao nhiêu người Việt trong số này.\r\n\r\n\"Đại sứ quán Việt Nam ở Myanmar đã yêu cầu giới chức sở tại bảo đảm an toàn và điều kiện sinh hoạt cho công dân Việt Nam\", phát ngôn viên Bộ Ngoại giao nói.\r\nBộ Ngoại giao đang phối hợp với các cơ quan xác minh nhân thân của công dân Việt Nam bị tạm giữ, sẵn sàng triển khai các biện pháp bảo hộ, hỗ trợ công dân.\r\n\r\nCác hang ổ lừa đảo trực tuyến thường dụ dỗ nạn nhân trên khắp thế giới bằng lời chào mời về \"việc nhẹ lương cao\", nhưng sau đó bắt họ làm con tin và ép phải tham gia đường dây lừa đảo. Những người từ chối hoặc làm không đủ chỉ tiêu sẽ bị trừng phạt bằng các biện pháp khắc nghiệt.\r\n\r\nKhu vực biên giới giữa Thái Lan và Myanmar, nơi các nhóm phiến quân kiểm soát, là một trong những điểm nóng mà loại tội phạm này tập trung hoạt động. Liên Hợp Quốc ước tính khoảng 120.000 người, nhiều người trong số đó là đàn ông Trung Quốc, có thể đang bị cưỡng ép làm việc tại các trung tâm lừa đảo ở Myanmar.', '1744548401_h169.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:46:41', 'Thế giới', 0, 0),
(181, 'Có công dân Việt Nam trong nhóm 7.000 người được giải cứu ở Myanmar', 'Bộ Ngoại giao xác nhận có công dân Việt Nam trong số 7.000 người nước ngoài được giải cứu khỏi các băng nhóm lừa đảo ở Myanmar tuần này.\r\n\r\n', '\"Cơ quan chức năng Myanmar đang tạm giữ nhiều người nước ngoài, trong đó có công dân Việt Nam, sau khi truy quét các cơ sở lừa đảo trực tuyến tại khu vực biên giới giữa Thái Lan và Myanmar\", người phát ngôn Bộ Ngoại giao Phạm Thu Hằng cho biết trong cuộc họp báo hôm nay.\r\n\r\nTuyên bố được bà Hằng đưa ra khi được hỏi về thông tin khoảng 7.000 công dân nước ngoài tuần này được giải cứu khỏi các băng nhóm lừa đảo trực tuyến ở Myanmar.\r\n\r\nCơ quan chức năng Việt Nam đang làm việc với giới chức Thái Lan và Myanmar để xác minh về số lượng, nhân thân người Việt Nam và kịp thời có các biện pháp bảo hộ công dân nếu cần thiết, người phát ngôn nói.\r\n\r\nBà Hằng cho hay Việt Nam luôn sẵn sàng các biện pháp hỗ trợ cần thiết với những công dân là nạn nhân của các băng nhóm lừa đảo, nhưng cũng hợp tác chặt chẽ với các nước để điều tra, làm rõ, xử lý nghiêm hành vi vi phạm pháp luật của người Việt Nam tại nước ngoài theo đúng quy định, luật pháp nước sở tại.\r\nCác hang ổ lừa đảo qua mạng thường dụ dỗ nạn nhân trên khắp thế giới bằng lời hứa về công việc thu nhập cao, nhưng sau đó bắt họ làm con tin và ép họ phải tham gia đường dây lừa đảo hoặc bị phạt nghiêm khắc. Khu vực biên giới giữa Thái Lan và Myanmar là một trong những điểm nóng mà loại tội phạm này tập trung hoạt động.\r\n\r\nChính phủ Thái Lan gần đây tăng cường nỗ lực chống các trung tâm lừa đảo ở khu vực biên giới Myanmar, gọi đây là \"vấn đề an ninh quốc gia\". Thái Lan đã cắt giảm nguồn cung điện cho một số khu vực giáp biên giới Myanmar, những nơi bị coi là hang ổ của tội phạm lừa đảo.', '1744548459_h170.webp', 'Huỳnh Ngọc Quang Vinh', '2025-04-13 12:47:39', 'Thế giới', 0, 3);
